INSERT INTO `talk_art` VALUES (4243, '女：你很幽默！', '1、彼此彼此，你长得也挺搞笑的\r\n2、这就觉得幽默了？那要是以后做了我女朋友你还不笑死掉\r\n3、不得不说。。。。宝贝，你笑点太低了', '情感链接');
INSERT INTO `talk_art` VALUES (4244, '\\', '1、如果你稍微懂点科学，我就继续跟你聊\r\n2、我可以帮你打下来，但要你自己去捡，下回有流星落下我会通知你\r\n3、天上的星星有什么好的，明天带你去看动物园的猩猩，你会有找到亲人般的感觉', '情感链接');
INSERT INTO `talk_art` VALUES (4245, '女：你和前女友咋个分的', '男：以前的异地恋女朋友装作男的给我发信息，说我女朋友和他在一起了，然后我急了，发信息直接开骂，给女朋友打好多电话，她接了过后 哈哈哈的笑，说是在考验我，老子当时就挂了电话，直接分手 你的考验我过了，我的考验没有你', '情感链接');
INSERT INTO `talk_art` VALUES (4246, '女：你很忙吗', '1、恩，在忙着回答你这个明知故问的问题\r\n2、不怎么忙，也就一天工作 16 个小时而已\r\n3、确实很忙，但如果是你要找我约会的话，再忙我也会考虑下的', '情感链接');
INSERT INTO `talk_art` VALUES (4247, '女：哎呀，你不知道就别说啦~~~', '1、恩，我要对你不耻下问\r\n2、我江湖人称“地上全知道，天上知道一半”，看来这问题属于天上那另一半的\r\n3、我怎么会不知道“哎呀”，跌倒时我都会叫哎呀的', '情感链接');
INSERT INTO `talk_art` VALUES (4248, '女：你怎么老上晚班', '1、因为最近迷上了蝙蝠侠的电影，我要学习他的精神\r\n2、白天紫外线太强，我要为你保护好自己的皮肤啊，省得人家说你未来老公是非洲来的\r\n3、我也想就这个问题让我们领导发表下看法', '情感链接');
INSERT INTO `talk_art` VALUES (4249, '女：没见过你这么傻的', '1、你平时都不照镜子的吗？\r\n2、我也没见过你这种傻到极点还硬说别人傻的厚脸皮\r\n3、（白她一眼，转身背对）：我从来不接傻子的话茬', '情感链接');
INSERT INTO `talk_art` VALUES (4250, '\\', '1、提鞋这么高端的事我肯定做不来，但要提起你扔到垃圾站我还是绰绰有余\r\n2、我从不会歧视眼睛有问题的 妹子', '情感链接');
INSERT INTO `talk_art` VALUES (4251, '女：你真懒，你是懒羊羊吧', '1、你真色，你是灰太狼吧？打算唱狼爱上羊吗？\r\n2、你真勤快，你是来做钟点工的保姆吧？\r\n3、乱说，我。。。算了，懒得否认了。。。\r\n其他答案：你真笨，你是笨羊羊吧？（一直很想爱你 ）', '情感链接');
INSERT INTO `talk_art` VALUES (4252, '女：你喜欢哪个类型的女孩子？', '1、就喜欢你这种类型的，大方可爱美丽善良，你请我吃个感恩饭吧\r\n2、只要不是你这型的其他都可以考虑考虑\r\n3、喜欢在乎我感受的，比如那种问我喜欢什么类型女孩子并打算拿答案来当标准改善自己的', '情感链接');
INSERT INTO `talk_art` VALUES (4253, '女：你忘了我吧！', '1、对不起，请问你是？\r\n2、忘了你倒是小事，但你欠我的青春损失费可不能忘\r\n3、说得轻松。。要忘掉你这张诡异的脸谈何容易', '情感链接');
INSERT INTO `talk_art` VALUES (4254, '女：我好累', '男：出门小卖部就有红牛卖，帮我也带一瓶', '情感链接');
INSERT INTO `talk_art` VALUES (4255, '女：你怎么这么傻。。。', '1、还不是被你传染的\r\n2、我在配合你，你竟然傻得看不出来，还是我配合傻的程度不够到位呢\r\n3、唉。。。都说傻人有傻福，但为什么不是一个美女问我这个问题呢\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4256, '女：怎么你说话我这么不爱听，竟扯些没边的', '1、不爱听可以戴个隔音的耳机，当然，买棉花来塞耳朵可以更省钱\r\n2、我的语言艺术犹如浩瀚汪洋，肯定是无边无际的，你再钻研几年文学兴许能听懂点', '情感链接');
INSERT INTO `talk_art` VALUES (4257, '女：你的微信多少？', '1、冒昧问句-----你不是盗号的吧？\r\n2、想要帅哥微信得先请吃饭，咱们可以边吃边聊', '情感链接');
INSERT INTO `talk_art` VALUES (4258, '女：哪有那么复杂？偶像剧看多了吧', '1、你看过哪部偶像剧女主角长成你这样的\r\n2、抱歉，我没想到这番话对你的大脑来说会很复杂\r\n3、对你应该来个恐怖片的才行', '情感链接');
INSERT INTO `talk_art` VALUES (4259, '女：脑残。。。。智残。。。', '1、你不用说，我也看得出来，我不会歧视你这缺点的\r\n2、你在夸我长得年轻（90 后）吗？\r\n3、我会尽量放慢语速，这样你理解起来会容易些吗？', '情感链接');
INSERT INTO `talk_art` VALUES (4260, '女：你打击不了我的', '1、我打击你干嘛？你觉得自己长的像沙包吗？\r\n2、我的级别还不足以攻破你这么厚的脸皮\r\n3、我不想打击你，踹你两脚就行了', '情感链接');
INSERT INTO `talk_art` VALUES (4261, '女：兄弟', '男：女孩子家矜持点，你该叫我亲爱的', '情感链接');
INSERT INTO `talk_art` VALUES (4262, '女：还轮不到你', '1、其实我没有要参与排队的意思\r\n2、你这个说法让我想起了在洗手间外等包厢拉肚子的场景', '情感链接');
INSERT INTO `talk_art` VALUES (4263, '女：我不爱你，我已经有爱了（她用开玩笑）', '1、早就知道你自恋了，不用说出来\r\n2、是哪个女人，你告诉我', '情感链接');
INSERT INTO `talk_art` VALUES (4264, '女：怎么样~小样', '1、小样叫谁？（非原创）\r\n2、你被做得不合格，通知爸妈，重做小样\r\n3、你这小样真不怎么样', '情感链接');
INSERT INTO `talk_art` VALUES (4265, '女：你说的都是爱你的人会做的事，但是我不爱你呀', '1、暗恋我也是一样的\r\n2、谢天谢地\r\n3、我也不爱上班，但还是照样要为它去做那些事呢', '情感链接');
INSERT INTO `talk_art` VALUES (4266, '我对一个 妹子 说：你不了解我。她答：我没必要', '1、你听过狐狸吃葡萄的故事吗（暗示她吃不到葡萄就说酸）\r\n2、这样婚后性生活会不协调的\r\n3、是没必要，以你的智商就算来了解也是白搭', '情感链接');
INSERT INTO `talk_art` VALUES (4267, '女：“你一定经常这样哄/骗小姑娘？”', '1、我经常这样哄我兄弟\r\n2、大姐，请不要装嫩\r\n3、不一定，偶尔也哄哄你这样的上年纪的女人', '情感链接');
INSERT INTO `talk_art` VALUES (4268, '女：为什么你这几天都不联系我', '男：你不联系我还好意思恶人先告状？？', '情感链接');
INSERT INTO `talk_art` VALUES (4269, '女：我全身最满意的就是我这双腿了 （她人比较矮）', '1、我觉得你更应该为自己的脸皮厚度骄傲\r\n2、我全身最不满意的就是我这双眼，老是看上不要脸的人', '情感链接');
INSERT INTO `talk_art` VALUES (4270, '女：他不用介绍', '1、虽然人家都知道叫我帅哥，但大家叫我的名字我会更喜欢\r\n2、大家还不知道我们的关系呢，你想做隐婚族吗\r\n3、不用担心把我介绍给别人，我是很专一的\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4271, '女：不早了，你早点睡吧！', '1、你不觉得不早了和早点睡是反义词吗\r\n2、你迫不及待要到梦里和我接着聊呀', '情感链接');
INSERT INTO `talk_art` VALUES (4272, '女：你准备叫我出来干吗？~', '1、你想我干嘛？吃饭呗，你别想那些乱七八糟的事情\r\n2、我知道你的担忧，放心，我保证不跟你借钱', '情感链接');
INSERT INTO `talk_art` VALUES (4273, '女：不好意思 我不喜欢个子矮的男生 还是做朋友吧', '1、高个子你够得到吗\r\n2、我不会嫌你没有自知之明的', '情感链接');
INSERT INTO `talk_art` VALUES (4274, '女：你不要操心了，我活该', '1、我是为你父母担心，老人家是无辜的\r\n2、我是在想怎么开口找你拿辛苦费', '情感链接');
INSERT INTO `talk_art` VALUES (4275, '女：你忙', '1、我不盲，两只眼睛都是 1.5 的\r\n2、正忙着跟你聊呢，市场不景气，陪聊费也不好挣了，你看着给就行\r\n3、确实忙不过来，热情的粉丝太多了', '情感链接');
INSERT INTO `talk_art` VALUES (4276, '女：晕，不至于吧？', '1、才一百块你就乐晕了，不至于吧？\r\n2、先别晕，把答案说完再晕，我好去你空间把你踩到醒\r\n3、你不会嫌少吧？要么。。。我再私人赞助 5 块吧！这已经是最大极限了，这个月伙食费全都押给你了，男子汉大丈夫，哪怕饿死，也不能输这个赌，请赐予我力量吧！', '情感链接');
INSERT INTO `talk_art` VALUES (4277, '女：好久不见，你还是那么寒碜', '1、这么久不见，你眼睛还没治好吗？\r\n2、从你身上可以印证品位这东西还真不是时间长就能提高的\r\n3、你会觉得寒吗？可能是肾亏哦\r\n其他答案：好久不见 你还是那么不诚实', '情感链接');
INSERT INTO `talk_art` VALUES (4278, '女：你是不是在约我？', '1、你那么想我约你吗？笑一个给我看，我可以考虑一下\r\n2、对啊，我想约你来我家帮我打扫卫生\r\n3、你会这么问我，是因为我表达能力有问题，还是你理解能力有问题呢？', '情感链接');
INSERT INTO `talk_art` VALUES (4279, '\\', '1、女士优先是中华民族传统美德\r\n2、妈妈说女孩子的话不能随便信，特别是漂亮女孩子\r\n3、我想深刻表达我的诚意', '情感链接');
INSERT INTO `talk_art` VALUES (4280, '已经碰到好几次了,我打压对方的时候,比如我打压女生', '1、你的指甲油抹的很难看。\r\n2、你的发型很乱，该好好整理一下了\r\n3、你脸上的粉抹的很厚\r\n女：这就不用你操心了！或者：这就不用你管了。\r\n1、那不行啊，还指望过年把你带到菜市场卖钱呢，没卖相怎么喊得起价？\r\n2、职责所在，我是城管大队的，今天便衣执勤，你已经影响到市容了\r\n3、我从小就将助人为乐当成人生格言，看到你的瑕疵，一定要帮你指出来的，你不会拒绝这善意的提醒吧\r\n其他答案：我不说管你，我是担心你吓着别人(O°回归)', '情感链接');
INSERT INTO `talk_art` VALUES (4281, '女：干吗呢？', '男：头忽然有点晕，可能贫血了，让我扶一下', '情感链接');
INSERT INTO `talk_art` VALUES (4282, '女：很难约你哦，你总是这么忙的？', '1、我在为了我们的未来奋斗！你不用太感动\r\n2、最近确实有点忙，你可以提前一星期预约\r\n3、你到底想不想约我啊，不然怎么总是挑我最忙的时候约我呢', '情感链接');
INSERT INTO `talk_art` VALUES (4283, '女：你在嗨什么？', '1、看到比我迟钝的人禁不住心情嗨起来\r\n2、以你听完笑话的反应来看，我就是跟你讲估计你也听不懂', '情感链接');
INSERT INTO `talk_art` VALUES (4284, '\\', '1、随便是谁？你不是随便的人，你是我的人（非原创）\r\n2、那你觉得我是个随便的男人吗\r\n3、我只知道我们是同一个类型的人，并且我还知道，我深爱你这种类型的人', '情感链接');
INSERT INTO `talk_art` VALUES (4285, '女：“为什么要认识我啊？”', '1、因为你看起来不像坏人（非原创）\r\n2、刚刚还准备了一个理由，结果跟你说话一紧张，给忘了（非原创）\r\n3、因为我妈逼我相亲，我觉得与其这样还不如自己到街上找个看着讨人喜欢的呢，为了我这辈子的幸福，留个号码给我吧', '情感链接');
INSERT INTO `talk_art` VALUES (4286, '女：你和你女朋友没结婚就住在一齐，要是我，我不会的', '1、以这个封建程度来推测，你们那里应该还保留着父母包办婚姻的习俗吧?\r\n2、没关系，我会给你时间慢慢习惯的\r\n3、亲爱的，人类其实是一直在进步的', '情感链接');
INSERT INTO `talk_art` VALUES (4287, '女： 你好阴险啊', '男：过奖过奖，你一直是我学习的榜样', '情感链接');
INSERT INTO `talk_art` VALUES (4288, '女：“如果我是你女朋友，你愿意花多少时间陪我？', '男：你做了我女朋友就知道了', '情感链接');
INSERT INTO `talk_art` VALUES (4289, '\\', '1、以前的女孩子都只聊一次就主动约我了哦\r\n2、就是因为没有，所以我才约你一起来见证这奇迹的时刻\r\n3、你习惯聊三次才受约吗？不然我先挂电话，马上再打过去补聊一次也行啊', '情感链接');
INSERT INTO `talk_art` VALUES (4290, '女：你和你女朋友好登对啊，什么时候结婚啊？', '1、我结婚，你急什么？真那么急就把红包先包了当订金吧\r\n2、没办法，谁让你不长的美一点呢？（一直很想爱你）', '情感链接');
INSERT INTO `talk_art` VALUES (4291, '好女性化的名字怎么回', '1、很想知道你的名字特殊到什么程度\r\n2、我的名字跟你那男性化的样子正好相得益彰\r\n3、你如果自认为对名字这么有研究，那以后给我们孩子取名的任务就交给你了', '情感链接');
INSERT INTO `talk_art` VALUES (4292, '女：你是什么时候开始想起我的？', '1、在外面吃完饭发现没带钱的时候\r\n2、看动物世界的时候', '情感链接');
INSERT INTO `talk_art` VALUES (4293, '女：“你会只对我一个人好吗？”', '1、不会，我会对很多人好，包括我们的父母，还有我们以后的孩子，\r\n2、那首先得看你够不够乖\r\n3、这么私人的问题。。。请我吃饭就告诉你', '情感链接');
INSERT INTO `talk_art` VALUES (4294, '女：太没意思啦，以后不跟你聊天啦', '1、我们两个的想法在这一刻极有默契地吻合了\r\n2、不聊天你想跟我干嘛？我可不是那么随便的人', '情感链接');
INSERT INTO `talk_art` VALUES (4295, '女：有美女陪伴就不理我了，真是重色轻友啊', '男：你去韩国深造一下整个更美的，我就重你轻她了', '情感链接');
INSERT INTO `talk_art` VALUES (4296, '女：我还没有遇到我想要的人', '1、你想要啊,你想要我会给你的,你想要我不会不给你的,不可能你说要我不给你,你不想要我偏要给你\r\n2、那个人应该感到庆幸还没被你遇到', '情感链接');
INSERT INTO `talk_art` VALUES (4297, '女：我打了好多次电话给你啊，你的电话就是打不通', '1、你帮我交个费就解决了\r\n2、最近在跟人谈一笔 50 块的大买卖，所以打不通很正常，男人以事业为重嘛', '情感链接');
INSERT INTO `talk_art` VALUES (4298, '女：“就我们两个人吗？你为什么不约某某某……？', '1、我在给你制造能与我独处空间，你不懂得感恩还想七想八的\r\n2、我手机上有电筒，不需要另外带灯泡了\r\n3、我的手机中毒了，除了你的号码谁的都拨不通，这个答案你满意否？\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4299, '女：不要吧~我不想告诉你我的电话', '1、老板也不想发我工资，结果还是发了，何况你长得比他善良呢\r\n2、我先把电话告诉你也行\r\n其他答案：不告诉我你的电话，那就告诉我你的电话号码吧 ( 一直很想爱你 转)', '情感链接');
INSERT INTO `talk_art` VALUES (4300, '女：你弄得我几晚都睡不着，快赔偿我的损失', '男：赔什么呢。。。。陪你吃个饭吧，但你要买单哦', '情感链接');
INSERT INTO `talk_art` VALUES (4301, '女：“你总是这样约女孩吗？”', '1、女孩总是这么约我\r\n2、不经常，一个月也就 29 天去那里\r\n3、没关系，去你总是去的地方也行', '情感链接');
INSERT INTO `talk_art` VALUES (4302, '女：“ 你喜欢我嘛？ ”', '1、谈不上喜欢。。。但是很爱\r\n2、喜欢你不要问这种白痴问题了\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4303, '女：“你是只跟我玩玩，还是打算以后走到一起？”', '1、这也是我正想问你的\r\n2、傻瓜，你又不是变形金钢，有什么好玩的', '情感链接');
INSERT INTO `talk_art` VALUES (4304, '女：你多大了，别跟个小孩一样好不好', '男：你见过腿毛（什么毛都行）这么长的小孩吗？', '情感链接');
INSERT INTO `talk_art` VALUES (4305, '\\', '1、你肯定听不懂了，你去猪圈跟它们沟通起来应该比较合拍\r\n2、以你的语言能力，找哆啦 A 梦要一块翻译年糕才能帮你跟人正常沟通\r\n3、你肯定是乔装打扮的外星人！你把 XX（对方的名字）抓哪去了？', '情感链接');
INSERT INTO `talk_art` VALUES (4306, '女：你冷静一下', '1、阿弥陀佛\r\n2、麻烦你帮我搬个冰箱过来，要完全静音的那种\r\n3、。。。。。。。好了，一下已经过去了，我们继续\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4307, '女：你真麻烦，你真烦人', '1、我哪有烦人？你也算人吗？？你是神仙姐姐好不好\r\n2、实际我是你的冤家重金聘请来专门烦你的，能得到你认可，我深表荣幸', '情感链接');
INSERT INTO `talk_art` VALUES (4308, '女：因为在本地生活没有压力，所以没有动力... ', '1、你想要压力的话，可以选择炒股的\r\n2、我可以为你增肥，总有一天压力能达到你的要求\r\n3、据说打点鸡血动力会很足', '情感链接');
INSERT INTO `talk_art` VALUES (4309, '女：我是属于那种又丑又矮脾气又差的那一种', '男：丑一点有安全感，矮一点背起来会轻松些，脾气差可以锻炼我的修养，你就是我的百分百女孩', '情感链接');
INSERT INTO `talk_art` VALUES (4310, '女： 如果我是你的话，我会自己走开', '1、以你的体重，不自己走难道你还想我抱着你走？\r\n2、不用自卑，你其实也还可以的', '情感链接');
INSERT INTO `talk_art` VALUES (4311, '第一次见面，她朋友讲了一整晚枯燥无味的笑话', '1、我怕打扰到你朋友的演讲\r\n2、我在观察研究到底怎样才能将笑话讲得不那么好笑\r\n3、不是不爱说，是没时间说，整晚都在忙着擦飞到脸上的唾沫星子', '情感链接');
INSERT INTO `talk_art` VALUES (4312, '女：君子动口不动手。你怎么这样？', '1、你见过买猪肉不先捏捏肥瘦，就直接当着肉贩子面一口咬上去的？\r\n2、你想我从哪下口？\r\n3、关关雎鸠，在河之洲，窈窕淑女，君子好逑，但。。。。首先你得是淑女我才能做君子', '情感链接');
INSERT INTO `talk_art` VALUES (4313, '女：你叫什么名字？', '1、问帅哥姓名之前先报上自己的名字比较有礼貌呢\r\n2、我告诉你名字，你不会拿出银角大王的紫金红葫芦“咻”的一声把我收了带回去吧？\r\n3、告诉你之前我要先确定你有没有一个叫做死亡笔记的本子\r\n', '情感链接');
INSERT INTO `talk_art` VALUES (4314, '第二次单独见面，她问你一个问题，我还没说完', '1、你的思维很富有跳跃性，看来我得去买只青蛙装口袋里才能跟得上你\r\n2、我知道你对我确实很感兴趣，但能不能等我一个一个的回答\r\n3、还好幸运五十二没请你去当主持', '情感链接');
INSERT INTO `talk_art` VALUES (4315, '女孩空间写很多诗,夸她是才女,她说:“我不是。”', '1、过分的谦虚其实是骄傲的表现，你应该承认并回赠我：谢谢你的夸奖，我想请你吃饭\r\n2、其实不用这么较真，我也就是随便礼貌一下而已\r\n3、那就更棒了，女子无才便是德啊，你一定是个品德高尚的人', '情感链接');
INSERT INTO `talk_art` VALUES (4316, '女：你以为你是谁啊', '1、我就是我啊，你经常搞不清楚自己是谁吗？\r\n2、唱：其实你爱我像谁，扮演什么角色我都会，快不快乐我无所谓，为了你开心我忘记了累不累', '情感链接');
INSERT INTO `talk_art` VALUES (4317, '女：我为什么要听你的？', '1、不听我的就把你拴起来，不喂你肉骨头，饿你几天\r\n2、你猜在精神病院里如果你对医生这么说的话，会得到几次电击？\r\n3、父母之命，媒妁之言，不懂吗？你这个不孝的家伙', '情感链接');
INSERT INTO `talk_art` VALUES (4318, '女：我感觉见你很面熟。或，我们是不是在哪里见过', '1、不要以为装不认识之前欠我的钱就可以赖掉\r\n2、你肯定误会成某位明星了，虽然我确实明星脸，但还是不喜欢别人将我们混为一谈\r\n3、前世，我们是一家，我放牛，你织布，后来被你妈用一条河拆散了。。娘子，我们终于又见面了', '情感链接');
INSERT INTO `talk_art` VALUES (4319, '女：你怎么到现在还没结婚？', '男：我从小心地善良，由于担心你嫁不出去，所以我不忍心断掉你后路', '情感链接');
INSERT INTO `talk_art` VALUES (4320, '女：你越这样我越觉得不可能', '1、本着物极必反的道理，你越觉得不可能我越这样就行了\r\n2、这样不可能我就那样吧\r\n3、你肯定没去李宁买过东西，他们的口号是-----------一切皆有可能', '情感链接');
INSERT INTO `talk_art` VALUES (4321, '女：你别做梦了', '1、请问你都是在不睡觉的情况下做梦的吗？\r\n2、看到你我确实不敢做梦了，会吓醒的', '情感链接');
INSERT INTO `talk_art` VALUES (4322, '女：我们为什么要在一起？', '男：反正不是为了分开', '废w测试');
INSERT INTO `talk_art` VALUES (4323, '女：你好幼稚（小）', '男：成熟与不成熟，只和男人的性格经历与心理年龄相关，和身体年龄无关，最好的体现就是能控制自己的情绪，顾及女性的感受。就像我呵护你一样，现在感觉我是不是熟透了', '废w测试');
INSERT INTO `talk_art` VALUES (4324, '女：你为什么老是给我夹菜', '男：我听说带女朋友参加不熟悉的饭局，不能只顾自己埋头大吃，要给她夹菜，因为她很可能因为害羞而只能看看远处自己喜欢的菜，谁叫我体贴呢', '废w测试');
INSERT INTO `talk_art` VALUES (4325, '女：为什么约我呀', '男：实践是检验真理的唯一标准，所以，让我们遵从组织指示，升级下革命的友谊', '废w测试');
INSERT INTO `talk_art` VALUES (4326, '女：我们还不熟', '男：五秒钟过去了，相信我们已经越过了陌生', '废w测试');
INSERT INTO `talk_art` VALUES (4327, '女：你套路深', '男： 没有套路的人生就像没有高跟鞋的小短裙，少了点趣味', '废w测试');
INSERT INTO `talk_art` VALUES (4328, '女：你怎么总这么贱，非去勾引招惹别人', '男：主要是你太优秀，很多女孩子不服气想挑战你而已，结果我成了无辜的牺牲品', '废w测试');
INSERT INTO `talk_art` VALUES (4329, '女：你为什么总是爱拿我跟你的朋友比？', '1、我想给朋友们树立一个完美的榜样\r\n2、因为你跟我比不了，所以只能跟 TA 们比咯\r\n3、亲爱的，有压力才有动力，我在帮你提高自己呢', '废w测试');
INSERT INTO `talk_art` VALUES (4330, '女：你臭美！', '1、不可能呀，我出门刚喷了香水，怎么会臭\r\n2、你给我来个香的看看\r\n3、臭美也是需要资本的，像你就没办法了', '废w测试');
INSERT INTO `talk_art` VALUES (4331, '女：没有情人的情人节，多少会有落寞的感觉', '1、没有猪骨的猪骨汤，多少会有欺诈的意思\r\n2、没有动车的动车轨，多少会有相信的奇迹', '废w测试');
INSERT INTO `talk_art` VALUES (4332, '女：睡觉好冷', '男：想要我抱就直说，别拐弯抹角的', '废w测试');
INSERT INTO `talk_art` VALUES (4333, '女：你太以自我为中心了！', '1、你玩过旋转木马吗，只要能看到你开心地坐在马背上，我愿意做中心的那根默默无闻的柱子\r\n2、你希望我以你为中心吗？免费给我捏一年的脚就考虑一下\r\n', '废w测试');
INSERT INTO `talk_art` VALUES (4334, '女：你小气！抠门~！', '1、我开门都用钥匙，从来不用抠的\r\n2、你怎么不明白呢？我这么节省还不都是为了我们能有个美好的将来\r\n3、那是肯定的，论大方我哪能跟你比，借点钱来花花呗', '废w测试');
INSERT INTO `talk_art` VALUES (4335, '女：你眼睛多少度（本人是近视眼）', '1、四十五度仰望天空，泪流满面\r\n2、我视力即使再好，也不可能将你看成一个美女', '废w测试');
INSERT INTO `talk_art` VALUES (4336, '女：我说你不好你就是不好', '男：男不坏女不爱，这是你表白的一种方式吗？我很赞同', '废w测试');
INSERT INTO `talk_art` VALUES (4337, '女：我们是两条平行线，永远不可能相交', '男：我不觉得这样保持平行的距离就能产生你的美', '废w测试');
INSERT INTO `talk_art` VALUES (4338, '女：我不想和你走在一起', '男：不用担心，亲爱的，以我的内涵，就算是你和我走在一起，别人也不会因此而看轻我的', '废w测试');
INSERT INTO `talk_art` VALUES (4339, '女：你不要把我想的太好', '1、你多虑了，我的思想从来没有把你跟好字联系起来过\r\n2、你大可不必这么谦虚了，相信我，我完全没有要向你借钱的意思\r\n3、看来你也怕被我发好人卡呀（慎用）', '废w测试');
INSERT INTO `talk_art` VALUES (4340, '女：那你又不找我玩', '1、想跟帅哥玩必须要学会主动点呢\r\n2、我陪玩费用很贵的哦', '废w测试');
INSERT INTO `talk_art` VALUES (4341, '女：一边去', '1、我做不到，我要是去那边了，万一等下哪辆汽车撞到你，谁把你送医院去呢\r\n2、请不要用对宠物的口吻跟你的主人说话\r\n3、哪边？你的左边还是你的右边？', '废w测试');
INSERT INTO `talk_art` VALUES (4342, '女：这顿饭你准备欠多久', '1、准备欠到你良心发现要主动买单的时候\r\n2、山无棱，天地合，才敢请吃饭', '废w测试');
INSERT INTO `talk_art` VALUES (4343, '女：你真的很不要脸也！！！', '1、没错。。。我就是千寻的朋友-----无脸男\r\n2、你这脸要得太多了，搞得脸皮厚得跟千层糕似的', '废w测试');
INSERT INTO `talk_art` VALUES (4344, '女：你太老实了', '1、有眼光！我那些粉丝们都说我长得很老实哦，其实我有一颗不羁的心啦\r\n2、不要以为夸我一句就会请你吃饭！至少。。。你得再夸一遍', '废w测试');
INSERT INTO `talk_art` VALUES (4345, '女：能不能不谈工作上的事情', '1、我非常明白你面对我这样一个帅哥时迫不及待的心情，但我认为男人还是应该以事业为重，你认\r\n为呢\r\n2、那谈什么？谈恋爱？', '废w测试');
INSERT INTO `talk_art` VALUES (4346, '女：你无耻', '1、让我咬一口，你就知道有没有齿了\r\n2、一个导演说过“人不能无耻到这个地步”，而我又偏偏喜欢挑战权威，所以拿你来实践一下\r\n3、你说的很对，而你又这么卑鄙，我们正好配一对呢', '废w测试');
INSERT INTO `talk_art` VALUES (4347, '女：你以前是个坏蛋，为什么这几天变得这么好啦', '男：任何坏蛋跟你比较起来都会显得变好很多', '废w测试');
INSERT INTO `talk_art` VALUES (4348, '女：你说话真有意思', '1、你的脸长得也很有意思\r\n2、觉得有意思吧。。。那就把演讲费给结了吧\r\n其他答案：\r\n1、难道你开始爱上我了？(一直很想爱你 )\r\n', '废w测试');
INSERT INTO `talk_art` VALUES (4349, '女：你是坏人，不理你', '1、你这识别坏人的能力很了不起，但要小心 FBI 把你抓去帮他们破案哦\r\n2、明明知道我是坏人，竟然还敢不理我，你活腻了吗？\r\n3、男不坏，女不爱，你对帅哥表白的方式很特别哦\r\n', '废w测试');
INSERT INTO `talk_art` VALUES (4350, '女：好烦，好烦！！！！', '1、你听过一个成语叫庸人自扰吗\r\n2、我或许能帮你出出主意，你的烦恼是到底怎么才能泡到我是吗？\r\n', '废w测试');
INSERT INTO `talk_art` VALUES (4351, '女：你不要对我笑（我微笑看着她）', '1、不用不好意思，其他 妹子 也跟你一样无法抵御我的微笑\r\n2、对你微笑，纯属礼貌，你比较喜欢无礼的人吗\r\n3、但。。。。我现在哭不出来呀', '废w测试');
INSERT INTO `talk_art` VALUES (4352, '女：今天有个傻 B 男，说我除了长得丑了一点，没啥', '1、你除了嘴贱一点，没啥优点了\r\n2、你除了审美观有问题一点，没啥缺点了', '废w测试');
INSERT INTO `talk_art` VALUES (4353, '女：干嘛？想约我吃饭？', '1、你觉得自己长得像巴菲特吗\r\n2、对呀，你都好久没请我吃饭了', '废w测试');
INSERT INTO `talk_art` VALUES (4354, '女：我很笨的', '1、对于这种公认的事实，就不需要一再重复了\r\n2、你何止笨，你简直就是笨得可爱\r\n3、不用在意，亲爱的这很正常，在我面前任何人都会显得笨点的', '废w测试');
INSERT INTO `talk_art` VALUES (4355, '女：没有房子，没有车，只要我们感情好就好', '1、2020 就要来了，到时那些现在大家看重的房和车之类也许会在顷刻间灰飞烟灭，但即使这样，我还是坚信可以看到我们的感情经过世界末日之后依然飘荡在天堂上空的画面\r\n2、感谢裸婚时代的全体剧组工作人员', '废w测试');
INSERT INTO `talk_art` VALUES (4356, '女：“你這個人有一點主見好不好。”', '1、这。。你的话让我很纠结，我得考虑一下\r\n2、你跟我在一起本来就会自卑，我要是再强势点你怎么活哦？', '废w测试');
INSERT INTO `talk_art` VALUES (4357, '女：我鄙视你', '1、笔试是我的强项，在学校每次都考很高分的哦\r\n2、有一句谚语很适合你------狗眼看人低\r\n3、你得先戴副墨镜，免得被我的光芒灼伤眼睛', '废w测试');
INSERT INTO `talk_art` VALUES (4358, '女：你是 AM，我是 FM，我们根本不是一路人', '1、但是我们的终点在同一个地方，你不觉得这是一种缘分吗\r\n2、还好不同路，不然被我的粉丝们看到你和我一起，你还不被打死掉\r\n3、太相似的频道是无法并存的，你不知道性格能互补才是长相厮守的门票吗', '废w测试');
INSERT INTO `talk_art` VALUES (4359, '女：什么事啊', '男：好像中毒了，神奇的是它竟然会自动联系好友里那些不好看的女孩子！', '废w测试');
INSERT INTO `talk_art` VALUES (4360, '女：你很奇怪呀', '1、帅哥的心理世界你是永远无法理解的\r\n2、我去猪圈喂猪的时候，它们看我应该也很奇怪\r\n3、那你帮我申报个吉尼斯世界奇人纪录吧，得了奖请你吃饭', '废w测试');
INSERT INTO `talk_art` VALUES (4361, '女：今天吹的哪门子风哦', '1、东南西北四个风，你要胡哪个我就给你哪个\r\n2、你感觉到的不是风，而是帅哥强大的气场\r\n3、那得看谁吹，如果是你吹的话，老人家肯定要中风的', '废w测试');
INSERT INTO `talk_art` VALUES (4362, '女：不知道你是不是对每个人都这么好', '1、那是肯定的，但对每个人收取的服务费是不一样的，像你我就会打九折\r\n2、我对其他残障人士也是一样好的', '废w测试');
INSERT INTO `talk_art` VALUES (4363, '女：你没有尽到做弟弟的责任', '男：这位阿婆，请你不要装年轻好吗', '废w测试');
INSERT INTO `talk_art` VALUES (4364, '女：不愿意说算了，不稀罕', '1、你的态度让你失去了做一个狗仔队明日之星的机会\r\n2、物以稀为贵，你给点贵重的东西我才好说啊\r\n3、别急嘛，我不愿意说，但愿意为你唱一段：苏三。。离了洪洞县。。', '废w测试');
INSERT INTO `talk_art` VALUES (4365, '女：你很单纯', '1、是呀，上次有个纯净水公司还来找我谈代言的事呢\r\n2、任何复杂的人跟你一比，都会单纯的\r\n3、哪里是单唇，我有两片嘴唇好不好\r\n其他答案：呵呵，这么夸奖我，我怎嚒好意思呢？（一直很想爱你）', '废w测试');
INSERT INTO `talk_art` VALUES (4366, '女：我看你是闲的没事情做', '1、哪里会没事情做，我不正在忙着逗你玩吗\r\n2、对我调查得很仔细哦，不过你是怎么知道我爱吃咸的呢？\r\n3、你想为我们找点什么事情做呢，我不是那么随便的人哦', '废w测试');
INSERT INTO `talk_art` VALUES (4367, '女：你怎么这么笨，我们相处不了', '1、你没听过笨鸟先飞吗？不笨怎能让我的鸟先飞到你的窝里去\r\n2、为了怕你理解不了我的话，我一直在辛苦地配合着你的智商，你却完全感觉不到，竟然比我预想的还笨啊。。。。\r\n3、你想太多了，其实我没有打算跟傻瓜相处的意思', '废w测试');
INSERT INTO `talk_art` VALUES (4368, '女：我只想找一个真心对我的', '1、要把心掏出来对着你？那太血腥了，不然我去菜市场买个猪心来对着你行吗\r\n2、为了证明我是真心，我不介意去照个心电图给你看，绝不是人工心脏\r\n3、我也想找个能真心接受我爱的人，你是吗？', '废w测试');
INSERT INTO `talk_art` VALUES (4369, '女：呵呵，你好，我不想认识你！', '1、你知道吗，世界上没有不认识的人，只有还没认识的朋友，让我们彼此多一个朋友好吗（非原创）\r\n2、我知道自己就这么错过肯定会留下遗憾，所以才鼓起好大勇气过来的，让我认识你一下好吗（非原创）\r\n3、那你想干嘛，让我请你请饭吗？给我电话号码才能约你哦，你怕我狂打你电话吗，放心好啦，我一天最多也就只打 20 来个（非原创）', '废w测试');
INSERT INTO `talk_art` VALUES (4370, '向妹子要号码,结果妹子说她要换号码了', '1、我们认识的时候你是在用这个号码，所以上面寄宿着我们的缘分啦，无论如何请把这串幸运数字报给我，我照着去买彩票中了分你一半怎么样！\r\n2、没关系的，你把这个号给我至少在你换号前我们还能聊天哦，聊得开心了你再把新号码告诉我就是了', '废w测试');
INSERT INTO `talk_art` VALUES (4371, '女：想跟我约会呀？', '1、是呀，最近准备写一个分析剩女心理的论文，想跟你取取经\r\n2、你说对了一半，我的最终想法是 TD\r\n3、不是，我一个人上街怕被人打劫，有你陪着，歹徒就不敢靠近了', '废w测试');
INSERT INTO `talk_art` VALUES (4372, '女：你怎么天天出去鬼混啊？', '1、我知错了，以后每隔一天去才去鬼混好吧\r\n2、有你在我怎么出去鬼混，鬼都被你吓跑了\r\n3、不出去，难道你让我在家鬼混吗？', '废w测试');
INSERT INTO `talk_art` VALUES (4373, '女：我觉得我还是做你姐姐好了', '1、你怎么知道我喜欢姐姐型的女孩子，很用心哦\r\n2、你虽然看上去老点，但请相信我绝不会嫌弃你的', '废w测试');
INSERT INTO `talk_art` VALUES (4374, '女：我只想认认真真的谈', '男：你说的是谈生意还是谈恋爱？', '废w测试');
INSERT INTO `talk_art` VALUES (4375, '女：我的幸福跟你没关系', '男：我知道你为我好。。。你不想拖累我是吗？', '废w测试');
INSERT INTO `talk_art` VALUES (4376, '女：“是的，这是我的强项。”', '1、我不这么认为，我觉得你的强项是吃饭\r\n2、下次我要挖谁墙角，在 TA 们约会的时候把你带去搞破坏肯定好使', '废w测试');
INSERT INTO `talk_art` VALUES (4377, '女：你还是做我哥哥吧', '1、你是不是韩剧看太多了\r\n2、好呀，妹子先给哥哥抱抱\r\n3、看过射雕英雄传的都知道你在拿郭靖黄蓉暗示我', '废w测试');
INSERT INTO `talk_art` VALUES (4378, '男：“你妈妈是哪个单位的。”', '女：“你管户口啊，问这么多。”\r\n1、不是，我是对哪个单位的员工会比较容易生怪胎感兴趣\r\n2、公务员可是铁饭碗呢，我决定从一名优秀的户籍调查员先做起，先从你练练手\r\n3、实际上，我是想先了解你是否富二代，才好决定我们的进一步发展', '废w测试');
INSERT INTO `talk_art` VALUES (4379, '女：可是我不习惯把电话号码告诉陌生人！', '1、所以我才冒着被拒绝的危险来帮你养成一个能多交朋友的好习惯哦，感动吧？\r\n2、那告诉我 QQ 号也行啊\r\n3、不用告诉我，直接拿我电话拨过去就行\r\n其他答案：你把号码告诉我，我们就不是陌生人了（一直很想爱你）', '废w测试');
INSERT INTO `talk_art` VALUES (4380, '女：多给我带点巧克力回来，我弱弱的又开始流口水', '1、我会跟巧克力说让它把我带回来的，你不用流口水了\r\n2、我更愿意相信流口水只是幌子，你是想和我过情人节的瘾呢', '废w测试');
INSERT INTO `talk_art` VALUES (4381, '男：“我对你妈妈没兴趣。', '女：“好啦，没人要你有兴趣。”\r\n1、你经常对我眉来眼去的，现在的意思是你不承认自己是人咯？\r\n2、但我对你爸爸比较感兴趣，特别是他的银行存款数额\r\n3、能把你教成这样足可见慈母多败儿，我可没兴趣知道她是怎么宠坏你的', '废w测试');
INSERT INTO `talk_art` VALUES (4382, '女：为什么要把电话号码告诉你呢？', '男：因为你长得不像坏人哦（非原创）\r\n女：我就是坏人\r\n1、那太好了，我也是坏人，把号码告诉我下次约你一起去干坏事吧\r\n2、因为。。因为什么来着。。糟了。。我一紧张把本来想好的理由给忘了。。（非原创）你看你害得我多尴尬，留个号码表示歉意吧', '废w测试');
INSERT INTO `talk_art` VALUES (4383, '女：我从一开始对你有感觉 慢慢的就没感觉了', '1、哪里都没感觉吗？会不会是中风了？\r\n2、你这个人最大的毛病就是虎头蛇尾\r\n3、我踹你一脚不信你没感觉\r\n其他答案：据说婚姻是爱情的坟墓，难道说你已经开始幻想和我的婚姻了?(一直很想爱你)', '废w测试');
INSERT INTO `talk_art` VALUES (4384, '女：我很像你的保姆也~！', '1、哪里像了，保姆很贵的好不好\r\n2、不会啊，保姆身材比你稍好一点点', '废w测试');
INSERT INTO `talk_art` VALUES (4385, '女：你滚。。。。 太丑陋了', '1、我不嫌你丑\r\n2、丑媳妇总得见公婆的，不用滚着去，走就行了', '废w测试');
INSERT INTO `talk_art` VALUES (4386, '女：“晕”', '1、 是贫血吗？多吃些红枣\r\n2、白痴，你觉得这样我就会给你人工呼吸吗？\r\n3、生理学上说看到极度爱慕的异性在面前会有眩晕感是很正常的', '废w测试');
INSERT INTO `talk_art` VALUES (4387, '女：你喜欢不喜欢关我屁事', '男：一直听人家说长得丑的女孩子说话都很粗鲁，但你非得亲自验证这点吗', '废w测试');
INSERT INTO `talk_art` VALUES (4388, '女：想搭讪的话，建议你换一个方法', '男：我对你的建议很感兴趣，讲来听听，讲得好可以考虑留电话给你哦\r\n女：那得看你值不值得我建议。\r\n1、那得看你的价值观及不及格\r\n2、我给你 5 块钱，怎么样,还不用找钱哦\r\n3、看你年纪不大，怎么记性这么不好，如果不值得你建议那你刚才怎么会建议我换一个方法呢', '废w测试');
INSERT INTO `talk_art` VALUES (4389, '女：我不干涉你和 N 人聊天但是我介意你回复这么慢', '1、亲爱的，你这样虽然减轻了我的手指负担，但增加了我的心理负担\r\n2、回复慢是因为我在乎你，所以会思考得久些，对别人我一般很快地回复一个“哦”，对你就不一样， 我会回复“哦，原来是这样”', '废w测试');
INSERT INTO `talk_art` VALUES (4390, '女：想象不出来 你也会弹吉他', '1、我还会吹口琴呢，给你试试？\r\n2、我最喜欢的不是吉他，是箫，尤其是喜欢你给我吹奏\r\n3、依你的想象力想象不出来这很正常', '废w测试');
INSERT INTO `talk_art` VALUES (4391, '女：我不需要你喜欢啊', '1、你很有潜质，我的粉丝团就是不需要我喜欢她们，只需要享受往死了追我的过程，可以考虑让你入团哦\r\n2、我做不到！', '废w测试');
INSERT INTO `talk_art` VALUES (4392, '女：想搭讪的话，建议你换一个方法', '男：我对你的建议很感兴趣，讲来听听，讲得好可以考虑留电话给你哦\r\n女：我建议你，离我远一点。\r\n1、你怕自己忍不住来抱我吗？\r\n2、离远点干嘛？你又不咬人\r\n3、虽然距离产生美，但我还是认为离远点看你也不会变得更好看呢\r\n其他答案：这个建议挺好，为了不影响我的品味，确实应该这样做。(一直很想爱你 )看来你还是有点自知自明的哟，知道自己会拖累我的形象(一直很想爱你 )', '废w测试');
INSERT INTO `talk_art` VALUES (4393, '女：我把你微信删除', '1、不用你麻烦了， 我直接把你格式化就行\r\n2、谢谢你放过我，此大恩大德没齿难忘\r\n3、你微信是不是中毒了，最近很流行会自动删帅哥号的病毒呢', '废w测试');
INSERT INTO `talk_art` VALUES (4394, '女：你别老牵我手，你又不是我对象', '1、你认为一定要跟狗处对象才可以遛狗吗\r\n2、我怕不牵着你万一你走散了正好让想找对象的人遇上，会给别人心理带来阴影的\r\n3、我只是手冷握着你取取暖，你怎么就联想到对象了', '废w测试');
INSERT INTO `talk_art` VALUES (4395, '女：我觉得我还是当你妹妹吧~', '1、难怪最近老是被人说-------你妹，原来他们说的是你呀\r\n2、亲爱的你真体贴，我懂你的意思，有这个幌子其他 妹子 就不会乱吃醋了，我也听过张震岳的《干妹妹》哦', '废w测试');
INSERT INTO `talk_art` VALUES (4396, '女：爱我的人和我爱的人肿么总不是同一个呢？', '1 、你妈和你老公能是同一个吗\r\n2、TA 们也是这么想的\r\n其他答案：这说明你的品位和你吸引力不在同一水平线上(一直很想爱你 )', '废w测试');
INSERT INTO `talk_art` VALUES (4397, '女：你肯定和 N 个人聊天', '男：不跟人聊天难道跟狗聊', '废w测试');
INSERT INTO `talk_art` VALUES (4398, '女：我伤不起', '男：我虽然不能支付医药费，但帮你买个创可贴还是可以的', '废w测试');
INSERT INTO `talk_art` VALUES (4399, '女：你觉得我需要暗恋谁吗？', '1、不需要，因为怎么恋都没戏\r\n2、看得出来你会比较直接的表白，如无意外对方应该也会直接的拒绝\r\n3、需要，因为我喜欢腼腆的女孩子', '废w测试');
INSERT INTO `talk_art` VALUES (4400, '女：晚上我怕你牵手然后逃回来的', '1、有那么可怕吗？我上完厕所都有洗手的好吧。。。\r\n2、还好你回去了，我真怕牵了你的手以后，你赶也赶不走就麻烦了', '废w测试');
INSERT INTO `talk_art` VALUES (4401, '女：你永远也学不会', '1、那你学一个我看看\r\n2、送你一句中国移动的口号----我能\r\n3、从措辞可以看出你是典型的悲观主义者，其实女孩子乐观点会老得慢些哦\r\n其他答案：是的，生孩子我怎么也学不会(猪哥亮)', '废w测试');
INSERT INTO `talk_art` VALUES (4402, '女：每次你都没有空？', '1、干嘛每次你都偏偏在我没空的时候出现呢？我不相信能巧到这个程度\r\n2、亲爱的，是我不对，我想为咱们以后打好一个坚实的基础，所以有时不得不忍痛放弃一些和你相处的快乐时光，你把每次都记下来，先存着，我保证以后都会还给你，还附带利息哦\r\n3、我又不是填空题，怎么会有空', '废w测试');
INSERT INTO `talk_art` VALUES (4403, '：我的缺点就是看见 q 上有人就忍不住和他聊', '男：你适合去做专业陪聊呢', '废w测试');
INSERT INTO `talk_art` VALUES (4404, '女：我觉得你很没气质，我不想认识你', '1、就是因为考虑到你没气质我才装成这样过来的，不料倒被你给嫌了，失算啊\r\n2、我和你对气质的理解有不同意见，留个电话给我没事可以探讨一下\r\n3、气质是可以通过后天形成和提高的，而你的长相除了整容就没办法改变了，我是 XX 整形医院的， 留个号码呗', '废w测试');
INSERT INTO `talk_art` VALUES (4405, '女：没意思', '男：去照下镜子你就会发现原来自己长得这么有意思', '异议处理');
INSERT INTO `talk_art` VALUES (4406, '女：我要冷落你', '1、像我平时对你那样？\r\n2、太好了，正好最近有点上火', '废w测试');
INSERT INTO `talk_art` VALUES (4407, '女：好了，你可以走了', '1、请问我刚是被你非法拘禁了吗？\r\n2、走？服务费还没给我呢', '废w测试');
INSERT INTO `talk_art` VALUES (4408, '女：你晚上一般有活动吗？', '1、不对，我是白天晚上都有活动\r\n2、要约我就赶快，等下行程就排满了', '废w测试');
INSERT INTO `talk_art` VALUES (4409, '女：好啊，你的脸经抽吗?', '1、不知道，有个参照我心里才会比较有数，我先试试你的经不经抽吧\r\n2、在你的钢铁脸皮面前我哪敢自夸\r\n3、没抽过不清楚，你经常抽自己吗？', '废w测试');
INSERT INTO `talk_art` VALUES (4410, '女：有女孩子约你出去过吗？', '1、我妈小时候经常约我去动物园的\r\n2、有，但是不多，平均每天两个', '废w测试');
INSERT INTO `talk_art` VALUES (4411, '女：没人要了，怎么办', '1、一般商场里对没人要的东西都是打折或半卖半送的，你可以参考下\r\n2、你先告诉我赔多少嫁妆，我再回答你这个问题\r\n3、亲爱的放心啦，不会没人要的，你见过菜市场猪肉卖不出去么？', '废w测试');
INSERT INTO `talk_art` VALUES (4412, '女：如果我约你呢？ 后果会跟那些女孩子一样', '1、肯定不一样，你的后果要惨很多\r\n2、首先前提是要我愿意接你电话\r\n3、不知道呢，要不我们赌一顿饭，改天约下试试，看看后果会不会一样', '废w测试');
INSERT INTO `talk_art` VALUES (4413, '女：我约你你会赏面吗？', '1、那要看你的约会经费有多少了\r\n2、吃面容易胖，约会的晚餐还是赏你一碗饭吧\r\n3、互相欣赏对方的脸没意思，我觉得一起赏月会浪漫得多', '废w测试');
INSERT INTO `talk_art` VALUES (4414, '你现在一定很开心吧。。。。终于丢了这个包袱', '（包袱是她姐姐。。。说这句话的人是她妹妹。。。。我跟她姐姐分了）\r\n1、你错了，这个包袱会永远压在我心里\r\n2、你是来道贺的？红包带来了吗？\r\n3、不要这样说你姐姐，她是个好女孩，我没这个福气，希望她能找到自己的幸福', '废w测试');
INSERT INTO `talk_art` VALUES (4415, '女：今天你不忙了？', '1、我从来不盲，视力好得很\r\n2、怎么，你见不得我清闲是吧', '废w测试');
INSERT INTO `talk_art` VALUES (4416, '女：你真是越来越幽默了！', '1、你是不是想找我借钱？\r\n2、你也是越长越幽默了', '废w测试');
INSERT INTO `talk_art` VALUES (4417, '女：是哦是哦。嫌问题多我就收起问题。继续', '男：不要盲从，我喜欢有主见的女孩子', '废w测试');
INSERT INTO `talk_art` VALUES (4418, '女： UFO ', '1、看到你就能想到你说的交通工具\r\n2、你想骗我回头去看，然后转头过来时偷吻我吗？\r\n3、嘘，小声点，我们可以拍照卖到报社去赚稿费呢', '废w测试');
INSERT INTO `talk_art` VALUES (4419, '在餐馆里人多都在吃，怎么和二人组的妹子搭讪', '男：请问下你们吃的这个是什么菜呀，看上去好像很好吃', '异议处理');
INSERT INTO `talk_art` VALUES (4420, '女：你能为了我不再去相亲吗？', '1、那要看你怎么对我了\r\n2、能啊，为了你跟别人结婚都行，别说相亲了\r\n3、你能为了我说服我妈不再叫我去相亲吗', '异议处理');
INSERT INTO `talk_art` VALUES (4421, '女：你不是故意的？ 那就是有意的咯！', '1、你有情，我才会有意\r\n2、你有妄想症吧\r\n3、你就这么希望我是有意的呀', '异议处理');
INSERT INTO `talk_art` VALUES (4422, '女：我总感觉你图谋不轨！ （去搭讪的时候）', '1、这都被你看出来了，没错，我专门图谋不轨之徒\r\n2、是啊，我就是坏人，专门来打劫你的，赶紧把电话号码交出来\r\n3、很正常，内心是什么样子的，看世界就是什么样子的', '异议处理');
INSERT INTO `talk_art` VALUES (4423, '女：厉害厉害', '男：我喜欢低调 请用一个低调的么么哒表达你的称赞（进逻）', '异议处理');
INSERT INTO `talk_art` VALUES (4424, '女：我和你妈妈同时落水，你会先去救谁？', '1、我会救未来的妈妈，因为我妈是你未来的妈，你是咱孩子未来的妈（非原创）\r\n2、我让我妈救你，她小时候能在长江两岸游两个来回。”', '异议处理');
INSERT INTO `talk_art` VALUES (4425, '搭讪妹子开场时', '她说：“我想认识你一下”，是吧！这句话我都听了不知道多少遍了~！\r\n1、那太好了，想必接下来的话你也听过无数遍了，以下省略一万字，直接留号码给我吧\r\n2、如果你觉得听腻了我可以换一句-----我想你认识我一下\r\n3、这证明我的品味没有脱离大众哦。 。 。。不过同一句话，从不同的人嘴里说出来，感觉是不一样的，你认为呢', '异议处理');
INSERT INTO `talk_art` VALUES (4426, '女：我不是你想的那种人', '1、我没把你当成人啊，我把你当小宠物看待的\r\n2、你怎么知道我是如何想你的，你又不是我肚子里的蛔虫我知道，实际上你还不如我想的那种人', '异议处理');
INSERT INTO `talk_art` VALUES (4427, '女：我等着你的好消息哦！', '1、你这么一说搞得我压力山大，要是坏消息我就得躲到山里去一辈子不能见你了吗\r\n2、能不能别这么守株待兔，好消息是要主动去争取的\r\n3、不要盲目期待好消息，塞翁失马焉知非福呀', '异议处理');
INSERT INTO `talk_art` VALUES (4428, '女：我不想说话的时候，谁都不想理', '1、我不想理的时候，你再想跟我说话也没用\r\n2、那当然，你都不想说话了，谁还想理你呀\r\n3、那如果你不想说话的时候钱包掉了，我用什么方式提醒你呢', '异议处理');
INSERT INTO `talk_art` VALUES (4429, '女：你这两天都不上线，是上班很忙，累了吗？', '1、是线上粉丝粉丝太热情回应不过来，所以避避风头\r\n2、我两天没在线你都知道，是上班太闲，想我了吗？', '异议处理');
INSERT INTO `talk_art` VALUES (4430, '女：对不起', '男：人这一辈子哪有不犯错的，我没有那么计较（道歉）', '异议处理');
INSERT INTO `talk_art` VALUES (4431, '女：拜托，你这也太假了吧！', '1 、还能假的过你的假发（假指甲，假牙，假胸。。 。 。 ）？\r\n2、还不是因为你，我才被迫昧着良心说这些好听的假话，说真话我怕你受不了刺激', '异议处理');
INSERT INTO `talk_art` VALUES (4432, '女：我喜欢直接,不喜欢罗里啰嗦', '男：我很欣赏你这种直来直去的性格，一点也不像其他那些女孩子扭扭捏捏的，你这样才有男人味', '异议处理');
INSERT INTO `talk_art` VALUES (4433, '女：我不想加你微信', '1、你还真含蓄，那我加你也行\r\n2、你在害怕加了我以后会不小心爱上我吗', '异议处理');
INSERT INTO `talk_art` VALUES (4434, '女：我怕黑', '男：还好我主角光环比较强大 ，可以照亮你内心的黑暗（进逻）', '异议处理');
INSERT INTO `talk_art` VALUES (4435, '女：我喜欢发脾气', '1、 发脾气没关系，不咬人就行\r\n2、那你的肝可能不太好，可以试试吃点鱼肝油\r\n3、我家狗也喜欢发脾气的，你们一定能玩得来\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4436, '女：你说，你到底爱过没有！~', '1、你竟然感觉不到我爱没爱过，说实话我有点失望\r\n2、爱没爱过，不是用嘴可以说出来的，是要用心去感受的', '异议处理');
INSERT INTO `talk_art` VALUES (4437, '女：傻瓜', '1、傻瓜叫谁（感谢杨过）\r\n2、笨到跟你打交道，我确实够傻的\r\n3、傻一点好，太聪明了跟你就没有共同语言了', '异议处理');
INSERT INTO `talk_art` VALUES (4438, '女：小弟弟，给姐姐笑一个！', '男：姐姐，看着你悲剧的脸，我笑不出来', '异议处理');
INSERT INTO `talk_art` VALUES (4439, '女：你把我当什么了~？！', '男：你就是你，不会因为别人把你当什么而有任何改变，我喜欢的就是你这一点', '异议处理');
INSERT INTO `talk_art` VALUES (4440, '女：话都说不清楚', '1、我一见你就紧张，所以话就说不怎么清楚了，你别往心里去，从小到大我看到狗都会这么紧张的\r\n2、看来你不太明白说不清楚和理解不了这两个概念的分别呀', '异议处理');
INSERT INTO `talk_art` VALUES (4441, '女：你什么学历啊？', '1、小学本科\r\n2、社会大学研究生', '异议处理');
INSERT INTO `talk_art` VALUES (4442, '女：难道我对你就是可有可无吗？', '男：对，我会让你拥有最开心的感觉，将烦恼忧愁都忘得无影无踪', '异议处理');
INSERT INTO `talk_art` VALUES (4443, '女：睡觉了', '男：允许你做个有我的梦（进逻）', '异议处理');
INSERT INTO `talk_art` VALUES (4444, '妹子总有意无意在我面前提起别人的好车高价值', '男：任何车子，我都可以通过赚钱买到，但我这里面的东西（指着自己脑袋） ，可不是钱能买到的', '异议处理');
INSERT INTO `talk_art` VALUES (4445, '\\', '1、打两个字“真的”再加 QQ 表情花发过去\r\n2、中国人送东西讲究礼尚往来，你要回我什么花，你的小菊花么\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4446, '女：我生气了，老是你说我...', '男：那轮到你说我..把我往死里说..\r\n女：我说过我不擅长打架和吵架的..\r\n男：所以我现在才要通过说你来锻炼自己，要是你我都不擅长，以后别人找你吵架我怎么保护你', '异议处理');
INSERT INTO `talk_art` VALUES (4447, '女：见面后多久能知道是不是合适？我的答案是15秒', '1 、哪用 15 秒，我如果看到恐龙 1 秒钟就能知道不合适\r\n2 、那我一定要争取练到 14 秒，以后见到你这型的在你 15 秒觉得我合适前先 PASS 掉你', '异议处理');
INSERT INTO `talk_art` VALUES (4448, '女：你情人节是怎么过的啊~！', '男：被一堆情人追着跑，跑着跑着，摔了一跤，就醒了', '异议处理');
INSERT INTO `talk_art` VALUES (4449, '小妞生气发火的时候我不哄她，她说我不在乎她 我哄她她又说我故意让她生气', '1 、下次她再生气发火就问她：你觉得我是哄你好还是不哄你好\r\n2、你生气的样子比平时可爱很多哦\r\n3、听说王老吉可以降火，你去买一罐来喝呗，给我也带一罐\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4450, '女：人家看到你会害羞', '1、原来，害羞是不分脸皮薄与厚的\r\n2、这没什么的，一般女孩子看到我都会害羞，你习惯就好了\r\n3、我最想看的，就是你害羞的样子，连你都会害羞，那多不可思议啊', '异议处理');
INSERT INTO `talk_art` VALUES (4451, '女：我已经习惯一个人了', '1、这可是不良习惯，要是以后年纪大了走不动路，得有人扶你上厕所的\r\n2、习惯哪个人？我吗？', '异议处理');
INSERT INTO `talk_art` VALUES (4452, '女：也许我们都会变！', '1、对，变得越来越恩爱\r\n2、当然都会变，我会越来越成熟，你会越来越懂事，到最后，还能从无到有变出一个小孩来\r\n其他答案：\r\n1、女大十八变，你现在多大了？\r\n2、是的呀，我们都会慢慢变老呀！\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4453, '女：你好小气啊', '1、我请你吃面包还叫小气啊，你不请回我也就算了，连姐妹也不请才叫小气呢\r\n2、聪明的女人都喜欢小气的男人，这样结婚以后他们才不会在别的女孩子身上多花一分钱', '异议处理');
INSERT INTO `talk_art` VALUES (4454, '女：我讨厌你，因为你触到我的雷点了。 。。', '1 、下次跟你聊天我带根避雷针绑头上就不会了\r\n2、 我更讨厌你，因为你雷到我了', '异议处理');
INSERT INTO `talk_art` VALUES (4455, '女：男人靠得住？我不信！', '1、这个问题你的去问问你妈，看她是怎么靠爸把你养这么大的\r\n2、井里的青蛙也不相信世界原来不止井口大小，但它不相信，不代表不存在\r\n3、所以你打算做拉拉吗\r\n其他答案：看来你受伤挺重的。 （ 一直很想爱你）', '异议处理');
INSERT INTO `talk_art` VALUES (4456, '女：你希望我开心吗？', '1、当然希望，但前提是不能将你的开心建立在我的痛苦上\r\n2、那得看你是跟我一起开心，还是跟别人\r\n3、不希望，人的心脏打开会挂掉的，我希望你能快乐地活着', '异议处理');
INSERT INTO `talk_art` VALUES (4457, '女：我想找一个好男人！~', '1、我想找一个值得让我变成好男人的女人\r\n2、你是想找个穷光蛋吗，男人一有钱就变坏你不知道么\r\n3、那还得从自身做起，先提高自己的魅力，不然再好的男人遇到你也会变坏的', '异议处理');
INSERT INTO `talk_art` VALUES (4458, '女：我可没对你承诺什么哦~', '1 、才碰一下就提到承诺了，你想太多了吧\r\n2、千万别承诺什么，到时候甩不掉可就麻烦了。。', '异议处理');
INSERT INTO `talk_art` VALUES (4459, '女：我没你想象的那么好', '1、我也没你想象的那么认为你好\r\n2、我知道，我只是礼貌一下而已，', '异议处理');
INSERT INTO `talk_art` VALUES (4460, '女：大白天的你就少做梦吧你！', '1、大白天的梦到你还好点，要是大半夜的梦到你那得多恐怖\r\n2、我只有催眠自己这是在梦里，才不会觉得跟你相处那么浪费时间', '异议处理');
INSERT INTO `talk_art` VALUES (4461, '女：要不要吃啊？~', '1 、要吃啊，我要你喂，看你会不会照顾人\r\n2、不吃，我的那份省下来留给你吃吧，你长胖点抱起来比较有手感\r\n3、怎么每次你提到吃的话题，眼睛里都会发光呢', '异议处理');
INSERT INTO `talk_art` VALUES (4462, '女：你没性格', '1、还好我没性格，有性格的如果别你这么说，这个时候就该抽你了\r\n2、我当然没姓格，我姓 XX(你的姓氏)好吧\r\n3、能耐着性子跟你聊天，本来就是一件很有性格的事', '异议处理');
INSERT INTO `talk_art` VALUES (4463, '女：我到现在都想不通', '1、想不通我为什么长得这么帅吗\r\n2、那就别想了，你那本就不多的脑细胞经不起这么折腾的\r\n3、要不要我帮你通通，专业疏通管道，价格可以优惠', '异议处理');
INSERT INTO `talk_art` VALUES (4464, '女：“看你年龄不小了，怎么给人感觉老是像小朋友啊”', '1、 我承认，我确实长了一张娃娃脸\r\n2、看你年龄还小，怎么总是装成一副老气横秋的样子\r\n其他答案\r\n1、看你年龄不大，怎么给人感觉老是像老太婆啊？\r\n2、你是在羡慕我的快乐吗？', '异议处理');
INSERT INTO `talk_art` VALUES (4465, '女：大锅！', '男：你要是把“锅”，改成“爷”的话 我会更喜欢！\r\n女：是改成“孙”吧！ ！~\r\n1 、孙悟空是和尚，不近女色的，那咱俩这事就得黄了', '异议处理');
INSERT INTO `talk_art` VALUES (4466, '女：我们重新开始吧！~', '1 、一直就没结束过啊\r\n2、你听过好马不吃回头草吗，还好我是人，可以回头，浪子回头金不换哦\r\n3、你好，刚在那边看到你，觉得你挺有气质的，就想过来认识你一下，我叫 XX，你呢？', '异议处理');
INSERT INTO `talk_art` VALUES (4467, '女：我妈说 我未来的老公 不能太穷 也不能太有', '1、不会吧，你妈认识我？\r\n2 、不能全听家长的，我爸说，我未来的老婆，不能太笨，也不能太聪明，要是听他的你这么笨我就没法跟你好了\r\n3 、这要是照着你妈的标准去努力，我也太纠结了，穷还能努力赚，难就难在赚到一半发现自己变得太有钱时还得想办法往外散财啊', '异议处理');
INSERT INTO `talk_art` VALUES (4468, '男：你干嘛一直盯着我看啊。', '女：你不看我怎么知道我看你啊。\r\n1、你能不能回答得更老套一点\r\n2、拜托，我都快被你的目光灼伤了，哪还用得着看', '异议处理');
INSERT INTO `talk_art` VALUES (4469, '妹子 的 QQ 签名：陈老妈杀了我的老鼠（她宠物）', '1、没事，我家厨房最近老鼠很多，抓一只送你吧\r\n2、替你们家猫谢谢她吧', '异议处理');
INSERT INTO `talk_art` VALUES (4470, '打压，开玩笑过度后怎么解决', '1、你不是吧，这么开不起玩笑啊。。好了好了，我错了，我不是人，我是 XX（她的名字）\r\n2、可能我的思维方式太前卫了，导致你接受不了，要不我倒退个几十年怎么样----姑娘，在下有礼了\r\n3、 我发现你一个很大的优点，你现在生气的样子，真可爱', '异议处理');
INSERT INTO `talk_art` VALUES (4471, '女：搞那么正式干什么', '1 、因为我跟你不一样，我是一个有礼貌有素质并懂得尊重的人\r\n2、你现在看到的是我外表很正式，其实此刻我的内心相当不羁\r\n3、你就那么喜欢看到我穿着睡衣拖鞋出现在你面前的样子？', '异议处理');
INSERT INTO `talk_art` VALUES (4472, '女：好啊，那你慢慢等着吧~', '男：等待这段时间，你找个小妞陪我聊聊天解闷啊，实在没有你也凑合了', '异议处理');
INSERT INTO `talk_art` VALUES (4473, '如果女孩子叫你送东西（手机）给她', '（但又不能让人说你小气，应该怎么回答才好， 刚认识不久）\r\n1 、我没有送东西给女孩子的习惯，平时都是人家送东西给我的\r\n2、（捏她的脸） 也不怎么厚啊，那这种要求你是怎么能提的出口的呢\r\n3 、给我一个合理的理由\r\n4、不过这种情况被说小气是几乎一定的， 这只是女人的招数而已', '异议处理');
INSERT INTO `talk_art` VALUES (4474, '女：男人负责赚钱养家，女人负责貌美如花。 。。', '1、你先去整成貌美如花的样子再来问这个问题吧\r\n2、赚钱养家只要努力就能做到，但你再怎么努力也无法貌美如花养得起养不起跟你有半毛钱关系啊，又不养你', '异议处理');
INSERT INTO `talk_art` VALUES (4475, '女：单纯 执着没什么不好，只要是对的就行！', '1 、关键是一般单纯和执着的人，最难分清楚对错\r\n2、那如果像你这么单纯的人，执着地追求我，我该怎么办\r\n3、世事无绝对，除了对和错这两面还有灰色地带，你让单纯的人怎么分辨，执着的人怎么妥协', '异议处理');
INSERT INTO `talk_art` VALUES (4476, '女：你就想着钱，你俗 俗不可耐！', '1、我没想钱，我在想你，但我觉得自己更加俗不可耐了\r\n2、你不想俗不可耐，那就把你的钱都给我，让我一俗到底吧\r\n3、我觉得又想着钱又打死不愿意不承认的才俗不可耐呢，我没说你啊，说你也不会承认嘛\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4477, '女：其实我不是故意的，你信不信！', '1、我要是信了以后咱们的孩子智商会变低的\r\n2、你猜我信不信\r\n3、信啊，一看你就不是故意的，故意的那个应该是我（夸张讽刺）', '异议处理');
INSERT INTO `talk_art` VALUES (4478, '女：我劝你还是省点心，少打我主意！', '1、你有被害妄想症吗\r\n2、像你这么厚的脸皮，我怎么敢打你主意\r\n3、这你大可放心，我对女孩子打主意也是有标准底线的', '异议处理');
INSERT INTO `talk_art` VALUES (4479, '女：你是开车来的吗？~', '1、我没开车，是司机开车送我来的\r\n女：那司机呢\r\n男：司机去载其他客了\r\n2、 你这么一问，我想如果在古代，我是不是应该骑白马来', '异议处理');
INSERT INTO `talk_art` VALUES (4480, '女：你的口气很像领导哦！', '1、我刷了牙的，哪来的口气\r\n2、你这口气比我更像好吧\r\n3、还好我长得不像\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4481, '女：你在哪上班·····················', '男：地球，专门保卫地球和平，干掉各种怪兽，你就是我下一个目标(你是坐什么的)', '异议处理');
INSERT INTO `talk_art` VALUES (4482, '女：我觉得你很龌蹉！~', '1 、智者说过，内心什么样，看别人就是什么样\r\n2、是不是所有对你不感冒的帅哥你都认为人家龌龊', '异议处理');
INSERT INTO `talk_art` VALUES (4483, '女：连这个你也不知道啊？', '1、没有什么事物是必须要知道的，你不知道吗\r\n2、我知道有百度就可以了\r\n3、我所有的无知还不是为了能给你点优越感让你不再自卑嘛\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4484, '女：谁像你这么没良心！ ！', '1、空心菜\r\n2、陈世美\r\n3、我确实没有，因为我的心已经因为你这句话，碎掉了\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4485, '女：做女人，如果心不够狠，死的就是自己！', '1、恭喜，最毒妇人心在你身上得到了完美的诠释\r\n2、你心中想象两个人的世界是什么样子，就会得到什么结果，如果想象成厮杀的战场，那么你永远只是一名狠辣的战士，即使干掉了敌人，却伤害了自己；如果想象成美丽的天堂， 那你将会变成快乐的天使，将幸福布满到两个人的心房\r\n其他答案：你现在心够不够狠？', '异议处理');
INSERT INTO `talk_art` VALUES (4486, '女：你要是敢骗我，小心我打你~', '1、放心，如果以后你得了绝症，我一定会老实告诉你的\r\n2、别这样啊，搞得以后我都不敢称呼你为美女了没关系，我打算骗走你的心，然后让你一辈子追着我打', '异议处理');
INSERT INTO `talk_art` VALUES (4487, '女：刚刚转发了一部电影', '1、我品味这么高，我觉得好看的估计你不一定觉得好看吧\r\n2、雇佣我帮你试看电影是要收费的刚看完了，我觉得没你好看', '异议处理');
INSERT INTO `talk_art` VALUES (4488, '女：是不是我想要什么，你都会给我啊~！', '男：没错，想要什么就给你什么，只要你付得起钱，我就能去买', '异议处理');
INSERT INTO `talk_art` VALUES (4489, '女：没有我的份，我去干什么！~', '1、红花也需绿叶，你去当陪衬啊\r\n2、去给我当备胎打酱油啊', '异议处理');
INSERT INTO `talk_art` VALUES (4490, '女：同事说，你平时啥都不干的', '1、你这么说的意思是想告诉我你暗恋我然后帮我默默干了很多活吗？不过很遗憾我不会喜欢颠倒黑白的类型\r\n2、你的意思就是说领导失察？谁做事谁没做事他看不到？我表示完全不能认同你的看法\r\n3、（端详她的脸）好厉害，说谎话你的脸竟然完全不会红哦\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4491, '女：是真的心里有我还是哄我开心呀 ？~', '1、当然是哄你开心啊，不先把心打开怎么把你装进去呢\r\n2、心里有你的人不一定会哄你开心，有可能是你欠了钱不还才能一直记在心里的呢\r\n3、让你问出这个问题，我觉得自己真的很失败（最近很火的万能答案，套用下很有意思）', '异议处理');
INSERT INTO `talk_art` VALUES (4492, '女：你说话的语气很像我前男友／你性格和我前男友', '1、那当时是你甩他还是他甩你\r\n女：我甩他/他甩我\r\n2、因为他虽然脾气像但长得不像我对吗/那你做好被同一个性格甩两次的心理准备没\r\n3、 我跟他性格肯定不一样，因为以我的性格是不会跟你分手的\r\n女：是我跟他分的手\r\n4、那也不一样，要是以我的性格，你跟我分手，你就不会活到现在还能跟我说话', '异议处理');
INSERT INTO `talk_art` VALUES (4493, '女：我想要天上的星星！', '1、动物园的猩猩要不要\r\n女：要\r\n2、你去买个镜子自己照一照就有了\r\n3、去打盆水来放地上，天上如果有星星就会装里面了\r\n4、我扇你两个大嘴巴子或者你饿个几天就能有了', '异议处理');
INSERT INTO `talk_art` VALUES (4494, '女：你是闷骚型的', '1、你是狂骚型的\r\n2、这你都能看出来，同道中人啊你不知道含蓄是中国传统美德嘛', '异议处理');
INSERT INTO `talk_art` VALUES (4495, '女：我觉的你没爱心，不善良~', '1、我觉得你没脑子，不善观察\r\n2、我是一个有原则能独立思维的人，爱心也是要分对象的，善良也是要分场合的，对蛇和狼有爱心就会变成农夫和东郭先生\r\n3、我承认他们比我善良多了', '异议处理');
INSERT INTO `talk_art` VALUES (4496, '女：难道我买的这东西，真的被宰了？', '1、 你肯定没被宰，宰死了是没法跟我说话的\r\n2、以你的智商来看，不宰你就是商家智商有问题', '异议处理');
INSERT INTO `talk_art` VALUES (4497, '女：你是我无法言语的伤痛', '男：老师教我们，喝点热水能缓解疼痛', '异议处理');
INSERT INTO `talk_art` VALUES (4498, '女：为什么我会有那么多不顺！我真想大声哭出', '1、哭解决不了任何问题，找到问题的根源然后解决才是正确的选择。不顺的话可能是大便太干燥，用甘油可以快速顺畅\r\n2、想哭就到我怀里哭吧，不收你慰安费\r\n3、世界上任何事皆有两面，只要仔细思考，你就能看到好的那面，善于找到好的面，你会活得轻松很多', '异议处理');
INSERT INTO `talk_art` VALUES (4499, '女：随你怎么说喽', '1、随你是谁？她是不是想通过你接近我？\r\n2、天呐，我可不是那么随便的人。我说话可是很谨慎的\r\n3、不要以为你做出无所谓的态度，我就会不收费了。别人听我相声可以要买票的呢', '异议处理');
INSERT INTO `talk_art` VALUES (4500, '女：我生君未生，君生我已老,此生无缘！', '1、很多老富婆和小帅哥都挺有缘分的好吧\r\n2、不会啊，我向来都很尊敬老人家的\r\n3、有一个词语叫穿越你知道吗，其实我是从你生下来的时代穿越过来的哦\r\n', '异议处理');
INSERT INTO `talk_art` VALUES (4501, '女：我前男友居然喜欢上我的闺蜜！', '男：一点也不奇怪，爱屋及乌嘛', '异议处理');
INSERT INTO `talk_art` VALUES (4502, '女：我的婚礼你一定要来！', '1、喜欢那种抢婚的感觉？偶像剧看多了吧？\r\n2、当然得来，哪有婚礼没新郎的\r\n3、是以大话西游里孙悟空参加牛魔王婚礼的那个形式出现吗', '异议处理');
INSERT INTO `talk_art` VALUES (4503, '\\', '1、你是计生办的还是人口普查的还是八卦门的？\r\n2、问题 1 想要几个孩子取决于我老婆， 她不想生了我就不要了.问题\r\n3 、生男孩女孩都喜欢，生女孩我保护老婆和女儿，生男孩我和他一起保护妈妈（非原创）', '异议处理');
INSERT INTO `talk_art` VALUES (4504, '女：我又不要你的！小气鬼！', '1、我完全没打算对你大方，你又不是我老婆\r\n2、我智商跟你不一样，激将法这套对我没用的', '异议处理');
INSERT INTO `talk_art` VALUES (4505, '女：是我做错了什么吗？', '1、在你疑惑的时候，请记住，世上的事情，本没有绝对的对错，不同的立场有不同的观点，请相信自己，无论结果如何，也没有任何人有资格说你是错的\r\n2、做错并没关系，有关系的是你连自己做没做错都不知道，更有关系的是你居然还来问毫无关系的我', '异议处理');
INSERT INTO `talk_art` VALUES (4506, '女：你打算在我结婚的时候给多少红包钱啊？~', '1、那得取决于你在我结婚的时候包多少\r\n2、没听过自己结婚还给自己包红包钱的这个', '异议处理');
INSERT INTO `talk_art` VALUES (4507, '女：我爱你 不是说说而已！', '1、恩，除了 QQ 说说，还可以发微博呢\r\n2、不用买礼物表真诚这么佣俗吧', '异议处理');
INSERT INTO `talk_art` VALUES (4508, '女：我对你特别动心怎么办呀 ！', '1、那就表白啊\r\n2、请我吃饭啊', '异议处理');
INSERT INTO `talk_art` VALUES (4509, '女：小样，我不打你就好了~', '男：那你也不能亲我，亲我要给钱的', '异议处理');
INSERT INTO `talk_art` VALUES (4510, '女：我是个很普通的人~', '1、但你认识了我，从此将不普通\r\n2 、完了，我是个很文艺的人，以后咱们的孩子会不会是 2B 啊\r\n3 、我喜欢跟普通的人一起做不普通的事，那感觉一定会超级棒', '异议处理');
INSERT INTO `talk_art` VALUES (4511, '女：老实交代！', '男：你不对我动之以利，晓之以情，让我怎么老实交代', '异议处理');
INSERT INTO `talk_art` VALUES (4512, '女：今天我碰见一个帅哥 我有点心动！~', '男：心动？想到我了是吗', '异议处理');
INSERT INTO `talk_art` VALUES (4513, '女：笨', '1、大家都知道你笨，不用说出来\r\n2、智商笨到每句话只能说出一个字？\r\n3、那也要看跟谁比，跟你比我一下子就显得聪明了', '异议处理');
INSERT INTO `talk_art` VALUES (4514, '妹子 不小心撞了我一下，我该如何搭讪她！', '1、请不要触碰高价品，碰坏你赔不起的（非原创）\r\n2、哎呀，好痛，我被你撞骨折了，你要赔我医药费，误工费，精神损失费。。 。 。', '异议处理');
INSERT INTO `talk_art` VALUES (4515, '女：我们没有共同话题', '1、干嘛要有共同话题，你说你的话题我说我的话题，这样才可以互补\r\n2、其实我也不介意跟你聊聊肤浅点的话题', '异议处理');
INSERT INTO `talk_art` VALUES (4516, '女：我们不合适，没有未来', '1、未来怎样，谁说了也不算，未来是靠我们一起开创的\r\n2、我不会嫌弃你的，未来我会给你时间改\r\n3、我也觉得不合适，要不你去整个容？', '异议处理');
INSERT INTO `talk_art` VALUES (4517, '\\', '你房子买了多少钱，装了多少钱，你家里人是干什么的之类的问题，目的无非是了解我的家底，看我有没有关系，有些甚至通过这些来对比，奚落，该如何以话术来应对。\r\n1、（对无恶意男人）从你充满智慧的面相看，我觉得咱们聊的话题可以更有技术含量一点\r\n2、（对女孩子）我现在有种仿佛在相亲的感觉，我对相亲有恐惧感，咱们换个别的话题好吧\r\n3、（对恶意者）现在的社会到底怎么了，一见面就非得问这么恶俗的问题吗', '异议处理');
INSERT INTO `talk_art` VALUES (4518, '女：你的 QQ 怎么是 8 开头（8 位数的）的？', '1、我也在纳闷呢，要不你帮我问问腾讯是怎么回事\r\n2、这又不是我的主号，我的主号比这个屌多了，有 12 位呢\r\n3、中国人喜欢这个数字，为此我从几年前就开始等，等了很久很久才等到注册出来是 8 位呢', '共鸣故事');
INSERT INTO `talk_art` VALUES (4519, '女：哦', '1、饿了就去吃，没人拦着你\r\n2、心理学上说老是爱回哦字的人不善于思考，智商普遍比较低下', '共鸣故事');
INSERT INTO `talk_art` VALUES (4520, '女：你是花心大萝卜呀~', '1、尽管我不是花心的人，但你吃醋的样子这么可爱，为了下次还能看到，我还真有点想去花一花\r\n2、你怎么这么死脑筋，不能换个角度来理解，我这叫博爱好吧', '共鸣故事');
INSERT INTO `talk_art` VALUES (4521, '女：请我吃饭啊！ 。。 。 。 。。', '男： 你觉得你长得好看吗？\r\n女：好看啊/不好看/\r\n男：我不请没有自知之明的人吃饭/那还好意思叫我请吃饭，对着你哪有胃口/', '共鸣故事');
INSERT INTO `talk_art` VALUES (4522, '女：我还是没控制住我的情绪！', '1、没关系，以后按时吃药就会改善的\r\n2、太正常了，我的粉丝经常会为了我情绪失控\r\n3、可以的控制自己的情绪对身体其实没好处的，有情绪就应该发泄出来，那边那堵墙挺牢固的，去撞吧', '共鸣故事');
INSERT INTO `talk_art` VALUES (4523, '女：你的 QQ 好高级啊！', '1、再高级也是国产的低档车\r\n2、好马配好鞍，好人当然配好 QQ', '共鸣故事');
INSERT INTO `talk_art` VALUES (4524, '女：你见到女人就走不了路了。 。', '1、那也得看什么女人，看到像你这样的我跑得说不定比刘翔还快\r\n2、没错，女士优先，我会发扬绅士风度让女士先行', '共鸣故事');
INSERT INTO `talk_art` VALUES (4525, '女：我又不骗你糖吃！', '男：骗子要骗人前从来不会说自己要骗对方什么的', '共鸣故事');
INSERT INTO `talk_art` VALUES (4526, '女：看来你肯定很会泡女孩子啊 嘴巴那么会讲', '1 、那照你这么说讲相声的不都成把 妹达人了\r\n2、你之所以觉得我嘴巴会讲，只因为你的嘴巴太笨\r\n3、胡说，口才到了我这个阶段，都是女孩子来泡我了好吧', '共鸣故事');
INSERT INTO `talk_art` VALUES (4527, '女：你猜我现在最想干什么？', '男：想睡觉，或者想去玩。\r\n女：其实我现在最想看到你啊，猪头\r\n男：想看我就来看，想看猪头请去菜市场', '共鸣故事');
INSERT INTO `talk_art` VALUES (4528, '女：听说你很牛哦！', '1 、你怎么这么单纯，道听途说也信，万一被人家骗了怎么办。。 。 。不过还好这传言是真的\r\n2、听谁说的，改天请他吃饭', '共鸣故事');
INSERT INTO `talk_art` VALUES (4529, '女：有时候发觉你的思想也有点孩子气', '1、每个人都有内在的一面，外表再坚硬的钻石，也有它脆弱的某个点，何况是人\r\n2、这个社会已经将我们的童真扼杀完了，我想，能保存一点是一点，这样我们以后的生活才不会那么无趣\r\n3、你没看书上说，有孩子气的男人，特别可爱嘛', '共鸣故事');
INSERT INTO `talk_art` VALUES (4530, '女：你要是把我摔了，我要你养我一辈子', '1、没听过谁家宠物可以养一辈子那么久的\r\n2、一直都只是听说，想不到今天我也遭遇了碰瓷', '共鸣故事');
INSERT INTO `talk_art` VALUES (4531, '女：你还不走是等着拿遣散费吗？', '1、等着看上帝怎么用雷帮你电头发\r\n2、走不走是我的事情，你这么关注我干嘛？', '共鸣故事');
INSERT INTO `talk_art` VALUES (4532, '女：我们之间还要有秘密吗？', '1、要的，长痔疮这件事我不想告诉你\r\n2、秘密是谁？男的女的？\r\n3、与其说是秘密不如说是尊重，人与人之间都有起码的尊重，更何况是我们之间', '共鸣故事');
INSERT INTO `talk_art` VALUES (4533, '女：少和我装糊涂！', '1、你又不是垃圾袋，怎么往里装东西\r\n2、这都能被你看出来，好吧，我不装糊涂了，其实我早就知道你暗恋我了\r\n3、 那要我装什么？装大象？', '共鸣故事');
INSERT INTO `talk_art` VALUES (4534, '女：你最终会在我身边吗？', '1、最终的事情谁也不能预测，如果我现在跟你说会，那一定是骗你的，但我可以跟你说的是，我的心一定会在你身边\r\n2、那得看你伺候的好不好了', '共鸣故事');
INSERT INTO `talk_art` VALUES (4535, '女：什么呀！', '1、都是浮云\r\n2、别问了，以你的智商跟你说你也不懂的\r\n3、不知道，找百度', '共鸣故事');
INSERT INTO `talk_art` VALUES (4536, '女：我就喜欢高个子，而且稍微瘦一点的！', '1、长颈鹿挺适合你的，而且两三天不给它喂草料就会瘦\r\n2、瘦的不行，你那么生猛，被你压上去他还不得散架呀\r\n3、电线杆很符合你的条件，但你得小心，一般电线杆都有流浪狗划过地盘的，你极有可能会被咬\r\n4、恩，高的好，这样打起架来不会被你挠到脸导致破相', '共鸣故事');
INSERT INTO `talk_art` VALUES (4537, '女：是不是我任性点，叛逆点，你才会喜欢我？', '1、又任性又叛逆的，不是吕布吗\r\n2、你只要不整天想这些乱七八糟的，我就会喜欢你', '共鸣故事');
INSERT INTO `talk_art` VALUES (4538, '女：扯淡！', '男：知道扯淡你还说', '共鸣故事');
INSERT INTO `talk_art` VALUES (4539, '女：别惹我', '1、你会咬人吗\r\n2、你说这句话的时候好有气场，下次再拍李小龙选角你可以去试试\r\n3、别指挥我\r\n其他答案：1、我老家有句俗话是这样说的：惹大不惹小，惹猪莫惹狗，你是哪一类？（一直很想爱你）', '共鸣故事');
INSERT INTO `talk_art` VALUES (4540, '女：做梦去吧', '1、你以为我们在拍盗梦空间吗\r\n2、你真是个不愿意面对现实的人', '共鸣故事');
INSERT INTO `talk_art` VALUES (4541, '女：2012 没时间嫁了！', '1、想办法弄张船票还可以在诺亚方舟上举办婚礼呢\r\n2、即使到了世界末日最后一刻，我也会在洪水淹没我们前将戒指戴在你的无名指上', '共鸣故事');
INSERT INTO `talk_art` VALUES (4542, '女：人都像你这样，那还了得。', '1、是啊，那全世界的女人都会幸福死的\r\n2、不可能的，物以稀为贵，真要都像我这么优秀了，反而没什么了不得了\r\n3、你这么喜欢我这种类型的，那还不得挑花眼呀', '共鸣故事');
INSERT INTO `talk_art` VALUES (4543, '女：这么多人，我要是每个人的话都记得，那我成神', '1、你一直就神神的\r\n2、你们家都把录音机当神来拜是么\r\n3、不用记得每个人的话，你记住我的话就行', '共鸣故事');
INSERT INTO `talk_art` VALUES (4544, '女：你什么都不懂', '1、不会啊，我懂得你这样的长相是不及格的\r\n2、你怎么知道我的外号叫机灵小不懂', '共鸣故事');
INSERT INTO `talk_art` VALUES (4545, '女：姐姐今天晚上失恋了', '1、恭喜你，现在你有机会跟更优秀的人在一起了\r\n2、那真不幸，接下来我有可能会跟你发生一段故事了\r\n3、他眼瞎么，怎么会选择跟你分手？！眼神好的话一开始就不应该跟你在一起嘛\r\n其他答案：哥哥明天带你去婚姻介绍所，那里有好多大叔哦（疯狂机器）\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4546, '女：猪头', '1、乱讲，你的脸要再瘦一点才像猪头呢\r\n2、猪头叫谁（感谢杨过）\r\n3、猪头多少钱一斤啊，但是你卖猪头也不用顶着个猪头来打广告吧', '共鸣故事');
INSERT INTO `talk_art` VALUES (4547, '女：你好会夸人', '男：夸得不好，请多担待\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4548, '女：你周围应该有很多女人把', '男：别打击我了，我妈都准备给我换名字了，叫单生狗', '共鸣故事');
INSERT INTO `talk_art` VALUES (4549, '女：不合适', '男：世界上没有天生合适的人，所谓的合适，是经过相处磨练出来的', '共鸣故事');
INSERT INTO `talk_art` VALUES (4550, '女：你个卑鄙小人！', '1、 每个人都是从 BABY,到小人，再成长到大人的，你别跟我说你不是\r\n2、你那么脸厚心黑，我不卑鄙小人一点，哪里是你的对手', '共鸣故事');
INSERT INTO `talk_art` VALUES (4551, '女：我们还不熟', '男：给你加把火，让友谊升温\r\n女：我高冷 -----\r\n男：高冷到一定程度，智力就会冻结', '共鸣故事');
INSERT INTO `talk_art` VALUES (4552, '女：你什么意思啊你！', '1、我对你的理解能力表示遗憾\r\n2、我很欣赏你这种不懂就问的求知欲\r\n3、也许是我的表达能力不太好，你需要我再重复一遍吗？\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4553, '女：你想干什么', '男：撬开你的心门 ,变成房间的主人', '共鸣故事');
INSERT INTO `talk_art` VALUES (4554, '\\', '1、你是哪个时代穿越过来的，搞不清楚时间状况吗\r\n2、几世纪不重要，重要的是把你放哪个世纪都 OUT 了', '共鸣故事');
INSERT INTO `talk_art` VALUES (4555, '女：在做什么', '男：呼吸还有想你', '共鸣故事');
INSERT INTO `talk_art` VALUES (4556, '女：屁啦', '1、女孩子不要把屁整天挂在嘴边，会口臭的\r\n2、第一次见到屁从口出的人', '共鸣故事');
INSERT INTO `talk_art` VALUES (4557, '女：我很丑的', '男：你这么侮辱你爸妈的结晶，你爸妈知道吗', '共鸣故事');
INSERT INTO `talk_art` VALUES (4558, '女：我不想以后生活捉襟见肘', '1、那还不好吗，拉一下衣襟就能吃到猪肘子，这是小康水平哦\r\n2、衣服做大点就不会了', '共鸣故事');
INSERT INTO `talk_art` VALUES (4559, '女：我们距离很远', '男：不怕只要心近', '共鸣故事');
INSERT INTO `talk_art` VALUES (4560, '女：你怎么不去陪你女朋友出去玩阿 ？', '1、你是哪家报社的，怎么比狗仔队还八卦\r\n2、我比较喜欢像你这样有男人味的', '共鸣故事');
INSERT INTO `talk_art` VALUES (4561, '女：为什么出来玩总是最后一个看到你呢', '1、你没听说过压轴这个词语吗\r\n2、因为我讲义气，如果先看到我，再看其他男性朋友岂不是变得很没意思了', '共鸣故事');
INSERT INTO `talk_art` VALUES (4562, '女：不喜欢戴眼镜的~', '1、我也不喜欢戴眼镜的，特别是有色眼镜\r\n2、戴眼镜是为了能更看清楚你一点，这样以后你的鼻毛不小心长出来我就能提醒你了\r\n3、我知道你不喜欢戴眼镜，所以我更要适应戴着它，如果将来有一天你年纪大了，视力变差了，那时候你可以不戴眼镜，因为有我来当你的眼镜', '共鸣故事');
INSERT INTO `talk_art` VALUES (4563, '女：一个女孩说自己情绪低落，如何回答？', '1、以后情绪低落你就照照镜子，看到自己搞笑的脸你就会笑了\r\n2、我完全能了解你的感受，这种低落的感觉一定很糟糕，但你一定要坚强点（前半段认真安慰语气） ，我知道你喜欢我，但我不希望看到你因为得不到我而变得这么低落的样子（后半段调侃语气）', '共鸣故事');
INSERT INTO `talk_art` VALUES (4564, '女：喂，不可以抄袭我的话啦！', '1、你的话？先把注册的版权证明拿出来看看再说\r\n2、你干嘛不说大家都抄袭你用嘴吃饭呢\r\n3、什么抄袭？！你这人说话怎么这么难听，我这是借鉴', '共鸣故事');
INSERT INTO `talk_art` VALUES (4565, '女：你是不是暗恋我？', '男：你觉得你什么地方是值得我暗恋的，帮我举几个例子呗，我太愚钝了，实在找不着', '共鸣故事');
INSERT INTO `talk_art` VALUES (4566, '女：如果你喜欢的人，她不喜欢你了，你会怎么', '男：我喜欢的人不会不喜欢我的，不喜欢我的人我也不会喜欢她', '共鸣故事');
INSERT INTO `talk_art` VALUES (4567, '女：你背得起我吗？', '男：估计背不动，肚子（屁股或脑袋）大的女人特别沉不知道，有扛过麻袋，但生猪倒没背过', '共鸣故事');
INSERT INTO `talk_art` VALUES (4568, '女：你这么知道我是 XX 星座的', '1、因为我是研究天文学的\r\n2、昨晚夜观星象发现 XX 座在向我的星座靠拢，想来想去，也就是你了\r\n3、我觉得你的性格脾气和你的一切特征都跟 XX 座非常吻合，特别体现在对帅哥的品味这一点上', '共鸣故事');
INSERT INTO `talk_art` VALUES (4569, '女：“你真瘦”怎么回答？ （本人确实很瘦）', '1、是啊，很烦人，经常有各种减肥药公司来找我做代言，赶都赶不走\r\n2、你知道多少明星花了多少钱想达到我这种骨感的效果吗\r\n3、这个地球已经满目疮痍，为了下一代的未来，节能环保要从我做起，你知道我这种身材要为地球省下多少布料吗\r\n\r\n其他答案：我这样的身材，你是不是羡慕嫉妒恨呢', '共鸣故事');
INSERT INTO `talk_art` VALUES (4570, '她的身材很瘦小，怎么调侃', '1、 以后找老婆就要找你这样的\r\n女：为什么\r\n男：你这么瘦肯定吃得少，吃得少可以给家里省很多钱呀\r\n2 、以后找老婆就要找你这样的\r\n女：为什么\r\n男：你看上去这么弱小， 跟你在一起走在街上男人会觉得自己特别有男子气概， 能极大满足虚荣心的', '共鸣故事');
INSERT INTO `talk_art` VALUES (4571, '女朋友的腿要拆钢板了，有好的话术和她互动吗', '男：我最近一直在求神拜佛，保佑你腿上的钢板早点拆掉，终于感动了神明，我太高兴了，以后跟你吵架不用担心你踢我了', '共鸣故事');
INSERT INTO `talk_art` VALUES (4572, '女：我看你是吃不到葡萄就说葡萄是酸的', '1 、你的体型明明是西瓜（瘦的话换成甘蔗） \r\n2、我吃得到柠檬还不是照样说柠檬酸', '共鸣故事');
INSERT INTO `talk_art` VALUES (4573, '女：听说你找了女朋友，带来看看', '1、当年刘德华也这么被狗仔队问过，我的答案跟他一样\r\n2、你去照镜子就能看到了\r\n3、你消息太不灵通了，我找了女朋友的绯闻是去年的事了，今年的绯闻应该是生了孩子或离婚了吧', '共鸣故事');
INSERT INTO `talk_art` VALUES (4574, '女：你的工作，不稳定，工资少。 。没钱途！', '男：你觉得如果我能多挣钱，还会在这里忍受你这个庸俗的女人吗钱少才有坚持奋斗的动力，不稳定才能有居安思危的警觉心，这是份磨练意志的好工作呀', '共鸣故事');
INSERT INTO `talk_art` VALUES (4575, '女：你为什么老是打电话给我？', '1、不看到你那张脸聊起来会更有想象空间\r\n2、因为你不给我打电话呀\r\n3、因为打给别人她们不接', '共鸣故事');
INSERT INTO `talk_art` VALUES (4576, '女：问你借钱，你给借不?', '1、你得拿东西抵押，还有多少利息咱们先说好\r\n2、不借，咱们关系这么好，谈借多伤感情，直接拿去，不用还了，是要五块还是十块，你只管开口，千万别客气', '共鸣故事');
INSERT INTO `talk_art` VALUES (4577, '女：你愿意讲，我还不愿听呢！', '1、你怕听了以后会爱上我吗\r\n2、在这个时代面对新鲜资讯要虚心地吸收，不然就会像你这样变得孤陋寡闻了\r\n3、 你知道什么行为很幼稚吗，就是对成年人用激将法，这还不是最幼稚的，最幼稚的是用了还不承认\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4578, '女：你是不是想追我？', '1、那得看你跑得快不快\r\n2、我一般也就追追小偷什么的\r\n3、多大了，还玩猫抓老鼠的游戏呀', '共鸣故事');
INSERT INTO `talk_art` VALUES (4579, '女：就这样吧。 （指两者的关系）', '男：（唱）那就这样吧，再爱都曲终人散了，再给抱一下，闻一闻你的长发（感谢动力火车）', '共鸣故事');
INSERT INTO `talk_art` VALUES (4580, '女：我被老板炒鱿鱼了……', '1 、那太好了，我正要招聘一个二十四小时贴身女朋友呢\r\n2、工作呢，最要紧的就是开心。不被炒鱿鱼呢，是不能强求的。\r\n3、呐，老板不用你呢，是他不懂得珍惜你。这种事呢，大家都不想的。所谓吉人自有天相，在哪里工作关键看你自己、呐，饿不饿？我给你煮碗面', '共鸣故事');
INSERT INTO `talk_art` VALUES (4581, '女：好了，不理你了，你要忙就去忙吧', '1、那不行，你不理我就得忙着哄你了\r\n2、你不理我会影响我的心情，忙工作就忙不好，忙不好就被老板开除，开除了就没钱吃饭，到时候没饭吃你理不理我呀\r\n3、你太通情达理了亲爱的，我这么为了你牺牲跟你聊天的快乐时光去努力工作是值得的\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4582, '女：你女朋友呢？在哪？不是骗我的吧？', '1、（你女朋友呢）我将女朋友放在我心里\r\n2、（在哪）在我面前啊\r\n3、（不是骗我的吧）骗你干嘛？你又卖不了几个钱', '共鸣故事');
INSERT INTO `talk_art` VALUES (4583, '女：你的声音太女性化了！', '1、（针对普通人）恩，很多女孩子都这么夸过我，说我作为男人声音很温柔，以后适合当老公\r\n2、（针对女人）对，还是你的声音比较有男人味\r\n3、（针对男性好朋友）我只是声音温柔而已，不像有的男人性格都很女性化，会像女人那样\r\n爱对朋友的身体特质说三道四指指点点呢\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4584, '女：你们男人不管有钱没钱，帅还是不帅~都是骗子', '1、我也这么认为，最可恨的就是那个骗了你妈的老骗子，还生了一个怀疑一切的小骗子\r\n2、能被所有的男人骗，这得多低的智商才能达到\r\n3、 你们女人不管有身材没身材，漂亮还是不漂亮~都没脑子。 。 。当然，除了我妈\r\n', '共鸣故事');
INSERT INTO `talk_art` VALUES (4585, '女：你还挺洋气的勒！', '1、奖，你这么说我真是受之有愧，其实只是你比较土罢了\r\n2、我一个大活人，又不是鬼，当然阳气，难道还吐阴气啊\r\n3、一直很洋气，从未落伍过', '共鸣故事');
INSERT INTO `talk_art` VALUES (4586, '女：与其生活在网络里，还不如生活在现实中！', '1、你是蜘蛛侠吗\r\n2、还真不一样，上网费便宜，生活费贵啊', '共鸣故事');
INSERT INTO `talk_art` VALUES (4587, '女：“一点绅士风度都没”', '男：绅士都是披着文明外衣的狼而已，你不知道？', '共鸣故事');
INSERT INTO `talk_art` VALUES (4588, '女：我为什么要跟一个不爱的人一起受苦受累', '男：因为佛说：我不入地狱谁入地狱', '共鸣故事');
INSERT INTO `talk_art` VALUES (4589, '女：身体不舒服', '男：哎 水土不服\r\n1 、水土一起服下去就不是不舒服的事了，会挂掉的\r\n2、 那我现在跟你聊两句，你是不是舒服很多了\r\n女：完全没有', '共鸣故事');
INSERT INTO `talk_art` VALUES (4590, '女：做人不要太高调，很虚伪！', '1 、貌似过分的低调才是虚伪来着，看来你语文不怎么好呀\r\n2、恩，看到你我就会引以为戒了\r\n3、我很认同你的观点，特别是那种高高在上对别人指指点点很高调的人，最虚伪', '共鸣故事');
INSERT INTO `talk_art` VALUES (4591, '女：你好吝啬', '1、我不吝啬怎么显出你大方\r\n2、你好像误解节约这个传统美德的意思\r\n3、即时现在再多人说我吝啬，也比不上以后我用省下的钱让家人过得更幸福重要', '共鸣故事');
INSERT INTO `talk_art` VALUES (4592, '女：我是认真的，跟我在一起你会很累', '1、每一对最终有结果的情侣都是认真的，也没见累死几个\r\n2、没关系，男女搭配，干活不累', '共鸣故事');
INSERT INTO `talk_art` VALUES (4593, '女：我要是能像你一样阳光就好了', '男：仁者见仁，心中有爱，全世界都充满阳光', '表达兴趣');
INSERT INTO `talk_art` VALUES (4594, '女：仔细看你长的还有点好看嘛~', '男：建议你别继续看下去了，不然很容易爱上我的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4595, '女：你个小白脸~', '1、我不会告诉你怎么保养的，除非你请我吃饭\r\n2、跟你的脸一比，我确实显得非常白', '表达兴趣');
INSERT INTO `talk_art` VALUES (4596, '女：我比较被动，随遇而安吧', '男：恩，不亏待每一份热情，不讨好任何的冷漠，一旦攒够啦失望，就要离开，爱够了就在一起', '表达兴趣');
INSERT INTO `talk_art` VALUES (4597, '女：女人爱老公，更要爱自己！', '男：终于找到你自恋的原因了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4598, '男：叫声哥又少不了肉,不明白', '女：那我反问一句，叫声哥又多不了肉，不明白\r\n男：那叫老公吧，叫老公就给你买肉吃\r\n男：你叫一声，我给你买块肉骨头，不信咱们现在就去菜市场，连我家旺财都没这个待遇呢', '表达兴趣');
INSERT INTO `talk_art` VALUES (4599, '女：你真前卫！', '1、是你太老土\r\n2、不，我在球队一般是踢中后卫的9跟你站在一起，谁也会显得前卫的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4600, '女：为什么不找个女朋友', '1、因为你一直没对我表白呀\r\n2、我就是太善良了，老是考虑你的感受\r\n3、你为什么不找个男朋友', '表达兴趣');
INSERT INTO `talk_art` VALUES (4601, '女：我做不到', '男：加油你行的，我经常也有那种感觉，如果这个事情来了，你却没有勇敢去解决掉，它一定会再来，生活真是这样，它会一次地让你做这个功课直到你学会为止', '表达兴趣');
INSERT INTO `talk_art` VALUES (4602, '女：你能不能别整天给我整一幅很高深的样子', '1、那你能不能别整天给我整一副很幼稚的样子\r\n2、如果觉得对方高深，解决的方法不是要求对方改变，而是想办法提高自己的深度\r\n3、你还不错，能明了我的高深，不像坐井观天的那只青蛙永远不知道天有多高，井有多深', '表达兴趣');
INSERT INTO `talk_art` VALUES (4603, '女：我觉的你很开放！呵呵！', '1、我只对开放的人开放\r\n2、那你为什么拘束呢？\r\n3、中国一开放很多人马上就富了，我这是贯彻国家政策呢\r\n其他答案：其实我是个低调内向，心地善良的好孩子', '表达兴趣');
INSERT INTO `talk_art` VALUES (4604, '女：我看你就是吃软饭的！', '1、是啊，饭煮烂点对胃比较好\r\n2、其实面条我也挺喜欢的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4605, '女：冷汗', '1、出冷汗了，要帮你叫医生吗\r\n2、抱着我就不会冷了\r\n3、我认识一些女孩子也像你这样看到帅哥就会紧张得冒冷汗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4606, '女：我天生就懒', '男：哈哈小懒猪，你不知道一个人越懒，明天要做的事情越多吗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4607, '女：明天是我生日，你帮我过好不好', '1、好啊，你的过生日预算是多少，然后把我的劳务费加进去（坏笑）\r\n2、好啊，谁叫我这么乐于助人呢\r\n3、那不行，你自己的生日怎么能我帮你过呢，应该是我们一起过才行', '表达兴趣');
INSERT INTO `talk_art` VALUES (4608, '女：你是认真的吗', '1 、你看不出来吗？我绝对是很认真的。。 。 。 。。在跟你开玩笑\r\n2、（拿过她的手放在你心脏的位置） ：心是不会撒谎的，你可以自己感觉下我的心跳', '表达兴趣');
INSERT INTO `talk_art` VALUES (4609, '我约她', '女： 她说不忙就出来，忙就不行，回了句，你不忙阿，怎么回\r\n1、忙里偷闲跟你约个会\r\n2、忙啊，正忙着约你\r\n3、不忙，工作已经堆了一堆了，也不差这一天，说不定看到你后会很有工作灵感呢', '表达兴趣');
INSERT INTO `talk_art` VALUES (4610, '那你女：还坚持追我吗？ （追有男朋友的女生）', '1、那得看你往哪跑，跑去女厕我是追不了的\r\n2、当然追啊，上次追小偷，他跑了一条街最后还不是被我给追到了，你更不在话下', '表达兴趣');
INSERT INTO `talk_art` VALUES (4611, '女：我们还没有好到那个地步~！', '1、没错，我们面对面目前都还穿着衣服呢\r\n2、所以你可千万别开口找我借钱\r\n3、你究竟还想跟我好到哪个地步', '表达兴趣');
INSERT INTO `talk_art` VALUES (4612, '女：如果你月工资 3000 元,你会给女朋友一个月多少钱', '1、我觉得能自给自足的女孩子最有吸引力，如果我女朋友是这样的类型，就算工资全部花光也无所谓\r\n2、那得看她一个月给我做多少好吃的表现好就多给些，表现不好就不给', '表达兴趣');
INSERT INTO `talk_art` VALUES (4613, '女：“你都已经有女朋友了！”', '1、你不也有男朋友嘛，谁规定我们不能交异性朋友的？\r\n2、我已经过了法定年龄，有女朋友证明我是正常的\r\n3、那你还喜欢我！', '表达兴趣');
INSERT INTO `talk_art` VALUES (4614, '女：我想找个人和我一起做饭！~', '男：人才市场很多这种做饭阿姨的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4615, '女：看到商场一件 999 元的衣服,我很喜欢,你会给我买吗', '1、只要你喜欢，我一定天天带你去商场看，过足眼瘾\r\n2、我只给懂得怎么让我喜欢的女孩子买\r\n女：什么是懂得如何让你喜欢的\r\n就是不会一天到晚想些 2B 问题为难我的女孩子\r\n3 、买啊，怎么不买，必须买，只要我买彩票中奖，多贵也给你买', '表达兴趣');
INSERT INTO `talk_art` VALUES (4616, '女：你和我圈子不同，没有共同话题。', '1、为什么要被自己的圈子禁锢，不能走出来吗\r\n2、每个圈子都是一个不同的世界，不同的世界才有新鲜的话题\r\n3、我这个圈子是孙悟空用金箍棒画的，你的圈子是蜡笔小新画的吗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4617, '女：心不知道为什么莫名其妙的乱！', '1、是不是因为想我了\r\n2、心太花就很容易乱\r\n3、最近有没借钱出去？\r\n其他答案：\r\n1、过来我给你看看。\r\n2、不知道为什么，每个碰到我的美女，都有你这种经历呢。\r\n3、是心不知道为什么，还是你不知道为什么？（一直很想爱你）', '表达兴趣');
INSERT INTO `talk_art` VALUES (4618, '就快考试了。 。和 妹子 QQ 聊天', '女：你会去复习的吗？(讽刺我）\r\n男：马上要考试了，肯定要复习啊，《作弊的一百个方法》很久没看都快忘光了\r\n男：连你都知道要考试这回事，我还能不复习吗我可得复习，但以你的智商是完全不用复习的，再怎么复习也没用', '表达兴趣');
INSERT INTO `talk_art` VALUES (4619, '女：你为什么觉得你是爱我的', '1、你为什么觉得我不爱你\r\n2、我也不知道，我想爱一个人真的没有理由', '表达兴趣');
INSERT INTO `talk_art` VALUES (4620, '女：你常常这样撩妹吧', '男：其实撩这件事，看感觉，遇到对眼的，能把你逗开心，我也就开心了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4621, '女：你觉得尴尬吗？我不觉得啊？', '1、你听过知耻而后勇这句话吗\r\n2、我明白，每个人的脸皮厚度是不一样的\r\n3、我知道你是个反应迟钝的人，但没想到能迟钝到这个地步', '表达兴趣');
INSERT INTO `talk_art` VALUES (4622, '有两个消息：一个好消息，一个坏消息，你想先听哪个', '1、有两个回答，一个是都想听，一个是都不想听，你想要哪个\r\n2、消息不论好坏都没关系，关键我想看你讲话的样子\r\n3、祸兮福所倚，福兮祸所伏，讲哪个都一样', '表达兴趣');
INSERT INTO `talk_art` VALUES (4623, '妹子总是回复一个 恩', '1 、借点钱来花花---\r\n女： ？\r\n2、你怎么不“恩”了？\r\n3、我完全相信你是个饮水思源的人，但除了感恩节那天，其他时间能不能别老把恩挂在嘴边\r\n4、你的键盘是不是坏到只剩一个 E 键可以用了？\r\n', '表达兴趣');
INSERT INTO `talk_art` VALUES (4624, '女：你们男人都是外貌协会的', '男：你没发现到目前为止我都还在坚持跟你讲话吗？我要是外貌协会的早就不理你了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4625, '女：你帮我介绍一个合适的男人吧。 。 。', '女：我很需要好男人拯救\r\n1、地球上很难找到合适你的男人，我现在就去报考神九宇航员，争取下次去外星帮你找一个\r\n2、你祸害人也就算了。还专门想祸害好男人？我干不来拉皮条的事', '表达兴趣');
INSERT INTO `talk_art` VALUES (4626, '女：懒的理你', '1、谢谢你让我享受清静的时间\r\n2、知道你不勤快，但没想到你会懒到这么恶劣的地步', '表达兴趣');
INSERT INTO `talk_art` VALUES (4627, '女：你能不能不要这么幼稚？', '1、每天说这么多废话---22恩，我确实太幼稚了，竟然幻想能通过多跟你沟通打开你的心扉\r\n2、你能不能不要这么装 B，我每天那么多话你也不回应下\r\n3、不能，我要不多说些话，会被你这么无趣的人给同化的\r\n', '表达兴趣');
INSERT INTO `talk_art` VALUES (4628, '女：不客气', '1、客气是中华民族五千年的传统美德，怎能轻易舍弃\r\n2、亲她一下\r\n女：你干吗？\r\n男：不是你叫我不客气的吗（舒适感到达一定程度才可以）\r\n3 、谁跟你客气了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4629, '女：我认识你吗?', '1、你确定认识我妈？别想用这个套近乎哦\r\n2、一个歌迷问周杰伦同样的问题，你觉得杰伦应该怎样回答呢？我又不是你肚子里的蛔虫，哪知道你认不认识我', '表达兴趣');
INSERT INTO `talk_art` VALUES (4630, '女：我呸', '1、你怎么随地吐痰这么没素质\r\n2、（模仿黑客帝国子弹时间动作）我闪~~~~~~~~~', '表达兴趣');
INSERT INTO `talk_art` VALUES (4631, '女：放屁', '1、不用报告我，直接放就行\r\n2、大家是文明人，请不整天把屁挂在嘴边', '表达兴趣');
INSERT INTO `talk_art` VALUES (4632, '女：你怎么总问一些莫名其妙的问题?', '1、像你现在这样？\r\n2、因为莫名其妙的问题只有莫名其妙的人才能回答，难道你叫我去问那些正常人吗\r\n3、那我问个正常的-----你为什么长成这样', '表达兴趣');
INSERT INTO `talk_art` VALUES (4633, '女：我的脾气很不好，很多人受不了！', '1、脾气不好挺好的，可以磨练我的意志，所以我在街上最喜欢逗那些吠得很凶的狗狗了\r\n2、这样好，很有安全感，不容易招蜂引蝶\r\n3、恩，太好了，这样我省掉一笔买沙包练拳的钱', '表达兴趣');
INSERT INTO `talk_art` VALUES (4634, '女：跟你拍拖？？？你是人见人爱吧', '1、我是人见人爱，但我的对女人的品味却一直不怎么样\r\n2、你说的不对，就算见不到我人，光听我的声音也会爱上我的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4635, '女：住嘴', '1、请不要试图剥夺别人的话语权\r\n2、如鲠在喉，不吐不快，要不你拿支笔给我，能写下来也不错', '表达兴趣');
INSERT INTO `talk_art` VALUES (4636, '女：如果我晚了回去，你会不会担心啊？', '1、当然会啊，那么晚的时间，路人很容易被你吓到的\r\n2、担心什么，你这么强壮，谁还能把你怎么样\r\n3、怎么不担心，你那么晚回来，吵到我睡觉怎么办\r\n', '表达兴趣');
INSERT INTO `talk_art` VALUES (4637, '女：你这个人怎么那么自恋啊，你以为你长的很帅', '男：小时候捡到一面会说话的奇怪镜子，它说我是世界上最帅的哦\r\n女：那是荒唐镜，就喜欢说些荒唐的话\r\n1 、难怪，我也觉得那镜子说得很荒唐，我哪里是世界上最帅的，明明就是全宇宙最帅的\r\n2、咦，第一次听说你的小名叫荒唐镜呀，是因为你说话一直很荒唐才取这个名字的吗\r\n3、是吗？我倒是觉得那镜子说话还是比较靠谱的，它还说你不是笨蛋来着\r\n', '表达兴趣');
INSERT INTO `talk_art` VALUES (4638, '女：你是羡慕还是嫉妒啊', '1、我是羡慕嫉妒恨\r\n2、看到别人过得好时你的人生只能容纳这两种情绪吗？其实还有一种你不懂的情绪叫欣赏\r\n3、你听过一句成语叫以小人之心度君子之腹吗？', '表达兴趣');
INSERT INTO `talk_art` VALUES (4639, '女：你不是在说我吧？', '1、那算了，千万别再追了。你最好对我不要再有念想，否则连朋友都不跟你做，懂不\r\n2、你的想象力还可以再丰富点（非原创）\r\n3、能不做朋友太好了，终于跳出友谊区了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4640, '女：我们不合适，我知道心里不喜欢你！', '男：心里是哪位？她不喜欢我跟我们合不合适有什么关系', '表达兴趣');
INSERT INTO `talk_art` VALUES (4641, '女：你真懒，你是懒羊羊吧', '1、懒得理你\r\n2、我哪里懒了？每天吃饭都是自己吃，衣服都是自己穿，走路也都是自己走的好吧\r\n3、你勤快，那帮我把衣服洗了吧，堆了一星期了都', '表达兴趣');
INSERT INTO `talk_art` VALUES (4642, '女：我很清楚自己不喜欢你，对不起，没办法勉强！', '1、你不喜欢我，是你自己的问题，不好好反省，还来跟我说七说八干嘛\r\n2、你了解我的想法就好，这样省的我跟你费口舌了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4643, '女：我看不上你', '1、我表示理解，同时表示绝不会歧视盲人\r\n2、谢天谢地，躲过一劫', '表达兴趣');
INSERT INTO `talk_art` VALUES (4644, '女：刚才在想， 我尝试给你机会', '1、 但是真的不行， 就是对你没感觉， 明白吗？我一直不想说，怕会很打击你，希望你能从我的态度明白，可以自己退出，可你不明白，当然我们是可以做朋友的\r\n2、分手了，就不要做朋友，知道你跟别的男人过得好，我会很嫉妒；知道你跟别的男人过得不好，我会很心痛', '表达兴趣');
INSERT INTO `talk_art` VALUES (4645, '女：你说话怎么这么像我男朋友啊？', '1 、那说明你运气真的很好 \r\n女：?能够有个跟自己心目中男朋友标准一样的男人出现，是很幸运的一件事\r\n2、第一次听到这么独特的表白方式', '表达兴趣');
INSERT INTO `talk_art` VALUES (4646, '女： 你们男人都是这样吗？', '1、你们女人都是这么以点概面吗？\r\n2、不清楚，没去了解过，我只对女孩子感兴趣\r\n3、我才不要告诉你，不过你可以去问问你爸爸，他也是男的哦', '表达兴趣');
INSERT INTO `talk_art` VALUES (4647, '女：爱你的人和 你爱的人 你会选择', '女：不管是爱我的还是我爱的，都是跟我很有缘份的人，缘分这个东西，往往你越去刻意追求和选择，越是得不到，所以顺其自然是最好的选择', '表达兴趣');
INSERT INTO `talk_art` VALUES (4648, '女：某某某，你今天好丑！', '1、某某某，你今天眼瞎了吗\r\n2、某某某，你今天口好臭\r\n3、有你在身边作比较，再丑我也不用担心', '表达兴趣');
INSERT INTO `talk_art` VALUES (4649, '女：我老觉得你不是什么好人', '1、你的直觉真棒，我早就看出来你不是普通人，能帮我预测下期六合彩开什么吗\r\n2、姑娘，人不可貌相呀，我虽然看起来不像好人，其实内心。 。 。\r\n3、我什么时候说过我是好人了，你赶紧离我远点，不然被我带坏了可不负责任', '表达兴趣');
INSERT INTO `talk_art` VALUES (4650, '女：你这么能说，肯定是练出来的~哼。', '1、恩，每天去菜市场跟菜贩子探讨价格练出来的\r\n2、不用练，你跟个嘴特别笨的聊天，你也会显得很能说的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4651, '女：你瘋了吗', '1、暂时还没，但已经快被你逼疯了\r\n2、我也一直这么问自己，竟然对你有感觉，我是不是疯了\r\n3、我要疯了你还能好好站这跟我讲话吗？疯子会咬人好吧', '表达兴趣');
INSERT INTO `talk_art` VALUES (4652, '女：别以为你很帅就了不起', '1、我知道，我的帅还必须配合我独有的人格魅力才能称之为了不起\r\n2、恩，能生出这么帅的我，了不起的应该是我的父母\r\n3、面对这么帅的大帅哥还能说出这种话，我觉得你也挺了不起的\r\n其他答案：如果帅能当饭吃 本帅能养活 30 亿人', '表达兴趣');
INSERT INTO `talk_art` VALUES (4653, '女：我是一个很普通的人，所以我过着很普通', '1、一个在移动上班的女孩说的生活。\r\n2、这句话让我感觉很无力 不知道怎么回答\r\n3、有我的出现，你会发现你的生活将不再普通，而是充满惊喜', '表达兴趣');
INSERT INTO `talk_art` VALUES (4654, '女：你太慢了', '男：知道什么叫欲速则不达吗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4655, '女：我不想我的孩子比别人过的差', '1、所以你才要选择我这支潜力股啊\r\n2、我也不想我的孩子她妈整天想这些杞人忧天的东西\r\n3、那也没关系，其实我们可以做丁克家族的，你就不用这么烦恼了（慎用）', '表达兴趣');
INSERT INTO `talk_art` VALUES (4656, '女：昨晚一不小心晚睡了', '1、我还纳闷昨晚干嘛一直打喷嚏呢，你是想我想的睡不着的吧\r\n2、恩，以后尽量还是别晚睡，要是皮肤变差了吓到我怎么办你想变成国宝来讨我喜欢吗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4657, '女：你十句话有十一句是假的', '1、你不识数呀\r\n2、这都能听出来，你真不是一头猪，你觉得我在说假话吗', '表达兴趣');
INSERT INTO `talk_art` VALUES (4658, '女：能不能和你一起照张相~', '男：得先把价钱谈好，动物园跟猴子合影也要收费的好吧，何况我比猴子帅多了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4659, '女：不要这么想念哥', '1、哥只是个传说，传说中的山顶洞人\r\n2、我无法控制自己不想念哥，特别是没钱花的时候', '表达兴趣');
INSERT INTO `talk_art` VALUES (4660, '女：你真假', '1、到底是真还是假呀\r\n2、以你的智商不应该有分辨真假的能力呀\r\n3、建议你去食品监督部门应聘，那里太需要你这样的人才了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4661, '女：我和我同学聊，顺便和你聊（表情）', '1、好的，聊胜于无，不嫌多你一个\r\n2、这个习惯好，下次跟同学去吃大餐时也顺便带上我', '表达兴趣');
INSERT INTO `talk_art` VALUES (4662, '女：我这么一说您老就信了啊', '1、一会给你讲个狼来了的故事吧\r\n2、原以为漂亮的女人才会骗人，谁知道不漂亮的也是一样\r\n3、我的缺点就是真诚，特容易相信人，所以老是被各种王八蛋欺骗，哦，别误会，我不是说你，我说王八蛋呢', '表达兴趣');
INSERT INTO `talk_art` VALUES (4663, '女：你看，我脸都红了~', '男：皮太厚，看不出来', '表达兴趣');
INSERT INTO `talk_art` VALUES (4664, '女：你真是个铁公鸡，小气鬼。一件衣服都不舍得买', '男：你这么大方怎么不给我买一件', '表达兴趣');
INSERT INTO `talk_art` VALUES (4665, '女：你觉得我们合适么？', '1、不怎么合适，但无所谓啦，我不介意你比我差\r\n2、合适啊，豺狼虎豹，蛇鼠一窝\r\n3、还没 TD 过，不知道，试过了再告诉你（慎用）\r\n', '表达兴趣');
INSERT INTO `talk_art` VALUES (4666, '女：我怕你会爱上我！', '1、我只能说，你多虑了\r\n2、你觉得我去逛动物园时会爱上假山上的猴子么\r\n3、你整天都在 YY 各种帅哥爱上你的场景呀？倒是挺会自娱自乐的', '表达兴趣');
INSERT INTO `talk_art` VALUES (4667, '晚上 10:30 和一妹子同事发短信。', '男：在干嘛？\r\n女：睡觉啊，干嘛\r\n男：没什么事，很奇怪，就是突然想到你\r\n女：肯定是你手机号码存少了，才会突然想到给我发短信的，不怪你，好了我睡觉了哈， 晚安。\r\n1、其实是窗外野猫在叫，我马上就想到你了，所以看看你在干嘛\r\n2、手机里号码哪里会少，这条信息群发了我 10 块钱呢\r\n3、你一定不敢相信我遇到的这起灵异事件，昨天其实是手机自己给你发的信息，我在想，莫非……是我的电话想念你的电话了？要不待会找个好环境一起吃晚饭，让它们好好聚聚吧', '表达兴趣');
INSERT INTO `talk_art` VALUES (4668, '女：我今天要去奶奶（奶奶爷爷七大姑八大姨）家', '1、爱情输给了孝心，不过这样的爱情才有意思，我喜欢\r\n2、要不明天呗，\r\n女：哦。 。 。明天不行，明天我要去大姨妈家\r\n男：我也一起去吧，我想亲自慰问一下把你制造出来的元老之一，她太伟大了', '表达兴趣');
INSERT INTO `talk_art` VALUES (4669, '\\', '1、能看上你的，你觉得品位能高到哪去\r\n2、你这么直接表白。 。 。我会不好意思的', '文字进逻');
INSERT INTO `talk_art` VALUES (4670, '女：你就没一个正经', '男：我到现在给你的，都是保质保量的正经的温柔好嘛', '文字进逻');
INSERT INTO `talk_art` VALUES (4671, '女：靠', '男：我这有肩。\r\n女：你的太小了\r\n1、比起你的虎背熊腰，我确实有点单薄\r\n2、这么说你挺适合跟马恋爱的', '文字进逻');
INSERT INTO `talk_art` VALUES (4672, '女：你这么瘦小 怎么保护我啊（我的确很瘦）', '1、你这么粗壮，一般歹徒看到也不敢近我们身的\r\n2、你是从原始社会穿越过来的吧。 。 。', '文字进逻');
INSERT INTO `talk_art` VALUES (4673, '女：我不想知道！', '1 、是吗，一般我比较欣赏求知欲的女孩子。 。\r\n2、你真可爱，一点也不八卦，我要给你加分\r\n3、那你以后最好不要上百度', '文字进逻');
INSERT INTO `talk_art` VALUES (4674, '我晚上卖饰品的，想借这个机会搭讪，', '女：你的手链只是有灯光照得才闪，\r\n1、不会啊，太阳光直射的时候它也会闪的\r\n2、那是因为它在你手上呆的时间还不够，如果你带个一年半载还不闪，我就有足够理由解释了\r\n3、是的，你可以从我这再买一盏灯配套回去用的', '文字进逻');
INSERT INTO `talk_art` VALUES (4675, '女：你的眼光太高了', '男：哪怕有再多的不舍，也不能一再降低自己的底线去将就', '文字进逻');
INSERT INTO `talk_art` VALUES (4676, '女：我很胖呢（求能夸的她不胖那种 求奉承话）', '1想我夸你瘦就直说，别拐弯抹角的\r\n2你这叫旺夫，难怪我最近运气不错呢\r\n3这样才好，我抱着你散步还可以帮我减肥\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4677, '女：难道你是异地恋~？', '1、是的，我喜欢这种距离产生美的感觉\r\n2、不是，我们都在地球上恋爱的\r\n3、那取决于你喜欢跟我在一起还是异地\r\n其他答案： 恩， 我的心在这里， 她的心在那里， 我怎么才能把这异地变成一个地方去呢？', '文字进逻');
INSERT INTO `talk_art` VALUES (4678, '女：你觉得我怎么样', '男：人模狗样，跟我挺般配的', '文字进逻');
INSERT INTO `talk_art` VALUES (4679, '女：你的手链太小了', '1、你为什么不想想是手太粗的缘故呢\r\n2、原来你跟其他女孩子一样喜欢大的\r\n3、你有当暴发户的潜质，去找个煤老板也许比较适合你', '文字进逻');
INSERT INTO `talk_art` VALUES (4680, '女：大笑的表情', '男：淑女点，会影响我对你可爱气质的判断的', '文字进逻');
INSERT INTO `talk_art` VALUES (4681, '女：你是哪里人', '1、地球人/中国人\r\n2、你想知道将来自己嫁得远不远吗\r\n3、我妈说不能轻易把自己家在哪告诉别人，万一你要上我们家去偷东西就完蛋了', '文字进逻');
INSERT INTO `talk_art` VALUES (4682, '女：怎么，发现我最近越来越有内涵了呀？', '男：女孩子跟我接触多了都会变得更有内涵的', '文字进逻');
INSERT INTO `talk_art` VALUES (4683, '女：我对你有好感（建立了一定的舒适度）', '1 、所以。 。 。想请我吃饭？\r\n2、大部分女孩子对我都有好感，我觉得你这么独特的女孩子不应该跟她们停留在一个层面，你可以对我有爱\r\n3、你，不是想跟我借钱吧？', '文字进逻');
INSERT INTO `talk_art` VALUES (4684, '女： 问我喜欢什么类型的女孩，要怎么回答啊。', '1 、我就喜欢你这个类型的女孩。 。 。 。 。你希望我这样回答吗？\r\n女：希望/不希望\r\n男：那就要请我吃饭了/其实表里不一真的不利于身体健康呢\r\n2、 如果我喜欢你了,你觉得我还会去纠结你是什么类型的女孩吗?\r\n3、 你想找到努力的方向吗?', '文字进逻');
INSERT INTO `talk_art` VALUES (4685, '女：再见的表情', '女：什么意思？？？\r\n男：一见钟情，再见倾心，相信我们的再次相见感情一定很好了吧', '文字进逻');
INSERT INTO `talk_art` VALUES (4686, '女：这个帖子快完了，楼主更新都慢了', '1、各位该干嘛干嘛去吧\r\n2、慢工出细活，楼主表示更新慢点是对大家负责\r\n3、完不了，你完了它还没完呢\r\n4、如果这是激将法，那好吧，你的目的达到了', '文字进逻');
INSERT INTO `talk_art` VALUES (4687, '女：明天的事 明天再说', '1 、看来你跟我一样，是今朝有酒今朝醉的典型\r\n2、假如今天就是世界末日的前一天吧\r\n3、明日复明日，明日何其多，我生待明日，万事成蹉跎\r\n其他答案： 1:你一向这么生活无计划的吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (4688, '女：你的要求好高啊~', '1、比你的鞋底还高么(如果她穿高跟鞋)\r\n2、那你喜欢高的还是矮的', '文字进逻');
INSERT INTO `talk_art` VALUES (4689, '女：约会见面的时候', '男：你怎么比照片看起来瘦一下/漂亮，早知道半年前就约你了', '文字进逻');
INSERT INTO `talk_art` VALUES (4690, '女：我可是有男人的 怎么回答', '1、我知道，你说的是你爸爸\r\n2、这我知道，你不可能有女人的\r\n3、你必须不停的用表达你有男人来增加自己的安全感吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (4691, '女：你觉得我是一个什么样的人！~', '1、你是一个内心渴望我不断赞美的人\r\n2、问得好，我也在想你到底是什么样的人才能让我这么欣赏呢\r\n3、你是一个。 。 。不带把的人', '文字进逻');
INSERT INTO `talk_art` VALUES (4692, '男：看你面相，很贤惠', '女：为什么？\r\n男： 因为我，有品位啊', '文字进逻');
INSERT INTO `talk_art` VALUES (4693, '女：我老公很爱我的!你死了这条心吧!', '1、你老公很爱你关我什么事，我又不是要泡他\r\n2、你真是个残忍的女人，你知道哀莫大于心死吗', '文字进逻');
INSERT INTO `talk_art` VALUES (4694, '女：我最近才去 XX 公园玩，不去了', '1、咱们可以玩点不一样的，上次你肯定是走大门进去的吧，这次试试翻墙怎么样\r\n2、你最近还经常吃饭呢，不是也照样吃\r\n3、 上次你没跟我一起，那能一样么', '文字进逻');
INSERT INTO `talk_art` VALUES (4695, '女：你好像很内向啊', '1、那证明你还不太了解我\r\n2、我对九分以上的 妹子 很外向的\r\n3、那你喜不喜欢内向的类型呢？\r\n其他答案：是啊，我是个低调、内向、善良的好孩子。妈妈告诉我不能跟坏女人玩。\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4696, '女：笑的表情', '男：你和我聊天，能不能矜持点，一直在笑，笑出感情我不负责的', '文字进逻');
INSERT INTO `talk_art` VALUES (4697, '女：我很想去 X-X 玩，但是我没时间呀。', '男：你比我好多了，我想去 X-X 玩，我也有时间，但。。 。 。没有你一起的话，这些忽然都变得没意思了（适合 C 阶段）\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4698, '女：你迟到了！?', '1、好吧，我认罚，罚你亲我一下吧，只能一下哦\r\n2、你怎么早到这么多呀\r\n3、那下回你也迟到一次我们就扯平了', '文字进逻');
INSERT INTO `talk_art` VALUES (4699, '女：你眼光好差！不懂得什么叫靓', '1、如果你这样叫靓，那新华字典就忒误导人了\r\n2、眼光与你之间要选其一的话，我宁愿它就一直这么差下去\r\n3、其实我知道你这种就叫靓，但我觉得还是靓不过凤姐', '文字进逻');
INSERT INTO `talk_art` VALUES (4700, '女：看不出你还挺有品位捏~', '1、其实很简单的，任何有关品位的东西跟你持相反观点就行了\r\n2、对事物的品位我还行，但对人的品位就惭愧了，比如我竟然会喜欢你。 。 。\r\n3、我曾经很有品位，可是最近堕落了——我居然看上你了。\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4701, '男：你的小帽子，成功引起我的注意，小少女', '注：看朋友圈女生有戴帽子，你说她其他的东西也可以，反正都是一个套路\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4702, '女：在啊！?', '1 、啊~~~~~~~~~~\r\n女：?你叫我啊的呀，啊完了，奖品呢\r\n2、 您好，这是自动回答模式，请按提示操作\r\n男：求约会按 1，求聊天按 2，求 TD 按 3，求借款请找别人\r\n3、 我不在啊，我在家，啊是什么地方？', '文字进逻');
INSERT INTO `talk_art` VALUES (4703, '女：你不要破坏了我对你的好印象！', '1、如果展现真我是破坏印象的话，我宁愿这印象支离破碎\r\n2、说说你对我的印象是怎样的吧', '文字进逻');
INSERT INTO `talk_art` VALUES (4704, '女：我感觉我今年桃花运好多 不过都不是我想要的男', '男：自作多情的人，总感觉天下所有的人都喜欢自己。 （一直很想爱你）', '文字进逻');
INSERT INTO `talk_art` VALUES (4705, '女：我想找个有个性的男友（老公）~', '1、吵架后可以个性到一年不跟你说话的那种？\r\n2、其实可以换个角度思考，举个例子，就像我现在即使再有个性，但我们真正在一起后， 就是一个整体了， 我肯定时刻都会照顾到你的感受， 那么所谓个性到时也是相对我们俩而共同存在的，你觉得呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4706, '女：我说一件事， 你不要生气，', '女：我觉得你很没有安全感，觉得的会靠不住， 很让我不放心，而且还会在街上街搭女孩。但是我想一直和你在一起，希望能能把你变小装在兜里，天天见到你，而且想要和你一直在一块。\r\n1 、还好是变小装兜里。 。 。对了，你看过香港电影《灯草和尚》吗\r\n2 、我发现最适合你的是哆啦 A 梦，你看他整天陪着大雄，又很靠得住，关键他还有缩小枪呢\r\n3 、亲爱的，真的很开心你能告诉我你内心的想法', '文字进逻');
INSERT INTO `talk_art` VALUES (4707, '女：我忘不了前男友', '1、 所以只有找比他优秀这么多倍的我才行吗\r\n2、我完全明白你的感受，他是不是没付你分手费？\r\n3、欣赏的就是你这点，我老是丢三落四的，你记性这么好以后可得多帮我记着点\r\n其他答案： 1：那就是说，我要是把你甩掉的话，你也会忘不了我的了？', '文字进逻');
INSERT INTO `talk_art` VALUES (4708, '问妹子一个问题，然后她答了。', '男：差劲。\r\n女：我哪里差劲了~我这不是很正常的回答嘛\r\n1、我说这问题差劲，连你都答得出来\r\n2、回答很正常，人不怎么正常，当然，是聪明得不正常。所以我觉得自己比较起来很差劲', '文字进逻');
INSERT INTO `talk_art` VALUES (4709, '女：我当然知道啊，我关注你已经很久了', '1、你不是公安部派来对我跨省的吧？\r\n2、那你怎么不给我观赏费，逛动物园还要门票呢\r\n3、难怪最近一直觉得后背凉飕飕的有种被偷窥的感觉', '文字进逻');
INSERT INTO `talk_art` VALUES (4710, '女：你给我唱首歌吧~（其实我真的不想唱歌）', '1、点歌的话请先把点歌的费用付了\r\n2、光唱多没意思，我给你连唱带表演的\r\n3、你就这么想我用歌声来对你表白吗，很有成就感是吧', '文字进逻');
INSERT INTO `talk_art` VALUES (4711, '女：你每次说话都不算数！', '1、你每次说话时都要算自己说了多少个字吗？\r\n2、语文和算数不能一起来的，一心不能二用嘛\r\n3、这个我承认，因为每次跟你说话我的注意力都在你身上了，所以根本不知道自己说过些什么呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4712, '女：你这是耍嘴皮子！', '1、论嘴皮子我哪耍的过你，我只有一张嘴好吧\r\n2、那你干嘛喜欢耍嘴皮子的人呢\r\n女：我哪有喜欢\r\n男：不喜欢还叫我跟你耍嘴皮子干嘛？情侣才一起耍嘴皮子的\r\n3 、那你希望我耍你哪？', '文字进逻');
INSERT INTO `talk_art` VALUES (4713, '女：我真是看透你了~', '1、是吗？那你说说我今天穿什么颜色的内ku\r\n2、我终于找到一个能彻底了解我的人了', '文字进逻');
INSERT INTO `talk_art` VALUES (4714, '女：你觉得我们非常合适？~比如说呢？~！', '1、你是想给自己爱上我找理由吗\r\n2、比如你现在问的这个问题正是我很想回答的呀\r\n其他答案：比如你是腐女，我是宅男\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4715, '女：为什么你总说下次告诉我！或者明天再告诉', '1、因为我不想说下下次或后天再告诉你\r\n2、这样每一个下次和每一个明天你都会从我这里得到新的东西\r\n3 、你没看到电视连续剧和说书的都用这一招嘛？不然怎么挣钱', '文字进逻');
INSERT INTO `talk_art` VALUES (4716, '女：贫嘴！', '1 、是啊，缺的就是嘴，给我 KISS 一个呗\r\n2、你到这个年纪还领悟不了幽默与贫嘴的区别吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (4717, '女：看什么看，没看过啊你？~！', '1、确实没看过长得这么奇怪的\r\n2、对不起，打扰你了，其实我在练习长时间凝视无聊的物体来增强自己的耐性\r\n3、不知道为什么，就是感觉怎么看都看不够', '文字进逻');
INSERT INTO `talk_art` VALUES (4718, '女：我晚上做梦很怕怎么办？', '1、没关系，起来喝个咖啡或红牛什么的，你就睡不着了\r\n2、可以喝点酒壮胆', '文字进逻');
INSERT INTO `talk_art` VALUES (4719, '女：你学我干嘛~？', '1、 最近想去参加模仿秀，先拿你练练手\r\n女：那你模仿我干嘛\r\n男：谁说要模仿你了，我要模仿凤姐\r\n2、以后万一你不见了，我就可以到街上将你的神态特征模仿给每一个遇到的人看，这样更容易帮助我找到你\r\n3、我觉得自己不够了解你的想法，所以就照书上教的方法来试试看------要了解傻瓜的思维，必须先变成傻瓜', '文字进逻');
INSERT INTO `talk_art` VALUES (4720, '女：好纠结啊~！', '1、你这就叫纠结？那让麻花情何以堪呀\r\n2、想跟我表白就直接点，纠结什么\r\n3、我也经常像你这么纠结，一些事情真的很难做出抉择，比如出门是应该先迈左脚还是右脚', '文字进逻');
INSERT INTO `talk_art` VALUES (4721, '有個女孩名叫佩方~不過我事前已知她的名字', '男：你好~妳名字叫什麼?\r\n女：佩方\r\n男：吃了能減重那種嗎? (求更好的答)\r\n1 、算命先生说我这辈子一定要找到属于自己的配方，没想到这么快就找到了\r\n女：BLBLBLBLB\r\n男：他说这样我的痔疮就有救了\r\n2、我去搭讪的时候经常被人家说有病，看来你能帮我治好呢\r\n3、我叫独家', '文字进逻');
INSERT INTO `talk_art` VALUES (4722, '女：没搞错~连我这种大美女的电话你都忘记了？', '1 、脸皮厚也是要有限度的。\r\n。 。\r\n2 、像你这个级别的大美女都不会记在电话里，是要直接记在心里的\r\n女：那你怎么不记得？\r\n男：大美女太多，记不过来了', '文字进逻');
INSERT INTO `talk_art` VALUES (4723, '女：你累了逛不动了为什么不跟我说呢？', '1 、我已经累得说不出话了。 。 。 。\r\n2、 我怕你误会我想拖你后腿\r\n3 、我想看看爱情的力量能不能神奇到让我超越身体机能的极限', '文字进逻');
INSERT INTO `talk_art` VALUES (4724, '女：我们之间就像一杯温开水一样~唉~！', '1、这才能细水长流，干柴烈火的话水一下子就会烧干的\r\n2、那下次我买瓶印度神油滴杯子里吧\r\n3、就你这个心态，应该好好庆幸下杯子里不是冰水\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4725, '\\', '女：你还真走啊？\r\n1、你没发现我刚转身时把手留在你可以拉得到的位置吗\r\n2、我去拿椅子坐你边上，一直站着累\r\n3、我向来尊重我你的意愿', '文字进逻');
INSERT INTO `talk_art` VALUES (4726, '女：我今天去你空间看了有点像某某男明星哦~', '1、我告诉你一个八卦，其实他就照着我的身份证整的\r\n2、那怎么办呀？真是个令人头疼的问题， 你说以后我给你签名\r\n是签自己的名字呢还是签他的\r\n3、建议你以后都去他空间看，这样就会发现他长得很像我的', '文字进逻');
INSERT INTO `talk_art` VALUES (4727, '女：和你交流简直是对牛弹琴！', '1、没错，就算我变成牛，也是只能听懂音律的牛\r\n2、你什么时候学会正常与人交流的？\r\n3、弹琴？我还以为你只会吹箫呢（慎用）\r\n其他答案：你居然对着牛谈情说爱，真是笨到无可救药', '文字进逻');
INSERT INTO `talk_art` VALUES (4728, '女：我考虑考虑~', '1、又不是在跟你求婚，考什么虑\r\n2、连智者千虑都必有一失呢，你还坚持考虑，你想做那个必有一得的吗\r\n3、那你慢慢烤，我要七分熟的，可别烤糊了', '文字进逻');
INSERT INTO `talk_art` VALUES (4729, '女：不是吧，来看我，我怕我会吓着你。 。', '1、那你就戴个面具呗\r\n2、是说你对我的热情会吓到我吗\r\n3、不是啦，人家教我想知道网友到底长什么样就说要去看她，一般不用过去她自己就会讲的\r\n其他答案：1.没关系，我吓大的，厦门大学的；', '文字进逻');
INSERT INTO `talk_art` VALUES (4730, '女：没骗你，我不喜欢骗人的~', '1、不止骗我，还变着法骂我不是人\r\n2、我不信，你敢做测谎实验吗？\r\n女：怎么做\r\n首先，闭上眼睛（kiss 某一个你们目前关系可以 kino 的地方，她肯定会睁开眼）靠，我就知道你骗我的，说好闭眼你还睁开！\r\n3 、我看看你的眼睛就知道你有没骗我(目光凝视片刻)恩，你的眼睛承认你骗我了\r\n女：听你鬼扯，哪有\r\n男：看来，你不只会骗人，还长了一双会说谎的眼睛哦（非原创）', '文字进逻');
INSERT INTO `talk_art` VALUES (4731, '女：解释等于掩饰！', '1、是的，我要用解释来帮你掩饰\r\n2、我这么魅力四射，必须掩饰下出门才不至于被粉丝们围堵\r\n3、亲爱的，你这理论也就跟我说说，以后真犯了事被关进去了问你认不认罪可千万得解释呀\r\n其他答案：解释是因为重视，我在乎你的想法', '文字进逻');
INSERT INTO `talk_art` VALUES (4732, '女：最近在干嘛？', '男：在研究不正常的人与正常人之间的差别\r\n女：什么差别\r\n男：不正常的人老是问正常人最近在干嘛', '文字进逻');
INSERT INTO `talk_art` VALUES (4733, '女：你在敷衍我！', '1、有眼力，我对你刮目相看了\r\n2、我哪会敷衍你呀，一边玩去，乖敷衍你是因为爱你，你看对别人我连敷衍懒得去做\r\n3、你的眼睛肿了，需要敷一下', '文字进逻');
INSERT INTO `talk_art` VALUES (4734, '女：好大的雾啊~', '1、免费体验游伦敦的感觉真好\r\n2、你信（耶稣/佛）吗？\r\n3 、小心！ （拉着她躲到一边的角落）\r\n\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4735, '女：你怎么一点都不怜香惜玉', '1、首先要搞清楚谁是香谁是玉\r\n2、我还没说你怎么一点都不会吹拉弹唱呢\r\n3、对你来说，我懂得偷香窃玉就可以了', '文字进逻');
INSERT INTO `talk_art` VALUES (4736, '女：我很聪明的', '1、聪不聪明还不知道，但从这句话已经知道了你很不要脸\r\n2、哦？那你知道今天六合彩开什么吗？\r\n3、看得出来，一般除了神经病，敢这么明目张胆自夸的都是聪明人', '文字进逻');
INSERT INTO `talk_art` VALUES (4737, '女：你傻傻的', '1、 这真是个贼喊捉贼的年代\r\n2、我承认，我一直很傻，正因为这样才会跟你聊呀', '文字进逻');
INSERT INTO `talk_art` VALUES (4738, '女：年少轻狂', '1、我虽年少，但不会嫌你太老成的\r\n2、如果要我选择，我宁愿年少轻狂\r\n3、谢谢你的夸奖，粉丝们都喜欢我这点呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4739, '女：我为什么要认识你', '1、因为这样下次见面我们就能跟对方打招呼了\r\n2、我既不是卖保险也不是搞传销的，你为什么不认识我呢\r\n3、因为你看上去是一个很友善的女孩子（非原创）', '文字进逻');
INSERT INTO `talk_art` VALUES (4740, '女：你不是我的菜！', '1、当然不是，因为我是做菜给你吃的那个人\r\n2、但你却是我的 FAN（饭）\r\n3、原来你喜欢偷人家菜。 。 。', '文字进逻');
INSERT INTO `talk_art` VALUES (4741, '女：我很胖的', '1、这才好，咱们买一个枕头给你用就行了\r\n2、正好，这样我就省的买杠铃了', '文字进逻');
INSERT INTO `talk_art` VALUES (4742, '女：等我想学打球的时候再找你', '1、好的，记得带够学费\r\n2、想约我吃饭的时候更欢迎你来找我\r\n3、你这个人能不能不这么现实，非得有求于人才联系呀', '文字进逻');
INSERT INTO `talk_art` VALUES (4743, '女：我又胖了（其实还好，不怎么胖）', '1、你希望我说还好，不怎么胖是吧？做梦！\r\n2、再加把劲长肉吧，我一直想亲眼看看人变猪经历的过程\r\n3、感动呀，你一直孜孜不倦地在为我抱起你的困难程度添砖加瓦呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4744, '我旅游时候给她发信息，结果回复如下，', '女：你就天天气我吧！欺负我哪也去不了。你怎么不说带我一起去\r\n1、天天气你的不是我，是天气预报\r\n2、你怎么会哪也去不了，你跟我隔这么远，还能一直来到我心里呢\r\n3、真把你带来了，这边女粉丝们就不敢近我的身了\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4745, '女：希望我的选择是对的', '1、没问题的，这个概率比选择体育彩票头奖大多了\r\n2、是呀。 。 。你从小到大经历了这么多次选择，总也得对一次吧\r\n3、肯定对不了，因为我还没给你幸运之吻呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4746, '三个字，八个字母，说出来', '1、I LOVE YOU ....你想对我说就直说，干嘛非得要我帮你说出来\r\n2、QIN YI XIA---亲一下', '文字进逻');
INSERT INTO `talk_art` VALUES (4747, '女：那你保护我啊', '1、恩，保护女人是男人的天职\r\n2、那你站着别动，我在你周围画一个圈，千万别出来哦，不然被妖怪抓走了我不负责的\r\n3、我倒是很愿意保护你，不过我觉得没有那个坏蛋会对你有兴趣呢', '文字进逻');
INSERT INTO `talk_art` VALUES (4748, '一说到妹子的相貌时候', '女：我都不觉得自己有气质，我觉得我好丑。 。\r\n遇到这些话真不知道怎样答，安慰她，我怕像其他男人一样讨好她，这该怎么回\r\n首先要看对方的样子是不是真不怎么样，如果确实低分，那千万不要再调侃她，如果本身长得高分还要故意这么装逼，那就毫不犹豫地调侃吧\r\n1、（针对低分）你本身长得就属于可爱类型的嘛，而且再好看的女人，到了某个年龄段都是一样，不过这也不重要，因为我注重的女人的，从来都只有内涵\r\n2、（针对高分）其实丑点真没关系，关键是要有内涵，你觉得自己有内涵吗？\r\n3、（针对装逼的高分）长相是父母注定的，丑并不是你的错，但你确实不应该出门，更不应该让我看到，我睡觉要是不幸做噩梦你得全额赔偿精神损失费', '文字进逻');
INSERT INTO `talk_art` VALUES (4749, '女：我会很乖不会让你生气的~', '1、如果你每天都像现在这个样子，估计我可以活到一百岁呢\r\n2、我就受不了你这么乖，赶紧说，想吃点什么好吃的，穿点什么好看的？我恨不得马上就全部买给你', '文字进逻');
INSERT INTO `talk_art` VALUES (4750, '女：那你又有陪过我吗，哼… ！ ？', '1、你又不给小费。 。\r\n2、亲爱的，是我错了，为了咱们的将来所以我把时间都花到工作上去了，我以为你会明白所以也没特别跟你讲过，这是我的疏忽，我会改善的，好吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (4751, '女：真的不知道你心里到底想些什么…', '1 、我在想明天带你去哪吃好吃的\r\n2、我在想你每天到底在想些什么？', '文字进逻');
INSERT INTO `talk_art` VALUES (4752, '女：或许也是我太蠢了吧', '1、英雄所见略同\r\n2、不管你蠢成什么样，我都会对你不离不弃的\r\n3、亲爱的，别着急，明天我就给你买点脑白金补补\r\n', '文字进逻');
INSERT INTO `talk_art` VALUES (4753, '女：说！你有什么阴谋！', '1、阴谋谈不上，我只是想跟你借点钱\r\n2、我最大的阴谋，就是想趁你不注意把你娶回家做老婆', '文字进逻');
INSERT INTO `talk_art` VALUES (4754, '女：你恨对你说慌话的人吗？', '1、如果你已病入膏肓无可救药，别人骗你说还可以治愈的，你会恨他吗？\r\n2、不会的，小时候我问妈妈我是从哪里来的，我妈说天上掉下来的，我到现在都不恨她\r\n3、如果我现在跟你说你长得很漂亮，你会恨我吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (4755, '女：约会干什么', '男：来邂逅一个不一样的烟火', '联系感建立');
INSERT INTO `talk_art` VALUES (4756, '女：你敢欺负我就不理你了', '男：你要走，我不留，你要敢留，我他妈就惯坏你，把你惯的上天入地无所不能\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4757, '女：女生称呼你“亲”', '男：我没亲你，你别诬陷老子.......我隔壁就住着警察叔叔\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4758, '女：哎 为什么你们男人都没有注意到我呢？', '1 、注意到了呀，但我想你的气质使大家都错把你认成男人了\r\n2、（四下张望一圈）：咦？刚刚谁在跟我说话\r\n3 、那是因为你穿着衣服', '联系感建立');
INSERT INTO `talk_art` VALUES (4759, '女：你太有才了', '男：饱读诗书三百卷，只为遇见你', '联系感建立');
INSERT INTO `talk_art` VALUES (4760, '女：我就不相信我找不到男朋友！~', '1、恩，我也不相信你找得到男朋友\r\n2、最后如果实在找不到，就用百度搜搜吧\r\n3、这句话你跟我说说还行，可别出去说，不然都没男人敢上街了', '联系感建立');
INSERT INTO `talk_art` VALUES (4761, '女：你太有才了', '男：一身的才华横溢，只为和你邂逅在红尘滚滚之中', '联系感建立');
INSERT INTO `talk_art` VALUES (4762, '女：说实话~我是带着诚意来寻找人生伴侣的', '1、你这么诚实我也不能隐瞒，我也是带着诚意来寻找性伴侣的\r\n2、你的诚意在哪里呢？给我说说吧，我挺感兴趣的\r\n3、又没跟你玩真心话大冒险，忽然说这个干嘛？', '联系感建立');
INSERT INTO `talk_art` VALUES (4763, '女：你太有才了', '男：你不要套路我，这样我会觉得你只想得到我，而不是欣赏我的才华横溢', '联系感建立');
INSERT INTO `talk_art` VALUES (4764, '女：我认为，我应该跟一个可以和我牵手一起', '1、牵着手的话，你们会不方便打伞的\r\n2、面对风风雨雨，我觉得这个男人为你叫辆的士显得更实在\r\n3、你这个想法的最后结果确实是白头到老-------白了少年头，空悲切', '联系感建立');
INSERT INTO `talk_art` VALUES (4765, '女：女生老是推迟你们的邀约约会', '男：感觉我们两个的唯美邂逅，要变成一场拉锯战了', '联系感建立');
INSERT INTO `talk_art` VALUES (4766, '女：发张可爱的自拍', '男：我是照骗不美\r\n女：那就让美图秀秀，把我这颗心一起骗走好了（要照片）', '联系感建立');
INSERT INTO `talk_art` VALUES (4767, '\\', '1、我什么时候跟你说过甜言蜜语了，我压根就不是一个会浪费口水的人\r\n2、你这么可爱，我怎么舍得打你呢？再说你又不是傻子，我打你你怎么可能不动来让我打呢，你得跑嘛\r\n3、我能感觉到你是务实的人，甜言蜜语这种东西对你不起作用的，那咱们谈个价吧', '联系感建立');
INSERT INTO `talk_art` VALUES (4768, '对于那种喜欢安静的女生', '1、找个开锁的师傅\r\n2、带她们去参加相声培训班\r\n3、我觉得，两个人在一起聊天最开心的是能够互动，但你好像不太爱说话，这样我感觉有点怪怪的， 你能告诉我原因么', '联系感建立');
INSERT INTO `talk_art` VALUES (4769, '女：我们之间的代沟很深！', '1、我不嫌你老/幼稚\r\n2、沟这么深，去找个木头当桥嘛', '联系感建立');
INSERT INTO `talk_art` VALUES (4770, '女：你的 星座让我感到我们不适合在一起~！', '1、难道你要跟我异地恋？\r\n2、你的星座让我感到你很迷信', '联系感建立');
INSERT INTO `talk_art` VALUES (4771, '女：给我送饭来吃！我可以勉强考虑一下！', '1、别勉强考虑了，你也一把年纪的人了，想太多对脑子不好\r\n2、你又没被关到牢里，要我送什么饭\r\n3、你想做白吃（白痴）么', '搭讪开场白');
INSERT INTO `talk_art` VALUES (4772, '女：我们之间的问题已经很严重了，你不觉得吗', '1、有问题，找百度\r\n2、多重？拿个电子秤能称出来么？称不出来那就是还不够重\r\n3、我是一个后知后觉的人，也很在乎你的感受', '联系感建立');
INSERT INTO `talk_art` VALUES (4773, '女：对!没错，你要是有钱人，我就嫁给你！', '1、不要说嫁不嫁了，我要是有钱人，你认为我还会在这里跟你讲话么？\r\n2、钱贿赂得到的，不是爱情，是欲望（感谢冷爱）', '联系感建立');
INSERT INTO `talk_art` VALUES (4774, '女：哈！看见你这样，我由衷的高兴！', '1、你知道吗，从一个人的言语可以看出她的品德和素质\r\n2、哪还用得着别人帮，你自己就是一位天屎了，天上的鸟屎姐姐\r\n3、还好你出了这口恶气，要是一直憋着出不来，呆会放个屁，我死得更惨', '联系感建立');
INSERT INTO `talk_art` VALUES (4775, '女：我们之间有隔阂！', '男：有隔阂不一定是坏事，距离还能产生美呢\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4776, '女：想得美', '1、我知道你想自己更美点，但还是要告诉你事实和理想往往有很大差距的\r\n2、没有想得美，你比我想象中要难看呢\r\n3、卡耐基说过，敢想才是成功的第一步呢', '联系感建立');
INSERT INTO `talk_art` VALUES (4777, '女：怎么样？我终于报仇雪恨了！', '1、可喜可贺，赶紧到酒店摆两桌庆祝下吧，我肚子都饿了\r\n2、仇恨会改变一个人，你看你现在都扭曲成什么样了\r\n3、冤冤相报何时了，施主，放下屠刀，立地成佛，回头是岸呀', '联系感建立');
INSERT INTO `talk_art` VALUES (4778, '女：不要用你的那些小把戏！你就像个小丑！', '1、在扑克牌中，每一个人都能找到自己的定位，而小丑就是王\r\n2、在欧洲中世纪，小丑是只有宫廷里才有的高贵表演，我也不会怪你的孤陋寡闻', '联系感建立');
INSERT INTO `talk_art` VALUES (4779, '女：你这个号码不错哦，让给我好不好~', '1、好啊，你开个价\r\n2、号码的主人更不错，一起送给你吧。。。想得美哦你', '联系感建立');
INSERT INTO `talk_art` VALUES (4780, '女：把东西还给我！快点！', '男：求人做事可不应该是这个态度，我觉得你给我按按摩会比较有效率', '联系感建立');
INSERT INTO `talk_art` VALUES (4781, '女：什么时候把你女友带给我看看！', '1、你这个说话的语气让我想起小时候要求爸爸带我去动物园看猴子的情景\r\n2、那可是个大工程，每天见一个的话，你得忙到年底才能见完，还不算中途加入的哦\r\n3、买一个戒指盒大小的精致小盒子，跟她说：我无法带她来，但可以给你看看照片，打开盒子就行（然后，盒子打开是一面小镜子展现在她面前）', '联系感建立');
INSERT INTO `talk_art` VALUES (4782, '女：你说实话撒，我又不会怪你的', '1、你这句话很耳熟，小时候，我犯错妈妈就跟我说过这句话，结果是---一顿好打，我当时就发誓不再相信女人\r\n2、不是怕你怪我，我怕你接受不了事实昏过去\r\n3、你先说说，我说了实话能给我什么好处吧\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4783, '女：你不觉得我是你玩弄的对象吗？', '男：请不要贼喊捉贼', '联系感建立');
INSERT INTO `talk_art` VALUES (4784, '女：你知道吗~我的心被你深深的伤害了', '1、你的心？不是早就被狗吃了吗？\r\n2、去买个速效救心丸吃下\r\n3、莫非我无意间练会了九阴正经里的摧心掌', '联系感建立');
INSERT INTO `talk_art` VALUES (4785, '女：或许我这样做，你会感到失落！', '1、我觉得，你只有把我从楼顶推下去我才会有失落的感觉\r\n2、一直没跟你说，其实从见到你的那一刻起我已经很失落了\r\n3、有玩蹦极那么刺激么?', '联系感建立');
INSERT INTO `talk_art` VALUES (4786, '女：就算你不要我，我也有很多男生追。', '1、我在街上看到很多男生追的。。。。一般是小偷\r\n2、我不要你了你就打算去参加马拉松比赛减肥吗？\r\n3、对于这点，我知道是我的错，我诚恳的道歉，是我害了他们', '联系感建立');
INSERT INTO `talk_art` VALUES (4787, '女：说实话，你的思维方式实在不怎么样~！', '1、确实不怎么样，只比你好一截而已，太失败了\r\n2、你可以去角逐金马奖了，你说起假话来真像那么回事呢\r\n3、恩，跟你在一起呆久了，思维都变迟钝了', '联系感建立');
INSERT INTO `talk_art` VALUES (4788, '女：我才不呢！', '男：我有点小感动了，你裁布是要给我做衣服吗？', '联系感建立');
INSERT INTO `talk_art` VALUES (4789, '女：哎！那算了，你都这么健忘了，那就不打', '1、你这个人怎么一点爱心都没有，对于健忘的人就是要不停打扰他才能对他加深记忆有帮助呀\r\n2、健忘是假装的啦，主要是筛选掉一批没诚意的，非诚勿扰嘛，结果你马上就原形毕露哦\r\n3、我哪里健忘了，我记性可好了，对了，你是哪位？', '联系感建立');
INSERT INTO `talk_art` VALUES (4790, '女：反向思维你用的太烂了！', '1、你说的是指你想我而我不想你这种反向思维么，我承认\r\n2、烂点对胃好，容易消化\r\n3、你用得好，给我来一个见识下呗', '联系感建立');
INSERT INTO `talk_art` VALUES (4791, '女：我可以理解为，你在求我吗？', '1、早知道你不聪明，但没想到你的理解能力能差成这样\r\n2、没错，我求你有多远走多远。。。\r\n3、你觉得自己长得像个球吗？\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4792, '女：我不喜欢你！', '1、我知道你不喜欢我，喜欢只是前期的表现形式，现在已经过渡到爱的阶段了\r\n2、还好不是我，不然可就头疼了\r\n3、为你喜欢的那个人默哀', '联系感建立');
INSERT INTO `talk_art` VALUES (4793, '女：你也有今天啊！嘿嘿！', '1、每个人都有今天啊，我又不是穿越时空过来的，我是一天一天实打实走过来的\r\n2、我托你的福才有今天，比不了阁下天天有今天这样的情况\r\n3、今天天气晴朗，万里无云，有什么问题么？', '联系感建立');
INSERT INTO `talk_art` VALUES (4794, '女：你忍心让一个姑娘去啊，大晚上的', '男：只要你的脸不吓到路人，其他还需要担心什么？', '联系感建立');
INSERT INTO `talk_art` VALUES (4795, '男：做男人就得对自己狠一点！', '女：像你这么无情的人，谁会喜欢你呢？\r\n1、你肯定没看过 TVB 的少年四大名捕，无情可是相当受欢迎呀\r\n2、关于这个问题我也很纳闷，怎么那么多女孩子会喜欢我呢', '联系感建立');
INSERT INTO `talk_art` VALUES (4796, '女：要不就是你怕冷，就知道窝在被子里面！', '1、怕冷的话抱着你就好了，干嘛要窝在被子里呢\r\n2、怕冷就窝被子里，那怕热不是要在冰箱里呆着？', '联系感建立');
INSERT INTO `talk_art` VALUES (4797, '女：我想知道，我在你心目中是什么样的形象', '1、只能回答你一半，因为目中有，但还没进入心中呢\r\n2、你是无语伦比的，而且是非常令人无语的那种\r\n3、在我心目中。 。 。 。让我想想。 。 。我现在脸红没？女：没有啊（窃喜）\r\n不可能啊，我应该会为你感到脸红啊', '联系感建立');
INSERT INTO `talk_art` VALUES (4798, '女：好！我养你！', '男：那先把这个月生活费打人家卡上嘛', '联系感建立');
INSERT INTO `talk_art` VALUES (4799, '女：你好没有良心~把借口都推在我身上！', '1、这才证明了你在我心目中的重要地位\r\n2、这是检验你以后是否能做个合格老婆的测试\r\n3、我不会推借口，只会 TD 你', '联系感建立');
INSERT INTO `talk_art` VALUES (4800, '男:能认识一下?', '女：为什么?\r\n男：因为你不像坏人\r\n女：可是我怎么知道你是不是坏人 或 可是你是坏人\r\n男：没办法，男不坏女不爱呀\r\n男：我要是坏人就直接把你打晕拖走了，哪会用认识你这么没效率的方法', '联系感建立');
INSERT INTO `talk_art` VALUES (4801, '女：这件事我做的还算妥当吧！你觉得呢？', '1、何止妥当，简直是天衣无缝，为了表示鼓励，我决定赏你一个吻，把嘴靠过来\r\n2、不错。 。挺妥当的。 。 。不过，以后这种事还是交给我来做吧\r\n3、由你出马，我绝对是放心的，看都不用看，就知道一定搞砸了', '联系感建立');
INSERT INTO `talk_art` VALUES (4802, '女：原来你是这种人！', '1、原来你到现在才了解我，我也得重新评估你的智商\r\n2、是不是有惊喜的感觉\r\n3、你有学过女生密码分类法？（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (4803, '女：最近受的打击真不少，我快崩溃了。', '1、平时有兼职去健身房当沙包吗\r\n2、以你的心理承受能力和脸皮厚度，不敢相信还有什么能打击到你的\r\n3、崩溃前把银行卡和密码给我呗，别浪费呀', '联系感建立');
INSERT INTO `talk_art` VALUES (4804, '女：你喜欢，但是我不喜欢啊！', '1、你总是这样故意唱反调来显示独特个性么\r\n2、那就对了，我喜欢的连你也喜欢不是显得我品味差么\r\n3、你喜欢我就可以了，其他夫唱妇随\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4805, '女：你会回信息给我吗？', '1、稍等下， （马上拿手机给她发信息）--会\r\n2、不会，我会直接给你打电话，因为我喜欢听你的声音\r\n3、现在流行微信了，又好玩又省钱，比短信有意思多了，我给你装一个吧', '联系感建立');
INSERT INTO `talk_art` VALUES (4806, '男：天气变凉了，注意添加衣服，替俺照顾好自', '女：神经啊，我照顾好自己和你有什么关系？\r\n男：马上就过年了，你不照顾好自己，到时候我把你拉到菜市场就不值钱了', '联系感建立');
INSERT INTO `talk_art` VALUES (4807, '女：你以为你是我啊！和我比你差远了！', '男：请不要做这种假设，一想到要变成你那样我就从心底难受', '联系感建立');
INSERT INTO `talk_art` VALUES (4808, '女：一个 妹子 调侃我:HI！ 小帅哥！', '1、我发现你一个很大的优点----看人的眼光不错\r\n2、你这搭讪方式可不怎么样，有点老套了', '联系感建立');
INSERT INTO `talk_art` VALUES (4809, '女：你会等我吗？', '男：不会，我会陪着你一起前进，而不是先走一步去等你', '联系感建立');
INSERT INTO `talk_art` VALUES (4810, '女：你好像对很多事情都没多大兴趣哦', '1、恩，现在习惯每天晚上到梦里与睡神沟通\r\n2、就是以前与人沟通太多了，我逐渐对这种虚伪的生物失去了兴趣', '联系感建立');
INSERT INTO `talk_art` VALUES (4811, '女：我觉得我从来都没有真正的爱过一个人', '1 、莫非你。 。 。喜欢动物？\r\n2、那你爱谁？如来佛还是玉皇大帝\r\n3、如果是因为怕受伤，那你就错过了爱的幸福；如果是因为没有发现值得你真正去爱的人，那你就错过了我', '联系感建立');
INSERT INTO `talk_art` VALUES (4812, '女：这个要看你表现了！等你什么时候让我满意', '男：关键是我的表现太好了你的情敌会比现在多很多的', '联系感建立');
INSERT INTO `talk_art` VALUES (4813, '女：我说过， 我想谈次恋爱到结婚', '1、你是不是想太多了，你跟每个异性朋友都这么说么\r\n2、我们的目标一样，但达到目标的方法有分歧呢，我喜欢听不同的意见，这样很有挑战性哦\r\n3、 我不要答案，我对男 PUA 没兴趣，我只要你（恶搞）', '联系感建立');
INSERT INTO `talk_art` VALUES (4814, '\\', '1、那你是想老牛吃嫩草咯？\r\n2、挺识货的嘛', '联系感建立');
INSERT INTO `talk_art` VALUES (4815, '女：感觉你挺眼熟的，你之前是不是有来过这里？', '1、其实…我喜欢你这个套近乎的方式\r\n2、我觉得你也挺眼熟的，上辈子我们是不是认识\r\n3、有的，之前在这里掉了钱所以回来找找，请问是不是你捡到了', '联系感建立');
INSERT INTO `talk_art` VALUES (4816, '女：我不想那么快付出！我害怕受伤害！', '1、菜市场里你不给钱人家是不会把猪肉给你的\r\n2、你有被害妄想症吗你是想我一直付出然后伤害起来会比较方便是吧\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4817, '女：你是？', '1 、你知道吗，你冷漠的言辞深深伤害了一个帅哥的心。 。 。 。\r\n2、你是不是经常忘了把朋友的号码存起来呢\r\n3、给你三个答案 A 前男友 B 现男友 C 下任男友', '联系感建立');
INSERT INTO `talk_art` VALUES (4818, '女：你有钱有房有车吗？', '1、这个，跟你有关系吗？\r\n2、你想送我那些吗？我可不是那么庸俗的人\r\n3、我有这个，你有吗？', '联系感建立');
INSERT INTO `talk_art` VALUES (4819, '女：你不用天天给我短信电话，我觉得有点负担', '1、有负担是想表达我在你心目中有很重的份量吗\r\n2、我觉得你还是让我继续短信电话的好，因为我向来对朋友都是直接去串门不带打招呼的\r\n3、亲爱的，我知道你在心疼我的话费，这让我越来越爱你了', '联系感建立');
INSERT INTO `talk_art` VALUES (4820, '女： 你是做什么的', '1、做鸭的，烤鸭，啤酒鸭，盐水鸭都会做呢\r\n2、有凳子的情况我会选择坐凳子，地上我基本是不会坐的\r\n3、 那要看面对什么了，面对你我就适合做男朋友\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4821, '女：我无法承受你再让我失望！', '1、去买个千斤顶吧，修车的用这个连汽车都能承起，别说是失望了\r\n2、希望越大，失望越大，你如果不抱希望，得到的往往就是惊喜\r\n3、这是个磨练坚强意志的好机会，我在帮你提高自己呢', '联系感建立');
INSERT INTO `talk_art` VALUES (4822, '女：我很聪明的', '1、你在跟我解释自作聪明这句成语的意思吗\r\n2、有多聪明？你会叼报纸回来吗？给你脖子上套个菜篮子你能去菜市场买菜吗？\r\n3、是吗，看来人真是不可貌相啊', '联系感建立');
INSERT INTO `talk_art` VALUES (4823, '女：和你走在一起，完全掉我面子！', '1、这个我明白，所以在我的光环下你要学会提高自己了\r\n2、你从什么时候开始有面子了？\r\n3、如果你有体力的话，我不反对你跑步前进，这样看上去别人就会以为我在追你，只要他们不把你当小偷，你会很有面子的', '联系感建立');
INSERT INTO `talk_art` VALUES (4824, '女： 我只喜欢靓仔. 可是我觉得你不是好靓', '1、没关系，我只喜欢丑女，我觉得你很符合要求\r\n2、你说的喜欢靓仔是指喜欢将最靓的那个宰掉么你的喜好和审美正好成反比', '联系感建立');
INSERT INTO `talk_art` VALUES (4825, '女：你应该是个宅男！就是整天呆在家里的那种', '1、是不是我的皮肤看上去很好让你有了这个认知呢\r\n2、你想利用跟你一样整天呆在家里来找共同点套近乎吗', '联系感建立');
INSERT INTO `talk_art` VALUES (4826, '女：我很自恋的', '男：那你品味可不咋地呀', '联系感建立');
INSERT INTO `talk_art` VALUES (4827, '女：问你个问题哦，你为什么要请我吃饭呀？', '1、我觉得你很懂得饮水思源，这点我很欣赏，相比之下，养殖场的猪就从来不会问饲养员这个问题\r\n2、我不知道是什么令你误会的，但我的本意，是想叫你请我吃这顿\r\n3、最近胃口不好，我想吃饭时看到你的吃相我会比较有食欲', '联系感建立');
INSERT INTO `talk_art` VALUES (4828, '女：我漂亮不', '男：漂亮。 。 。 （一会自捶胸口）\r\n女：你干嘛\r\n1、刚说了违心的话，心脏疼\r\n2、你在我身边让我觉得很有面子（非原创）', '联系感建立');
INSERT INTO `talk_art` VALUES (4829, '男：那女的长得不行，我看不上。', '女：那你看得上我吗？\r\n男：看你我们就不谈长相了，看内涵吧', '联系感建立');
INSERT INTO `talk_art` VALUES (4830, '女：最近比较烦', '男：烦什么？说来听下\r\n女：没什么了\r\n（求接下如何回复比较有趣。 。 。 。）\r\n1、你是想听李宗盛那首歌是吧，难怪不能说来听，得用唱的\r\n2、讲一讲嘛，我也挺烦的，讲出来给我解解闷呗\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4831, '女：不带你这样欺负人的！ ！你真让我头疼！ ！ ！', '1、（针对头疼）虽然你长得有点像孙猴子，可我也不是唐三藏，你头疼关我白龙马事\r\n2、（针对欺负）我从不欺负人，只是偶尔欺负下小猫小狗\r\n3、（针对欺负和头疼）欺负是在磨练你的意志，等你什么时候头不疼了就可以来磨练我了', '搭讪开场白');
INSERT INTO `talk_art` VALUES (4832, '女：我靠！', '1、我比较喜欢能自力更生谁也不靠的女人\r\n2、请别靠过来，男女授受不亲\r\n3、你能不能注意点站姿，别这么懒，站哪都想靠着', '联系感建立');
INSERT INTO `talk_art` VALUES (4833, '女：你觉得我是怎么样的女人', '1 、你是一个执着的女人\r\n女：为什么\r\n男：因为我不告诉你为什么你会一直追着我问（非原创）\r\n2、你是个缺乏安全感的女人，因为你一直需要得到别人的认同\r\n3、夸我一句，我就告诉你', '联系感建立');
INSERT INTO `talk_art` VALUES (4834, '3 个嫉妒话术~！', '1、（大众方法）在 妹子 面前赞美其他女人（特别是要针对 妹子 不具备的优点）\r\n2、（想让她嫉妒某女）晚上 XX 约了我一起吃饭，你觉得我该穿哪件衣服合适点呢\r\n3、（如果自己嫉妒某男）这个男人很不错哦，一看就是个好男人，我看得出来他很需要你，并且很怕失去你，现在这么老实的人不多了，你可要好好把握呀', '联系感建立');
INSERT INTO `talk_art` VALUES (4835, '搭讪过程中~~~~babalabnabbl', '男：你电话号码多少呀？\r\n女：10086\r\n男：求楼主还有什么好的回应？？？\r\n男：我的答案是：那服务态度是不是很好呀？？\r\n1、原来你是在声讯台坐台的\r\n2、然后查动物园的号吗？', '联系感建立');
INSERT INTO `talk_art` VALUES (4836, '女：呸', '1、能不能稍微有点素质，你这么随地吐痰我跟你站一起都脸红\r\n2、根据你这一呸的口水量和口气腥度，我判断你最近有点上火，去买盒牛黄解毒片吃吧', '联系感建立');
INSERT INTO `talk_art` VALUES (4837, '女：你的搭讪方式好老套哦', '1、我一般是因人而异的，对什么类型的人就用什么类型的方式\r\n2、那你觉得自己这种类型的适合用什么方式搭呢\r\n3、这个东西就跟情歌一样，越老越有共鸣\r\n', '联系感建立');
INSERT INTO `talk_art` VALUES (4838, '女：你不老实', '男：你就希望我对你不老实是吧，想得美', '联系感建立');
INSERT INTO `talk_art` VALUES (4839, '女：不要为了结婚而结婚。我可能不会爱你。', '1、你是为了结婚而跟我结婚的吗？\r\n2、你真诚实，又发现你一个优点了，这让我越来越急不可待跟你结婚了\r\n3 、我哪是为了结婚而结婚的，我是为了长期霸占你呀（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (4840, '女：为什么眼泪是咸的?', '1、早叫你吃菜别放那么多盐，你看看，现在好了吧\r\n2、因为咸蛋超人（奥德曼）的眼睛是咸鸭蛋\r\n3、忘了放糖吧', '联系感建立');
INSERT INTO `talk_art` VALUES (4841, '女：你是谁', '男：我是月老派来的........男朋友', '化解');
INSERT INTO `talk_art` VALUES (4842, '女：说实话，我到底在你心里是什么样的人？', '1、你看过白眉大侠么，刀是什么样的刀，剑是什么样的剑，再接下来后面那句形容词就很适合你\r\n2、亲爱的，我认为，你不会因为我的赞美而抬高价值，也不会因为我的贬低而降低价值，无论别人怎么看，你依然是你，对吗\r\n3、在我心里当然就是放在心里的人咯', '化解');
INSERT INTO `talk_art` VALUES (4843, '女：你牵我手做什么~？', '1、怕你摔倒啊，地板砸坏了要赔钱的呢\r\n2、嘘。。。小声点，我发现那边那个女的盯我很久了，被看得浑身不自在，为了绝她的念头，你假装一下我女朋友吧\r\n3、我刚忽然感觉到天旋地转，呼吸不过来，情急下未经同意借你的手扶了一下，对于擅自触摸你私人财物表示歉意，如果你还有不满，随时也可以牵回来的', '化解');
INSERT INTO `talk_art` VALUES (4844, '女：你是猴子派来的逗逼吗', '男：我是月老派来的........男朋友', '化解');
INSERT INTO `talk_art` VALUES (4845, '女：那你就去找一个你喜欢的女孩子当女友撒~', '1、实际上我正在这么做\r\n2、要真能碰到我喜欢的女孩子，别说女友，让她当女神又何妨\r\n3、粉丝太多，导致我很难取舍呀，我不忍心伤害她们其中任何一个人\r\n', '化解');
INSERT INTO `talk_art` VALUES (4846, '女：我已经不指望你请我吃饭了~', '1、恭喜你，终于顿悟了\r\n2、那你是改变主意打算请我吃饭了?', '化解');
INSERT INTO `talk_art` VALUES (4847, '女：额，我们能不能不聊这个话题', '男：这个可以拉近我们彼此的距离，达到零点零一公分，我就可以顺理成章的对你，柔情似水', '化解');
INSERT INTO `talk_art` VALUES (4848, '\\', '1、要直接搬来我家住吗\r\n2、那你现在赶紧借我点钱吧\r\n3、想要人家主动联系你就直说，还拐弯抹角的', '化解');
INSERT INTO `talk_art` VALUES (4849, '女：嘴巴给我放干净点！', '1、（摸自己嘴）我嘴巴上有脏东西吗？\r\n2 、你多虑了，其实我有每天刷两次牙的习惯\r\n3、（慢慢逼近她）\r\n女： 退开，你干嘛？\r\n男：我想看看你干净的嘴巴是什么样子', '化解');
INSERT INTO `talk_art` VALUES (4850, '女：喜欢一个人时什么感觉', '男：就是一眼触电，一眼就勇敢，一眼就想和你吃早餐的那种感觉', '化解');
INSERT INTO `talk_art` VALUES (4851, '女：你只有在无聊的时候才想起我', '1、你怎么这么没自信呢，要坚定自己的信念-------我有聊无聊都不会想你才对\r\n2、因为你是我专属的灵丹妙药，只有你能拯救我的寂寞', '化解');
INSERT INTO `talk_art` VALUES (4852, '女：那你也要穿的帅气点啊，不要丢我脸！', '1、第一次听说你有脸\r\n2、太帅了别的女孩子都会被我吸引过来的，到时候你就傻眼了\r\n3、你直接戴个面具不是更简单么', '化解');
INSERT INTO `talk_art` VALUES (4853, '女：我的白马王子，你到底撒时候出现列~！', '1、等你变成公主以后\r\n2、骑白马的不一定是王子，也有可能是唐僧；王子也不一定骑白马，很有可能他只是为了来骑你的', '化解');
INSERT INTO `talk_art` VALUES (4854, '女：与妹子谈旅游', '男：你去多的地方多吗 \r\n女：还没去到你心里，能叫去过很多地方嘛', '化解');
INSERT INTO `talk_art` VALUES (4855, '女：不是说了吗 不要再联系我！', '1、太久不联系，我怕自己会忘了你的\r\n2、不是我要联系你的，是我的电话想你的电话了。。。\r\n3、女人一般都口不对心的，我怕误会你的意思给彼此留下遗憾，所以才厚着脸皮试一试，你可以生气，你可以不屑，但，真的不可以不让我联系你', '化解');
INSERT INTO `talk_art` VALUES (4856, '男:你回的太慢了，在你回复的时间间隔，我足够吃', '女：那你就去吃呗\r\n1、不吃了，最近减肥中，不节制饮食的话你就我的榜样\r\n2、边吃夜宵还边等你回复，急都急死了，哪里还吃得下\r\n3、说到吃的，我知道有一家很特色的小吃哦，你饿没，带你一起去吧', '化解');
INSERT INTO `talk_art` VALUES (4857, '怎么让女人主动发信息的话术', '1、我跟你赌一百块，今天一定是我给你先发信息\r\n2、我手机好像出问题了，一到某个时段就好多信息收不到，你晚点到那个时候帮我个忙发几条信息给我，我想看看到底收不收得到\r\n3、手机闹铃坏了，我 X 点有个会议，所以想麻烦你帮个忙。到那时候短信通知我一下，我记性不太好哈哈哈', '化解');
INSERT INTO `talk_art` VALUES (4858, '女：你在我眼里就是一个废物~', '1、你在我眼里就是一个废品站，各种废物都去你那卸货哦\r\n2、你喜欢垃圾桶和废物这种奇怪的角色扮演么\r\n3、彼此彼此，但我们还是有区别的，我是可回收的，你是不可回收的', '化解');
INSERT INTO `talk_art` VALUES (4859, '女：你不会真的被女人给骗怕了吧', '1、其实，以前我是被男人给骗怕了，后来我就喜欢女人了\r\n2、那你被男人骗过吗', '化解');
INSERT INTO `talk_art` VALUES (4860, '女：再见的表情', '女：？？？？ \r\n男：再次相见，希望你温柔一点', '化解');
INSERT INTO `talk_art` VALUES (4861, '女：你只是想和我做朋友？', '1、我对你还不够了解，我想多了解你一点再说其他的吧\r\n2、听你这问题，你是想跟我做什么？我很感兴趣\r\n3、宝贝，你很失望么？', '化解');
INSERT INTO `talk_art` VALUES (4862, '女：谁给你的勇气', '男：我是因为看到这张照片，才这么勇敢的，我不想错过我内心的？（发她的照片）', '化解');
INSERT INTO `talk_art` VALUES (4863, '女：我可不是外星人！', '1、我也不是黑超特警，你不用害怕\r\n2、但你长得很像外星人，可以去参加 ET 模仿秀呢\r\n3、你真让我失望，我一直期待有外星人能来把我带走的\r\n', '化解');
INSERT INTO `talk_art` VALUES (4864, '女：干嘛叫我小妞！', '1、难道你喜欢我叫你小姐姐么\r\n2、把妞字拆开你就知道为什么了\r\n3、那你说说你希望我叫你什么？我参考一下', '化解');
INSERT INTO `talk_art` VALUES (4865, '女：给我相信你的理由', '1、给我个不相信我的理由\r\n2、相信一个人需要理由吗\r\n3、信任是互相的，因为我相信你，所以我觉得你也会相信我', '化解');
INSERT INTO `talk_art` VALUES (4866, '女：给我让我和你交往的理由', '1、因为找不到不和我交往的理由\r\n2、做任何事都需要理由吗？这样的人生会很累的。。你让我想起了一个朋友(LBLBLBLLB)\r\n3、因为有爱', '化解');
INSERT INTO `talk_art` VALUES (4867, '女：有自信没自信，跟你没关系吧，', '1、有关系呀，一般跟自信点的女孩子相处起来会令人愉快很多呢\r\n2、我以前的女朋友就是因为对自己不自信，很不放心我，整天疑神疑鬼最后导致我们分手了。。你觉得两个人在一起老是去查对方应该吗？\r\n', '化解');
INSERT INTO `talk_art` VALUES (4868, '女：最后我需要让我感动的表白', '1、我不善于花言巧语，我喜欢从行动上体现我对你的爱\r\n2、我不觉得你是注重所谓口头表白的肤浅女孩子\r\n3、有的人会因为一朵花而感动，有的人会因为一个钻戒而感动，你先告诉我，你知道感动是什么吗？妹子：BLBLBLBLBL', '化解');
INSERT INTO `talk_art` VALUES (4869, '女：你以后发达了，会不会包二奶啊？', '1、你以后做了二奶，会不会发达啊？\r\n2、别开玩笑了！光你就够我受的了，那还有心思包龘二奶\r\n3、不要二奶，太费钱了，小三会好一点', '化解');
INSERT INTO `talk_art` VALUES (4870, '女：这种话你是不是和很多人说过了~？', '1、哪有很多人，绝对不超过 500 个\r\n2、没有啊，刚从电视里学的，第一次实践呢\r\n3、你经常听到别人跟你这么说吗？', '化解');
INSERT INTO `talk_art` VALUES (4871, '女：反正不管怎样。我有我的原则，你管不着', '1、你的原则就是固执？\r\n2、我又不是你爸，干嘛要管你？(一直很想爱你 )', '化解');
INSERT INTO `talk_art` VALUES (4872, '女：如果我家人不同意我们在一起怎么办？', '1、那就坚持到他们同意为止\r\n2、为什么不同意呢？怕我太优秀么？\r\n3、你居然这么快就想到见家长了呀。。。怕我跑掉么', '化解');
INSERT INTO `talk_art` VALUES (4873, '女：你别癞蛤蟆吃天鹅肉了哦，我们怎么可能嘛！', '1、但我真的不会嫌你像癞蛤蟆\r\n2、天鹅在哪没看到，野猪面前倒是有一只\r\n3、原来你喜欢动物这么重口味的玩法', '化解');
INSERT INTO `talk_art` VALUES (4874, '女：你经常这样亲别的女人吗？', '1、你难道希望我经常这样亲别的女人吗？\r\n2、六岁前经常这样亲各种大姐姐，长大后就不行了\r\n3、别的女人经常想这样亲我，不过都被我拒接了', '化解');
INSERT INTO `talk_art` VALUES (4875, '女：去！把碗和衣服洗了！', '1、请不要抢我的台词\r\n2、如果你是认真的，我想先把你的脑洗了\r\n3、亲爱的，你可以放心，我绝不会把你惯成一个好吃懒做的女人', '化解');
INSERT INTO `talk_art` VALUES (4876, '女：我想听听你对第三者的看法？', '1、当局者迷，旁观者清，所以第三者看得最客观呢\r\n2、凡事皆有两面，如果第三者真的出现，那也只能证明两人世界本来就有问题存在导致不牢固呢\r\n3、现在很流行小三哦，一切能流行起来的事物我觉得都可称之为时尚', '化解');
INSERT INTO `talk_art` VALUES (4877, '女：你好会弄啊！（TD 的时候）', '1、要分对象的哦，因为你让我很有感觉呢\r\n2、那当然，我每天都买块猪肉来练习的\r\n3、恩，因为现在富婆的要求都很高，所以不得不把技术练好些\r\n', '化解');
INSERT INTO `talk_art` VALUES (4878, '女：你结婚了没有？或者你多大了', '1、（针对结婚）请问我们是来相亲的吗？\r\n2、（针对多大了）你想了解我的生辰八字跟你合不合吗？\r\n3、（针对结婚和多大了）没结婚，我的小孩 6 岁了', '化解');
INSERT INTO `talk_art` VALUES (4879, '女：你不知道我们俩的内衣不能混在一起洗吗', '1、我明白你的用心良苦，你怕把妇科病传染给我，但我用行动证明我不会嫌弃你\r\n2、我知道，可是洗衣机不知道\r\n3、为了做一个合格的居家男人，我想把洗衣粉的钱节省下来', '化解');
INSERT INTO `talk_art` VALUES (4880, '男：要感谢我，就请我吃饭啊', '女：好啊，我请你买单~\r\n1、买单没问题呀，一张纸能有多贵，你把饭菜钱付了就行\r\n2、你这句话太老套了，我在小学就已经没人讲了，换个有创意的行不\r\n3、中国绅士风度，凡事女士优先，买单当然也不例外', '化解');
INSERT INTO `talk_art` VALUES (4881, '女：我们一起去吧,我妈不会说的~', '1、你妈真好，我们以后一定要好好孝顺她\r\n2、不会说，她是要选择直接动手吗？\r\n3、关键男人打人比较疼，所以我想先了解下你爸的意见', '化解');
INSERT INTO `talk_art` VALUES (4882, '女：为了庆祝我找到男友（老公），你应该说些祝福', '1、祝你们在天为比翼鸟，在地为连理枝，然后我就是猎鸟的和砍柴的\r\n2、你得先祝我逃离你的魔爪', '化解');
INSERT INTO `talk_art` VALUES (4883, '女：不清楚，你想什么，想干什么，我没兴趣', '1、因为我对你来说没有可得感吧\r\n2、你曾经被帅哥深深伤害过是么\r\n3、真遗憾，你这些话让我对你也失去兴趣了', '化解');
INSERT INTO `talk_art` VALUES (4884, '女：你怎么这么开心', '1、跟你在一起不知道为什么就是特别开心，是因为你长得搞笑吗？\r\n2、出门刚吃了一包开心果，我要将它的效果释放出来，不然就白吃了', '化解');
INSERT INTO `talk_art` VALUES (4885, '女：呵呵，我怕被你女朋友知道了打我', '1、你喜欢自己打自己么？（暗示她就是你女友）\r\n2、你这么壮实，谁打得过你呀\r\n3、我女朋友那么多，不可能都知道的。。。至少你不会被群殴嘛', '化解');
INSERT INTO `talk_art` VALUES (4886, '女：我过两天就要走了，你不想说些什么吗？', '1、你想听我讲什么，我都可以满足你\r\n2、还要两天？不能快点走么？\r\n3、有一句话，但现在不说，等你回来再告诉你', '化解');
INSERT INTO `talk_art` VALUES (4887, '女：你太不会说话，太不会聊天，都懒得理你', '1、你没预付我陪聊费，当然就是这个结果\r\n2、谢谢你让我清静\r\n3、你觉得自己很会说话和陪聊的话可以去声讯台兼职赚外快的', '化解');
INSERT INTO `talk_art` VALUES (4888, '女：你又不知道我是个什么样子的人', '1、你不就是我面前这个样子吗？难道你整过容？\r\n2、你怎么确定我不知道你是什么样子的人呢\r\n3、大概知道。。。两个眼睛，两个耳朵，一个鼻子，一张嘴。。哦不，是两张嘴（慎用）\r\n', '化解');
INSERT INTO `talk_art` VALUES (4889, '女：你说你要钱没钱，要房没房，要车没车，', '1、还好你不是我要找的类型，不然爸爸妈妈会很失望的\r\n2、没钱可以赚，赚到可以买车买房，这些比你想变漂亮得往脸上动刀子自残强吧\r\n3、这个答案以你的价值观是理解不了的，我就不浪费口水了', '化解');
INSERT INTO `talk_art` VALUES (4890, '女：难道我就是你前女友的影子吗？', '1、你要是影子，我就是你的太阳，有我在的地方，都有你的存在\r\n2、你是你，她是她，我不喜欢你妄自菲薄\r\n3、你不是别人的影子，是我的，如影随形，我走哪都要你跟着\r\n', '化解');
INSERT INTO `talk_art` VALUES (4891, '女：你什么意思哦！说话不要带刺好不好，', '1、说话怎么带刺，会扎到舌头的好吧\r\n2、我觉得以你脸皮的厚度，我说什么根本影响不了你', '化解');
INSERT INTO `talk_art` VALUES (4892, '女：当心我叫我老公揍你！', '1、怎么奏？太监给皇帝奏本那么奏么\r\n2、你这恶毒女人，想利用我铲除你老公？\r\n3、我不是乐章，你老公也不是音乐家', '化解');
INSERT INTO `talk_art` VALUES (4893, '女：难道你不知道女士优先吗~让开！', '1、我同意你说的，女士优先让开\r\n2、不好意思，还真没分清楚你的性别\r\n3、女士这个称呼跟你的年龄段不适合，你属于（小 P 孩或老太婆）', '化解');
INSERT INTO `talk_art` VALUES (4894, '女：你这饭做得也太难吃了吧~ 我都想吐了！', '1、你吃了？？那份是我专门拿来毒耗子的呀！！\r\n2、想不到我的饭会使人怀孕\r\n3、我也想吐，但不是因为饭，而是因为你', '化解');
INSERT INTO `talk_art` VALUES (4895, '女：看了我空间咋的不留言啊', '1、因为对你空间很无语\r\n2、盐吃多了不好，所以没留给你', '化解');
INSERT INTO `talk_art` VALUES (4896, '女：我想找个能在市区买房子的男友', '1、我也想找个能在市区买房子的女友，如果你有这个条件，那我们再来谈别的吧。\r\n2、原来你的择偶要求这么低呀。\r\n3、我想找个长相对得起市区观众的女友，如果你没有这条件，我想我们连朋友都没得做哟(一直很想爱你 )', '化解');
INSERT INTO `talk_art` VALUES (4897, '女：我在想如果我不答应你的话，会有什么后果？', '1、我会很生气，后果很严重（模仿葛优语气）\r\n2、有想这些的时间，干嘛不直接答应呢\r\n3、当然严重，你后半生也许会生活在悔恨中呢', '化解');
INSERT INTO `talk_art` VALUES (4898, '女：我讨厌你跟着我，不要坏我名声 OK？！！', '1、其实完全不需要我帮忙，你的名声已经够坏了\r\n2、我讨厌你走在我前面，不要让我的粉丝误会，OK?!!\r\n3、那你可以跟着我，你的名声会大幅提高的', '化解');
INSERT INTO `talk_art` VALUES (4899, '女：其实我也没和你吃饭的意思 我的档期都排满了', '男：大排挡原来是这么理解的', '化解');
INSERT INTO `talk_art` VALUES (4900, '女：下次你再穿成这样子，不要和我逛街了~', '1、下次和我逛街，别再鸡鸡歪歪的了\r\n2、如果你还不去整容，也不要和我再逛街了\r\n3、怕我抢你的风头？放心，我会适当收敛的\r\n4、你如果看不下去可以逛街给我买新衣服', '化解');
INSERT INTO `talk_art` VALUES (4901, '女：听到你的话，我自己就要吐了~', '1、我知道一般女孩子听我的话就想和我 TD,但想不到你听到我的话直接就有怀孕的感觉了\r\n2、别担心，有些人在情绪特别感动的时候是有这样的反应的', '化解');
INSERT INTO `talk_art` VALUES (4902, '女：我不笑，难道你要我哭啊，你真太缺德了 1你除了笑就是哭吗？这属于是情绪紊乱综合症', '1、你除了笑就是哭吗？这属于是情绪紊乱综合症\r\n2、哭是释放压力很好的方法，你不懂么\r\n3、我其实更喜欢看你哭笑不得的样子', '化解');
INSERT INTO `talk_art` VALUES (4903, '女：你知道死字怎么写吗？不想死就离我远点', '1、你不知道写吗？小学没学好哦，不过我可以教你\r\n2、你是良品吗？\r\n3、我懂的，小时候一个同学就是掉粪坑里淹死的，从那时开始老师就教我们要远离粪坑', '化解');
INSERT INTO `talk_art` VALUES (4904, '女：谁要是帮我买，或者借我些钱', '1、建议你去信耶稣\r\n2、咱们的想法总是这么心有灵犀地一致', '化解');
INSERT INTO `talk_art` VALUES (4905, '女：那当然哦，我是主角，你是配角！', '1、我们是在演春光灿烂猪九妹吗？你是主角猪九妹。\r\n2、小德子给慈禧太后请安了。', '化解');
INSERT INTO `talk_art` VALUES (4906, '女：抱歉我从不吃晚餐！！！！！', '1、你们家是有多穷？\r\n2、不吃晚餐不用跟我抱歉，你该跟辛苦做饭的妈妈抱歉\r\n3、虽然节俭是传统美德，但也不用省成这样吧', '化解');
INSERT INTO `talk_art` VALUES (4907, '女：你说！我流泪的时候你在哪里', '男：我一个人默默承受生活压力的时候你又在哪里？~\r\n1、你流泪时我的心在滴血，你承受生活压力时我在承受无法帮到你的内心摧残\r\n2、我不忍心亲眼看到你痛苦的样子，于是躲起来了', '化解');
INSERT INTO `talk_art` VALUES (4908, '女：我丢不下心里的包袱~', '1、女孩子的包袱一般在肚子(子宫)里呢\r\n2、有包袱多好呀，郭德纲就是因为包袱多才红的\r\n3、没关系，亲爱的，有我在，一切包袱我会帮你扛着的', '化解');
INSERT INTO `talk_art` VALUES (4909, '女：怎么样？我的衣服好看吧~你嫉妒吧？！', '男：你没有吧？！气死你~呵呵????\r\n1、我嫉妒，我生气，我为什么没有你这样的一身行头，看来我无法跟你争夺丐帮帮主之位了\r\n2、确实气得半死，我恨铁不成钢，我生气你怎么连最起码的穿衣品位都没有\r\n3、衣服确实很好看，你是属于那种穿什么都好看的自然美类型，而且自然美最大的优点就是---如果什么都不穿会更好看', '化解');
INSERT INTO `talk_art` VALUES (4910, '女：如果和你分开是痛苦的话~', '男：我愿意接受这样的痛苦~\r\n1、如果分开会让你痛苦的话，我是无论如何不会分开的\r\n2、你有自虐的情结呢，建议你用 502 把手粘在头发上，再使劲分开，我想那个痛苦更能满足你\r\n3、分开并不痛苦，痛苦的是失去了一种习惯', '化解');
INSERT INTO `talk_art` VALUES (4911, '女：你没听清楚吗？你这样做是不对的！', '1、小时候，老师经常也给我讲你这句话，后来事实证明他很多时候都是忽悠人的\r\n2、为了保持礼貌，我就当没听清楚吧，同时也希望你不要再重复了\r\n3、凡事都有对错两面，你看问题太单一了\r\n', '化解');
INSERT INTO `talk_art` VALUES (4912, '女：可以一起去看落日吗？', '男：据说日落的时候许个愿就可以成真哦', '化解');
INSERT INTO `talk_art` VALUES (4913, '女：你说我是上天赐给你的礼物？', '男：可能从天上下来时没着陆好，摔到脑袋，感官神经受了损伤吧', '化解');
INSERT INTO `talk_art` VALUES (4914, '女：我是个慢热的人', '1、我会随身携带打火机给你加热的\r\n2、别人今天给你送花，要等到后年才会爱上他的那种么', '化解');
INSERT INTO `talk_art` VALUES (4915, '\\', '1、恩，早听说过最毒妇人心，我就是想见识下能有多毒，我的目标是超越尝百草的神农氏呢\r\n2、爱的越深会恨得越深，但如果给我重来一次的机会，我不会后悔那深深的爱\r\n3、你说的是谜男方法的事后后悔吗（慎用）', '化解');
INSERT INTO `talk_art` VALUES (4916, '女：你说你不是大男子主义，', '1、不和你商量，是不想让你操心，撒谎，是不忍心看你伤心，现在回答你这些问题，是表达我对你的真心\r\n2、我确实不是大男子主义，不然你说这话早就被一巴掌抡上脸了\r\n3、你喜欢优柔寡断前怕狼后怕虎的男人还是我？', '化解');
INSERT INTO `talk_art` VALUES (4917, '女：你做过最疯狂的事是什么', '1、喜欢上你\r\n2、假装正常人', '化解');
INSERT INTO `talk_art` VALUES (4918, '\\', '1、想认识帅哥先做自我介绍吧\r\n2、这个真不重要，等我们多了解对方一点再说也不迟', '化解');
INSERT INTO `talk_art` VALUES (4919, '女：我的经验告诉我，我不能相信你！', '1、看你外表纯纯的，原来对男人这么有经验\r\n2、经验只能证明过去的事，不能决定未来的事，你其实可以改变下这种保守的思维方式', '化解');
INSERT INTO `talk_art` VALUES (4920, '女：你不是说我不是你的菜吗？干嘛还要来找我~', '1、你不是我的菜，却是我的饭（FAN），我是很疼自己 FANS 的\r\n2、正因为你不是我的菜，所以我打算来偷菜，我喜欢这种感觉\r\n3、最近吃惯了大鱼大肉，想换换口味', '化解');
INSERT INTO `talk_art` VALUES (4921, '女：都怪你~要不是你~事情也不会变的那么糟糕', '1、确实怪我，我不该跟你扯上关系的\r\n2、我一直很佩服你这种出了问题能迅速推诿掉的能力，可以教教我么，教会了一定请你吃饭\r\n3、我终于进步了，要是按以前的做事方法，你现在已经不会跟我说话了', '化解');
INSERT INTO `talk_art` VALUES (4922, '女：我希望我能找到一个永远永远对我好的老公', '1、对我提要求能不能婉转点\r\n2、你比紫霞还贪心，人家也就是要个 10000 年，你竟希望永远', '化解');
INSERT INTO `talk_art` VALUES (4923, '女：我就想找个依靠~可是我的心决定了你不是', '1、（唱一首任贤齐的依靠送给她）\r\n2、我当然不是你的依靠，我是你的港湾，依靠只是暂时的，港湾才是永久的\r\n3、门外拐弯 50 米就有根电线杆，靠去吧', '化解');
INSERT INTO `talk_art` VALUES (4924, '女：有人说你很花心哦！', '1、还有人说你很滥交呢，但关键是我相信你，完全不信别人造的谣\r\n2、别人要说我像刘德华，我难道就会更帅吗？\r\n3、说我花心你就来问，说我有钱又大方你现在是不是就该来借钱了', '化解');
INSERT INTO `talk_art` VALUES (4925, '女：我没成仙，道行不够 ！', '1、但我觉得你很神\r\n2、我放个屁给你制造点祥云的感觉会比较像仙\r\n3、没鲜可以加点鸡精', '化解');
INSERT INTO `talk_art` VALUES (4926, '女：跟她一刚结婚的姐妹闹别扭，', '1、赌气说要找一个有权有势又有钱的男人作老公，就是结了再离都无所谓，只为了比过她的姐妹，我该怎么说？\r\n2、我完全支持你的想法，到时候我们里应外合骗光那个男人的家产\r\n3、有权有势又有钱？你让我太失望了，你漏掉了有才的呀，我一般看不起庸俗的女人呢\r\n4如果要对我表白也不用这么直接吧？', '化解');
INSERT INTO `talk_art` VALUES (4927, '女：我只是喜欢落日黄昏的感觉，很安静', '1、我知道有个地方欣赏落日黄昏不错哦，下次我们一起去感受吧\r\n2、恩，一般偶像剧演到这里接下来就是 KISS 了\r\n3、稍等（拿手机出来按几下）我们把手机调振动的才能避免打扰这个安静的氛围', '化解');
INSERT INTO `talk_art` VALUES (4928, '女：哈哈,难道你暗恋我？', '1、摸她的脸\r\n妹子：你干嘛\r\n看看这么厚的脸皮手感会是怎样\r\n2、请告诉我怎么回答又能说实话又能不伤害你呢\r\n3、现在女孩子都流行用反问句表白么\r\n', '化解');
INSERT INTO `talk_art` VALUES (4929, '女：你觉得多交朋友的好处是什么？', '1、好处就是经常有惊喜，比如像你的出现并让我认识了你\r\n2、你觉得多交朋友的坏处是什么\r\n女：不知道\r\n男：没有坏处的事干嘛不多干呢\r\n3、走投无路时借钱的对象会多些呢', '化解');
INSERT INTO `talk_art` VALUES (4930, '女：你不是我想要的那种！你可以找到更好的', '1、我确实可以找到更好的女孩，但关键谁让我就他妈只喜欢你呢\r\n2、但算命的师父说你就是我的真命', '化解');
INSERT INTO `talk_art` VALUES (4931, '女：你认同我的想法吗？', '1、我还不够了解你，所以不好评价，你可以先多给我讲讲你自己\r\n2、寻求认同也是缺乏安全感的表现，我想我可以给你缺的东西\r\n3、想法不重要，因为想再多没有行动也是空谈，关键是做法，所以。。要对我表白的话就赶快吧', '化解');
INSERT INTO `talk_art` VALUES (4932, '女：女生不回复', '男：你看，我没说错吧，劳资分分钟把天聊死', '设立框架');
INSERT INTO `talk_art` VALUES (4933, '女：无所谓', '1、你这句说得比杨坤唱得还好听，再来一遍吧，我喜欢2、女孩子不要老是抱着无所谓的态度，要知道多少女孩就是被这态度害了\n3、如果无所谓的话，那就顺便在借我点钱吧', '设立框架');
INSERT INTO `talk_art` VALUES (4934, '\\', '1、吃得苦中苦，方为心上人，你把这个当成做我心上人前的考验就会好很多的\n2、先苦后甜的爱情最浪漫了\n3、你要是拿跟我在一起时的甜蜜作比较，世间一切事物都将是苦的', '设立框架');
INSERT INTO `talk_art` VALUES (4935, '女：你有病', '男：已病入膏肓，除了你的柔情似水，无人能救', '设立框架');
INSERT INTO `talk_art` VALUES (4936, '女：不乖哦！', '1、你也知道你不乖啊，那还不乖乖把手伸过来给我亲一下\n2、请给个标准示范，你乖一个我看看\n3、我要乖起来你一比较肯定望尘莫及了，所以我把这个可爱的好习惯让给你，让你可以更讨我喜欢', '设立框架');
INSERT INTO `talk_art` VALUES (4937, '女：我的直发真美', '1、确实很美，如果能换张脸搭配那就更完美了\n2、我的腿毛拉直也很美的\n3、恩，这假发多少钱？\n妹子：不是假的，这是真的\n不可能，少忽悠我，我才不信呢（稍使点力去扯下她头发，她会知道被你捉弄了）诶呀，果然是真的，扯都扯不掉', '设立框架');
INSERT INTO `talk_art` VALUES (4938, '女：我们好久没聊天了吧', '男：隔了半个世纪没聊了，你怎么还没变老', '设立框架');
INSERT INTO `talk_art` VALUES (4939, '女：我恨全世界所有男人', '1、不孝的家伙，你把你爸也捎上了\n2、没关系。。不要悲观失望，我相信只要你努力改变自己，最终总有男人看得上你的\n3、女同性恋的第一步都是从有这个想法开始的', '设立框架');
INSERT INTO `talk_art` VALUES (4940, '女：我跟你性格不合，所以我不喜欢你这样的', '1、分久必合合久必分，我已经分了很久了，该合了\n2、你除了对男人的品味有问题，其他我都挺喜欢的', '设立框架');
INSERT INTO `talk_art` VALUES (4941, '女：我就是这样子的', '1、找别人就能改变你吗？那你告诉我该去找谁\n2、要改变你的样子其实很简单，找家整形医院就行了\n3、儿不嫌母丑，狗不嫌家贫，我不嫌你差', '设立框架');
INSERT INTO `talk_art` VALUES (4942, '女：在干嘛', '男：在做某某某\n女：这么同步，看来是月老安排我们，一定有故事', '设立框架');
INSERT INTO `talk_art` VALUES (4943, '女：你喜欢我什么', '男：我喜欢你，喜欢我的样子，对我柔情似水，千娇百媚，让我油然而生一种，架起肩膀保护你的欲望', '设立框架');
INSERT INTO `talk_art` VALUES (4944, '女：废话', '1、知道是废话你还说？\n2、你再说废话前先等一下，我去拿个垃圾桶过来接\n3、如果废话是有形的东西，那我们早就被你的废话埋掉了', '设立框架');
INSERT INTO `talk_art` VALUES (4945, '女：你为什么这么牛？', '1、我是我妈生的，不是天生的，天生的是孙悟空\n2、我也很想知道我为什么这么牛，也许是因为喝了三鹿奶粉，导致百毒不侵或变异什么的\n3、纯属后天练习：拓展知识面，累积，练习。我也从不相信有所谓的天才，我认为要么是从小的生长环境造就了天才，要么是不为人知的努力造就了天才', '设立框架');
INSERT INTO `talk_art` VALUES (4946, '女：你...跟我有什么关系？', '1、就你这逻辑思维能力，估计很难跟你说清楚', '设立框架');
INSERT INTO `talk_art` VALUES (4947, '女：你吃饭了没？', '1、没吃，你运气好，可以请我吃\n2、吃了，但为了你我可以牺牲自己的胃陪你再吃一顿', '设立框架');
INSERT INTO `talk_art` VALUES (4948, '女：你想多了', '男：这明显是你给我的诗情画意的灵感', '设立框架');
INSERT INTO `talk_art` VALUES (4949, '女：谢谢', '1、你打算怎么谢？你可千万别说以身相许，我要求很高的\n2、咱们这关系还说谢谢就太见外了，你还是请我吃饭实际点\n3、竟然会说谢谢？看来我低估了你的素质', '设立框架');
INSERT INTO `talk_art` VALUES (4950, '女孩每次都回哼~~~怎办', '1、你知道最喜欢哼哼的是哪种家畜吗\n2、你再哼我就“哈”，我们可以组成传说中的哼哈二将', '设立框架');
INSERT INTO `talk_art` VALUES (4951, '如果我老了难看了你还会爱我吗？', '1、你老了难看了你觉得我那时候还能年轻好看么，我又不是妖怪\n2、你的意思是觉得自己现在还挺年轻挺好看？\n3、肯定不会，不管你老成什么样，你妈肯定比你更老，我又怎么会爱她', '设立框架');
INSERT INTO `talk_art` VALUES (4952, '女：壁咚', '男：那我要是霸气的将你双手紧握，依偎在墙角，你会不会含羞欲滴，心跳加速呢', '设立框架');
INSERT INTO `talk_art` VALUES (4953, '我也没有打算领奖哦', '1、你最近进步很多，越来越有自知之明了\n2、拜托，那你不会是打算演讲得奖感言吧\n3、你真没常识，举办方式不会把奖品快递到你家的', '设立框架');
INSERT INTO `talk_art` VALUES (4954, '女：你先回吧，不用等我', '1、我正有要走的意思，你这么一说，搞得我倒不好意思了，你害的我很纠结呢\n2、那好吧，如果我不在，你遇到危险就打110\n3、那我在家等你，你也快点，不然一会洗澡水该凉了', '设立框架');
INSERT INTO `talk_art` VALUES (4955, '女：男人，是不是都喜欢和美女搭讪', '1、是的，但是，你肯定不会被搭的\n2、女人，是不是都喜欢和帅哥搭讪？\n3、也不一定，同性恋就不搭女的', '设立框架');
INSERT INTO `talk_art` VALUES (4956, '你这孩子，怎么这么调皮', '1、你的不调皮可以中和我的调皮，以后我们的孩子就乖了\n2、你想跟我要个调皮的孩子吗？那得先把嫁妆拿过来\n3、看到好玩的玩具任何孩子都会忍不住调皮的（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (4957, '不吃早饭你不听话哦', '1、不吃早饭是不动嘴，跟耳朵听不听话没关系好吧\n2、乖，还是你比较听话，帮我把那份早饭吃了\n3、听你话了就给我TD么', '设立框架');
INSERT INTO `talk_art` VALUES (4958, '你身上看不到希望', '1、你想让我去纹这两个字在身上吗？那会很疼的哦\n2、我身上又没长眼睛，怎么看得到东西', '设立框架');
INSERT INTO `talk_art` VALUES (4959, '太丑了', '1、我很丑，可是我也不温柔，你再说我丑我就揍得你比我还丑\n2、我眼睛里倒映出的是你的尊容呢\n3、你有没有常识，男儿无丑相不懂么？', '设立框架');
INSERT INTO `talk_art` VALUES (4960, '\\', '1、宝后面还要加个贝才适合你\n2、没错，而且还是无价之宝\n3、你也宝一样，而且是活生生的宝，简称活宝', '设立框架');
INSERT INTO `talk_art` VALUES (4961, '朋友给介绍了一个在编护士 怎么和她聊', '1、要是我小时候能认识你，打针时就不会疼得像杀猪那样叫了\n2、有个护士朋友好处就是多，以后我在家躺着，可以帮我端茶递水呢\n3、难怪现在街上有气质的女孩子越来越少，原来全跑去做护士了', '设立框架');
INSERT INTO `talk_art` VALUES (4962, '你是管什么的', '1、我是管钱的，你有这个需要我可以帮你管的，利息是请你吃饭，比银行人性化多了\n2、管饭，一日三餐，你来不\n3、专管那些喜欢问我笨蛋问题的傻女生', '设立框架');
INSERT INTO `talk_art` VALUES (4963, '我为什么要给你电话', '1、因为你长得很大方呀\n2、你每认识一个朋友，都需要理由么？\n3、我不要你的电话，我自己有一个，给我你的电话号码就可以了', '设立框架');
INSERT INTO `talk_art` VALUES (4964, '尊重长者嘛！也是你给我打电话啊！', '1、打电话太没诚意了，尊重长者应该去老人院看你才对\n2、请不要倚老卖老\n3、老人家，电话对你身体不太有利，我们直接见面才会有益你身心', '设立框架');
INSERT INTO `talk_art` VALUES (4965, '你是不是觉得我是个很随便的女孩子？', '1、我觉得你跟我是一个类型的人，你觉得我随便吗？\n2、请不要这么说自己，随变，是牛奶味的冰激凌，吃起来很甜很腻的，我认为你比它还甜，并且我觉得一点也不腻\n3、亲爱的。。。我不许你这么假设自己', '设立框架');
INSERT INTO `talk_art` VALUES (4966, '不要尽损我撒', '1、哪有钱尽笋你，菜市场的笋现在卖很贵的\n2、谦受益，满招损，从自己身上找原因才是关键\n3、以你的脸皮厚度，我想怎么损都没用的', '设立框架');
INSERT INTO `talk_art` VALUES (4967, '你...', '1、在前面加上我爱两个字听着会顺耳很多\n2、怎么？看到我就害羞得接不上话来了\n\n3、不要整天把我挂在嘴边，这样别人会误会的，影响我市场了你赔啊？', '设立框架');
INSERT INTO `talk_art` VALUES (4968, '如果我不能给你满意的答复？你怎么办？', '1、我早有心理准备的，人生不如意之事，十有八九，何况是给傻瓜提问这种不靠谱的事情\n2、那就给我满意的TD也行', '设立框架');
INSERT INTO `talk_art` VALUES (4969, '就我没有人疼！', '1、你希望哪里疼，我帮你掐\n2、我疼你啊，宝贝\n3、你撒娇的样子真可爱（接KINO）', '设立框架');
INSERT INTO `talk_art` VALUES (4970, '男:你好 我想认识你。', '女：恩,好啊\n1、因为完全认识你得要很长的时间，所以为了不耽误你我打算留下你的电话，然后慢慢认识你\n2、你是我今年遇到的唯一一个这么爽快的女孩子，为了表达我对你的敬佩，请一定要留下号码给我\n3、为了表达对你这么友善回答的诚意，我决定将电话号码告诉你，你电话几号，我打过去你就知道了', '设立框架');
INSERT INTO `talk_art` VALUES (4971, '你说 你是吃硬的还是吃软的', '1、我还有得选，你要吃我的，就没得选了，肯定就是软，硬，软这个过程', '设立框架');
INSERT INTO `talk_art` VALUES (4972, '女：你哪个单位的？', '1、回答问题前先告诉我你是哪个派出所的\n2、想给我单位写表扬信夸我帅吗？千万别，我们领导已经很嫉妒我了\n3、不用担心单位的事情，我们单位福利可好了，会分房子的', '设立框架');
INSERT INTO `talk_art` VALUES (4973, '女：汗', '1、经常出汗，促进新陈代谢，所以你皮肤不错2、出汗了？看到帅哥（或与帅哥聊天）你会觉得热吗？\n3、介绍你一种香水很不错，我懒得洗澡时就靠那个遮盖汗味', '设立框架');
INSERT INTO `talk_art` VALUES (4974, '说话越来越放肆了你~', '1、早说你比较喜欢我行为越来越放肆不就好了\n2、我跟那些没种的伪君子不一样的，畅所欲言多好，老是戴着面具我们大家都很累的\n3、从眼神中我看到你内心深处喜欢我这么放肆的感觉呢', '设立框架');
INSERT INTO `talk_art` VALUES (4975, '我就是不信', '1、你是光荣继承了马克思怀疑论思想的精神楷模\n2、总是逃避现实不是一个好方法\n3、你宁愿相信世上有鬼也不愿相信帅哥这张嘴啊', '设立框架');
INSERT INTO `talk_art` VALUES (4976, '女：一万句我爱你抵不上一句我娶你', '1、一万句我娶你抵不上一张新婚姻法\n2、你希望我们的爱情这么快就走到坟墓里去吗？请回答YESORNO\n3、那句话要等把你的嫁妆搞清楚才能说的', '设立框架');
INSERT INTO `talk_art` VALUES (4977, '女：你是做什么的？', '男：年薪500万，配车配房，你猜是做什么的\n女：猜不到\n做梦的（非原创）\n男：我是生物工程学研究员专门研究世界上还活着的恐龙\n女：世界上还有活着的恐龙吗？有啊，比如你。。\n男、通常情况我都坐椅子，不坐床，更不坐地上', '设立框架');
INSERT INTO `talk_art` VALUES (4978, '女：我就要说！或者 我就要怎么怎么样', '1、唱：巴拉巴拉波~~(王力宏麦当劳主题曲-《我就喜欢》)\n2、磁铁的两面是互相吸引的，你故意唱反调，就是想吸引我吗？', '设立框架');
INSERT INTO `talk_art` VALUES (4979, '不要仿造我的一切。你并不合适这样', '1、我又不是卖盗版的，仿造你什么\n2、请先搞清楚谁在仿造谁\n3、你知道自己适合怎样吗？', '设立框架');
INSERT INTO `talk_art` VALUES (4980, '你烦不烦啊', '1、干嘛这么问，你要给我吹个小曲儿解闷吗\n2、不凡，卓尔不凡\n3、烦得很呢，最近一直不来大姨妈，什么时候我要去医院看看是不是怀孕了', '设立框架');
INSERT INTO `talk_art` VALUES (4981, '你不觉得我们是不可能的吗？', '1、李宁说：一切皆有可能\n2、不管你觉得可不可能，反正我觉得可能，这是个奇迹\n3、我这个人，最喜欢挑战的就是不可能完成的任务', '设立框架');
INSERT INTO `talk_art` VALUES (4982, '妹子：你根本就不在乎我', '1、我如果真不在乎你，你没机会让我听完这句话的\n2、我也觉得很不可思议，你长得一般，又不是富婆，还老是这么疑神疑鬼，但傻B的我偏偏就是这么没有理由地在乎你\n3、你根本就不了解我，不然不会说出这句话', '设立框架');
INSERT INTO `talk_art` VALUES (4983, '你不要吃里扒外好不好', '1、看过无间道吗，其实。。我是一个卧底\n2、谁知盘中餐，粒粒皆辛苦。不吃里扒外怎么对得起农民伯伯！', '设立框架');
INSERT INTO `talk_art` VALUES (4984, '本人字典里没有这个字啊', '1、建议你去买这字典的书店找老板退钱，他不肯就直接打315举报他卖假字典\n2、字典里肯定是有，它认识你，你不认识它而已，没文化真可怕\n3、那你用手机搜搜看吧', '设立框架');
INSERT INTO `talk_art` VALUES (4985, '我觉得你一点都不真诚', '1、现在不流行橙，现在全世界都在用苹果\n2、请你先了解清楚什么是真诚再质疑我吧', '设立框架');
INSERT INTO `talk_art` VALUES (4986, '有啥好说的！', '1、你要这么问的话，那就来段相声吧，相声挺好\n2、女孩子活泼点，不消极才会比较可爱呢\n3、没啥好说的，唱首歌算了，不许问有啥好唱的，挑拿手的来吧', '设立框架');
INSERT INTO `talk_art` VALUES (4987, '你不觉得你很 2（傻）吗？', '1、以前有觉得，自从看到你以后，就完全不觉得了，谢谢你\n2、我2，你213（213=2B）\n3、哪个2？520的2？你觉得我2吗？请不要这么自恋', '设立框架');
INSERT INTO `talk_art` VALUES (4988, '你和我交友的目的是什么？', '1、你平时做任何事都一定要有目的吗？\n2、交朋友开心就好，我觉得你很友善，所以想跟你交朋友\n3、不以结婚为目的的TD就是耍流氓，不以TD为目的的交友就是伪君子（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (4989, '和你说话浪费我时间，或者浪费我口水', '1、说话又不是接吻，浪费什么口水，跟你接吻才叫浪费口水\n2、确实浪费时间，我高估你了，原以为你的思维能力能理解我的话\n3、如果你能专心对话而不是在对话时老想些时间和口水之类的东西，对话会顺利很多', '设立框架');
INSERT INTO `talk_art` VALUES (4990, '那好，你介绍下你自己？', '1、性别--男，爱好--女\n2、我姓秦，叫艾德（亲爱的）\n3、我这个人从小到大一直对命理学很感兴趣，前几日算命先生说我今天在这能遇到一位（形容她特征）的朋友，果不其然啊', '设立框架');
INSERT INTO `talk_art` VALUES (4991, '你经常来这里玩吗？', '1、你为什么会长成这样呀\n2、因为算命先生跟我爸妈说有朝一日有个女人问我为什么取这个名字的傻B问题时，就可以带她见家长了\n3、因为当时我不具备发言权', '设立框架');
INSERT INTO `talk_art` VALUES (4992, '你为什么会取这个名字啊', '1、你为什么会长成这样呀\n2、因为算命先生跟我爸妈说有朝一日有个女人问我为什么取这个名字的傻B问题时，就可以带她见家长了\n3、因为当时我不具备发言权', '设立框架');
INSERT INTO `talk_art` VALUES (4993, '我怎么又看见你了？', '1、虽然这个套近乎的方法有点点过时，但我很喜欢\n2、佛说前世的五百次回眸才换来今生的一次相遇，看来我们前世回眸了1000次哦', '设立框架');
INSERT INTO `talk_art` VALUES (4994, '那要是我就是拒绝你呢？', '1你会让我多一次被拒绝的经验，累积一定经验后我就无敌了，但无敌还有另一个捷径，那就是---你直接接受我\n2那就是你瞎了眼呗', '设立框架');
INSERT INTO `talk_art` VALUES (4995, '我要是不去，会怎么样呢？', '1我会被很多热情的粉丝瓜分掉，我需要你去保护我\n2那就不一定了，要抛纸团决定下一个陪我去的美女是谁', '设立框架');
INSERT INTO `talk_art` VALUES (4996, '呵呵，我不稀罕。', '1、你又不是毛巾，当然不吸汗\r\n2、你确实不西汉，西汉女人据说都是以身轻如燕，身姿窈窕为美\r\n3、物以稀为贵，你不稀罕也就不金贵了', '设立框架');
INSERT INTO `talk_art` VALUES (4997, '你为什么要认识我？', '1、因为我最近在学习卡耐基的人生指南，他教我每天要认识一个新朋友，你是今天的那一位哦（非原创）\r\n2、因为你看上去不像坏人（非原创）\r\n3、你每次认识新朋友都需要给自己一个理由吗？\r\n其他答案：当你的朋友认识你的时候你也这么问的吗？', '设立框架');
INSERT INTO `talk_art` VALUES (4998, '怎么样，我的衣服漂亮吧？', '1不能用漂亮来形容，简直是完美啊!!\r\n2漂亮！连穿在你身上都能这么漂亮，我无法想象这衣服挂起来得有多漂亮了\r\n3穿在你身上相当一般。。。。因为跟你的美丽比起来，任何衣服都会失去它的光彩\r\n', '设立框架');
INSERT INTO `talk_art` VALUES (4999, '哎，你怎么能这样想呢？', '1你要明白，人的想象力是无穷的\r\n2你怎么能让我不这么想呢\r\n3恩，下次我那样想吧', '设立框架');
INSERT INTO `talk_art` VALUES (5000, '即使你名草有主，我也要移草接木', '1你是移花宫来的么？怜星邀月是你什么人？（感谢绝代双骄）\r\n2移草接木这么破坏大自然平衡的事你也干得出来呀\r\n3草皮很昂贵的，移的时候请你轻点', '设立框架');
INSERT INTO `talk_art` VALUES (5001, '妹子：请问姐姐真的像猪吗？', '1你自信点好不好，就算全世界说你像猪，但自己一定要坚信----我不是猪！\r\n2像一只可爱的荷兰猪，真想抱在怀里好好疼你\r\n3你去猪圈里站一会，看种猪们拱不拱你，你就知道答案了', '设立框架');
INSERT INTO `talk_art` VALUES (5002, '目标：我对你不感兴趣', '1你对我感不感兴趣与我何干呢？我只是觉得你看上去比较友善所以才过来跟你打个招呼而已，你想太多了\r\n2你不用对我感兴趣，只想请教你一个问题，像你这样类型的女孩子一般对怎样的男人感兴趣呢\r\n3你认为每个男人都需要你对他们感兴趣吗', '设立框架');
INSERT INTO `talk_art` VALUES (5003, '你真的觉得我很好吗？', '1脉搏不会骗人，你摸着我脉搏再问一遍就知道我有没骗你了\r\n2在没有更好的出现前，你真的很好\r\n3目前就我了解的部分确实很好，但没有任何人是完美的，我还想更多的了解你不好的方面，爱你， 就要爱你的全部，包括所有的好与不好\r\n', '设立框架');
INSERT INTO `talk_art` VALUES (5004, '你也不看看你自己什么样子，还来追我！', '1抱歉，我刚意识到自己这么帅又这么直接会让你更自卑的\r\n2没听过追贼前还要先照镜子的\r\n3连你这素质都敢上街了，我还有什么事不敢做的', '设立框架');
INSERT INTO `talk_art` VALUES (5005, '你碰我干什么~不要碰我不好吗？！', '1实际上，刚刚帮你拍掉了一只小虫，但你的反应着实吓到我了，我拍掉的该不会是你养的宠物吧\r\n2我想测试下不同类型的人对陌生人的接触会有怎样的反应，现在已经有结果了，想知道你是什么类型的人么（接冷读）\r\n3有一种人叫玻璃人，天生体质就是像玻璃那样脆弱，别跟我说你也是哦', '设立框架');
INSERT INTO `talk_art` VALUES (5006, '你和他（她男友）是无法比拟的！', '1没人告诉你尝试将过去时与现在进行时比较是很愚蠢的做法吗\r\n2比什么呀？x能力么？我们要先 TD 才好比呀\r\n3亲爱的。。现在不流行比武招亲了', '设立框架');
INSERT INTO `talk_art` VALUES (5007, '利用冷读猜女生的性格', '男：你是个感情丰富的人，但是不善于表达，有时会被人误解为一个冷漠的人\r\n男：你是个感情丰富的人，但是不善于表达，有时会被人误解或给人冷淡的印象。（冷读）', '冷读语录');
INSERT INTO `talk_art` VALUES (5008, '利用冷读猜女生的性格', '男：有事会怀疑自己以往的人生选择和行动是否真的正确 .', '冷读语录');
INSERT INTO `talk_art` VALUES (5009, '利用冷读猜女生的性格', '男：保护着自己的感性、欲望与真实的冲动，往往带着一些黑暗，不可告人，所以必须要伪装得好，掩饰得严。”\r\n男：我觉得你因该把自己的心扉去打开.多去接触一下外面的人.这样或许会让你的生活更好一些（内向型）（冷读）', '冷读语录');
INSERT INTO `talk_art` VALUES (5010, '利用冷读猜女生的性格', '男：有时候会怀疑自己以往的人生选择和行动是否真的正确 . .', '冷读语录');
INSERT INTO `talk_art` VALUES (5011, '利用冷读猜女生的性格', '男：如果那个人忠厚老师，你只要说：”你的好朋友很喜欢热闹，属于爽朗型的人吧？”就行；\r\n如果那个人好像具有领导力，你就说：”的好朋友好像是比较老实、顺从型的人。”很多时候都不会错。（冷读）', '冷读语录');
INSERT INTO `talk_art` VALUES (5012, '利用冷读猜女生的性格', '男：你试图装作坚强，但是很少人知道你有个柔软而又怕触动的心。\r\n你不想让人触碰到你的最柔软的地方，于是你就用你的坚强好像鸡蛋的坚硬外壳把它包裹起来，可万一那看是坚固的蛋壳破了，你就会很受伤....（冷读）', '冷读语录');
INSERT INTO `talk_art` VALUES (5013, '女：你那么会聊天，女孩子骗了多少个', '男： 今天你是今天第一个，希望也是今生最后一个\r\n男：你一个还不够吗', '框架与延伸');
INSERT INTO `talk_art` VALUES (5014, '女：你以后会只对我一个人好吗？', '男：不会，我会对很多人好，包括我们的父母，还有我们以后的孩子 \r\n女：你一定经常这样哄/骗小姑娘（撩妹）？ \r\n男：不一定，偶尔也哄哄你这样的上年纪的女人（关系升级）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5015, '女：你好自恋', '男：妈妈说，一个男人连自己都不爱，怎么去爱身边的人（框架引领）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5016, '女：你咋回复的这么慢', '男：回复慢是因为我在乎你，所以会思考得久些（关系升级）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5017, '女：你还挺爱干净', '男：做一个爱生活 , 爱家的人 ,这是成功男人的特有标志（框架引领）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5018, '女：你有病', '男：我有药 ，刚在京东上买的 ，看了一下 ，还没过期 ，可以亲口喂给你', '框架与延伸');
INSERT INTO `talk_art` VALUES (5019, '女：我不是随便的人', '男：跟我不算随便 ， 毕竟咱们都一起经历过那么大的风浪', '框架与延伸');
INSERT INTO `talk_art` VALUES (5020, '女：嘴甜的男人不安全很花心', '男：这么说以后跟我聊天，你是不是要打着胰岛素过来聊 ， 你怎么不被我甜死', '框架与延伸');
INSERT INTO `talk_art` VALUES (5021, '女：上班好累', '男：那为什么不逃进我的怀抱，让我肩膀做你永远的依靠（关系升级）', '框架与延伸');
