INSERT INTO `talk_art` VALUES (5025, '女：好累好难受啊', '男：来我怀里，用我的身体当支柱不让你倒下\r\n女：白眼 ----\r\n男： 看你身心疲惫，怕你受累，你不领情就算了（框架性释放）（关系升级）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5026, '女：你还要我请你看电影？', '男：吃完就回家不像我们年轻人的作风\r\n', '框架与延伸');
