INSERT INTO `talk_art` VALUES (5029, '女：你喜欢什么样的女生', '男：我喜欢思想大方独立 ， 有自己的想法 ，善良的女生', '框架与延伸');
INSERT INTO `talk_art` VALUES (5030, '女：真羡慕你', '男：不用羡慕，主动点你也可以拥有（框架引领）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5031, '女：我这件衣服好看吗', '男： 恩， 穿出来应该可以和我一样靠颜值吃饭了', '框架与延伸');
INSERT INTO `talk_art` VALUES (5032, '女：你幼稚', '男： 男人成熟的晚，你不知道吗，有时候人是难得糊涂，活的太明白也没劲 \r\n女：你可以找个人倾述 \r\n男：我也不会因为空虚寂寞去随便找个人倾述，总是不喜欢，特别沉重 ， 如果我愿意说了 ， 这个人也不是一般人', '框架与延伸');
INSERT INTO `talk_art` VALUES (5033, '女：你臭美', '男：听说人丑就要多读书，难怪我妈从小就说我不是读书的料', '框架与延伸');
INSERT INTO `talk_art` VALUES (5034, '\\', '男：缺个风趣幽默的小帅逼陪你吃 \r\n女：你来啊 ----\r\n男：吃饭不怕晚，试爱需等待 ， 今天蜀黎有事（关系升级）', '框架与延伸');
