INSERT INTO `talk_art` VALUES (5141, '女：我不想伪装自己', '男： 时间久了，习惯成自然，怎么能说是装呢 \r\n男：你要不假装爱我吧。从现在开始', '框架与延伸');
INSERT INTO `talk_art` VALUES (5142, '女：哈哈我开玩笑的', '男：当然能听出来，我不这样说，怎么能玲离尽致地感受你的善良', '框架与延伸');
INSERT INTO `talk_art` VALUES (5143, '女：城市套路深，我要回农村', '男： 除了养猪，一无是处', '框架与延伸');
INSERT INTO `talk_art` VALUES (5144, '女：你万一只看上我的美', '男：哎哟，你是照着镜子给我说这句话的吧', '框架与延伸');
INSERT INTO `talk_art` VALUES (5145, '女：你喜欢我吗', '男：因为我喜欢你喜欢我的样子 \r\n男：傻瓜 没感觉怎么会喜欢呢', '框架与延伸');
INSERT INTO `talk_art` VALUES (5146, '男：你多大', '女：二十 ---- \r\n男：旺夫的数字', '框架与延伸');
INSERT INTO `talk_art` VALUES (5147, '女：你套路我', '男： 套路是我为你修得高速路 难道你喜欢走乡间小路吗', '框架与延伸');
INSERT INTO `talk_art` VALUES (5148, '女：我太有个性了感觉你', '男：这一切都是你以为 ， 怎么不问问我真实情况', '框架与延伸');
INSERT INTO `talk_art` VALUES (5149, '女人表现得很淡定', '男：一副老司机的样子，其实我还是个宝宝什么', '框架与延伸');
INSERT INTO `talk_art` VALUES (5150, '女：嘴这么甜是天生的么', '男：那赶紧给而我个么么哒鼓励我', '框架与延伸');
INSERT INTO `talk_art` VALUES (5151, '妹子骂人', '男：好好说话 才是中华儿女传统美德', '框架与延伸');
INSERT INTO `talk_art` VALUES (5152, '女：很多人都没安全感，', '男：既然里面人很多，也不缺你一个，所以乖一点的话你的安全感可以完全源于我', '框架与延伸');
INSERT INTO `talk_art` VALUES (5153, '女：和妹子后期调情妹子', '男：不用说，开心就好 这就是喜欢，也不用怀疑自己的感觉', '框架与延伸');
INSERT INTO `talk_art` VALUES (5154, '女：说的跟真的一样', '男： 真男人，有腹肌哟', '框架与延伸');
INSERT INTO `talk_art` VALUES (5155, '女：你应该找个女朋友', '男：要认真谈恋爱，不能因为寂寞孤独就去试着爱一个盲目的恋爱\r\n女：你都这把年纪了还没女朋友 ---- 男：宁缺毋滥定会得偿所愿，饥不择食会悔不当初（框架引领）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5156, '女：我们认识没多久还不熟', '男：认识的时间长短并非是了解的关键，关键在于愿不愿意去了解（框架引领）', '框架与延伸');
INSERT INTO `talk_art` VALUES (5157, '很好，真乖啊，好好保持哈', '1、你说的没错，我比你乖多了，你是坏小孩\r\n\n2、你觉得自己是在马戏团驯兽吗\r\n\n3、海豚表演得好还有鱼吃呢，我呢？别说给我吃你哦，我是回民\r\n\n4、你想保持我们的关系就得持续请我吃饭\r\n\n', '勾起好奇');
INSERT INTO `talk_art` VALUES (5158, '最近刚网搭的 妹子。', '因为第一次开场时，讲了个故事，现在每天网上一见面就让我讲故事。我说给你讲故事，有什么好处吗。我送你一本故事大王，自己看吧。但是妹子，根本不理，还是喊着讲故事。\n1你先告诉我，你是真想听故事呢，还是喜欢我的声音呢？\n2现在我可以讲给你听，但你要答应我以后由你负责将我的这些故事讲给我们的孩子听\n3天桥下听相声还给钱呢，何况听大帅哥讲故事，你先把前面讲故事的费用给我再给你接着讲', '勾起好奇');
INSERT INTO `talk_art` VALUES (5159, '我从不喜欢自傲的人 我说说写什么不重要 ！', '1你也挺自傲，难道拿大嘴巴子抽自己？\n2确实不重要，现在都很流行在网上抄那些无病呻吟的说说呢\n3你好像分不清自傲和自信的区别', '勾起好奇');
INSERT INTO `talk_art` VALUES (5160, '妹子：你连这个都不知道啊？', '1下次给你买一本百科全书做男朋友\n2我又不是百度知道，干嘛要知道\n3我不知道的可多了，比如你今天穿什么颜se的nei裤我就不知道\n其他答案：故意给你一个表现的机会而已（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5161, '你怎么这么喜欢指使别人？', '1因为我不喜欢被人指使啊\n2你这人做点事怎么斤斤计较\n3看来我有当官的潜质呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (5162, '我有男朋友了，即使我们不能在一起，我们能做', '1原来你是练杂技的\n妹子：?\n只有练杂技的才能一脚踩两条船呢\n2没关系的，我会去泡其他女孩子，一直等到你跟他分手的那天，再与她结婚\n3我不会破坏你和你男友的感情，也不会强迫你跟他分手，即使只跟你做泡友我也心甘情愿（慎用）\n其他答案：我们？当然可以了，那我们说好了，你白天陪他，晚上陪我(一直很想爱你)\n当然可以啦，可是我怎么才能相信你是真心想做我的好朋友呢，这样吧，先请我如此如此这般这般', '勾起好奇');
INSERT INTO `talk_art` VALUES (5163, '过节没点气氛', '1你在街上裸奔肯定会给节日增添不少气氛的\n2那要看什么节了，要是过清明节搞得很有气氛会很诡异啦\n3我放个pi给你制造点气氛吧', '勾起好奇');
INSERT INTO `talk_art` VALUES (5164, '女：去去去', '1我郑重地告诉你，请不要用赶鸡的语气跟我说话\n2唱：欧蕾欧蕾欧蕾！（瑞奇马丁-TheCupofLife）\n3去哪？kai房也不用这么着急啊', '勾起好奇');
INSERT INTO `talk_art` VALUES (5165, '你是不是经常这样吻女孩子啊？', '1今天是第一次主动尝试，感觉太好了，难怪平时那些女孩子都这么吻我\n2吻你一次舌头都要被融化了，哪还能经常吻别人啊\n3你猜疑我的同时也在贬低自己呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (5166, '她说现在愿意做我妹妹，', '不过现在还没有成为女朋友就要我买这买那的，把我当银行了。像这种情况要怎么处理？\n这几天又说：买月饼寄给我，前几天说：买部相机当作礼物送我。晕死了，求解，这是何意？\n1买什么月饼，我觉得你应该直接住到月亮上去，天蓬元帅那么有钱最适合你了\n2买什么相机，你已经很像鸡了好吧\n3妹妹，你叫我买的这些东西我觉得都没有你贵重，我最想买的那个还是你，简称-------买你妹！\n其他答案：把钱寄过来我马上帮你买（brethart）\n买个套tao送你要不要？(一直很想爱你)', '勾起好奇');
INSERT INTO `talk_art` VALUES (5167, '干嘛说给你听啊！', '1以你的八卦个xing，不说出来会憋得很难受，我心地善良所以打算免费帮助你\n2你不怕麻烦的话写出来也可以的\n3你面对我会害羞吗？那我把电话的录音功能打kai你对着它说也行的', '勾起好奇');
INSERT INTO `talk_art` VALUES (5168, '妹子：看小说有什么用啊', '1可以提高自己的技巧啊，比如金瓶-梅\n2书中自有颜如玉，书中自有黄金屋\n3因为小说不会一天到晚问我这个有什么用那个有什么用', '勾起好奇');
INSERT INTO `talk_art` VALUES (5169, '我：＂你已经被这个时代给抛弃了＂', '她：＂是我抛弃了这个时代＂\n1我替这个时代感谢你的高抬贵手\n2你这个狠心的坏女人，我隐约听到时代被你甩了后低低的抽泣声呢\n3抛弃它是因为我的出现吗？我其实不喜欢喜新厌旧的女人', '勾起好奇');
INSERT INTO `talk_art` VALUES (5170, '你真是个用下半身思考的动物', '1我长得像虾子吗？\n2那也比你用xiong部思考好呢，因为我下半身够大，而你的xiong部思考能力肯定有限。。。\n3不管哪里能思考就行，但我很感兴趣你知道什么叫思考吗？\n其他答案：那也要下半身感兴趣才会去思考，就你这样我想思考都难（黑魔导）\n你太高估自己了，像你这样的笨Y头，我用脚趾头思考就够了', '勾起好奇');
INSERT INTO `talk_art` VALUES (5171, '妹子：我最讨厌这样的书呆子', '1我最讨厌没文化的人\n2那你喜欢哪样的书呆子？\n3那为什么你不看书也那么呆呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (5172, '自己 QQ 好友印象被人评论;——“好，假的；坏', '1你这个评价很中肯，我确实一眼就能看出别人假装好人，nei藏祸心\n2你真了解我，我就是这么坏，最好离我远一点，小心自己伤不起\n3喂，你这自我简介填错地方了', '勾起好奇');
INSERT INTO `talk_art` VALUES (5173, '我说看着他说你挺善良的，他笑着说：我一点都', '1我只是礼貌寒暄一下，你不用在意的\n2不善良才好，人善被人欺嘛，那么你肯定不会被人骑了\n3还好你诚实，作为回报，我答应你以后一定不会借给你钱的', '勾起好奇');
INSERT INTO `talk_art` VALUES (5174, '我：所以说我们也算一种缘分吧', '女：认识是一种缘分\n女：但可能不适合\n1我会给你时间适应的\n2也可能你爱我爱得要死我却不鸟你\n3实在不合适我会考虑适当降低自己的标准', '勾起好奇');
INSERT INTO `talk_art` VALUES (5175, '你知道吗,你真的让我很失望，很失望。', '1不要灰心，不要气馁，失败乃成功之母嘛\n2失望还好啦，没让你怀孕已经很不错了\n3我这招叫反撇，只用了前半段，效果看来很明显哦（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5176, '我：有什么好处', '女：你要什么好处\n1要什么都行吗？那我要你的银行卡和密码\n2让我想想还缺点什么，还缺个。。。女朋友，但可惜你不是我的菜，还是请我吃饭吧（非原创）\n3好像电影里肉体交易都是这个台词呢\n其他答案：1.<当面>（上下打量她）貌似我在你身上也捞不到没什么好处\n2.<QQ/短信/电话\n>请我吃饭，得三包。哪三包？\n包吃包玩包什么什么。\n什么什么？\nbalabala（包暖床之类，或者扯其他的。天马行空的瞎扯能力很重要。）（如果她拒绝三包）三包都不行啊，那就三陪（从不善解人衣）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5177, 'XX,我没来参加你的聚会,你生气了么???', '(我已经冷冻她3天了)\n1这种事生气有什么用，扁你一顿比较直接呢\n2什么聚会？哦~~~~你说的是上次吧，没关系啦，后来有别的美女参与弥补了你不在的空位，我们可以下次再聚嘛\n3我最近在卧床养病\n妹子：你病了？\n聚会被放鸽子气病的', '勾起好奇');
INSERT INTO `talk_art` VALUES (5178, '你的感情经历太丰富了', '1正因为有这些经验我才能更好的呵护和关心你\n2没有你参与的感情经历再丰富也是苍白的\n3你怎么这么肤浅，只看那些外在的事情，你不知道我的TD姿势更丰富吗？（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5179, '不过我就是那么个女孩 不讲理 霸道', '1你不去混帮派可惜寥寥\n2你是拆迁办的？\n3你是城管吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (5180, '我最讨厌欺骗我的人了!', '1我最讨厌容易上当的人了\n2知道这个特点以后你男朋友想甩掉你可就好办了\n3以你的智商，岂不是要讨厌很多人？忙得过来吗？\n其他答案；1谁把你的人给欺骗了？\n2一般我只欺骗别人的心（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5181, '见面后那个女孩确实很像很像赵薇', '但是我估计人家都会夸她我想对她进行打压偏偏说她不像应该怎么说好呢\n1我觉得你长得很像一个明星，是。。。。还珠格格里的。。。。皇后！好有气质啊\n2平时走到街上会有人要你帮他签名吗？\n妹子：有/没有\n他眼睛有问题吗？/我可以满足你这个愿望，待会吃完饭买单时你帮我签个名吧\n3你外表果然像赵薇，但不知道你nei涵怎么样，我得观察观察\n其他答案：有没有人说过你很像赵薇？\n妹子：balabala\n其实我只是想夸夸你，让你自信一点，因为我怕你在我这么完美的男人面前感到自卑。没想到你居然当真了。（从不善解人衣）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5182, '我：问多了 哈哈~;之前说和她聊几句，接着', '她：恩，不聊了，，~！88应该如何回答啊？\n1别急，我可以付你咨询费的\n2下次你也可以问回来哦，这养我们就可以扯平了\n3其实我在研究心理学啦，看不同的人对频繁被提问时的反应呢，下面我们可以正常聊了哦', '勾起好奇');
INSERT INTO `talk_art` VALUES (5183, '你再这么做，我不喜欢你了', '1升级为爱我是吗\n2你再不让我这么做，我也不喜欢你了\n3心理学上讲女人说不要，往往就是还要的意思呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (5184, '我干什么也没人在意 你管我吗？', '1我会筹钱为你kai个孤儿院的\n2管饭\n3你的意思是想表达自己很低调吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (5185, '妹子：看你这么文绉绉的', '1mo自己的脸：皱吗？不会啊，我皮肤还是蛮光滑的\n2不，其实不用看就能感受到我极高的文化nei涵\n3敢问小生何处文绉绉乎？', '勾起好奇');
INSERT INTO `talk_art` VALUES (5186, '给我介绍的女孩子长的像赵薇 开场白怎么说啊', '1我从小就爱看还珠格格呢，所以你对我来讲比其他女孩子更有优势呢\n2赵薇都结婚生子了，你也要加把油了，我是来解救你的哦\n3我觉得你去参加超级明星脸很合适，我给你做经纪人怎么样，经纪费就免了，请我吃饭就行', '勾起好奇');
INSERT INTO `talk_art` VALUES (5187, '打车十公里过去找 妹子，', '给他惊喜，到楼下打电话，她说我睡了，你走吧，该怎么回复？先为你默哀，然后告诉你她的回应证明你单方面在两人感情里投资十公里路程的行为很挫1我没有回去的路费了。。。\n2你觉得我长得像召唤兽吗\n3睡觉没关系，走也没关系，但我没你家门钥匙，怎么走进去？', '勾起好奇');
INSERT INTO `talk_art` VALUES (5188, '只是那么一说 没人会在意的！', '1我是人类，我很在意\n2你说猪的语言，人们听不懂当然不在意\n3照你这么说谁在意你谁就不是人？', '勾起好奇');
INSERT INTO `talk_art` VALUES (5189, '在和以前女同桌聊天时', '她问我有没有女友，我兴致勃勃描述了一堆\nPS：我对这个同桌有意思！~她来了一句:“她又喜欢你什么呢？”1她喜欢我面对同桌的女孩子时完全不为美se所迷\n2我怎么知道，你得问自己啊\n3喜欢的优点太多，说不完，不过我可以告诉你她不喜欢我跟其他女孩子聊天，但我喜欢跟你聊天，哈哈', '勾起好奇');
INSERT INTO `talk_art` VALUES (5190, '打压 妹子 后，对面说：不想理你想不到什么出彩', '1你这语气就像是理发师在挑顾客呢\n2我可以去应聘狗不理的形象代言人了\n3谢谢你让我清静', '勾起好奇');
INSERT INTO `talk_art` VALUES (5191, '她老是喊我：小子！', '1你是有多老？\n2叫老子干嘛\n3我不小哦，我的有这么大（双手比一个夸张的长度）\n其他答案：我记得有一个电影叫“那小子真帅！”。。。。。。（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (5192, '我喜欢成熟稳重略带点幽默', '1我喜欢活泼可爱略带点xing感\n2喜欢我就直说，兜这圈子干嘛？\n3那你赚到了，我是成熟稳重狠幽默', '勾起好奇');
INSERT INTO `talk_art` VALUES (5193, '我的女友发信息给我说“我的眼睛要瞎了”', '她在测试我，看我会不会关心她，如果我回“怎么了”是不是进圈套了？应该怎么回？\n问怎么了属于正常的关心，不会掉进圈套，，你min感过头了如果对方眼睛真的受伤，你的幽默回答会适得其反，但我针对你的问题还是给予回答\n1所以我才不推荐一直长时间盯着我的照片看，至少你得眨眼吧。。事已至此，赶紧趁着还没瞎再多看我几眼吧\n2你的眼睛要是真瞎了，就可以对我唱萧煌奇那首歌了\n3没关系的，我只要你的身体能用就行', '勾起好奇');
INSERT INTO `talk_art` VALUES (5194, '我快要离kai了，你舍得我吗？', '1舍不得，至少得请我吃顿饭再走吧\n2狮子舍得把幼崽推到山脚下让它们自己爬上去，鸟儿也舍得把幼鸟推出窝外让它们自己飞翔，我当然也舍得看宠物离kai呀\n3你轻轻的走，正如你轻轻的来，不带走一片云彩，请还我那笔欠债（感情债）\n其他答案：kai学时我从家里出来，我妈也说过类似这样的话，不过她是这样说的（模仿语气，不用我说怎样的语气了吧？）：哎，又要好久才能再看到你了。(一直很想爱你)', '勾起好奇');
INSERT INTO `talk_art` VALUES (5195, '我认为你根本就不了解我', '1我也这么认为，你tuo掉衣服让我了解下吧\n2你了解我吗，不然怎么知道我不了解你呢\n3不会啊，我至少了解你的想法是认为我不了解你啊', '勾起好奇');
