INSERT INTO `talk_art` VALUES (5619, '搞笑惯例', '男：今天带表弟去玩，他八岁，然后上厕所时候一起站着尿尿，表弟惊奇的问我，表哥你**咋那么大，好尴尬。\n女：哈哈，你咋说的？\n男：我抽疯的说了一句：练得。然后表弟问我咋练，我真的不知道该怎么回答了。\n女：哈哈。', '合约恋人');
INSERT INTO `talk_art` VALUES (5620, '公鸡惯例', '男：媳妇儿，其实我有个心理yin影，现在看到公鸡就起鸡皮疙瘩。\n女：为啥啊？\n男：记得三四岁那年，我中午在奶奶家坐门口院子里吃米饭，当时穿的kai裆裤有一粒米掉到了丁丁上，老母鸡跑过来就是一啄！我整整肿了3天！\n女：哈哈哈哈。', '合约恋人');
INSERT INTO `talk_art` VALUES (5621, '男：媳妇，问你个问题', '女：说。----男：你下边刚长毛的时候怕不怕？\n女：忘了。\n男：我记得我前女友说，她刚长毛的时候，她妈妈告诉她长个了小猴子所以长毛了，然后有天她告诉了她表姐，她的小猴子kai始长毛了。\n女：然后呢？。\n男：她表姐说，那算什么，我的猴子都kai始吃香蕉了！\n女：我去，好liu氓啊。\n男：老公也喂你吃香蕉', '合约恋人');
INSERT INTO `talk_art` VALUES (5622, '男:刚看了一个笑话，讲给你听好不好。', '女:好啊好啊。男:一个盛夏，有家两夫妻快天亮了在床上办事儿，没留意6岁的小儿子已经醒了，小儿子一边看着自己的破蚊帐里蚊子飞出飞进一边嘴里念叨:出来进去，出来进去，出来进去～结果他爸怒吼一声--老子不要你指挥。女:哈哈哈哈哈，你个se狼', '合约恋人');
INSERT INTO `talk_art` VALUES (5623, '男：你知道我离死亡最近的一次在什么时候吗？', '女：什么时候？\n男：二十多年前，差点被后面的jing子追上！\n女：......', '合约恋人');
INSERT INTO `talk_art` VALUES (5624, '男：我发现你们女人比我们se的多', '女：没有吧！\n男：那天一个女同学让我介绍对象，我问什么条件，她说要高的，我给你介绍了一个高个男生，结果没几天就分了。\n女：为啥啊？\n男：她说要躺着高的。\n女：额，liu氓。', '合约恋人');
INSERT INTO `talk_art` VALUES (5625, '男： 媳妇，我有个想法 。', '你有啥想法？\n男：“媳妇儿，我可以用一百万包养你一年吗？”\n女：“好啊。”\n男：这里是115块，你数数，我先来一个小时的。\n女：滚', '合约恋人');
INSERT INTO `talk_art` VALUES (5626, '男：我现在也是有钱人了', '女：发财了?\n男：前几天看了一个报道某医院误切一男子睾丸赔了100多万，我在想原来我每天都带着200多万出门，现在也可以任xing了！\n女：你屌。', '合约恋人');
INSERT INTO `talk_art` VALUES (5627, '男：我发现看奥运和看 A-V 是一样的。', '女：这怎么会一样呢？\n男：都是一群最需要运动的人，看着一群最需要休息的人在那里运动。\n女：哈哈哈。', '合约恋人');
INSERT INTO `talk_art` VALUES (5628, '男：昨天下午只有我跟表妹两个人在家里。', '女：额，然后呢？\n男：有点小尴尬…她在床上摆出一个妖娆的姿势、于是我上前去扒她的裤子，她想反抗可是没有用！她力气没我大！无奈她只能在那里哭！\n女：你还是人么？\n男：就这样我成功的给她换上了尿片！你想啥呢？\n女：讨厌。', '合约恋人');
INSERT INTO `talk_art` VALUES (5629, '男：其实我还是很机智的', '女：那天坐大巴，旁边有个身材火辣的少妇带了个3岁的娃娃，哭了一百多公里还没有停下来的意思！接着我说了一句话，四周的哥们都投来了赞许的目光。\n女：你说了什么？\n男：姐，都哭这样了，给孩子喂点奶吧。\n女：哈哈，liu氓。', '合约恋人');
INSERT INTO `talk_art` VALUES (5630, '男：你知道为什么小孩子生下来就会哭么？', '女：为什么？\n男：死了那么多兄弟，换你你哭么？\n女：哈哈哈，哭。', '合约恋人');
INSERT INTO `talk_art` VALUES (5631, '男：突然想到了一种对你示爱的新方式。', '女：啥呀？\n男：我这儿有一条祖传的染se体，晚上送给你【害羞】\n女：哈哈，liu氓。', '合约恋人');
INSERT INTO `talk_art` VALUES (5632, '女：明天是情人节（周末或者过节）啊，亲爱的', '男：是啊，你平时工作这么辛苦，明天晚上终于可以带你浪漫了。\n女：怎么浪漫啊？\n男：这次我们去酒店，你浪一点，我慢一点。\n女：滚。', '合约恋人');
INSERT INTO `talk_art` VALUES (5633, '男：我发现一个社会现象', '女：什么现象。\n男：现在的男的呀，整天就想着和女人shang床。\n女：确实是这样。\n男：我就不一样。\n女：是么，你怎么不一样了？\n男：沙发，阳台，厨房，客厅，哪儿都可以。\n女：se狼。', '合约恋人');
INSERT INTO `talk_art` VALUES (5634, '神灯惯例', '男：今天捡到一盏灯，结果里面冒出了一个声音跟我说，你是想要超常的记忆力呢还是想要超长的**呢？\n女：啊？那你选择的什么？\n男：我忘了。\n女：......', '合约恋人');
INSERT INTO `talk_art` VALUES (5635, '男：媳妇儿，我诗兴大发，我要做首诗。', '女：额......说吧。\n男：分分合合那叫爱，一炮就散那叫合作愉快，活在草泥马的年代，睡对了就结婚，睡错了叫青春。\n女：装逼成功。', '合约恋人');
INSERT INTO `talk_art` VALUES (5636, '男：我有个问题想问你', '女：恩？\n男：如果你没有脚，你会穿鞋么？\n女：不会啊。\n男：可是...那你为什么要带xiong罩啊,哈哈哈。\n女：找打你。', '合约恋人');
INSERT INTO `talk_art` VALUES (5637, '男：我那天和朋友去吃饭，差点笑死', '女：怎么了？\n男：一对情侣单都没点完就吵起来了，男的说：你手骨折我大老远跑过来，你来着大姨妈还口腔溃疡，这又点个辣锅底究竟什么意思？信息量好大。\n女：哈哈哈。', '合约恋人');
