INSERT INTO `talk_art` VALUES (5846, '头发顺', 'mo女孩头发后说：我发现你头发还蛮顺的，医学杂志上说，头发顺的人xiong部都会比较挺，来，我看下。', '调q话术');
INSERT INTO `talk_art` VALUES (5847, '字谜游戏', '首先问女孩：如果我是一个赶马的你应该称呼我什么？\n	女孩：马夫！（如果没有回答正确，可以提醒）\n	再问：如果我是kai船的呢？\n	女孩：船夫\n	再问：如果我是赶车的呢？\n	女孩：车夫\n	再问：如果我是管账的呢？\n	女孩：账夫（与丈夫谐音）\n	呵呵，这时你可以：拉起她的手，轻松说，唉，可惜我还没做好心理准备，我们还是慢慢来好吗，先做男朋友好了。', '调q话术');
INSERT INTO `talk_art` VALUES (5848, '做梦', '男：最近我老是做梦。\n	女：怎么了？\n	（表示关心。）\n	男：我梦见一个女人……\n	（随便编点什么，不管她说什么。）\n	男：唉！那人怎么越看越像你……', '调q话术');
INSERT INTO `talk_art` VALUES (5849, '过马路时', '男：来，小朋友，叔叔带你过马路。\n	（赶紧抓住她的手哦！过完马路也牵着别放下，除非她手心出汗了）', '调q话术');
INSERT INTO `talk_art` VALUES (5850, '秘密', '男：我一直把你当作最好的朋友，有一个秘密很想跟你说。\r\n	女：什么秘密啊？\r\n	男：我喜欢上了一个女孩，不知道怎么办。\r\n	女：告诉她咯！\r\n	（注意观察她的表情。）\r\n	男：我怕她不喜欢我，那可怎么办呢？\r\n	女：……\r\n	（不用管她说什么。）\r\n	男：其实你认识她的。\r\n	女：哦？谁啊？\r\n	男：嗯……\r\n	（犹豫，然后转到她的耳边，轻声说出她的名字。）', '调q话术');
INSERT INTO `talk_art` VALUES (5851, '帮个忙', '男：你能不能帮我一个忙啊？\n	女：什么？\n	男：我买了一把玫瑰花你帮我养几天。', '调q话术');
INSERT INTO `talk_art` VALUES (5852, '打赌', '男：我们打赌？\n	（很多东西可以用来打赌。）\n	女：赌什么啊？\n	男假装思考：嗯……你输了的话做我女朋友，我输了的话做你男朋友。\n	女的一般表现：会笑，并且打你。男的抓紧时间追问，并顺势抓住她的手。环境好的话可以把她抱在怀里哦', '调q话术');
INSERT INTO `talk_art` VALUES (5853, '介绍男友', '男：哎，问你一个私人问题？\n	女：什么？\n	男：你有没有男朋友啊？\n	（之前必须调查她还没有！）\n	女：没有啊！\n	男：要不要我给你介绍一个？\n	女：谁啊？\n	男：那人跟我同姓，叫做……\n	（委婉而神秘地说出自己的名字）\n	注意：表情随意，傻笑，顺手来个亲昵动作，比如拥抱、牵手等。她没有明确反对你就成功！', '调q话术');
INSERT INTO `talk_art` VALUES (5854, '玩游戏', '男：来，我跟你玩个游戏\n	男：你一只手有几个手指\n	女：5\n	男：你有几只眼睛\n	女：2\n	男：你有几个嘴巴\n	女：1\n	男：我也爱你', '调q话术');
INSERT INTO `talk_art` VALUES (5855, '哪个是真的', '男：我杀过人\n	男：我喜欢耗子\n	男：我今年刚结婚\n	男：我喜欢你\n	男：我吸过毒\n	男：你猜哪一件事是真的\n	女：我喜欢你\n	男：我也喜欢你', '调q话术');
INSERT INTO `talk_art` VALUES (5856, '哮喘', '男：我发现我有哮喘\n	女：真的假的\n	男：每次看见你我都踹不过气', '调q话术');
INSERT INTO `talk_art` VALUES (5857, '看手相', '男：你把手给我看看\r\n	男：咦，你这，命里缺我啊', '调q话术');
INSERT INTO `talk_art` VALUES (5858, '眼睛', '男：你从我眼睛里看到了什么\n	女：没有呀\n	男：你再看看\n	女：真没有啊\n	男：那里有我喜欢的人', '调q话术');
INSERT INTO `talk_art` VALUES (5859, '好看', '男：你是我见过第二好看的女孩\n	女：那第一呢？\n	男：是我女朋友\n	女：你不是单身吗\n	男：所以你愿意做我女朋友，成为第一好看的女孩吗？', '调q话术');
INSERT INTO `talk_art` VALUES (5860, '别说话', '男：嘘，别说话，你听，听到了吗。\n	女：什么\n	男：我的心在向你示爱。', '调q话术');
INSERT INTO `talk_art` VALUES (5861, '魔力', '男：我觉得我有种神奇的魔力，就是总能找到美好的东西\n	女：比如？\n	男：你', '调q话术');
INSERT INTO `talk_art` VALUES (5862, '有人喜欢你', '男：我知道有三个人喜欢你\n	女：谁呀\n	男：我呀我呀我呀', '调q话术');
INSERT INTO `talk_art` VALUES (5863, '烧焦', '男：你闻到烧焦的味道了吗\n	女：啊没有啊\n	男：是我爱你的心在燃烧', '调q话术');
INSERT INTO `talk_art` VALUES (5864, '耀眼', '男：你脸上有什么东西，反射的我都睁不kai眼了\n	女：什么，哪儿？\n	男：是我爱慕的目光', '调q话术');
INSERT INTO `talk_art` VALUES (5865, '噩梦', '男：我昨晚做了个噩梦\n	女：什么噩梦\n	男：没有你的梦都是噩梦', '调q话术');
INSERT INTO `talk_art` VALUES (5866, '接吻挑duo', '在她说话的时候，深情注视着她认真听，一点点靠近她，作势想要亲她，但只是帮她擦掉嘴角的东西。\n	然后说：你知道我为什么不亲你的脸么？因为我想当你的骑士，你在我眼里就是公主，我现在不够好，当我觉得自己够好后会亲你的脸的。', '调q话术');
INSERT INTO `talk_art` VALUES (5867, '工作', '当对方问起我：你是做什么工作的时候。我会在这个时候kai一个小玩笑。\r\n	我会假装害羞不好意思的说：我的职业啊？说出来怪不好意思的，我是拍se情小电影的（或者写se情小说的）。\r\n	这个时候不用担心对方鄙视你，因为只要不是智商有问题的人都知道你是在kai玩笑。而且就算有那种比较单纯的人相信了你所说的话，她也不会因为你说的职业而鄙视你。\r\n	比如说当对方问题你的职业的时候，你就可以说：我啊，靠文字吃饭的，写写文章之类的。\r\n	对方就会觉得好像遇到文艺青年了，可能瞬间双眼变成小星星，哇塞，真的啊，你是作家啊？其实我也挺喜欢写文章的。有机会拿给你看。那你就可以说，呃，你拿给我看我也看不懂。我是写se情小说的。\r\n	这样对方的情绪就会来个180度大转弯。不要问我怎么知道的，因为我平时就有这些口头禅，和很多女孩子聊过。往往就因为这个玩笑，给对方留下了一个我这个人很不正经的样子，于是她们也会在后续和我相处的过程中kai一些不正经的玩笑。', '调q话术');
INSERT INTO `talk_art` VALUES (5868, '肚子难受', '女生有时会说：肚子好难受啊！(不是肚子难受也可以，只要说难受，你都可以使用此惯例）我会怜惜的看着她，说：怎么回事，是不是有了？\n	一阵粉拳袭来\n	有的女生甚至会说，是啊，还是你的呢！\n	然后我会贴近她肚子上说，孩子，快叫爸爸！结果可以想象！！！', '调q话术');
INSERT INTO `talk_art` VALUES (5869, '对于害羞的女孩', '你怎么都看我的xiong部啊？我的眼睛在这呢（指眼睛微笑）你都在想什么啊”？\n	很多时候女孩都会被逗笑，有的甚至会狂笑。', '调q话术');
INSERT INTO `talk_art` VALUES (5870, '天气冷', '男：天这么冷你穿这么少啊（天这么热你穿这么厚啊）\n	女：这还少？balabala。。\n	男：里边呢？你里边穿的什么\n	女：里边的毛衣，很厚的（也许女会给你看）\n	男：再里边呢（坏笑）\n	女：liu氓（娇笑）或者是粉拳就来了', '调q话术');
INSERT INTO `talk_art` VALUES (5871, '无厘头接吻案例', '男：唉，你信不信我可以吻到你而不接触你身体任何一个部位？\r\n	女：不信！\r\n	男：那我们打个赌。\r\n	女：好吧。\r\n	男：你先把眼睛闭上。\r\n	女闭上\r\n	此时想怎么吻怎么吻，根据场合，彼此状态而定。\r\n	女：你骗人，明明碰到xx了！\r\n	男：（装无辜）那好吧，算你赢了。你想要我是配吃饭还是配喝酒呢？\r\n	一场打闹。。。', '调q话术');
INSERT INTO `talk_art` VALUES (5872, '梦境接吻惯例', '男：说“最近晚上我都梦见你了”\n	然后这个时候妹子大多都会觉得很kai心，很羞涩，大多情况是不说话，要不就是“真的假的”\n	然后你再说“可惜都是恶梦”\n	然后你说“你想听我做了怎样的恶梦吗”\n	然后别管她巴拉巴拉说什么都好\n	继续自己说“我前晚梦见你，牵你的手，谁知道给你一巴掌拍过来，把我给拍醒了，太恐怖了”\n	“昨晚又梦见你了，这次更恐怖，我吻了下你，谁知道给你一脚踹出了马路，给车撞醒了，更加恐怖”\n	这个时候，你问“你说梦其实到底能不能信？”\n	这句话，可以试探妹子对你的态度，如果对你有好感，大多都会说“不能信”，或者说“梦肯定不能信啊，那是梦，梦都是假的啊”这个时候，你就可以说“那我试试”，然后吻上去。', '调q话术');
INSERT INTO `talk_art` VALUES (5873, '拆散情侣', '我：你知道如何用一句话拆散前面那对情侣吗?\n她：不知道耶?\n我：走到两人中间说借过~\n她：….\n我：还有我可以用一句话拆散我们之间的恋情。\n她：但是我们不是情侣阿？\n我：那你先假装跟我在一起，然后你就知道了！\n她：好阿，那我们在一起\n然后你牵起她的手\n她：占我便宜是吧\n我：严肃点，现在你是我女朋友\n她：…\n我：…\n走了一会\n她：拆散呢?\n我：拆散？什么拆散？', '调q话术');
INSERT INTO `talk_art` VALUES (5874, '牵手和谐', '男：你看看他们有没有觉得哪里不和谐的\r\n	女：哪里不和谐啊\r\n	男：这样就和谐了啊（牵手）', '调q话术');
INSERT INTO `talk_art` VALUES (5875, '过河', '说和女人玩一个游戏，让她把手放在你的手上。\n	\n	然后出题：随便指一条线，说成是大河，河的一边是一个男孩儿，另一边是一个女孩儿，但是男孩不会游泳，问他俩怎么才能见面。\n	\n	在女人猜的时候，你就一直轻握着女人的手就是了，同时无论她说啥都不对。\n	\n	在她说了两三次错误答案后，牵起女人的手说：边走边说。女人这个时候的思绪仍然在想答案上，没注意就被你牵起手了。\n	\n	等女人再说了次错误答案后，你可以告诉她答案：其实这个男孩儿见不到这个女孩，他只是想握着她的手而已。\n	\n	以这样一句调qing的话收尾。如果女孩反应好的话，你甚至可以直接亲上去了。', '调q话术');
INSERT INTO `talk_art` VALUES (5876, '红绿灯', '和女人出去玩时，牵着手过完马路，突然表现奇怪的表情，让女人好奇！\r\n	再问道：你知道小学生谈恋爱怎么牵手的吗？\r\n	女人:不知道啊！\r\n	我：牵一个指头！都很害羞呵！（边说边做，表现害羞的样子）\r\n	再问道：那你应该知道中学生谈恋爱怎么牵手吧？\r\n	女人：不知道！\r\n	我：笨啊！就是牵着手啊！（像握手一样，也是边说边做！）\r\n	再问道：那你总该知道大学生谈恋爱怎么牵手了吧？\r\n	女人:不知道啊！\r\n	我：你大学是白读了，不过还好遇到我！十指相扣啊！（也边说边做！）\r\n	（以上最好能有一些搞怪的动作就更好了）', '调q话术');
INSERT INTO `talk_art` VALUES (5877, '学生牵手', '男：哎，想不想谈恋爱啊？\n	（以恋人多的地方为基本环境。）\n	女：不想。\n	男：你心理有毛病，书上说这叫：爱无能。\n	女：你才有毛病呢！\n	男：是真的！看在朋友一场的份上，我想牺牲自己拯救你一把。\n	女：想啊！\n	男：我也有点想，咱们凑合一下吧！', '调q话术');
INSERT INTO `talk_art` VALUES (5878, '爱无能', '你连了我家的wifi就是我的人了\r\n	你mo了我就要对我负责\r\n	你xx了我就要对我负责\r\n	你做了xx就是我的人了\r\n	接受了我的撩就是我的人了', '调q话术');
INSERT INTO `talk_art` VALUES (5879, '我的人', '男:我们去看电影吧\r\n	女:什么电影\r\n	男:爱的无法自拔，主演我和你\r\n	女:不靠谱\r\n	男:你没听过？每一段感情都是一部jing彩的电影', '调q话术');
INSERT INTO `talk_art` VALUES (5880, '眼睛好看', '男：我觉得你的眼睛真好看\n	女：真的吗?\n	男：可没有我的好看，因为我的眼里有你', '调q话术');
INSERT INTO `talk_art` VALUES (5881, '看xiong部', '男：“你觉得你聪明吗？美国的科学家发明一个简单的测智商方法，你知道这个位置叫什么吗？”指着手肘等她回答。\n	如果她不懂你告诉她。\n	男：“要是一个人能把手肘碰到一起就说明一个人是聪明的，像这样。”\n	让她跟着你做这个动作，当她手肘向中间靠的时候你会发现对方的xiong被上臂往外挤出来了。\n	这时你看看她ru沟，然后给她一个坏笑，她就懂了。\n	现在你笑她：“看来像你这样的女孩不好带出来哦，太容易受骗了……”。\n	然后你可以问她“哎，对了，你多大？”\n	女生一般以为你是问年龄“22岁，你不是知道了吗。”\n	你指指她的xiong说：“我是指这里，你的zhaobei。”同时给她一个坏笑。\n	可以接着再kai她玩笑：“有C吗，我比较喜欢xiong大的女人。”\n	如果她打你，你可以说：“哎呀，都说男人没一个好东西，你可不能喜欢上我哦。”', '调q话术');
INSERT INTO `talk_art` VALUES (5882, '买背心', '男：最近天热，我想去买件背心，想让你给我点意见\n	女：买个背心嘛，还用我给意见？\n	男：我听说xiong小的女生都是穿小背心的，所以我想你会比较有经验啊\n	女：。。。。liu氓\n	男：有时候看到你的xiong，我就想想起she的一句歌词\n	女：什么\n	男：我不想，我不想，不想长大。', '调q话术');
INSERT INTO `talk_art` VALUES (5883, '眼睛里有东西', '男生：哎呀，我的眼睛里好像有东西\r\n	女生：我帮你看看\r\n	男生睁kai双眼\r\n	女生：什么都没有啊\r\n	男生：你没有看到么？我的眼里只有你', '调q话术');
INSERT INTO `talk_art` VALUES (5884, '小兔子', '从前有一只小白兔，在森林里迷路了，她非常着急，害怕极了，突然她遇到一直黑se的公兔子，她就对黑兔子说：“黑兔子哥哥，黑兔子哥哥，我迷路了，你能告诉我怎么走出去吗？”\r\n	黑兔子说：“你跟我XX嘛，你跟我X了我就告诉你...”\r\n	小白兔没办法，就同意了，大黑兔在XX完之后就跑了。\r\n	小白兔没办法，就继续走啊走，这是又遇到一只大灰兔，她又对大灰兔说：“灰兔哥哥，灰兔哥哥，我迷路了，你能告诉我怎么走出去吗？”\r\n	大灰兔又对小白兔说：“你跟我XX吧，你跟我X了之后我就告诉你....”\r\n	小白兔还是没办法，于是就跟她XX了，但大灰兔在和小白兔XX完之后又跑了。\r\n	小白兔很失望，也跟害怕了，于是她就继续走啊走啊。然后又遇到一只大白兔，小白兔又问大白兔：“我XX吧，你跟XX我就告诉你...”\r\n	小白兔还是跟他XX了，可大白兔XX完之后还是跑了，你知道最后小白兔是怎么走出去森林的吗？\r\n	女肯定会说：“不知道，是怎么出去的？（女一般会想知道）你就说“你跟我XX吧，你跟我XX我就告诉你..............', '调q话术');
INSERT INTO `talk_art` VALUES (5885, '女孩说你讨厌时', '女：你这么这么讨厌\n	男：你知道中国女孩和外国女孩的区别么？\n	她：不知道！（或者说别的但是没有关系）\n	男：区别就在于表达爱意的时候，外国女孩会说：我爱你！而中国女孩会说：你真讨厌！', '调q话术');
INSERT INTO `talk_art` VALUES (5886, '调qing', '当你和女孩约完会之后，你在微信上问她：你对我是什么感觉？\r\n	一般女生都会这么回答：挺好啊，怎么了\r\n	可能也会有其他的，比如打压你，说你不好\r\n	然后你就回信息：那么，你觉得咱们两个还有处下去的必要吗？\r\n	女生一般都会一惊，问怎么了，如果是有些经验的女生可能会回：好吧，那就不处了。\r\n	然后你说：咱们直接去结婚登记吧，嫁给我，你有吃不完的辣条。\r\n	女生一般到这时候会回过神，知道你是kai玩笑的，可能就会吐槽你“去死吧”之类的。\r\n	你就将计就计说：我就是想死了，你怎么知道的，不找死我为什么要跟你去登记啊', '调q话术');
INSERT INTO `talk_art` VALUES (5887, '看手相', '你对女孩说：你会看手相，她同意后,你就抓过手来，然后抚mo一番，然后看很久,最后凝视她一会，然后停顿一下说：好一条红烧鸡翅，果然是极品美女才能有的。\n	然后她肯定说你乱说乱扯，占她便宜之类的话。\n	然后你继续说：听我说完嘛，我还没说完呢，\n	她说你接着说。\n	你就接着说：其实我不会看手相，我只是想mo一下你的手而已。', '调q话术');
INSERT INTO `talk_art` VALUES (5888, '调qing模板', '在对方对你有点好感，做完联系感后，可以尝试着调qing四步后再升高。\n	\n	1.看着她，她一kai始肯定回避。而且前面五次，女人都可能回避，没关系，继续看。\n	\n	她会问你看着我干嘛，你就回：就是想看你啊，看到你喜欢上我为止。说完之后，不用管女人怎么回复，你可以去找男人聊天，或者敬女人一杯酒。继续聊正经的话题，比如，你喜欢你的工作吗？\n	\n	2.刚才那个表情太可爱了，挺想去亲你的。说完之后，不用管女人怎么回复，你可以去找男人聊天，或者敬女人一杯酒。继续聊正经的话题，比如，今天感觉怎么样，玩的kai心吗。\n	\n	3.再看着她几秒，她跟你对视，你跟女人对视，让她先移kai视线。说：说真的，刚才有没有想亲我。女人肯定会说，你想的美，你想多了，你真不要脸之类的。她必须吐槽你，因为女人要矜持，也可以理解为装逼。\n	\n	说完之后，不用管女人怎么回复，你可以去找男人聊天，或者敬女人一杯酒。继续聊正经的话题，比如，你最欣赏我哪一点，你知道我最欣赏哪一点吗？\n	\n	4看着她，她也看你，可以真诚点，也可以坏笑着说：如果我要亲你的话，你会不会闭上眼睛。说完之后，不用管女人怎么回复，你可以去找男人聊天，或者敬女人一杯酒。继续聊正经的话题。\n	\n	由于有了调qing后暧昧的氛围，女孩也有了心理准备，待会你去肢体触碰女孩，就不容易被拒绝了，这样也算最自然的升高方式，可以把调qing理解为肢体触碰的铺垫。', '调q话术');
INSERT INTO `talk_art` VALUES (5889, '抱着睡', '男：你喜欢抱着东西睡吗\r\n	女：喜欢啊\r\n	男：我也喜欢，不过也有很多麻烦\r\n	女：怎么说\r\n	男：我和前女友一kai始的时候很恩爱，每天睡觉我都用手臂揽着她，让她睡在自己宽厚的肩膀上，舍不得放kai，直到后来…我得了肩周炎，她得了颈椎病。', '调q话术');
INSERT INTO `talk_art` VALUES (5890, '获得灵感', '男：你知道哪些东西能够提升人的灵感吗？\n	女：不知道\n	男：毒品，美食，xing。\n	女：balabalabala\n	男：我现在在设计个东西，缺点灵感，你能帮个忙吗\n	女：下流\n	男：。。。这怎么说话呢。\n	男：我是说帮我做点美食。', '调q话术');
INSERT INTO `talk_art` VALUES (5891, '智商调qing', '男：“我猜你肯定是个很聪明的女孩。”\n	女：“为什么这么说？\n	男：你听过xiong大无脑这句话吧？”\n	女：“恩，听过。”\n	男：“我就是通过这个道理推算出你是个聪明的女孩，而且是极度聪明那种…”\n	\n	延伸出来的惯例－－当女孩表现的苯头苯脑的时候。\n	男：“听说过xiong大无脑吗？”\n	女：“恩，听过。怎么了？”\n	男：“我突然很想知道你是什么zhaobei的，怎么会笨成这样？”', '调q话术');
INSERT INTO `talk_art` VALUES (5892, '手表调qing', '在酒吧和和一个女孩互动，在顺利的吸引和牵手接吻之后，一直在看着自己的手表。女孩就好奇问我为什么一直看着手表。\n	我说：我这个手表有超能力，它会告诉我一些我想知道的事情。\n	女孩说：那你的手表都告诉了你什么。\n	我接着用kai玩笑的口吻说：我的手表告诉我你没穿nei裤。\n	女孩哈哈大笑说：那你错了，我穿了nei裤。\n	然后我用随口一提的口吻说：我这个手表的时间提前一个小时。（kai玩笑的语气暗示，一小时后你在我面前没有穿衣服）', '调q话术');
INSERT INTO `talk_art` VALUES (5893, '手指写字', '你用手指在她背上画圈圈！她肯定说别弄！你无辜且深情的看着她说：你没感觉到我写的是我爱你吗？\n	\n	女人只是会愣住被你的眼神蒙骗，信以为真，不等她kai口，立刻恢复戏谑的神情，掐一下他的脸：骗你啦我其实画了一头猪。。。。。。。', '调q话术');
INSERT INTO `talk_art` VALUES (5894, '四个谎言', '规则：对方回答你的四个问题，必须要用谎言。事先说好，如果输了就是一个Kiss。\r\n	第一个问题：现在是白天还是晚上。谎言：晚上。\r\n	第二个问题：你是男人还是女人。谎言：男人。\r\n	第三个问题：你想亲我一下吗？谎言：想。——这个问题有扩张，如果她说不想，那么真实答案就是想，那么你可以选择这时候和她一个Kiss。如果她回答了谎言，即是想，那么你可以取笑她一次。\r\n	第四个问题：你前面一个问题回答的是真话还是假话。这是陷阱问题，不论她回答真话还是假话，都会输掉这个游戏。', '调q话术');
INSERT INTO `talk_art` VALUES (5895, '孔明灯', '从淘宝上买点孔明灯，到晚上的时候，带女朋友到野外放飞。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5896, '日落', '穿暖和点，去一个空旷的地方看日落。在太阳落山的时候，看着天se一点点地变暗，看着冬季天空中惊艳的日落。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5897, '下雪天', '下雪的话可以一起堆雪城堡。堆一对情侣雪人。打雪仗', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5898, '许愿船', '买一些彩纸蜡成本很低。把蜡融化涂在纸上或直接在彩纸上贴上胶带。\r\n	叠成船或做成莲花似形状的河灯约女友一起到海边或有水的地方，许下心愿。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5899, '半夜钓鱼', '在月光皎洁的夜晚，两人来到湖边，带上预备好的鱼钩和诱饵和准备好的宵夜，寂静的湖畔只能听到你和她的声音。\r\n	当钓鱼累的时候可以吃宵夜，她还可以躺在你宽厚的肩膀上数着天上的星星，即经济实用又浪漫，又好像傻里傻气的。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5900, '水晶相片', '把对方的照片或者自己的照片或者两人的合照，拿去做水晶相片，然后约女孩到公园里，对她说，亲爱的，我对你的爱就像这水晶相片一样，永远不会褪se！', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5901, '互动', '带对方参加一次狂欢节或欢庆活动。例如：\r\n	(1)饮食节\r\n	(2)爵士节\r\n	(3)酒节\r\n	(4)音乐节\r\n	带朋友一起去，玩的适合注意氛围，但是也要有两人的独处。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5902, '户外', '去户外。\r\n	在一天远足后，建立一个篝火。\r\n	两人坐在一起，烤蘑菇、看着篝火的灰烬冉冉升向夜空。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5903, '旅行', '周四，邀请对方收拾好东西去度周末。告诉她将需要带上一些休闲衣服和步行鞋，但是不要告诉你做出了什么安排。\r\n	周五下班后去接她。\r\n	去一个浪漫的小客栈，度过放松的浪漫周末', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5904, '礼物', '调查对方的嗜好、买一件对她真正有用的礼物。\r\n	礼品越是特别，影响力就越大。\r\n	告诉她的朋友和家人，并用网络找你需要的信息。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5905, '水枪', '在一个酷热的夏天，买两只大水枪，带到沙滩\r\n	拿出来、扔给对方一只，然后kai始打水仗。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5906, '写故事', '给对方写一篇电子邮件故事。像这样kai始：\r\n	第一章：\r\n	这是一篇关于一个夏天的下午、在一次朋友婚约聚会上结识的李雷和韩梅梅的故事。\r\n	顺着这写下去。可以完全是虚构或者虚构和真实相结合。\r\n	结尾时写道：“现在是第二章，由你来写', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5907, '双轮马车', '租一辆双轮马车，两人一起去兜风。结束时，在公园野餐。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5908, '读书', '买一本你们两人都感兴趣的书.\r\n	轮流每晚为对方念一章。\r\n	这是看电视的极好替代活动。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5909, '按摩', '当你和对方坐在一起时，走到对方背后，给他/她一次背部、肩部和头部的按摩。以在对方面颊一个温柔的吻而结束。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5910, '跑车', '如果可行，周末租一辆跑车。带上女友，送给她一条长白羊绒披巾和太阳镜。kai敞篷，沿着海滩驾驶', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5911, '看星星', '在温暖的夏季夜晚，安排一次后院野炊。将野炊篮放在地上。拿来一些零食、巧克力和香槟。和对方躺在地上，一起看星星。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5912, '日出', '当你和对方在享受休闲时光，安排一次早起，去看日出。\r\n	这似乎很难做到，但是决定值得至少做一次。看见新的一天kai始的确是一件可以分享的特别事情。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5913, '早餐', '为对方提供床上早餐，例如：\r\n	(1)一只心形荷包蛋--可以在大多数厨具商店买到心形模\r\n	(2)法式面包+肉桂和糖枫汁\r\n	(3)麦片\r\n	(4)果汁\r\n	(5)鲜花', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5914, '保镖', '如果对方要离kai几天，告诉她你对她担忧，于是请了一位保镖随身保护她。然后，送上一只小玩具熊。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5915, '枕头大战', '进行一次”枕头大战“。买两个大nei填满羽毛的枕头。使枕头上有些孔，这样羽毛就会飞扬。然后在你感觉时机合适的情况下”袭击“对方。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5916, '荧光星星', '买一包荧光星星，将它们贴在床上屋顶，拼作“我爱你”。当灯光暗下，你的话就会显现出来！', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5917, '热气球', '将一次热气球旅行作为一个特别的惊喜。\r\n	多数旅行kai始时，打kai一瓶香槟，之后，你两乘坐热气去飘过乡间。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5918, '温泉', '当你能去泡温泉，在澡盆周围摆上一些蜡烛，并在水面上飘上一些花瓣来制造浪漫的气氛。\r\n	当对方享受温泉时，给她一些香槟、放有草莓的巧克力。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5919, '牵手吗', '将你的手影印下来，并通过微信发给对方，留言：“要牵手吗？', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5920, '共同成长', '两人一起买一棵树，然后种在一个特别的地点。每年当你们纪念日的时候，在树旁一起喝一杯香槟，然后，谈论一下你们的爱情，以及那棵树，的成长如何。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5921, '生日礼物', '联系对方的家人，问她在小时候总希望得到什么。\r\n	例如：如果她总希望有一只瓷器娃娃，为她生日时买一只。\r\n	她不但会喜欢这份礼物，而且，对你既然能如此考虑周到地去发现她曾希望获得的东西也会有感激。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5922, '按摩师', '买一个小的装点好的纸盒，一张彩se手纸，和一些按摩油，和一张白纸板。\r\n	在盒子周围包上卫生纸。将按摩油放入盒子nei，写上以下留言：\r\n	我认识一个很棒的按摩师。\r\n	预约电话：（你的电话号码）', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5923, '纪念物', '某次和对方在一起出游的时候，捡起一块光滑的石头，说你将让它成为你们出游的特别纪念物。\r\n	之后，交给珠宝商在上面刻出例如：“我爱XXX的字样后送给对方。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5924, '虚荣心', '某天到女朋友公司，接她去吃饭。\r\n	把花送到对方上班地方。\r\n	她不但会喜欢花，而且会从办公室同事那里获得评论和注意力，这会让她更快乐', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5925, '网站', '如果可能的话，以对方的名字注册域名。\n	在网页上放上浪漫的诗歌和一副玫瑰照片。当对方在上网的时候，你随意地问问是否那个域名被抢注。\n	让她输入自己的名字发现那个网页\n	适合IT男。。\n	\n	可以找我们吆', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5926, '永不凋零', '在特别的日子，给对方买11朵真正的红玫瑰，和一朵人造玫瑰。把那只假玫瑰放在花束中央。\n	附上留言写道：“我爱你直到最后一朵花谢', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5927, '雨天', '下次，真地下了很大雨的时候，和对方一起去散步。\n	忘了雨伞和雨衣吧。\n	一起在大街上穿行，跳入地上积水，来个落汤鸡。\n	将她抱起，旋转，轻吻。\n	品尝她脸上淌过的雨水，紧抱。\n	在回家时，洗个热水澡，然后在一起喝暖饮，听着音乐或者一起看电视。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5928, '天台', '道具：蜡烛，烟花、录音机、随身听\n	nei容：夜晚，用蜡烛在地上围成一个心形图案，用一根绳子将各个蜡烛的灯芯及烟火导火线连在一起，与她一起点燃烟花，接着打kai录音机一起慢舞，跳累了，静静地依偎在一起看星星，听音乐，整个世界只有你们两个的声音……', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5929, '浪漫的DIY饰品', '他带着她来到一个可以制作小饰品店，利用将近两个小时的时间共同制作她喜爱的饰品，并为她戴上，这是他们共同的结晶，她一定会好好珍惜、好好保留。\n	约会自制饰品不超过50元。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5930, '拼模型', '买了一个天坛的仿真木板模型，随即去了一个咖啡厅，找一个靠近窗户的角落，要一壶花茶。kai始拼图游戏\r\n	幽雅的音乐和灯光，两个人默契的配合，几个小时之后天坛拼好了！真的会很有成就感哦~~！看着它，再看着对方，觉得又亲近了一点儿呵呵！\r\n	模型几十元元，花茶几十元元（加水免费）。100不到', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5931, '承诺的瓶子', '在情人节那天晚上，送给她一个自己买的jing致瓶子。\n	里面放上对她的承诺，双手送给她，对她说你就是我的那片海，我的漂流瓶已经从海的另一端带着希望回到了自己手上，我把漂流瓶交给你，希望你能让我的愿望实现。\n	然后女的也把愿望写进去，告诉对方希望两人的愿望能够一起实现。\n	两个人带着这个瓶子去他们第一次遇到的地方，把瓶子埋在那里，等明年今天两个人约定一起去拿那个瓶子，看看愿望是否实现。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5932, '漂流瓶许愿', '漂流瓶：写上共同的祝福和心愿，希望任何一个捡到它的人都能同样幸福，只要花费去\n	投瓶地点的路费即可。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5933, '献花表真情', '1、在他（她）早晨睡醒时，放在他（她）枕边一朵代表爱情含义的花束。\n	2、在他（她）的房间里放满“我爱你”、“永远在一起”、“愿你永远幸福快乐”的小纸条。\n	3、买许多玫瑰干花，撒满你和他的房间，然后好好地热吻一次。\n	不需要多少钱，幸福在生活的点点滴滴！', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5934, '百年好合的心愿之旅', '1、首先，自制一些情人节卡片，贴上干燥的玫瑰花。百合花等恋人喜欢的花。\n	2、构思一份完美的礼物，比如是一个热吻，一句不好意思说的：“Iloveyou!当然不一定花钱了。\n	3、送完礼物和卡片后，带他到一个幽静的公园，买许多氢气球，上面写上你们的名字，然后放飞天空。\n	4、找一个空瓶子，写上两个人的心愿，放倒护城河里，让它漂流远方。\n	费用很低的了，估计也就不倒20元。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5935, '99个我爱你', '准备好99个气球。气球批发袋装，在带一个气泵也就30左右。我们算35。\r\n	然后去买5块钱的折纸，在每个折纸上面写下对爱人的祝福和心里话塞在气球里，吹起。\r\n	可以叠成心，或者很多各式各样的东西。越烦琐越好，拆的时间越长越让人幸福，何况是在大厅广众之下去拆呢。\r\n	地点：KFC将事先准备好的气球交于服侍生管理。或者放于厕所nei。找个中心位置，因为气球较多。在拿剩下的MONEY点两份套餐。（也可以和自己朋友野炊的时候用）\r\n	虽然麻烦，但是这一壮举不失让女友在众目睽睽之下感受一份甜蜜害羞的感觉。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5936, '视频记录', '和女朋友约会时，拿出自己亲手做的巧克力蛋糕，呈在男生面前并对他说：“这是我亲手为你做的，尝一下吧。”\n	女孩接过蛋糕咬了一口，香甜味充满整个口腔的同时，看到咬过的地方似乎有一样东西，拿出来仔细看原来是粉se字条，打kai，写着：“我永远爱你！”\n	女孩看过后，一定觉得自己是这世上最最幸福的女人，会决心更加爱你。\n	最后对她说：“其实这就是我一直想对你说的。”\n	接下来一定是浪漫又甜蜜的拥吻！', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5937, '视频记录', '做个视频，记录从你认识这个女人到目前的点点滴滴。\r\n	多放一些照片，配上当天发生的事情。\r\n	在女孩生日的时候送给她。\r\n	很多女孩会哭的哦', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5938, '视频记录', '做视频记录。\r\n	一kai始，你们两人坐在沙发上，对视频做说明，例如：“现在是xxxx年七月14日。我们决定录制因为这样就能在结婚25周年纪念日时欣赏”\r\n	然后，在录制时一部分是你独自讲述，告诉摄像机你对对方的感觉，为什么爱她。\r\n	同样，她也完成这一过程。\r\n	录完后，把录像放在一个银行保险箱中，\r\n	到了你们结婚25周年纪念日时，你们就能回顾过去，追忆彼此曾分享的一切。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5939, '愿望', '当你们一天晚上吃饭时，问对方一直喜欢做什么。\n	之后，将回答写下来，这样你就不会忘记，以后来实现它们。\n	例如，她也许说她希望有一天能和海豚游泳。\n	查阅以下能实现的地方，安排这项活动，在纪念日的时候作为一个特别的惊喜。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5940, '采访', '当你们两人要去某个特别的地方，准备好照相机，等她出门。\n	当她出现的时候，你要像专业摄影师那样带着闪光灯疯狂地为她拍照。\n	在拍照的时候，不断问将她当作女明星、你要给杂志做专访，不断对她提问，\n	这不断有趣，而且今后一起回顾时会带来极大乐趣。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5941, '跳舞', '你需要一个音乐播放器。\r\n	如果你和对方都喜欢同一首歌，那么将它拷贝，并在浪漫周末时携带在身旁。\r\n	当你们来到一个浪漫的地点后，问她是否乐意跳舞。\r\n	将一个耳塞放入她的耳中，另外那只耳塞放入你的耳中，然后尽享你们的私人舞池。\r\n	这个方法如果是选在那些人们通常不会跳舞的地方尤为奏效，例如，在城市高楼的天台，或，野营时山顶上。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5942, '乡间小道', 'kai车来到乡间，找一座长草的小山丘，和对方躺在一起，看着云彩。\n	做找出云朵形成哪些形状的儿童游戏。\n	当你怀抱对方，以一个kai玩笑地口吻询问：可以为你朗诵一首爱情诗歌吗?\n	她大概会说好吧，并期望你说出“玫瑰是红..类型的诗句。\n	但是，你看着她的眼睛，一边微笑者朗诵诗歌，一边温柔地抚mo她的脸。可以尝试下面诗句。如果太长了，只要记住前面四行和后两行。\n	莎士比亚爱情诗18\n	我能将你比作夏季的一天吗?\n	你却更可爱、更温和.。。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5943, '纪念杯子', '当你和对方度过纪念日时，买两只香槟玻璃杯，然后在上面刻上你们的名字和日期。\r\n	例如：李雷和韩梅梅2002年五月7日\r\n	去你们预定好的餐馆，然后要求用你带的特别的杯子装酒。\r\n	这会给对方一个极大的惊喜，而且可以作为两人的一个很棒的纪念品', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5944, '洗澡', '当对方在淋浴或泡澡时，在给她拿浴巾的时候，将之放在吹风机来让它变得很暖，然后当洗完后，将她裹入其中。（冬天使用）', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5945, '洗澡', '当你的配偶度过漫长艰难的一天，为她洗一次热水澡。\r\n	将一些芳香的洗浴液放入澡盆中，然后温柔地为她从头洗到脚趾。\r\n	将她抱入卧室。\r\n	温柔地用将她拭干后，放入刚铺好的床上，并给她的额头一个轻吻。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5946, '镜子', '买一面时尚的手持镜子，将它作为礼物送给对方。\n	在盒子nei卡片写道：“在镜子中，你看到的是世界上最美丽女xing的样子”\n	她每天用镜子的时候，都会想起你，而且花钱不多，特别适合异地恋的情侣。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5947, '散步', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\r\n	邀请对方散步。\r\n	在背包中放入以下物品：野炊垫毯、小容器nei水果：如草莓、葡萄、西瓜和猕猴桃。一些奶酪和饼干。一些三明治、一小罐鱼子酱。半瓶香槟和两个塑料杯。\r\n	如果对方问起里面装了什么，只要说是一件夹克和一些午餐。\r\n	当你们来到了浪漫地点，问她是否愿意听下来吃点东西。打kai包裹、然后按件地拿出来，摆好。最后拿出的应该是塑料杯和香槟酒。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5948, '隐匿情话', '如果你是在早晨第一个洗澡。让浴室间充满蒸汽，然后在镜子上用手指写下”李雷爱韩梅梅的留言。\n	在她用浴室时便能看见。\n	这个方法当天气很冷时写在车窗上也能奏效。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5949, '气球', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\r\n	在车箱nei装满氦气球，kai车到一个浪漫地点去散步。\r\n	这个理想地点应该在高处，对四周乡间有一个清晰视野的地方。\r\n	走出汽车，装作似乎你们要kai始散步。\r\n	一定让对方离车近，你假装系鞋带，然后扔给她一把钥匙，问一问是否她能帮你从车厢nei拿一件夹克衫。\r\n	当她打kai行李箱时，气球就会释放。\r\n	你还可以在车厢nei放写着”我爱你的标语。那么打kai后也能显现。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5950, '随机约会', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\r\n	当两人计划出去就餐时，建议你们进行一次“冒险晚餐”活动。\r\n	以下是如何进行：\r\n	在跑表上设定20分钟倒计时。然后让对方在5到10之间选择一个数字。比方说她选择的数字是7\r\n	给对方一个ying币，然后告诉她每过七个十字路口仍一次ying币。\r\n	如果结果是正面头像，左转，如果似乎反面，右转。\r\n	当你的计时表时间走完，你们要寻找最近一处就餐。\r\n	这是一个具有娱乐的出去寻找不同地方就餐的方法。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5951, '纪念日约会', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\n	在一个特殊的场合，例如对方的生日，买24朵玫瑰花。\n	安排出去吃晚饭之前，先去一个商场特定地点见面。\n	提前来到商场，告诉她有工作要忙，不能陪她过了，或者说晚点到。\n	然后，站到那个见面地点的拐角。\n	让一位路过的男士是否能帮一个忙。给他一朵玫瑰，指点出你的约会伴侣，然后请他走上前说：“XX祝你生日快乐，然后将玫瑰递给她后走kai。\n	再让另11位男士重复这个行动。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5952, '储物箱', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\n	在女朋友的生日的时候，带她去超市购物。\n	在超市的储物箱里，提前放上你为对方准备的礼物。\n	然后假装你们在进入超市前捡到一把钥匙，提议去打kai看看里面有什么。\n	当打kai储物柜之后，女孩看到了你给她准备的礼物会有惊喜。\n	这个时候你可以在她耳边对她说：亲爱的，生日快乐。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5953, '视觉冲击力', '和喜欢的女孩约会，不要太过于千篇一律，偶尔也可以给女孩一些惊喜、浪漫。\n	kai车接对方约会，蒙住她的眼睛，直至到达目的地。\n	重点是让目的地完全出乎意料。\n	例如：\n	在悬崖边摆一张桌子\n	或在老式船上共进晚餐。\n	城市里高楼的天台（夜景更美）\n	需要是在她拿去眼罩后能给她带来冲击力的地方。', '烂漫创意约会');
INSERT INTO `talk_art` VALUES (5954, 'kai启话题', '奇怪，我刚刚看到你双胞胎妹妹了，不是你在跟踪我吧！（用来kai启话题）', '约会后话术');
INSERT INTO `talk_art` VALUES (5955, '醉酒二次开场白', '男：刚才看的了一件奇葩的事情\r\n	女：怎么了\r\n	男：今天和朋友喝多了，路过一派出所的时候，丫对着门口大喊：“里面的人听着，你们已经被我包围了，给你们十分钟时间，缴！枪！投！降！”他喊完我瞬间就特么醒酒了，立刻把他拽走。。。\r\n	男：是不是我交友的方式不太对。。。。', '约会后话术');
INSERT INTO `talk_art` VALUES (5956, '许愿二次开场白', '男：刚才发生了件很倒霉的事\r\n	女：怎么了\r\n	男：刚才看到一个人不小心从楼顶摔了下来,啪地一声落在地上。我心里感觉特别难过\r\n	男：唉，我还没来得及许愿呢。还是先帮他叫救护车吧', '约会后话术');
INSERT INTO `talk_art` VALUES (5957, '闹事二次开场白', '男：刚才发生了件很倒霉的事\n	女：怎么了\n	男：刚才宿舍老大穿着nei裤去隔壁宿舍闹事，十分钟过后，人没回来，nei裤被送回来了。\n	——适合住在宿舍的人使用', '约会后话术');
INSERT INTO `talk_art` VALUES (5958, '闹事二次开场白', '男：刚才发生了件很倒霉的事\n	女：怎么了\n	男：刚才宿舍老大穿着nei裤去隔壁宿舍闹事，十分钟过后，人没回来，nei裤被送回来了。\n	——适合住在宿舍的人使用', '约会后话术');
INSERT INTO `talk_art` VALUES (5959, '调侃二次开场白', '男：我知道你在干嘛\r\n	女：我在干嘛？\r\n	男：在和我这个大帅哥聊天', '约会后话术');
INSERT INTO `talk_art` VALUES (5960, '整人二次开场白', '男：快打kai电视\r\n	女：怎么了？\r\n	男：美国白宫被炸了，整栋楼全塌了，警方已经封锁了整个华盛顿，19人死亡，32人受伤，11人失踪。。。\r\n	女：真的吗？\r\n	男：还有1人受骗！', '约会后话术');
INSERT INTO `talk_art` VALUES (5961, '按摩二次开场白', '男：刚才笑死我了\n	女：怎么了？\n	男：今天和朋友去洗脚城（正规的）按摩。小妹一直从脚指揉捏到大腿根部，但当她揉捏到大腿的时候我可耻的ying了！夏天裤子薄，顶得老高了！\n	男：我脸红透了，可是人家小妹去不以为然，说了一句＂大哥身体反应很健康哦＂此时朋友回头看见笑得半死。', '约会后话术');
INSERT INTO `talk_art` VALUES (5962, '墨镜二次开场白', '男：刚才发生了件特别奇葩的事\n	女：（她会问你）\n	你接着说：刚才我对着镜子念了一句咒语“魔镜啊，魔镜！谁是这个世界上最帅的男人！”神奇的事发生了！镜子里面真出现了我的样子！', '约会后话术');
INSERT INTO `talk_art` VALUES (5963, '感觉调侃', '男：上次见完面之后，你一直给我留下了个深刻的感觉\r\n	女：什么感觉\r\n	男：没遇到你之前，我的世界都是黑白的\r\n	女：哈哈，真会说话\r\n	男：现在看到你之后，我的世界全黑了。', '约会后话术');
INSERT INTO `talk_art` VALUES (5964, '默契二次开场白', '男：发现我们有个默契?\n	女：？？？\n	男：你不言，我也不语\n	或\n	男：据说两个人相处久了会达到一种默契，那就是，你不理我，我也不理你\n	或男：我们之间的默契，你不说话，我也沉默', '约会后话术');
INSERT INTO `talk_art` VALUES (5965, '失恋二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：在大街上，一个女孩哭着求她的男朋友不要甩了她，然后男人坚决甩了她，转头就走了。\n	男：她继续哭了3分钟左右，然后保持哽咽状态和她的备胎打电话含着泪说：因为太爱你了所以我和男朋友分手了。\n	男：这。。。我是不是太没见过世面了，还是有很多女孩都这样。', '约会后话术');
INSERT INTO `talk_art` VALUES (5966, '倒霉二次开场白', '男：刚才太倒霉了\r\n	女：怎么了\r\n	男：我到银行汇款，车临时停路边上。怕交警罚款，就让朋友留下看车，跟他说有查车的过来告诉我一声，进去几分钟果然有交警来了，那个朋友风风火火地闯进银行大声吼道：大哥，警察来了，快走啊！\r\n	男：偌大的一个大厅几十号人，顷刻间寂静无声，然后人潮像洪水一样涌出银行，然后我就被几个保安按在了地上…', '约会后话术');
INSERT INTO `talk_art` VALUES (5967, '郁闷二次开场白', '男：刚才郁闷死我了\n	女：怎么了\n	男：我刚才排队买东西，有个粗大的汉子插队到我前面，我生气的说你怎么插队呢\n	男：他回头看了看我，咧嘴一笑说，很简单啦，首先在前面找个位置，然后挤进去就行了。', '约会后话术');
INSERT INTO `talk_art` VALUES (5968, '美好事物二次开场白', '男：刚才我看到了特别温暖的一幕，让我很感动\n	女：你看到什么了\n	男：今天坐公交，看到一个男孩和一个女孩坐一块，女孩靠着窗睡着了，男孩看着女孩，轻轻的在女孩的脸上亲了一口，女孩醒了。这是个多么美好的画面。\n	男：如果他们两认识的话。', '约会后话术');
INSERT INTO `talk_art` VALUES (5969, '对骂二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：刚才我坐的出租车和另一辆出租车追尾了,两司机跳下来站路中间,一点不带夸张的,差不多8分钟时间两人轮流爆出200多种和逼有关的短语及词组,完全是以“逼”造句大赛。\n	男：两人坚决不动手就靠你一句我一句对穿肠。到最后完全就是你是逼逼逼逼逼逼逼的节奏,再他妈稍微给点儿鼓点,我都能下车直接摇头蹦的了', '约会后话术');
INSERT INTO `talk_art` VALUES (5970, '帅哥二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：我排队买东西，有人从背后拍了我一下，我回头一看，是俩姑娘，其中一个害羞地笑了笑说不好意思啊，认错人了……\n	男：然后我回过头去。隐隐约约地听见其中一个姑娘说帅个pi啊', '约会后话术');
INSERT INTO `talk_art` VALUES (5971, '犯错二次开场白', '男：刚才我差点铸成大错\n	女：怎么了\n	男：刚才我我突然有想学习的冲动，幸好喝了口水压压惊。\n	男：好了现在平静了，刚才实在是太冲动了，还好我自制力强', '约会后话术');
INSERT INTO `talk_art` VALUES (5972, '见识二次开场白', '男：地铁是个涨见识的地方\n	女：怎么说\n	男：我在地铁上听过有讨论几千万工程的，听过讨论要买超跑的，听过狗血出轨调qing的等等等等。\n	男：刚刚我还听到旁边俩哥们儿在讨论铀浓缩技术和原理以及离心机铀浓缩的设备原理。', '约会后话术');
INSERT INTO `talk_art` VALUES (5973, '淘宝二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：我上淘宝买衣服，选了付钱了联系卖家说，我已付款，请发货。谁知那货直接说我看到你地址了，自己上楼来拿吧！我就在你楼上。\n	女：哈哈，这么巧，你下去拿了吗\n	男：拿个毛线，我付了邮费的，他得给我送下来。', '约会后话术');
INSERT INTO `talk_art` VALUES (5974, '惊吓二次开场白', '男：刚才吓死我了\n	女：怎么了\n	男：我去医院体检，医生拿着我的报告单说：幸亏你来的早啊。\n	男：在我惊出一身冷汗的时候，医生不慌不忙的说：再晚点，我就下班了。。。', '约会后话术');
INSERT INTO `talk_art` VALUES (5975, '刺激二次开场白', '男：刚才看的了一件奇葩的事情\r\n	女：怎么了\r\n	男：刚才收到一个同学的微信，他说哈哈，刚刚嘿咻完，女朋友她妈回来了！我现在拿着衣服躲在她的衣柜里，像演电影，好刺激…\r\n	男：然后我拨通了他的号码……\r\n	男：就让情节朝更刺激的方向前进吧！', '约会后话术');
INSERT INTO `talk_art` VALUES (5976, '超载二次开场白', '男：刚才看的了一件奇葩的事情\r\n	女：怎么了\r\n	男：见一个摩的，加上骑车的一共四个人。已经严重超载了，在过十字路口的时候，交警叔叔就给他招手，让他停下\r\n	男：摩的师傅来了一句，坐不下了。kai着车就走了。', '约会后话术');
INSERT INTO `talk_art` VALUES (5977, '惊吓二次开场白', '男：刚才吓死我了\n	女：怎么了\n	男：刚刚被几个社会小青年堵在巷子里，非说我走路姿势太嚣张了。\n	男：我给他们说，哦，那是你们没见过我跑步的姿势，更屌！\n	男：他们说，来，跑一个看看，妈呀，一溜烟似的我就跑了，头都不回！', '约会后话术');
INSERT INTO `talk_art` VALUES (5978, '贫困kai二次开场白', '男：今天和一个美国朋友逛街，笑死我了\r\n	女：怎么了\r\n	男：我说我们现在也算是国富民强了，然后这个朋友说NONONO！国家是否富强，应该关注底层人民。比如说你看这里还是有乞丐，他们是多么贫……\r\n	男：正说着，乞丐拿出来了一个IPHONE手机接了个电话。', '约会后话术');
INSERT INTO `talk_art` VALUES (5979, '旺仔广告二次开场白', '男：刚才我看旺仔的广告，感觉好残酷啊。\n	女：怎么说\n	男：旺仔让这些未成年的少年少女，在广告里一边笑一边不知廉耻的大叫，我ya，我ya……\n	男：然后把白se的液体从柱状物体中倒在自己的嘴里，全部喝下去之后还要学狗叫', '约会后话术');
INSERT INTO `talk_art` VALUES (5980, '推销二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：街上一摆地摊女孩拉住我，奋力推销。我摆tuo不掉，看远处有警察，我说再不放手我叫警察了。她马上接道：距离这么远你喊太费劲，买个扩音机吧。。。\n	女：哈哈哈\n	男：我当场就要了她的联系方式，我公司里就需要这种锲而不舍外加不要脸的员工。', '约会后话术');
INSERT INTO `talk_art` VALUES (5981, '不要命二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：刚看见一个骑自行车的人闯红灯，一辆大卡车差点撞到他了\n	男：骑自行车的那个人对卡车司机大声喊，不要命啦你！，卡车司机当时就楞了，一句话也说不出来', '约会后话术');
INSERT INTO `talk_art` VALUES (5982, '保护二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：有个人跟我说，亲爱的，未来的路很长，充满了未知，可能随时有危险，所以乖乖躲在我身后，让我保护你好吗？…………\n	女：谁对你这么深情啊\n	男：深情个pi，就一个人想插队，还这么多pi话，我跟她说，滚后面去。', '约会后话术');
INSERT INTO `talk_art` VALUES (5983, '中学生恋爱二次开场白', '男：看到一对中学生在街上手牵手,不禁想起了中学时代的自己。\r\n	女：你这么早就谈恋爱了\r\n	男：不是，当年的我,也是在街上看着一对对中学生在街上手牵手。', '约会后话术');
INSERT INTO `talk_art` VALUES (5984, '搭讪二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：刚才和朋友坐公交车，车上有个姑娘挺漂亮的，朋友轻推我一下做了个嘘的手势，就看他一搂姑娘小细腰说：亲爱的今晚想吃什么？\n	男：姑娘一愣正准备发怒，朋友紧接着凑她耳边小声说了句话，只见姑娘眼神瞬间温柔无比，感激的看着我朋友。\n	男：随后姑娘抱紧她的背包惊恐的瞪了我一眼，后来姑娘和他一个站下车了！！！', '约会后话术');
INSERT INTO `talk_art` VALUES (5985, '情侣吵架二次开场白', '男：刚才看的了一件奇葩的事情\n	女：怎么了\n	男：我跟新来的同事出差，晚上一起住宾馆，他比较nei向，我俩就互相看着对方一句话也没说，气氛有些尴尬，于是我首先kai口道，你很闷啊，他扭捏了半天有些不好意思地说：你也很men啊\n	男：说着把手放到了我的腿上\n	男：我是不是说错了些什么', '约会后话术');
INSERT INTO `talk_art` VALUES (5986, '难伺候二次开场白', '男：现在的女生真难伺候\n	女：怎么了\n	男：生气了就傻站在大马路上，怎么哄也哄不好。我说给你买束花吧，不理我；我说带你去看电影吧，不理我；我说我们去吃好吃的，还是不理我。\n	男：后来他男朋友过来了说：麻痹的你谁呀？', '约会后话术');
INSERT INTO `talk_art` VALUES (5987, '噩梦二次开场白', '男：刚才我做了个噩梦，吓死我了，一堆人追着我打\n	女：balabala\n	男：太吓人了，他们也真狠，打了我三四个小时，都没有把我打趴下。\n	女：你还挺厉害的啊\n	男：他们把我绑树上打的', '约会后话术');
INSERT INTO `talk_art` VALUES (5988, '谈人生二次开场白', '美女！我看你骨骼清奇耳聪目明，定是一个不凡之人！你我谈一下人生如何？', '约会后话术');
INSERT INTO `talk_art` VALUES (5989, '老年人', '男：现在的老年人也太不像话了\n	女：怎么了\n	男：刚才碰到一个老头和我说。年轻人，我都八十了，身体撑不住呀，你就行行好给让个位吧，要懂得尊重老人啊。”\n	女：那你怎么不让人家一下啊\n	男：可是我还没有把屎拉完呢。', '约会后话术');
INSERT INTO `talk_art` VALUES (5990, '调qing', '当你和女孩约完会之后，你在微信上问她：你对我是什么感觉？\n	一般女生都会这么回答：挺好啊，怎么了\n	可能也会有其他的，比如打压你，说你不好\n	然后你就回信息：那么，你觉得咱们两个还有处下去的必要吗？\n	女生一般都会一惊，问怎么了，如果是有些经验的女生可能会回：好吧，那就不处了。\n	然后你说：咱们直接去结婚登记吧，嫁给我，你有吃不完的辣条。\n	女生一般到这时候会回过神，知道你是kai玩笑的，可能就会吐槽你“去死吧”之类的。\n	你就将计就计说：我就是想死了，你怎么知道的，不找死我为什么要跟你去登记啊', '约会后话术');
INSERT INTO `talk_art` VALUES (5991, '调侃', '男：上次跟你一起吃饭，我发现你一个特点\n	女：什么特点\n	男：我发现你很能说，但是比不过我一哥们。我和他去海边玩一圈，他牙都晒黑了。', '约会后话术');
INSERT INTO `talk_art` VALUES (5992, '约会时女孩玩手机', '男：嗯？你经常玩手机吗？\n	女：是啊，怎么了（八九不离十都是这个回答）\n	男：哇，那你的指甲一定是蓝se的\n	女：什么？（或者“怎么可能”之类的回答）\n	男：你不信？\n	这时候拿起她的手，把她的手托在你的手上，这个时候一般女的手都是掌心向上，如果不是就说\n	男：我要看的是你的指甲，别说你手指两面都有指甲\n	然后自然的转过女的手\n	这时候女都会疑惑的看着你不知道你要干什么\n	现在，女的手已经在你的手上了，且手背向上\n	接着，你说：知道吗？(说的同时用你的食指放在女两只手指的根部)经常用手机啦，电脑的人的手指甲会慢~慢~（说这两个字的时候一定要缓慢，同时用你刚刚放在女手指根部的那只手指随着你口里说的”慢慢“滑向女的指甲）变成蓝se的，看，就像你这里“\n	女：balbalbal\n	指甲蓝se的部分在指甲的根部，不仔细看是看不出的，而且因人而异', '约会冷场');
INSERT INTO `talk_art` VALUES (5993, '约会冷场', '早知道我带你去桑拿房了，这样肯定不会冷场', '约会冷场');
INSERT INTO `talk_art` VALUES (5994, '女孩玩手机冷场', '男：这里的信号是不是特别好\n	女：XXX（一般都笑了，知道你在笑话她老玩手机）\n	男：你知道我和别人吃饭，最讨厌那两点吗。\n	女：玩手机，还有一个呢\n	男：约会迟到（接着说）可能是我太无聊了吧。\n	女：没有没有，我是刚才有事情。\n	男：信不信我把你手机给吃了。', '约会冷场');
INSERT INTO `talk_art` VALUES (5995, '冷场调qing', '男：我们为什么一定要不停的说废话来维持气氛呢？你有没有觉得无聊\n	女：有点\n	男：既然这么无聊，我们接个吻活跃一下气氛吧？', '约会冷场');
INSERT INTO `talk_art` VALUES (5996, '眼神调qing', '约会时，如果你们之间冷场了，就可以说这个惯例。\n	首先，面带微笑着看着她。\n	女：“怎么了？”\n	男：不舒服，我的眼睛好像有点问题\n	女：怎么了\n	男：我无法将它们从你身上移kai\n	女：你真贫\n	男：怎么了，有问题吗，我要把你看我的那些都看回来。', '约会冷场');
INSERT INTO `talk_art` VALUES (5997, '约会时女孩玩手机', '男：你手机套子这好看，我看看。\n	女孩把手机递给你\n	男：我最讨厌吃饭的时候，别人玩手机了，不给你了。', '约会冷场');
INSERT INTO `talk_art` VALUES (5998, '约会冷场', '男：我突然想起来一个游戏，非常适合现在的场景玩\n	女：什么游戏\n	男：谁不说话谁就是王八蛋的游戏，我们现在是不是应该玩', '约会冷场');
INSERT INTO `talk_art` VALUES (5999, '约会冷场话题', '对了，你知道吗。。。。。。（随时可以kai启一个新话题）', '约会冷场');
INSERT INTO `talk_art` VALUES (6000, '约会冷场kai启话题', '我突然想起来一件事情。。。。。。（随时可以kai启一个新话题）', '约会冷场');
INSERT INTO `talk_art` VALUES (6001, '红酒美容', '酒桌上经常会喝红酒。\r\n	男：我喜欢喝红酒，你知道为什么吗？\r\n	女：为什么？\r\n	男：没听说过喝红酒能美容吗？\r\n	喝一口，很陶醉的样子，然后甩甩头发曰：啊~又帅了~\r\n	（红酒也可以换成任何一样东西，比如水果，咖啡，臭豆腐之类的）', '约会互动');
