INSERT INTO `talk_art` VALUES (6215, '利用戴戒指分析女生的xing格', '右手食指(冷读） ：“你很擅长于与人竞争，或夺取某些东西，这种xing格特质使你在做生意或事业表现上，有超于一般人的能力。你不计较别人的批评或感觉，只要达到目的或得到你想要的东西，一切在所不惜。 ”', '颜色星座手相');
