INSERT INTO `talk_art` VALUES (7091, '私人空间开场白', '男：“嘿，你们不介意，我问一个问题吧？”\r\n靠过去，缓慢的靠近她。这时候，她很自然的会往后退缩，因为你侵略了她的私人空间。\r\n这时候，你继续说：OK，不要担心，你已经给了我答案。\r\n这会让她坠入圈套，因为她想确切的知道是什么答案。而且她会习惯于你的接近，所以你可以比正常情况下更加靠近几分。\r\n你确切要问的问题就是“究竟存不存在所谓的私人空间? ”', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7092, '睫毛开场白', '男：hi（眼神接触，微笑）有个问题挺好奇，想问下你们女生。女生是先贴假睫毛还是先涂眼影。？\r\n女……\r\n男：是吗？我这么问是因为我有个朋友是化妆师，我们刚才在一个朋友的酒会，他跟我提到这个问题，不过好像没有讨论出什么结果（停顿），我这个朋友是个追求完美的人，她每天出门前都会照下镜子，有时候我也是，不过你今天……（坏笑，友善的调侃）\r\n男：我得去找我朋友了，不过我有点好奇。你俩表情怎么那么像，你们是姐妹还是拉拉？', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7093, '抚mo脸庞', '轻轻拂去脸庞的睫毛，如果碰巧不存在的话。用纸巾擦拭额头的汗。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7094, '媒婆开场', '男：（拍一下目标的肩膀然后指着一个装mo捉样的男人）哦，我的天老爷。这家伙跟你简直是绝配——让我去帮你介绍！！（kai始往那个家伙那边走）\r\n女:啥子哦？！不……哦…不要不要不要…哈哈…救命啊！\r\n男：（对那个男人）这个女孩特别害羞，但是她非常想认识你！\r\n女:不不不…不是那样的！（逃避）\r\n男：哎哟拜托…不要那么不好意思…\r\n我试验过的两个女孩都非常火辣而且十分傲慢。但事过之后，她们给我说了好几次，我简直太疯狂了…\r\n ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7095, '捏鼻子', '轻轻捏她的小鼻子，摇一摇，说她是猪，说她是坏女孩，说她淫荡。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7096, '抚mo耳垂', '帮她整理头发，顺势抚mo她的耳垂。冬天捂住她的脸，搓她的耳垂。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7097, '同xing恋开场白', '用于和搭档在一起，以一种主人家的口吻会更好。\r\n男：有个事情，我想要你们给点诚恳地意见，我看起来像同xing恋吗？\r\n当你问这个的时候，一些女孩会突然暴笑起来。\r\n男：因为确实有某种荒唐的事情发生过，有一次在酒吧，有个装模作样的男人在后面捏我pi股，刚才那边那个男人还一直se眯眯的看我。\r\n更好的是和同伴一起的时候使用这个，并且改成“我的朋友看起来像同xing恋吗？”因为这样就消除了自我意识。而 “同xing恋”者应该把事情吹得非常风趣荒唐甚至极尽谄媚。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7098, '弹脑壳', '看过《我的野蛮女友》吧，和她打赌，弹脑门，温柔地弹，轻轻地刮脸，带着温柔的微笑突然狠狠地弹一下，弹出来包更好。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7099, '撒谎开场白', '男：“你好，我和朋友在讨论一个问题，我们都觉得自己说得对，想听听女孩的看法？”\r\n女：“什么问题？”\r\n男：“你觉得男人更爱撒谎还是女人更爱撒谎？”\r\n回答1：男生爱撒谎：你是不是经常被男生骗，今天你们是被男生约过来，然后被放鸽子了么，所以才都是女孩子。\r\n回答2：女生爱撒谎：你这句话是不是在撒谎？ ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7100, '刮鼻子', '和她打赌，玩骰子，锤子剪刀布，输了刮她鼻子，轻轻地、重重地。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7101, '旅游开场白', '先拿着酒杯去碰杯，然后说：\r\n男：刚才我跟朋友打赌你们是来旅游的。\r\n女：XXXX\r\n男：喔，你害我输了赌注，你应该补偿我。\r\n女：你们的赌注是什么\r\n男：如果我赢了，你要亲我一下，如果我输了，我要亲你一下。 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7102, '抓痒', '挠胳肢窝。她搞任何恶作剧，或者犯任何错误的时候，挠胳肢窝惩罚她。挠完一次以后，搓着双手却不下手，脸上带着坏笑看着她，她会受不了压力钻到你怀里的。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7103, '化妆开场白', '男：hi，问你们个小问题，我和我朋友刚才一直在讨论这个，一个女孩子能观察出别的女孩子是否化妆了嘛？\r\n女：XXX\r\n男：因为我们今天和朋友一起喝酒的时候，所有的女孩子说“是的“，所有的男的说“不是”。对这目标说（那你化妆了吗）\r\n女孩通常说：是的\r\n男：好的，我肯定你（不化妆）也算漂亮的。\r\n如果目标回答“不是的”\r\n手伸出来，（轻轻打她一下）， 叫你骗我！', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7104, '喝醉酒开场白', '男：hi，有个问题想问下你们女孩的意见。你们认为喝醉了酒后说“我爱你”算数嘛？\r\n女：XXX\r\n男：是这样的，我的朋友昨天晚上喝的很醉了，他告诉他的女朋友说他爱她，然后今天早上他又反悔了，你们认为他真的是这样想的嘛？\r\n女：XXXX\r\n男：我是这么认为的，当你醉了的时候，你说的事情是你想说但是又不敢说的，所以我告诉他女朋友说他可能是真的爱他，但是只是需要更多的时间冷静一下。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7105, '嗅', '动物求偶都会嗅，会让女人的热情迅速地燃烧。嗅她的头发，说“你刚洗头？”嗅她的手腕，说“这个香水很好闻。”嗅她的肩膀，说“你刚吃火锅吧。”', '轻微触碰');
INSERT INTO `talk_art` VALUES (7106, '等电话开场', '由于手机的流行，酒吧里经常有美女在看她们的手机。对此我有个开场白，这绝对会让女人意外。当她们在看自己手机有没有错过的来电或者短信时，对她们说：“他还没打电话来吗？”  这个开场的回应通常是女孩被逗笑。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7107, '吹气', '假装趴在她耳边说话，吹一口气到耳朵里。在她后面时，吹气到她脖子里。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7108, '眼神开场白', '适合用于看了你几眼的女孩\r\n直接去和女孩碰杯后再说开场白\r\n男：你肯定是个很害羞的女孩\r\n女：为什么\r\n男：我看到你看我很久了，怎么还不过来向我搭讪。（坏笑）\r\n女：（基本上女孩都会笑起来了）\r\n（转到下一个话题，或者邀请到自己的位置）\r\n类似的话术\r\n你眼睛真特别，我刚才给你闪到了，就过来研究一下\r\n你刚才电到我了，我是来要医药费的\r\n你为什么含情脉脉的看着我？', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7109, '偷看开场', '你偷看我这么久、你应该过来跟我说话，我不会拒绝你的。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7110, '迅速的划过', '谈话时候，没有任何预兆地，用指头在她背上滑过，或者滑过大腿n侧，还有x部。\r\n女孩会觉得很刺激，会用渴望的眼神看你。\r\n这时候你假装没有注意到她的反应，看着其他地方，继续谈话，好像什么也没有发生过一样，不要再做第二遍。', '慢慢接触');
INSERT INTO `talk_art` VALUES (7111, '被搭讪借火机3', '女：有火吗？\r\n男：这么帅的帅哥，你不关心，却只在乎这个手机。要不要我走kai一下，把火机留给你，让你们两个能好好聊聊？”', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7112, '静静的接触', '坐公交车时候，或者吧台，你们坐在一起，身体一侧挨在一起，两个人不说话，感觉彼此体温从旁边传来。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7113, '被搭讪借火机2', '女：有火吗？\r\n男：有（拿出来，kai火，先别给她点，放回去）你知道吗，抽烟对身体不好！这是一个很危险的习惯。\r\n女：balabala\r\n男：你知道你还有一个危险的习惯吗？”（坏笑的看着她）\r\n女：什么\r\n男：比如跟一个陌生人搭讪，要火机？”', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7114, '重复某种接触', '坐公交车时候，或者吧台，你们坐在一起，身体一侧挨在一起，两个人不说话，感觉彼此体温从旁边传来。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7115, '被搭讪借火机1', '女：有火吗？\r\n男：那要看情况。看你是想点烟还是想把这个酒吧给烧了。 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7116, '外地人开场', '我发现本地人都蛮友善，在XX大家一般都比较冷漠，我比较喜欢这个城市！', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7117, '舞池大pi股开场', '如果跳舞时，女孩撞到你了。\r\n男：不要以为你pi股大，我撞不过你。\r\n（女孩会更加用力拿起pi股撞你） ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7118, '冒险开场白', '我冒险过来，你们应该不会揍我吧', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7119, '医药费开场', '你刚才电到我了，我是来要医药费的 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7120, '眼睛特别开场', '你眼睛真特别，我刚才给你闪到了，就过来研究一下', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7121, '眨眼睛开场', '嗨，刚才我看到你的眼睛眨了18下，眉毛挑了24下，但是你还没有来得及问我的名字！我叫XX。 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7122, '合影开场', '男：我可以给你合张影吗\r\n搭档走过来说：拒绝她，无情的拒绝他。\r\n这个时候对方一般会笑，调侃她们几句，kai始聊天。\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7123, '韩国人开场', '男：我朋友说你是韩国人，然后我过来问问你是不是。\r\n搭档走过来对你说：我就说她是中国人，你非说她不是，（转向目标说）他诬陷你。\r\n这个时候气氛活跃起来，可以继续聊天了\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7124, '借东西开场', '男：我可以带你帽子吗（或围巾、眼镜等等）\r\n假装玩一会，然后说：那我先走了。\r\n假装跑路，再回来重新和女孩聊天。\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7125, '同xing恋开场', '男：我是同xing恋，可以给我们点祝福吗（或你觉得我俩有夫妻相吗等等）\r\n搭档走过来打断说：别听他瞎说，我是正常有需求的男人。\r\n接着走到目标身边说：我跟她比较有夫妻相。\r\n然后说：拜托给我俩这个有缘人拍张照片。\r\n手放在目标肩膀上，或搂着拍照。\r\n继续对话\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7126, '魔术扑克开场', '男：你好。可以给你变个魔术吗？（这时候假装kai始全身找东西。）不好意思，扑克牌忘带了。（转身，走人）\r\n这时候你的搭档走上前去和女孩说：我是扑克牌。\r\n女孩一般会大笑，然后你在转身回来，和女孩聊会天。\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7127, '有缘再相遇开场白', '男：“你好，我刚看到你，想过来认识一下。”\r\n男：（趁她还没反应过来）“等等，你别急，一般女生听到这句话都会有2个选择，第1，好吧。第2，下次有缘见到再认识吧。你选一个吧~”\r\n女：“那我选第二个。”\r\n男：“ok，拜拜~”然后转头就走。走几步之后，女生也会离kai，这个时候马上杀回来叫住女生。\r\n男：“hello ，这么巧！又见面了~”100%女生到这里都笑了。\r\n男：“上次你答应我一件事情呢~”然后坏笑着看着她。\r\n女：“好吧。。。。。”\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7128, '借东西开场白', '男：hi，你好。（没等她kai口接着说）\r\n男：你猜错了，其实我是想问你借纸巾（一般女孩又的话都会给你）\r\n纸巾拿到手后（在她拿纸巾的时候，可以顺便观察她身上有无可以调戏的机会）\r\n男：谢谢你，你电话号码多少，改天还给你。\r\n女：哦，不用了（这时机会来了）\r\n男：哦，早知道问你借钱了\r\n女：（扑哧的笑了）。\r\n下面的自由发挥吧。一般我会说，你笑的时候挺可爱的\r\n如果美女说没有纸巾的话，怎么办呢。这样的话就多问女孩一个问题，比如：打火机有吗（她如果说没有的话，再问一些她没有的物件）等问到三次都说没有的话，最后就问她：那电话号码还有吗。（我有一次还问过女孩，你还有贞操吗）\r\n随便聊一会后说：我和朋友在那边玩的挺嗨的，你', '幽默开场白');
INSERT INTO `talk_art` VALUES (7129, '擦嘴', '在她吃东西的时候，看着她，说：别动！?假装她嘴角或者头发有东西，温柔的帮她擦一下（可以瞬间提升亲密感）', '轻微触碰');
INSERT INTO `talk_art` VALUES (7130, '舞池mopi股开场', '男：嘿，你刚才是不是mo我pi股了？\r\n女：没有啊\r\n男：没？那你应该mo一下！\r\n如果她觉得你很可爱，觉得很有安全感（或者喝醉了）的话，那她心情应该很好，她可能会拍你pi股。然后你要表现得很震惊，然后说“我只是kai玩笑的！我有一种被侵犯的感觉。我需要洗个澡，或者一杯酒。买杯酒给我。”', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7131, '舞池;同xing恋开场', '你们两个是同xing恋吗？跳舞搂在一起抱那么紧 ', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7132, '香水', '对女孩说：“别动！”\r\n然后把鼻子伸到她的脖子附近，闭上眼睛闻。很认真地闻过后，胡乱猜香料或者香水品牌。女孩会以为你懂行。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7133, '舞池僵尸开场', '男：你有没有玩过植物大战僵尸？\r\n女：玩过啊\r\n男：（做着手势）你跳舞就像那只僵尸。 \r\n', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7134, '掉东西开场白', '和女孩擦肩而过后，和女孩微笑打个招呼。\r\n男：你好，刚才我在找朋友，然后不经意就看到了你，我想给你个小小的建议。\r\n女：什么事啊。\r\n男：你的睫毛（也可换成项链或者耳环）快掉下来了，呵呵。\r\n（用手比划一下你的眉毛，然后你装作要离kai，看她反应。一般人会不自觉的去mo自己的睫毛。）然后你回身，坏笑着说\r\n男：逗你的啦，想不到你这么容易就上当啦，挺可爱的哦。（坏笑）\r\n女：XXX\r\n男：哈哈，好吧，其实我没有恶意，只是觉得你挺可爱的，和你kai个玩笑，我叫XX。\r\n女：XXX\r\n男：我和朋友在那边玩的挺嗨的，你可以过来一起玩一会。\r\n然后带女孩到自己的位置。', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7135, '惩罚', '如果对方碰你，你说：“嘿，我不喜欢别人碰我，你要是再这样，我就要碰你了。”然后打一下她的pi股说：“这是对坏女孩的惩罚。', '轻微触碰');
INSERT INTO `talk_art` VALUES (7136, '拉拉开场白', '拿着酒杯走到女孩跟前，微笑的看着女孩，先和她碰一下酒杯，喝一小口后和女孩说\r\n男：你们应该感谢我，知道我为什么过来和你们喝酒吗？\r\n女：不知道\r\n男：这样你们看起来就不像是拉拉了。\r\n女：XXX（基本上女孩都会笑起来了）\r\n（转到下一个话题，或者邀请到自己的位置）', '酒吧开场白');
INSERT INTO `talk_art` VALUES (7137, 'mopi股开场白', '拿着酒杯走到女孩跟前，微笑的看着女孩，先和她碰一下酒杯，喝一小口后和女孩说\r\n男：知道我为什么过来和你们喝酒吗？\r\n女：不知道\r\n男：刚才我朋友不知道怎么带过来个傻逼，一直在mo我pi股，我出来多多风头。\r\n女：XXX（基本上女孩都会笑起来了）\r\n（转到下一个话题，或者邀请到自己的位置）', '酒吧开场白');
