INSERT INTO `talk_art` VALUES (7199, '害怕推销员开场', '男：你好\r\n女：（发愣）\r\n男：我看你气质很好，所以过来和你聊会天。你站在这里，不会是推销员吧。你看起来有点像卖保险的。\r\n女：不是', '直接开场白');
