INSERT INTO `talk_art` VALUES (7308, '开场白惯例', '*男：我知道你在干嘛。 --女：干嘛？\r\n男：这是个心灵魔术，你先夸我帅，我就揭秘你在干嘛。女：你好帅！\r\n男：你在跟一个才高八斗，貌似潘安的帅哥聊天。\r\n女：哇，你好自恋哦。\r\n男：你自己都说我帅喽，我又没猜错。\r\n', '开场白惯例');
