INSERT INTO `talk_art` VALUES (9879, '开场白惯例', '*男：我觉得你应该赔偿我。女：啥呀？\n男：你的照片的眼神电到了我，我现在身负重----伤女：哈哈哈', '开场白惯例');
INSERT INTO `talk_art` VALUES (9880, '夜晚时间聊天开场白', '男：你是不是心情不好？\n目标：为什么这么说（好奇）\n男：因为一般人只有在空虚或心情不好时才会通过网络去发泄一下内心\n目标：只是睡不着（围绕情绪开始诱导对方自我暴露）', '开场白惯例');
INSERT INTO `talk_art` VALUES (9881, '开场白惯例', '*男：你有没有在失恋之后哭醒过?女：哭过，不过哭醒倒是没有。男：巴拉巴拉......', '开场白惯例');
INSERT INTO `talk_art` VALUES (9882, '开场白惯例', '*男：嗨。----女：哈喽。男：做个测试。----女：什么？\n男：你觉得男人穿西装帅，还是不穿西装帅？\n女：西装吧。\n男：那你应该是一个温柔的女生，渴望像父亲一样的男人给你安全感。\n女：哈哈，好像是......', '开场白惯例');
INSERT INTO `talk_art` VALUES (9883, '星探开场：适合时尚、个', '男：你好！（停顿）我想问你一个问题……（停顿）有没有人告诉过你，你很适合当模特？ \n女孩：是有人这么说过。 \n男：我觉得你很有气质想认识你。可以把你的微信告诉我吗 \n女孩：你是做什么工作的？ \n男：你看我像做什么的？ \n女孩：你不会是星探吧。 \n男：你的眼力真好……一下子就猜错了。我是***的。 ', '开场白惯例');
INSERT INTO `talk_art` VALUES (9884, '开场白惯例', '*男：问你个问题。女：什么啊？男：你觉得男人喝醉时候说的话能信么？\n女：怎么这么问？\n男：我有个朋友昨天喝醉了，然后表白了，看着特别让人心疼。\n女：哦哦，然后呢？\n男：然后第二天被他表白的哥们就跟他绝交了。\n女：哈哈哈，你身边都是奇葩。', '开场白惯例');
INSERT INTO `talk_art` VALUES (9885, '问题开场白：', '男：你好，我和朋友在讨论一个问题，我们都觉得自己说得对，想听听女孩的看法？ \n女孩：什么问题？ \n男：你觉得男人更爱吃醋还是女人更爱吃醋？ 问题讨论结束后转话题接故事，例如，有个朋友这两天很郁闷，他女朋友搜到了他和前女友合影的 照片，非要把这些照片烧掉。我朋友觉得都已经分手了，照片只是留下一点记忆，并不会再回到前 女友身边。但他女朋友觉得如果你爱我，就要全心全意对我，为什么不可以把这些照片烧掉。你觉 得他女朋友的做法是不是有点过分，我朋友该把那些照片烧掉吗？ （适合饭馆、茶馆、KFC、咖啡厅等环境 ）', '开场白惯例');
INSERT INTO `talk_art` VALUES (9886, '加好友后妹子不说话开场', '女 沉默中-----男：在捡金子吗？---女:???\n男:沉默是金呀，这一会儿捡了很多金子了吧女:BALABALABALA-----男:那记得分我些呀', '开场白惯例');
INSERT INTO `talk_art` VALUES (9887, '开场白惯例', '*男：嗨。女：哈喽。男：今天被一个女生的问题给弄头大了。女：什么问题？\n男：男人和女人谁更喜欢说谎？女：当然是你们男的咯。男：你看，你在说谎。---女：......', '开场白惯例');
INSERT INTO `talk_art` VALUES (9888, '开场白惯例', '*男：问个路。---女：哪儿啊？男：通往你心灵的道路怎么走？女：哈哈，不知道哎。\n男：没有，我最近在学心理学（释放需求）。\n女：是么，那你说说。\n男：恩，最近在看一部书叫做《打开女人双腿的钥匙》。女：啊？\n男：不好意思打错，是《打开女人内心的钥匙》，巴拉巴拉......', '开场白惯例');
INSERT INTO `talk_art` VALUES (9889, '适合酒吧或私密空间开场', '你：据统计，80%的女孩洗澡时会手淫，其他 20%会在洗澡时唱歌，你知道她们唱什么吗？\n女孩：...不知道\n你：看来，你是那 80%的', '开场白惯例');
INSERT INTO `talk_art` VALUES (9890, '网络开场白', '男：快到碗里来~----女：你才到碗里去？\n男：现在起你将认识一个特别的男人（建立冰山一角）\n女：有什么特别\n男：一个沧桑不失霸气，优雅而略带伤感的男人（偷笑），姑娘贵姓', '开场白惯例');
INSERT INTO `talk_art` VALUES (9891, '开场白惯例', '男： 我想问你个问题。女：什么问题？\n男：你觉得送女人礼物送什么比较好？女：巴拉巴拉......\n男：我是说，送给我妈，她快过生日了，从来没送过。\n女：哇，你好孝顺，我还以为是别的女人，巴拉巴拉......', '开场白惯例');
INSERT INTO `talk_art` VALUES (9892, '假性时间限制开场：适合', '我在这里等一个朋友，他过几分钟就过来了。你也在这里等人么？ ', '开场白惯例');
INSERT INTO `talk_art` VALUES (9893, '开场白惯例', '*男：我知道你在干嘛。   --女：干嘛？\n男：这是个心灵魔术，你先夸我帅，我就揭秘你在干嘛。女：你好帅！\n男：你在跟一个才高八斗，貌似潘安的帅哥聊天。\n女：哇，你好自恋哦。\n男：你自己都说我帅喽，我又没猜错。', '开场白惯例');
INSERT INTO `talk_art` VALUES (9894, '开场白惯例', '*男：您的快递到了，请签收。女：啊？我没有快递啊。\n男：有的，单子上写的你收到的是一位集帅气、才华、高智商、温柔于一身的绅士。我还有照片，不信你看。（偷笑）【然后发自拍过去】', '开场白惯例');
INSERT INTO `talk_art` VALUES (9895, '忘词开场，适合移动女孩', '男：你好……（长久看着对方） \n女孩：怎么啦？（惊奇地看着你）  \n男：我看到你气质很好，过来想认识你，本来编好了一个借口，一紧张又忘记了。你叫什么名字？ ', '开场白惯例');
INSERT INTO `talk_art` VALUES (9896, '开场白', '开场白content', '开场白惯例');
INSERT INTO `talk_art` VALUES (9897, '白龙马惯例', '男：一天，白龙马在吃草。唐僧看见了，就对它说：小白啊，我们要爱护这世界的花花草草，你怎么可以这样随意吃掉这些生命呢？就这样，唐僧数落了白龙马一个时辰。这时，白龙马起身跑进一片田地，躺进了一个泥沟。唐僧对沙僧说，小白是个好孩子啊，知错就改。沙僧对师傅说，师傅，小白这是在说我错了\n\r\n女：哈哈哈......', '初聊互动');
INSERT INTO `talk_art` VALUES (9898, '男：现在的小孩儿都学坏', '女：怎么了？----男：下班从超市买完东西出来，有一个两岁左右的小男孩不小心踩了我一脚，他马上奶声奶气的跟我说对不起，我看他挺乖的，就给了他几颗糖。他接过糖看了我一眼，迟疑了一下又踩了我一脚。\n女：哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9899, '*男：其实很多人从小就夸', '女：哦？真的么？\n男：不是有句话么，人丑就要多读书。我妈和我舅舅，亲戚什么的，老说我，一看你就不是读书的料，其实我知道他们就是说我长得帅。\n女：.......', '初聊互动');
INSERT INTO `talk_art` VALUES (9900, '总经理助理惯例', '男：记得上次去面试总经理助理，压力大惨了，各个都是美女。女：那你录用了么？\n男：当然。\n女：你是怎么被录用的呢。\n男：面试官是总经理的夫人，我连自我介绍都没做就直接录用了。\n女：......', '初聊互动');
INSERT INTO `talk_art` VALUES (9901, '*男：我最近在看一本书。', '*女：什么书？\n男：这是一本神奇的书，这本书拯救了我，教会了很多。\n女：什么书，我也要看。\n男：不行，看着本书是有前提的。\n女：为什么？\n男：书名叫《长的太帅如何与人相处》。\n女：自恋。', '初聊互动');
INSERT INTO `talk_art` VALUES (9902, '浪漫诗人惯例', '男：我一个哥们浪漫的像诗人，可是她女友每次都让他冷场。女：怎么会有女人不喜欢男人浪漫的 ，不会吧 ？\n男：那次我哥们发浪漫短信给女友：你在干嘛？在做梦吗？把梦传给我；在笑吗？把笑发过来；在哭吗？把你的眼泪发给我，让我一起悲伤。他女友回复：我在拉屎......\n女：哈哈哈哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9903, '男：今天晚上睡不着觉了', '*女：为啥？----男：去看电影了。\n女：什么电影，看了这么激动。----男：不是，是因为睡够了。\n女：......', '初聊互动');
INSERT INTO `talk_art` VALUES (9904, '男：你知道男人这辈子最', '女：啥呀。\n男：我哥们偶然看到女朋友的手机，发现自己在她的手机里的备注是，饿了就找他。\n女：哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9905, '男：那天朋友聚餐，偶遇', '女：怎么了？\n男：我们吃到了一半，她说她醉了，让我扶她回家。女:那你扶了么......\n男：当然..没有..她骗我，果汁怎么可能醉！当初就是骗我，和她分手的，这次分手了还想骗我......\n女：我好像听懂了什么。', '初聊互动');
INSERT INTO `talk_art` VALUES (9906, '男：这年头骗子太多啦。', '女：怎么了？\n男：刚刚回家，一女的跟我搭讪来着，说我长得像她前男友，我当时就拒绝了她。\n女：为啥呢？万一人家前男友真的和你像呢。\n男：骗人！那么普通的女孩儿，怎么可能跟吴彦祖谈过恋爱。\n女：自恋。', '初聊互动');
INSERT INTO `talk_art` VALUES (9907, '动物世界惯例', '男：刚看了一个电视节目突然感觉好失落   女： 什么节目      \n男：动物世界    \n女：额 ,这情调\n男：动物世界赵忠祥爷爷说，夏天来了，交配的季节又到了，看到最嗨的时候才突然想起自己连女朋友都没有\n女：哈哈   ,你咋个把两个联系到一起的\n男：进化论说过优秀的基因会获得最大的繁殖优先权，我这么优秀的人却还没女朋友，难道说配得上我的女人还没出现么\n女：那你慢慢等吧    \n男：或许吧，等到40岁娶个20岁的小姑娘做媳妇也是不错的活法', '初聊互动');
INSERT INTO `talk_art` VALUES (9908, '男：我抽过 500 块一根的烟', '女：怎么了？\n男：上次到三亚同学那里去玩，太热情了。说拿出三亚最好的烟招待我，超过 500 块一根呢。我抽了几口觉得也没什么两样。直到景区管理员把一张 500 块的罚单递到我手上。\n女：哈哈，真的假的。\n男：故事都是加工制造的，这个可是真的啊。\n女：你可真嘴贫。', '初聊互动');
INSERT INTO `talk_art` VALUES (9909, '男：好饿......几天没吃饭', '*女：怎么啦，减肥呢？----男：不是。\n女：那是什么。\n男：那天放假，饿了。结果找了家兰州拉面，坐下后，半小时还没上，我就饿急了“老板，我的面怎么还没上啊”老板说，“马上啊，我正在拉”......\n女：哈哈哈哈哈......\n男：还笑，后面那碗面我硬是没吃的进去......', '初聊互动');
INSERT INTO `talk_art` VALUES (9910, '男：昨天我见到了世界上', '女：谁啊【流口水】？\n男：我两相视久久，始终没有打破这份平静。\n女：你俩爱上了吧。\n男：没有，最后我放下了镜子，哈哈哈。\n女：自恋狂！！', '初聊互动');
INSERT INTO `talk_art` VALUES (9911, '看书惯例', '男：我准备用一壶茶和一本书度过今天下午，享受一下一个人的安宁女：挺会享受啊   \n男：只是多看了几遍金瓶梅想不到早已腹有诗书气自华\n女：你好能干嘛  \n男： 你赞美人的方式很特别，就喜欢你这种没有丝毫痕迹的马屁', '初聊互动');
INSERT INTO `talk_art` VALUES (9912, '校园时光惯例', '男：说到读书的时候，有好多经典的画面，还在我的脑海里呢。女：是呀，读书的日子总是美好的。\n男：我美好了，人家可不一定咯，我永远忘不了那个表情。\n女：恩，什么啊？说说\n男：就是高中那会儿，我上的是全日制封闭高中嘛，住校呢。有一次大半夜被冷醒了，发现被子不见了。由于当时睡得迷迷糊糊的，所以错把下铺朋友的被子当成自己掉下去的被子，然后顺着楼梯爬下来抓住他的被子就一个劲的往上拉，拉着拉着就感觉好像拉不动了。但是我当时还没回过神来没去看，直到僵持了几十秒，人清醒点了才往被子那头看情况，尼玛，我永远也忘不了我下铺那兄弟双手死死的抓着被子一脸惊恐加无辜的看着我的表情。\n女：哈哈哈哈......', '初聊互动');
INSERT INTO `talk_art` VALUES (9913, '男：我从小就很机智', '女：切，没看出来 。\n男：我记得有次小学期末考试，还有一分钟就交卷了，我突然发现自己名字没写，但当时有点激动竟然忘了自己叫啥，我灵机一动，就给我同桌一个耳光，我同桌大叫：老师***他打我！然后我写上了我的名字！\n女：哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9914, '男:不聊啦，我急着要去做', '女:啥事儿这么要紧。\n男:我要去拉个粑粑疏通疏通经络，管道通畅方能益寿延年。\n女：你真是......', '初聊互动');
INSERT INTO `talk_art` VALUES (9915, '话题荒谬幽默性：', '男：我准备去健身房锻炼我的八块腹肌了    \n女：现在几块   \n男：很圆的一块   , 哈哈\n女：那你慢慢练\n男：刚出门照了下精子真实帅到惨绝人寰，唉声遍野，寸草不生\n女：好自恋  \n男： 如果自恋能当饭吃，我能养活半个中国\n女：没见过你这样夸自己的  \n男： 那我现实点，等以后克隆技术发达了，我克隆个自己，然后做个变性娶了她', '初聊互动');
INSERT INTO `talk_art` VALUES (9916, '体育老师惯例', '男：记得上学那会儿遇到了一个数学很好的体育老师。女：是么，怎么好了？\n男：那次他让我们跑步跑五圈，我一口气跑了七圈，然后对老师说：老师，我多跑了两圈。下次我是不是只要跑三圈？ 你知道我那老师说啥，差点给我气死。\n女：说什么啊？\n男：他说，我不喜欢欠别人的，这样吧，你倒着跑两圈算是还给我的。\n女：哈哈哈哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9917, '男：现在的饭店越来越坑', '女：哦？说说看.\n男：那天和朋友吃海鲜，我看到上来的龙虾就问， 服务员！为什么这龙虾是没有钳子的？”服务员竟然说“先生，可能它是在我们饭店的其他龙虾打架打输了，手打断了。”哼，我秒回“那麻烦把那个打赢的给我端上来，谢谢！ ”\n女：哈哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9918, '男：刚刚被人骂了，为什', '*女：骂你什么了。\n男：你长得帅了不起啊。\n女：.....', '初聊互动');
INSERT INTO `talk_art` VALUES (9919, '男：你做什么的？', '女：在西装店做店长\n男：说到这个，上次我花了5千做了套西服，不会被坑了吧，我朋友从其他店做才两千多\n女：这个是要看料子的    \n男：料子啊，软软的那种，摸一下就立马想躺在这个男人怀里的男神装。', '初聊互动');
INSERT INTO `talk_art` VALUES (9920, '男：我至今还记得我找第', '*女：是么，说说看 。\n男：我面试的时候把自己说的很厉害。----女：然后呢？\n男：我就被录取了，他们说就需要我这种不要脸的年轻人。\n女：哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9921, '现场使用', '男：最近在看一部非常精彩的美剧。\n女：什么啦？----男：但是不能给你看，因为我怕你会自卑。\n女：怎么会自卑呢？我很自信的好吗。\n男：是么（然后不说话，把目光转向妹子的胸）准备接受女人的粉拳吧。', '初聊互动');
INSERT INTO `talk_art` VALUES (9922, '男：真郁闷我手机不小心', '女：额，给出租车公司打个电话。\n男：可幸运的是，司机师傅竟然解开锁，打给我朋友通知我拿回手机。\n女：啊？那司机这么厉害啊？\n男：恩呢，我问司机：“我是人脸识别解锁，你怎么打开的？”司机说：“我对着吴彦祖的海报一下就开了”\n女：喷了，你这夸自己帅也是没谁了', '初聊互动');
INSERT INTO `talk_art` VALUES (9923, '男：我小时候有一个梦想', '女：什么梦想？\n男：我要拯救世界，结果我错了。\n女：怎么错了?\n男：长大了我才发现是整个世界都拯救不了我.\n女：哈哈哈.', '初聊互动');
INSERT INTO `talk_art` VALUES (9924, '男：现在的小孩都好重口', '*女：怎么了？----男：今天听到一个八岁的小姑娘唱，两只老虎，两只老虎，谈恋爱，谈恋爱。两只都是公的，两只都是公的，真变态，真变态。\n女：哈哈哈。', '初聊互动');
INSERT INTO `talk_art` VALUES (9925, '\\', '女：会计----男：专业管钱这个职业是个当老婆的好职业\n女：哈哈是嘛    \n男：不过我想要很多孩子，那要找一个会照顾孩子的女人估计只有幼师了', '初聊互动');
INSERT INTO `talk_art` VALUES (9926, '男：今天差点被气死。', '*女：怎么了？----男：中午朋友过来，准备做个拿手的可乐鸡翅，于是去菜市场买鸡翅，买回来之后才想起来，可乐忘记买了！然后回去又买可乐。当可乐买回来之后发现特么我鸡翅呢？！拎丢了！！！这不是高潮，可乐放家里，再回去买鸡翅。当我再次回来的时候，家里这几个不要脸的又把劳资可乐喝光了！！！艹！不做了！！！吃你大爷！！！\n女：哈哈哈，损友太多......', '初聊互动');
INSERT INTO `talk_art` VALUES (9927, '男：我发现我有时候挺想', '女：为啥？----男：每次去 ATM 取一百元的时候，点钞的声音好像要给我几万一样。\n女：哈哈哈，我也发现了', '初聊互动');
INSERT INTO `talk_art` VALUES (9928, '男：有一次上英语课，我', '女：你怎么了？\n男：老师问：“知识是什么？”全班人回答：“knowledge ！”就我一个特么特大声的说：“力量！！！”\n女：哈哈哈，好尴尬\n男：当时看老师的眼神，我默默的低下头，已经有了抄一本子单词的觉悟', '初聊互动');
INSERT INTO `talk_art` VALUES (9929, '孕妇让座惯例', '男：我在公交上站起身来给一孕妇让座，她疑惑地看了看我，忽然明白过来，哭笑不得道：同学，我这是胖！”\n女：哈哈哈', '初聊互动');
INSERT INTO `talk_art` VALUES (9930, '男：在看中国好声音', '女：哦。----男：每次看好声音，当我看到四位导师听学员唱歌的时候，感觉他们的表情，好像是在蹲坑，然后便秘了，然后一拍通畅了！\n女：好恶心，我在吃东西。', '初聊互动');
