INSERT INTO `talk_art` VALUES (9963, '转移话题2', '女：你能帮我查点资料吗    \n男：好呀   ,但是我只为淑女提供服务\n女：好吧  ,我就是     \n男：淑女一般很可爱 , 请用撒娇卖萌的方式验证你的淑女身份\n女：额 ,我不会也，咋个弄  \n男： 温柔地说句，欧巴--莎郎嘿哟--\n女：欧巴--莎郎嘿哟--    \n男： 嗯，看在你这么诚心的份上，我就帮你查下吧', '打情骂俏');
INSERT INTO `talk_art` VALUES (9964, '男：你知道你为什么是个', '女：爸妈生的呗。\n男：no no no。\n女：那是什么？\n男：是因为上辈子，你屌爆了，哈哈哈。\n女：......', '打情骂俏');
INSERT INTO `talk_art` VALUES (9965, '女：你觉得我漂亮么？', '男：漂亮，跟我差不多。\n女：你就不能直接点。\n男：好的，漂亮，像个男的。\n女：你妹。', '打情骂俏');
