INSERT INTO `talk_art` VALUES (10427, '我又想到一个', '刚认识个女孩，她让我把工资卡交给她，何解？1先告诉我你要往里面存多少钱我再考虑要不要给你\n2封建社会早过去了，现在不流行买老婆了吧\n3我的工资卡材质太硬，家里门锁打不开的话，身份证的软度更好开呢', '搭讪话术');
INSERT INTO `talk_art` VALUES (10428, '她：就你这身材（我很瘦', '1我不嫌你比我胖\n2你不用嫉妒，明天把减肥的方子告诉你就是了\n3试过才能知道，没试过没有发言权，但哥哥我不想给你试', '搭讪话术');
INSERT INTO `talk_art` VALUES (10429, '你不要太可爱了', '1不会啦，在可爱这一点上你永远是我努力学习的目标\n2你怕忍不住爱上我吗\n3换个凶神恶煞的表情：这样会不会可憎点', '搭讪话术');
INSERT INTO `talk_art` VALUES (10430, '一直把你当弟弟', '1我也有个小弟弟哦，改天在合适的时间拿给你看，我保证你们会玩的很开心\n2那怎么行，我一直把你当奶奶呢，我又想吃奶了，少奶奶的奶奶好大\n3我不会嫌你老的（非原创）\n其他答案：你是女的 还想有弟弟？（重楼 ）', '搭讪话术');
INSERT INTO `talk_art` VALUES (10431, '你有多少个女人？', '1肯定没有陈冠希多\n2一个也没有，我从来不收集 AV 的，每次看完就删了\n3我记不起以前到底有多少，但能肯定以后就你一个', '搭讪话术');
INSERT INTO `talk_art` VALUES (10432, '我：为什么我跟你在一块', '她：因为我们是关系超好的朋友，关系不好的话就算是礼貌也要理一下的。我：。。。。1忘了告诉你我的小名就叫狗不理\n2什么时候跟你关系好了，请不要自来熟好吗\n3嘴都没亲过，（或者更亲密的要求）关系哪里超过好朋友啦', '搭讪话术');
INSERT INTO `talk_art` VALUES (10433, '为啥要跟你，你又没钱?', '1我给你介绍个有钱的，你可以试着跟银行里那个自动取款机谈谈恋爱\n2你跟我走，去菜市场把你卖了我就有钱了\n3一晚多少钱，你开个价，我跟 XX 宾馆的比下哪边划算', '搭讪话术');
INSERT INTO `talk_art` VALUES (10434, '对 我就是外貌协会的', '1听说做外贸做鞋子很赚钱哦，富婆，请我吃饭吧\n2我是外貌协会的内涵部的，我要通知你现在正式被协会开除，因为你内涵严重不及格\n3我就是为这个事来找你的，这位会员，你打算拖欠会费到什么时候？', '搭讪话术');
INSERT INTO `talk_art` VALUES (10435, '你怎么今天这么亢奋 受什', '1因为你啊，连公狗看到发情的小母狗都会亢奋哦\n2今天起床后吃了感冒药，吃完感觉好像哪里出错了，一看瓶子上写的竟是----万艾可\n3完全是因为你在我身边我才如此兴奋，（捂住心口做痛苦状）：啊。。说违心的话好难受', '搭讪话术');
INSERT INTO `talk_art` VALUES (10436, '我已经有男朋友了，关系', '1那你介意换一个吗？介意。（非原创）介意多一个吗？介意。（非原创）其实我不介意多一个女朋友\n2我不是同性恋也不是狗仔队，不用告诉我你男朋友的事或你们的八卦哦\n3我是最优女朋友调查联合协会的，你真是好女朋友的典范呢，请无论如何留下联系方式，我们协会将联系你领取奖品的', '搭讪话术');
INSERT INTO `talk_art` VALUES (10437, '你一天到晚净想些什么东', '1想东西？那。。。你算东西吗？\n2苍井空 松岛枫 樱井莉亚 ，不懂可以百度\n3在想你究竟穿什么颜色的小裤裤呢', '搭讪话术');
INSERT INTO `talk_art` VALUES (10438, '没看见我旁边站着一个帅', '1那不打扰你做“生意”了，今天赚到这一单，明天请我吃饭哦\n2虽然我确实够帅，但你也不应该对人家这么直接，会伤害他的\n3我就是想跟你说，你能不能别缠再着我男朋友了（重口味）', '搭讪话术');
INSERT INTO `talk_art` VALUES (10439, '暑假要回家陪男朋友 哪有', '1男朋友？！我还以为你结婚了呢（非原创）\n2那太好了，这么无聊的事我可以去陪你解闷，对了，他会请我们俩吃饭的吧？\n3你这么一说。。。我很惭愧，都好久没回家陪我的狗了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10440, '她带隐形眼睛很久了，我', '1我在帮你回忆刚买隐形眼镜时候的状态呢，现在想起来没？\n2早知道任何人跟你在一起呆久了都会被传染笨蛋的病毒，只是没想到我也不能免疫\n3带着这幅隐形眼镜看你漂亮了很多呢，我的眼睛都舍不得让我把眼镜摘下来了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10441, '你脑子有问题', '1哇你好厉害啊！你是脑科医生哦？\n2你有药吗？（感谢郭德纲）\n3恩，这个问题已经困扰我好久了，现在我要问出来：你可以请我吃饭吗', '搭讪话术');
INSERT INTO `talk_art` VALUES (10442, '不去 下午要出去逛街', '1去逛街就难免，去我那边实际是变相在帮你省钱哦\n2你在街上是没有回头率的，但如果在我身边的话结果就完全不同了，我可以委屈下帮你这个忙的\n3你要去街上搭讪帅哥吗？那会吓到人家啦', '搭讪话术');
INSERT INTO `talk_art` VALUES (10443, '你有什么特长或优点', '1我有一个地方特长\n2我没什么优点，但我能找到你的 G 点（慎用）\n3能与你这么笨的人正常交流是我的优点和特长', '搭讪话术');
INSERT INTO `talk_art` VALUES (10444, '我跟你在一块怎么总这么', '1你知道吗，我其实是你的守护神，要是我不在，你可能早就在车下面了\n2打车是个技术活，赤手空拳打上去会很疼的，下次我可以在边上帮你拿锤子或砖头\n3你说出了我的心里话，你真了解我', '搭讪话术');
INSERT INTO `talk_art` VALUES (10445, '我经常遇到这样的 无论你', '1拍一下手背，学单田芳的腔调：欲知后事如何，且请我去吃饭！\n2你果然是火星人，快说，你来地球有什么阴谋！\n3恩，完了。演出费是 500 元，请问是要付现金还是刷卡呢', '搭讪话术');
INSERT INTO `talk_art` VALUES (10446, '你谈过几次恋爱啦？', '1先等我算下（自言自语数完十个手指，十个脚趾，再对她说）可以把手借我数一下吗\n2要算上你吗？\n3不多，80 来次吧', '搭讪话术');
INSERT INTO `talk_art` VALUES (10447, '不去 天太热了', '1夏天多排汗有益身体，尽管你的身体再怎么排也救不回多少了。。。\n2把衣服脱掉会凉快很多，而且我也不介意你脱光了走在我身边\n3你不知道有种机器叫空调吗？', '搭讪话术');
INSERT INTO `talk_art` VALUES (10448, '你太胖了', '1我是在帮你省买枕头的钱\n2希望你不要为了我而去增肥\n3哪里会胖，我尿尿都还看得见自己小鸡鸡呢', '搭讪话术');
INSERT INTO `talk_art` VALUES (10449, '电话里(我们住处相差 20', '我：我过去。她：你别过来。我：。。。。但是如果我再缠两下她就会让我过去或者直接过去她也不会说什么，只是总感觉我被框架了1你猜我到你家需要多久时间\n目标：XX 时间吧不对，我们打个赌吧，赌一顿饭，我现在就过去，你计时吧\n2直接到她家：我刚听到消防车往你家方向开过来了，担心你所以过来看看，看到你没事真好\n3我：我过去\n她：你别过来\n我：书上说漂亮女人说话要反着理解，我想我明白你的意思了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10450, '我调侃她', '对方回应：“切”不破坏舒适感的前提下 如何应对才好呢1切掉的话。。你就没得玩了\n2想吃西瓜了吗？自己去买\n2 捂住肚子做痛苦状：好痛好痛。。目标：怎么了？你：你要带我去看医生。。你的气场太强，只说一个字剑气就真的切到我了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10451, '麻烦你，不要与我联系了', '我手机上已经没有与你联系的任何东西，找不到号码，才给你 qq 消息的…懂麽。\n1这是你找帅哥要电话号码的一种方式吗？\n2手机号删了没关系，QQ 号删掉也没关系，我只要呆在你心里就很满足了\n3想了半天还是不懂，找不到号干嘛要用 QQ 通知我呢？', '搭讪话术');
INSERT INTO `talk_art` VALUES (10452, '我们不互补..', '1你错了，我的长处完全可以填补你的漏洞\n2看看她的衣服，再看看自己的：衣服都没破，补哪里？\n3那明天我送你一斤燕窝，你送我一斤人参不就解决了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10453, '我没零钱给你', '1那太好了，我正好只收整钱\n2到便利店买个套套或卫生巾就找得开了\n3你今天身上居然带了钱！？太阳打西边出来了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10454, '她说：“反正你又不来。', '1难怪最近肚子经常会疼。原来肚子里有蛔虫了，你还敢跑出来，看你往哪跑\n2那得看你想不想我来，你要是想得一塌糊涂，我就勉为其难考虑一下\n3那我来了你请我吃饭吗？', '搭讪话术');
INSERT INTO `talk_art` VALUES (10455, '叫你大哥', '1别乱叫！大哥要多判三年的\n2你又不是林黛玉，我也不是贾宝玉，乱叫什么哥哥\n3明天我去买柱香，买点黄纸，你去买只鸡，咱挑个好时辰拜把子', '搭讪话术');
INSERT INTO `talk_art` VALUES (10456, '你为什么这么自信', '1因为站在我身边的是你\n2因为我刚看完卡耐基的《你一定会成为大富翁》\n3因为我是（这个慎用）\n其他答案：交学费 我就教你（重楼）', '搭讪话术');
INSERT INTO `talk_art` VALUES (10457, '我说：我给你安全感，妞', '1安全套才多少钱一个，我会买不起吗？\n2叫她开蓝牙，将王力宏的《安全感》用手机发给她\n3虽然你够重，但我去健身房练练应该勉强给得起', '搭讪话术');
INSERT INTO `talk_art` VALUES (10458, '别走，过路费！', '（先确定不是政府部门的女人在执行公务吧。。不过如果你上面有人的话可以回答下面的话）1这是你搭讪帅哥的标准开场吗（非原创）\n2都什么年代了还能碰上山贼？请你先别动我给你拍张照留念一下\n3过什么路费！老子爸爸叫李刚！', '搭讪话术');
INSERT INTO `talk_art` VALUES (10459, '最近没空，忙。。。', '1最近很流行装忙呢，明明没屁事却装得日理万机似的，哦哦，我不是说你，咱们不是说到这个话题了么，你肯定是真忙\n2谢谢你在百忙中能腾出时间来跟我说这句话（发这条信息），为了报恩我决定请你吃饭，你定时间吧\n1加油哦！为了我们的未来，你要多赚点钱', '搭讪话术');
INSERT INTO `talk_art` VALUES (10460, '你怎么这么不现实啊', '1因为我不想变得和你一样\n2变现实就不会找你了啊，我还想继续喜欢你，所以要保持不能变现实\n3送你一句书上的话，现实的女孩子很难嫁出去的', '搭讪话术');
INSERT INTO `talk_art` VALUES (10461, '你为什么跟以前的女朋友', '1你为什么跟以前男朋友分手？\n2我受不了她老问我为什么跟以前女朋友分手\n3呀呀呀。。屁股好躁动，他要说话了:我关你什么事（交流你的回答哦） \n其他答案：为了她，为了我，也为了你\n通过这种方法避免被我讨厌是作弊的行为（十丨三 ）', '搭讪话术');
INSERT INTO `talk_art` VALUES (10462, '求教，总是有女人说我骚', '1真的有这个潜质么，那我要去学钢管舞，以后要是红了给你签名\n2闻自己：不对啊，刚洗了澡出来的呢，不信你闻\n3所以说我跟你不一样嘛，我是骚在明处，级别比较高的像你就骚得很内在了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10463, '我不喜欢别人抱我', '1前提是要别人抱的动你\n2我认为别人也是这么想的\n3那你喜欢什么体位？\n其他答案：这里没有别人，只有咱两，不过你放心，我敬重你是条汉子，不会抱你的.', '搭讪话术');
INSERT INTO `talk_art` VALUES (10464, '你一点都不成熟', '1恩，我喜欢吃 7 分熟的，太熟咬不动\n2我要成熟起来就跟你没共同语言了小妹妹\n3这。。。你怎么知道。。。我还是处男的', '搭讪话术');
INSERT INTO `talk_art` VALUES (10465, '你有点嫩。跟你在一起没', '1什么嫩不嫩的，你当我是牛排吗\n2你可以为我们买个保险就有安全感了，我觉得人寿有一款不错哦\n3要安全戴套就可以了,我喜欢杜蕾斯巧克力味的', '搭讪话术');
INSERT INTO `talk_art` VALUES (10466, '你是笨蛋', '1咦？你怎么知道我小名（非原创）\n2不把自己搞得跟笨蛋一样怎么跟你有共同语言\n3我还好了（害羞状）华盛顿小时候就被人这么称赞过的，谢谢你的夸奖\n其他答案：自从我穿上裤子后，很少有人认出我是蛋类了，姑娘，好眼力.', '搭讪话术');
INSERT INTO `talk_art` VALUES (10467, '你的这些招数对小女生有', '1我知道你们男人老想着那些事。我只会和自己的男朋友 SC\n2这个。。。其实我是想跟你借钱。。。\n3大姐，我不会嫌你经验丰富的\n4我脸红了，跟你聊两句，你直接就说到 SC 了啊。。。\n其他答案：经验丰富？你有很多男朋友？', '搭讪话术');
INSERT INTO `talk_art` VALUES (10468, '你哪点帅 （在你骄傲风趣', '1你回去拿个显微镜来再仔细找找一定能发现\n2这里人多不方便，去我家我给你看\n3我带你去看眼科医生吧', '搭讪话术');
INSERT INTO `talk_art` VALUES (10469, '我是见异思迁那种人', '1我理解，一般有男朋友的女孩子见到我都会见异思迁的\n2我是见义勇为那种人，我们的个性有两个字一样哦，好有缘啊\n3这个性格适合我，以后跟你在一起腻了又不想伤害你的话，介绍个帅哥给你就可以了', '搭讪话术');
INSERT INTO `talk_art` VALUES (10470, '没有男人气概', '1你先让自己有女人味再来对我提要求吧\n2但我有男人气味，你闻\n3早说你喜欢男人气概啊，来！给老子按摩！', '搭讪话术');
INSERT INTO `talk_art` VALUES (10471, '你对我这么好干嘛？（追', '1因为你的眼睛很像我以前的一个朋友，她在我 9 岁那年离开了我。。她是一只吉娃娃\n2你想我干嘛？我们还没熟到那个程度，你可什么都别想！\n3我从小就喜欢打抱不平，别人（她男友）虐待动物这种事我看不下去！\n其他答案：我对女生都这么好 如果你真感谢我 请我吃顿饭好了 我保证不客气\n我想用对你的好，来弥补你内心的不安，特别是配不上我的感觉~ 不过你自己也要加油哦！', '搭讪话术');
INSERT INTO `talk_art` VALUES (10472, '。。。。。。。', '1你是专门研究标点符号的吗2沉默是金哦，你经常这样一定很有钱\n3这是在练习我的心灵感应能力吗', '搭讪话术');
INSERT INTO `talk_art` VALUES (10473, '你住哪，你做啥工作，你', '1你有成为户籍调查员的潜质呢\n2请问这是在相亲吗\n3花果山，保镖，孙悟空', '搭讪话术');
INSERT INTO `talk_art` VALUES (10474, '分手 做朋友好吗', '1叫她把手拿过来给你，再丢开说：分完了\n2分手是谁，对他不了解，不知道适不适合做朋友\n3你不跟我结婚了吗？我在加拿大有个有钱的表叔刚过世，留了好大一笔遗产给我哦', '搭讪话术');
INSERT INTO `talk_art` VALUES (10475, '你这也生气', '1没有，哪里有生气，我现在是愤怒！\n2对哦，跟你在一起呆久了我的素质也变差了呢\n3吴三桂冲冠一怒为红颜，我在效仿他，你虽然不是红颜也凑合了', '搭讪话术');
