INSERT INTO `talk_art` VALUES (10643, '想和你好好的聊一聊~~谈一', '1那我去买本聊斋给你看看\n2我好激动。。。这。。。这就是传说中表白的开场白吗\n3是谈恋爱的谈吗？', '价值型聊天');
INSERT INTO `talk_art` VALUES (10644, '女：我觉得我配不上你', '1又不是动物配种，有什么配不配得上的\n2我打赌配得上，要不要试试看，这里不方便，去我家吧\n3女人的直觉往往都是错的', '价值型聊天');
INSERT INTO `talk_art` VALUES (10645, '你自大中看出你有些自卑', '1早叫你少看点星座占卜算命这类的书，你看，着魔了吧\n2你自卑中会养成自渎的习惯\n3观察很仔细哦，你这句话术语称为冷读，我也在学习中，我们一起加油吧', '价值型聊天');
INSERT INTO `talk_art` VALUES (10646, '你好笨', '1恩，我的爱好就是喜欢笨笨的那种类型，你完全达标了\n2确实笨的可以，不然怎么会跟你聊天呢\n3算命的说傻人有傻福，但一看到你我就知道这肯定是个大富大贵的命', '价值型聊天');
INSERT INTO `talk_art` VALUES (10647, '被语言小小调戏之后..她说', '1你打算跟我来场肉搏么？\n2有种来试试啊，我可是跆拳道负五段哦！\n3学李小龙拿拇指刮鼻子后指着目标：不~要~逼~我！中国人——不是病夫！！（食指立起左右摆）', '价值型聊天');
INSERT INTO `talk_art` VALUES (10648, '\\', '1实际上我是院里特派出来抓你回去的，你得的是身份互换神经系统症候群\n2你这人太现实了，人走茶凉！亏了以前我们还是一个病房的呢\n3小声点，你不叫没人知道你是神经病的', '价值型聊天');
INSERT INTO `talk_art` VALUES (10649, '公仔可以给我虐待，可以', '1我可以把你丢来丢去\n2怎么虐待法？你想玩被滴蜡还是被皮鞭抽呢？我都可以满足你\n3早说你好这一口，我就送你充气娃娃了', '价值型聊天');
INSERT INTO `talk_art` VALUES (10650, '你有点自卑', '1其实我是非常自卑，而且应该谁跟你站一起都会觉得丢脸到自卑的程度好吧\n2自己能背自己？我不信，你给我演一个看看\n3（自己看自己身上）哪点？你给说说？', '价值型聊天');
INSERT INTO `talk_art` VALUES (10651, '你管我呢。', '1我管你干嘛？我长得像城管吗？\n2只听过管饭，管住，但这个管呢是什么意思？\n3我从小就想做动物园长，管那些动物们，你让我过过瘾呗', '价值型聊天');
INSERT INTO `talk_art` VALUES (10652, '你怎么那么二', '1你怎么那么二十三（2B）\n2因为老二确实很大\n3你以为大家都像你一样喜欢小三吗', '价值型聊天');
INSERT INTO `talk_art` VALUES (10653, '妹子 特喜欢看帅哥。 还要', '1我为那个帅哥祈祷，阿弥陀佛\n2我就不去了，你顺便帮我谢谢他，终于可以清净一下下了\n3加油，泡到了骗点钱来给我花哦妹子：', '价值型聊天');
INSERT INTO `talk_art` VALUES (10654, '妹子：“爱妃，劝你不要', '1朕已经玩腻角色互换的游戏了，现在玩你扮演朕坐骑的游戏\n2这位失心疯的公公，请你不要老是幻想自己是朕好吗？\n3皇上，咱们赶紧进入临幸的环节吧', '价值型聊天');
INSERT INTO `talk_art` VALUES (10655, '你有点自大', '1没错，我尺寸这么大是自己长的，没有用过任何药物哦\n2你有点奶小\n3你看我长得像夜郎吗？', '价值型聊天');
INSERT INTO `talk_art` VALUES (10656, '一女经常跟我唱反调，&', '我叫她去干嘛，她就说 就不去 怎么样。我说她神经病或者是想打架，她还是说就是神经病/想打架，怎么样？怎样破解这蛋疼句式？\n1（直接冲上去挠她痒痒：就这样 ）\n跟复读机交流真是件蛋疼的事\n2你是过来地球实习的外星人吧，还停留在拟声期哦\n3明天带你去医院看看吧，可能是语言神经出了问题。。。', '价值型聊天');
INSERT INTO `talk_art` VALUES (10657, '前和 妹子TD，做完之后', '我问她 高潮之类的问题， 我记得很清楚，她说了一句：我看见帅哥就高潮了。我很生气，这个要怎么破呢。\n1那你平时出门要带几条裤子换呢？\n2多几个你这样的可就解决了中国很多地方用水难的问题了\n3我觉得对你来说看见公狗也能高潮。。。。', '价值型聊天');
INSERT INTO `talk_art` VALUES (10658, '今天又有人和我表白了 可', '1谁运气这么不好，被你喜欢？我要提醒他去烧香\n2这年头怎么没品位的人这么多呢？\n3对方人品，学历，家庭，财富，样貌，身体怎样？可以免费帮你参考参考', '价值型聊天');
INSERT INTO `talk_art` VALUES (10659, '我是真的觉得你满好的', ',可我不想破坏我们之间的关系.做朋友不是满好的嘛,你不会说你不想要朋友吧? (和 妹子 谈到性话题以后的场景)\n1你总是这么心口不一吗？\n2发生关系是稳固关系的方法，不发生关系才会破坏关系\n3你这毒妇，想让我困在一个叫友情区的地方永不翻身吗？（慎用）', '价值型聊天');
INSERT INTO `talk_art` VALUES (10660, '女：我怕我忍不住抽你', '男：那你白白的屁屁上面，一定会留下我五指的血印', '价值型聊天');
INSERT INTO `talk_art` VALUES (10661, '女：都是走肾，一点都不', '男：走肾和走心都不过是个过程，感觉好了，走肾只不过是日后细水长流的基础', '价值型聊天');
INSERT INTO `talk_art` VALUES (10662, '女：能不能不这么拽', '男：不拽，你会喜欢？喝最烈的酒，操最爱的人，那是人生的最基本保证', '价值型聊天');
INSERT INTO `talk_art` VALUES (10663, '女：大爷是个浪子吧', '男：明明是柔情似水，温柔善良的美男子，被你诋毁成这样', '价值型聊天');
INSERT INTO `talk_art` VALUES (10664, '女：少年，别这么自我', '男：以前他们叫我少爷，现在改了，叫大爷', '价值型聊天');
INSERT INTO `talk_art` VALUES (10665, '女：我害怕', '     男：怕个啥 ，有我在，给你个小肩膀，赐予力量', '价值型聊天');
INSERT INTO `talk_art` VALUES (10666, '女：为什么和我开房', '  男：抱着你的裸体，我的灵魂才会睡的更加坦然，春风十里不如睡你（骚聊）', '价值型聊天');
INSERT INTO `talk_art` VALUES (10667, '女：发呆的表情', '     男：你再发愣 ，捏脸了哦（等女生回后说）乖啦 ，捏起来挺有手感', '价值型聊天');
INSERT INTO `talk_art` VALUES (10668, '女：叫老婆就要养我喽', '男：养分很多种，说，是包养，散养，还是放养', '价值型聊天');
INSERT INTO `talk_art` VALUES (10669, '女：你脾气应该很大', '男：我更喜欢那种吵完架后，依旧可以抱在怀里说说笑笑的温馨 脾气大的人有标准，只有不是吹毛求疵，都挺好', '价值型聊天');
INSERT INTO `talk_art` VALUES (10670, '你别做梦了', '1请问你都是在不睡觉的情况下做梦的吗？\n2看到你我确实不敢做梦了，会吓醒的\n3梦是亲密睡着后才做的事，睡着前你说我们该做点什么？', '情感链接');
INSERT INTO `talk_art` VALUES (10671, '有一朋友对我和 妹子', '说：如果不知情的话，别人还真以为你们是情侣。妹子 有男友的。我刚想答：别这样说，她会很高兴的。但 妹子 快了我一步说：他啊，不可能，像个小孩。1\n回 妹子：没见过你这么着急的，人家只是说像情侣而已，要跟我生小孩得等结婚后好不好\n2回朋友：兄弟你今天忘戴隐形眼镜了吧，要是你有戴，那就是我没戴\n3回两人：我不喜欢养宠物', '情感链接');
INSERT INTO `talk_art` VALUES (10672, '你越这样我越觉得不可能', '1本着物极必反的道理，你越觉得不可能我越这样就行了\n2这样不可能我就那样吧\n3你肯定没去李宁买过东西，他们的口号是-----------一切皆有可能', '情感链接');
INSERT INTO `talk_art` VALUES (10673, '你怎么到现在还没结婚？', '1算命大师说不让结，还说只有等那个问我“你怎么到现在还没结婚的女孩子出现”，才可以考虑一下\n2我从小心地善良，由于担心你嫁不出去，所以我不忍心断掉你后路\n3看得上的富婆实在太少', '情感链接');
INSERT INTO `talk_art` VALUES (10674, '你欺负我！', '1我可以给你机会还回来，我们是在这里，还是去酒店？\n2人善被人欺，但这并不能证明你的善良，要证明的话你得让我骑才行\n3为了表达我的歉意，我可以借电话给你打 110', '情感链接');
INSERT INTO `talk_art` VALUES (10675, '我为什么要和你分享', '1我不想将来咱们孩子的童年不完整\n2这样不太好吧。。你想全部给我吗？\n3因为我不想别人讲我未来老婆是一个自私自利的人', '情感链接');
INSERT INTO `talk_art` VALUES (10676, '你是潜力股吗？', '1我是翘屁股\n2得先搞清楚你是不是垃圾股才好回答你，你看不出我是绩优股吗？\n3我不喜欢你用这么晦气的称呼来比喻我，要知道不管什么股最后的结果都是被人炒的', '情感链接');
INSERT INTO `talk_art` VALUES (10677, '我感觉见你很面熟。或，', '1不要以为装不认识之前欠我的钱就可以赖掉\n2你肯定误会成某位明星了，虽然我确实明星脸，但还是不喜欢别人将我们混为一谈\n3前世，我们是一家，我放牛，你织布，后来被你妈用一条河拆散了。。娘子，我们终于又见面了', '情感链接');
INSERT INTO `talk_art` VALUES (10678, '最近有个女的，每次玩角', '说她是我女朋友时，她都会回复我一句：死去。但每次她说完死去之后，就会很开心的跟我玩角色扮演\n只不过玩完之后又要来一句：死去！\n昨天白天我正烦着呢，她突然给我发信息，然后损我\n我当时想都没想的就回了句：滚\n到了晚上我给她打电话，她不接，然后\n我发信息，就发了这么几个字：你好烦呀！她回：我更烦，等你意识到错误再来找我吧。\n那么，这个废物测试怎么回？\n人家跟你打情骂俏，你直接一句滚，很焚琴煮鹤的行为啊\n1别烦了，每个月总有那么几天的,喝点热开水会好些哦\n2我意识到了，你的错误在总叫我死去\n3在没意识到之前只能等你来找我吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10679, '我为什么要听你的？', '1不听我的就把你拴起来，不喂你肉骨头，饿你几天\n2你猜在精神病院里如果你对医生这么说的话，会得到几次电击？\n3父母之命，媒妁之言，不懂吗？你这个不孝的家伙', '情感链接');
INSERT INTO `talk_art` VALUES (10680, '你以为你是谁啊', '1我就是我啊，你经常搞不清楚自己是谁吗？\n2我以为我是陈冠希，你是张柏芝\n3唱：其实你爱我像谁，扮演什么角色我都会，快不快乐我无所谓，为了你开心我忘记了累不累', '情感链接');
INSERT INTO `talk_art` VALUES (10681, '女孩空间写很多诗，夸她', '1过分的谦虚其实是骄傲的表现，你应该承认并回赠我：谢谢你的夸奖，我想请你吃饭\n2其实不用这么较真，我也就是随便礼貌一下而已\n3那就更棒了，女子无才便是德啊，你一定是个品德高尚的人\n其他答案：其实我也是诗人,然后百度-藏头诗生成器-输入她名字-ok（qq123）然后说：难得今天我心情好撒谎夸你两句，你可要多珍惜，我不是经常这样虚伪的 (一直很想爱你)', '情感链接');
INSERT INTO `talk_art` VALUES (10682, '第二次单独见面，她问你', '1你的思维很富有跳跃性，看来我得去买只青蛙装口袋里才能跟得上你\n2我知道你对我确实很感兴趣，但能不能等我一个一个的回答\n3还好幸运五十二没请你去当主持', '情感链接');
INSERT INTO `talk_art` VALUES (10683, '妹子 问：你叫什么名字？', '1问帅哥姓名之前先报上自己的名字比较有礼貌呢\n2我告诉你名字，你不会拿出银角大王的紫金红葫芦“咻”的一声把我收了带回去吧？\n3告诉你之前我要先确定你有没有一个叫做死亡笔记的本子', '情感链接');
INSERT INTO `talk_art` VALUES (10684, '君子动口不动手。你怎么', '1你见过买猪肉不先捏捏肥瘦，就直接当着肉贩子面一口咬上去的？\n2你想我从哪下口？\n3关关雎鸠，在河之洲，窈窕淑女，君子好逑，但。。。。首先你得是淑女我才能做君子', '情感链接');
INSERT INTO `talk_art` VALUES (10685, '第一次见面，她朋友讲了', '1我怕打扰到你朋友的演讲\n2我在观察研究到底怎样才能将笑话讲得不那么好笑\n3不是不爱说，是没时间说，整晚都在忙着擦飞到脸上的唾沫星子', '情感链接');
INSERT INTO `talk_art` VALUES (10686, '如果我是你的话，我会自', '1真懂得体贴人。。。你要替我去买套套吗？\n2以你的体重，不自己走难道你还想我抱着你走？\n3不用自卑，你其实也还可以的', '情感链接');
INSERT INTO `talk_art` VALUES (10687, '1妹子 说：我是属于那种又', '我知道这就话是测试，但是 妹子 只有 5-6分。我是个 afc，是长期发展的\n1据史册记载当年伯乐相中的那匹千里马也是这个样子的\n2丑一点有安全感，矮一点背起来会轻松些，脾气差可以锻炼我的修养，你就是我的百分百女孩\n3这太棒了，我可以带你去环球展览，一只活的地精肯定会让哈迷们打开眼界！', '情感链接');
INSERT INTO `talk_art` VALUES (10688, '妹子 家境不错，选择在外', '她说：因为在本地生活没有压力，所以没有动力... 怎么回复？\n1你想要压力的话，可以选择炒股的\n2我可以为你增肥，总有一天压力能达到你的要求\n3据说打点鸡血动力会很足', '情感链接');
INSERT INTO `talk_art` VALUES (10689, '你能不能不这么混蛋', '1一共就两个蛋，肯定混不了\n2我混蛋关你什么事？你会蛋疼吗？\n3自己没那个器官就别嫉妒别人，有本事你也长个蛋混一混我看看', '情感链接');
INSERT INTO `talk_art` VALUES (10690, '你真麻烦。你真烦人', '1比大姨妈还烦人吗？\n2我哪有烦人？你也算人吗？？你是神仙姐姐好不好\n3实际我是你的冤家重金聘请来专门烦你的，能得到你认可，我深表荣幸', '情感链接');
INSERT INTO `talk_art` VALUES (10691, '男：我们来玩个游戏吧。', '女：我才不和你这样的人玩游戏。1那你想和我干嘛？我很贵的，没钱你就别想了\n2你一般喜欢自己玩自己吗？\n3喜欢玩人与动物？\n其他答案：那你喜欢和那种类型的生物玩游戏呢？ （一直很想爱你）', '情感链接');
INSERT INTO `talk_art` VALUES (10692, '以前人长的挺老实 ，实际', '1我一没摸你而没亲你，凭哪点估计到我不老实的\n2我亲密以后都很老实的，不信你可以试试\n3你这么说我就不爱听了，我长得从来就是风流倜傥，玉树临风，哪点跟老实沾边了', '情感链接');
INSERT INTO `talk_art` VALUES (10693, '你冷静一下', '1阿弥陀佛\n2麻烦你帮我搬个冰箱过来，要完全静音的那种\n3。。。。。。。好了，一下已经过去了，我们继续', '情感链接');
INSERT INTO `talk_art` VALUES (10694, '\\', '1你肯定听不懂了，你去猪圈跟它们沟通起来应该比较合拍\n2以你的语言能力，找哆啦 A 梦要一块翻译年糕才能帮你跟人正常沟通\n3你肯定是乔装打扮的外星人！你把 XX（对方的名字）抓哪去了？', '情感链接');
INSERT INTO `talk_art` VALUES (10695, 'TD 后想发展为长期关系，', '你的问题属于事后反悔，吸引不够所致，下面答案只是打开吸引之门的第一步\n1如果你借完钱给我后说这句话我会更开心点\n2那麻烦你先把这次的单买掉，当然，嫌贵可以分期付款\n3万一这次我不小心怀孕也不能来找你吗？你是这么没责任心的女孩子吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10696, '你别这样了，我真受不了', '1会让你受不了的。。。。这就是传说中的敏感带吗\n2真没用，这就受不了了，要不换你试试，看我受不受得了\n3想要做我的女人，先挺过这关吧', '情感链接');
INSERT INTO `talk_art` VALUES (10697, '“我们这样算是什么?\\', '1妖精打架\n2亚当和夏娃模拟秀\n3从生物学角度来说，我们都算是人类', '情感链接');
INSERT INTO `talk_art` VALUES (10698, '你多大了，别跟个小孩一', '1我从不会随便告诉女孩子我多大，但对你可以例外，自己去拿个尺子来量下吧\n2你见过腿毛（什么毛都行）这么长的小孩吗？\n3跟哪个小孩一样？我们的？', '情感链接');
INSERT INTO `talk_art` VALUES (10699, '“你是只跟我玩玩，还是', '1这也是我正想问你的\n2你已经在想完事后要怎么甩掉我了是吗？\n3傻瓜，你又不是变形金钢，有什么好玩的', '情感链接');
INSERT INTO `talk_art` VALUES (10700, '经常 约到女孩单独相处时', '她会突然 闪开 表现出 很为难的样子，然后 感觉思考了很久一样 问“ 你喜欢我嘛？ ”\n1不喜欢你的话，你以为自己怎么会亲得到我的？\n2谈不上喜欢。。。但是很爱\n3喜欢你不要问这种白痴问题了', '情感链接');
INSERT INTO `talk_art` VALUES (10701, '“你总是这样约女孩吗？', '1女孩总是这么约我\n2不经常，一个月也就 29 天去那里\n3没关系，去你总是去的地方也行', '情感链接');
INSERT INTO `talk_art` VALUES (10702, '你弄得我几晚都睡不着，', '1赔什么呢。。。。陪你吃个饭吧，但你要买单哦\n2我什么时候弄你了，别乱栽赃！\n3怎么赔？最近严打三陪你还叫我顶风作案？', '情感链接');
INSERT INTO `talk_art` VALUES (10703, '不要吧~我不想告诉你我的', '1老板也不想发我工资，结果还是发了，何况你长得比他善良呢\n2我先把电话告诉你也行\n3你想晚上直接去我家吗？\n其他答案：不告诉我你的电话，那就告诉我你的电话号码吧 ( 一直很想爱你 转)', '情感链接');
INSERT INTO `talk_art` VALUES (10704, '“就我们两个人吗？你为', '1我在给你制造能与我独处空间，你不懂得感恩还想七想八的\n2我手机上有电筒，不需要另外带灯泡了\n3我的手机中毒了，除了你的号码谁的都拨不通，这个答案你满意否？', '情感链接');
INSERT INTO `talk_art` VALUES (10705, '我打了好多次电话给你啊', '1下次拿你电话亲我电话一下，配个对，就可以打通了\n2你帮我交个费就解决了\n3最近在跟人谈一笔 50 块的大买卖，所以打不通很正常，男人以事业为重嘛', '情感链接');
INSERT INTO `talk_art` VALUES (10706, '我还没有遇到我想要的人', '1你想要啊,你想要我会给你的,你想要我不会不给你的,不可能你说要我不给你,你不想要我偏要给你\n2女孩子矜持点，不要一天到晚把“想要”挂在嘴边\n3那个人应该感到庆幸还没被你遇到', '情感链接');
INSERT INTO `talk_art` VALUES (10707, '有美女陪伴就不理我了，', '1你说得对，我会改进的，下次一起理，还没试过双飞呢\n2请问你是？\n3你去韩国深造一下整个更美的，我就重你轻她了', '情感链接');
INSERT INTO `talk_art` VALUES (10708, '太没意思啦，以后不跟你', '1我们两个的想法在这一刻极有默契地吻合了\n2不聊天你想跟我干嘛？我可不是那么随便的人\n3恩，聊天确实不是我的强项，大家都说我的嘴用来接吻感觉好得多', '情感链接');
INSERT INTO `talk_art` VALUES (10709, '“你会只对我一个人好吗', '1不会，我会对很多人好，包括我们的父母，还有我们以后的孩子，\n2那首先得看你够不够乖\n3这么私人的问题。。。请我吃饭就告诉你', '情感链接');
INSERT INTO `talk_art` VALUES (10710, '你是什么时候开始想起我', '1便秘的时候\n2在外面吃完饭发现没带钱的时候\n3看动物世界的时候', '情感链接');
INSERT INTO `talk_art` VALUES (10711, '\\', '2.好女性化的名字怎么回\n1很想知道你的名字特殊到什么程度\n2我的名字跟你那男性化的样子正好相得益彰\n3你如果自认为对名字这么有研究，那以后给我们孩子取名的任务就交给你了', '情感链接');
INSERT INTO `talk_art` VALUES (10712, '你和你女朋友好登对啊，', '1我结婚，你急什么？真那么急就把红包先包了当订金吧\n2你跟我也很登对啊，什么时候做我二奶啊？\n3你跟那根电线杆也很登对，什么时候爬上去啊？\n其他答案：没办法，谁让你不长的美一点呢？（一直很想爱你）', '情感链接');
INSERT INTO `talk_art` VALUES (10713, '\\', '1以前的女孩子都只聊一次就主动约我了哦\n2就是因为没有，所以我才约你一起来见证这奇迹的时刻\n3你习惯聊三次才受约吗？不然我先挂电话，马上再打过去补聊一次也行啊', '情感链接');
INSERT INTO `talk_art` VALUES (10714, '“如果我是你女朋友，你', '1你做了我女朋友就知道了\n2睡觉的时间都拿来陪你睡，不信现在就可以试试\n3那得看你付得起多少费用\n妹子：对女朋友还收\n钱？ 对你可以打折哦', '情感链接');
INSERT INTO `talk_art` VALUES (10715, '妹子： 你好阴险啊', '1哪里会阴险，我从来不把死老鼠丢人家窗户里去的，最多丢门口\n2过奖过奖，你一直是我学习的榜样\n不用崇拜我，每天坚持看新闻联播就能学会的\n其他答案：君子藏器于身，待时而动 ', '情感链接');
INSERT INTO `talk_art` VALUES (10716, '你和你女朋友没结婚就住', '1以这个封建程度来推测，你们那里应该还保留着父母包办婚姻的习俗吧?\n2没关系，我会给你时间慢慢习惯的\n3亲爱的，人类其实是一直在进步的', '情感链接');
INSERT INTO `talk_art` VALUES (10717, '我：考虑我啊 女孩：你都', '1锋芝都能闹婚变，别说我这还没盖章的了，所以你不用灰心的\n2你没听说过小三这个词语吗\n3我现在都给你机会了，你完全可以自信点的\n其他答案：我不在意多一个，男人三妻四妾平常事嘛！（一直很想爱你）', '情感链接');
INSERT INTO `talk_art` VALUES (10718, '搭讪时...“为什么要认识', '1因为你看起来不像坏人（非原创）\n2刚刚还准备了一个理由，结果跟你说话一紧张，给忘了（非原创）\n3因为我妈逼我相亲，我觉得与其这样还不如自己到街上找个看着讨人喜欢的呢，为了我这辈子的幸福，留个号码给我吧', '情感链接');
INSERT INTO `talk_art` VALUES (10719, '\\', '1随便是谁？你不是随便的人，你是我的人（非原创）\n2那你觉得我是个随便的男人吗\n3我只知道我们是同一个类型的人，并且我还知道，我深爱你这种类型的人', '情感链接');
INSERT INTO `talk_art` VALUES (10720, '妹子：你在嗨什么？（A', '1在嗨摇头丸，给你来一颗？\n2看到比我迟钝的人禁不住心情嗨起来\n3以你听完笑话的反应来看，我就是跟你讲估计你也听不懂', '情感链接');
INSERT INTO `talk_art` VALUES (10721, '很难约你哦，你总是这么', '1我在为了我们的未来奋斗！你不用太感动\n2最近确实有点忙，你可以提前一星期预约\n3你到底想不想约我啊，不然怎么总是挑我最忙的时候约我呢', '情感链接');
INSERT INTO `talk_art` VALUES (10722, '第一次去搂她的肩，她：', '1搭肩膀啊，你还想我干嘛？想也没用，我可不是那么随便的人\n2头忽然有点晕，可能贫血了，让我扶一下\n3有个女的在那边注视我很久了，为了用肢体语言让她死心，就算让你占点便宜也没关系的', '情感链接');
INSERT INTO `talk_art` VALUES (10723, '已经碰到好几次了，我打', '1.你的指甲油抹的很难看。\n2.你的发型很乱，该好好整理一下了\n3。你脸上的粉抹的很厚\n我发现她们都用同样的一句话回答我，那就是：这就不用你操心了！或者：这就不用你管了。都是在搭讪的时候的，该怎么对待？\n1那不行啊，还指望过年把你带到菜市场卖钱呢，没卖相怎么喊得起价？\n2职责所在，我是城管大队的，今天便衣执勤，你已经影响到市容了\n3我从小就将助人为乐当成人生格言，看到你的瑕疵，一定要帮你指出来的，你不会拒绝这善意的提醒吧\n其他答案：我不说管你，我是担心你吓着别人(O°回归)', '情感链接');
INSERT INTO `talk_art` VALUES (10724, '\\', '1女士优先是中华民族传统美德\n2妈妈说女孩子的话不能随便信，特别是漂亮女孩子\n3我想深刻表达我的诚意', '情感链接');
INSERT INTO `talk_art` VALUES (10725, '女：你是不是在约我？', '1你那么想我约你吗？笑一个给我看，我可以考虑一下\n2对啊，我想约你来我家帮我打扫卫生\n你会这么问我，是因为我表达能力有问题，还是你理解能力有问题呢？', '情感链接');
INSERT INTO `talk_art` VALUES (10726, '好久不见，你还是那么寒', '1这么久不见，你眼睛还没治好吗？\n2从你身上可以印证品位这东西还真不是时间长就能提高的\n3你会觉得寒吗？可能是肾亏哦\n其他答案：好久不见 你还是那么不诚实(bcvfghy\nd)', '情感链接');
INSERT INTO `talk_art` VALUES (10727, '我不需要', '1你确定以后也不需要吗，我最近有在兼职卖卫生巾哦\n2这位小姐，你面前的是绝种好男人！有木有？有木有？现在是免费试用期，试用过如果还觉得不需要，可以分文不收，你还等什么？不要犹豫，赶快拿起电话订购吧！\n3你平时都是用手解决的吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10728, '无脸兄', '第一条：我跟朋友赌了 100 块，他说你不会把答案告诉我，我赢了分你一半怎么样\n结果她给我发了一条空间短信，而且是几天之后才发的：晕，不至于吧？\n1才一百块你就乐晕了，不至于吧？\n2先别晕，把答案说完再晕，我好去你空间把你踩到醒\n3你不会嫌少吧？要么。。。我再私人赞助 5 块吧！这已经是最大极限了，这个月伙食费全都押给你了，男子汉大丈夫，哪怕饿死，也不能输这个赌，请赐予我力量吧！', '情感链接');
INSERT INTO `talk_art` VALUES (10729, '你怎么这么猥琐？', '1菩萨说过，心里什么样子，看别人就是什么样子，阿弥陀佛\n2你怎么这么装纯\n3我确实猥琐，但是要分对象的，比如你，就是给钱也别想让我猥琐你！\n其他答案：跟你认识这么久了，想不猥琐都不行 (一直很想爱你)', '情感链接');
INSERT INTO `talk_art` VALUES (10730, '\\', '她哥（认的）回复：估计是太矮被别人挡住了！\n我准备回复：“你哥这种自卑的情绪需要改进一下”\n面对这种情况，我该怎样回复？？？（以后类似 妹子 测试或是被人打击：你太矮了！怎样回复比较好？）1你哥在下面垫着，我站上去，你就看得见我了\n2对她哥：你长得那么高，可以描述一下你跟电线杆的区别吗？小狗会不会在你腿边撒尿划地盘呢\n3宝贝，在人群里你看不见我，是因为我藏在你心里了\n关于被人攻击太矮的回答参考第 312 问', '情感链接');
INSERT INTO `talk_art` VALUES (10731, '你忙', '1我不盲，两只眼睛都是 1.5 的\n2正忙着跟你聊呢，市场不景气，陪聊费也不好挣了，你看着给就行\n3确实忙不过来，热情的粉丝太多了', '情感链接');
INSERT INTO `talk_art` VALUES (10732, '你不要操心了，我活该', '1我是为你父母担心，老人家是无辜的\n2我是在想怎么开口找你拿辛苦费\n3我不想操心，只想操你（慎用）', '情感链接');
INSERT INTO `talk_art` VALUES (10733, '妹子 说不过我、于是你讨', '1一般女孩子对我都是由爱生恨的，讨嫌算很轻的了\n2嫌我太帅让你很没安全感吗\n3讨嫌总比找你讨债的好\n其他答案：我可没必要去讨你喜欢哟（一直很想爱你 )', '情感链接');
INSERT INTO `talk_art` VALUES (10734, '不好意思 我不喜欢个子矮', '1高个子你够得到吗\n2我躺下来海拔比较高，你要试试吗\n3我不会嫌你没有自知之明的', '情感链接');
INSERT INTO `talk_art` VALUES (10735, '你准备叫我出来干吗？~', '1你想我干嘛？吃饭呗，你别想那些乱七八糟的事情\n2我知道你的担忧，放心，我保证不跟你借钱\n3干你（慎用）', '情感链接');
INSERT INTO `talk_art` VALUES (10736, '不早了，你早点睡吧！', '1我很传统的，我想我们还没到可以一起睡的程度\n2你不觉得不早了和早点睡是反义词吗\n3你迫不及待要到梦里和我接着聊呀', '情感链接');
INSERT INTO `talk_art` VALUES (10737, '他（男）不用介绍', '1虽然人家都知道叫我帅哥，但大家叫我的名字我会更喜欢\n2大家还不知道我们的关系呢，你想做隐婚族吗\n3不用担心把我介绍给别人，我是很专一的', '情感链接');
INSERT INTO `talk_art` VALUES (10738, '已经被我吸引的女孩对我', '她人比较矮\n1我觉得你更应该为自己的脸皮厚度骄傲\n2恩，我今早在菜市买了两根大萝卜也是长得这么棒的\n3我全身最不满意的就是我这双眼，老是看上不要脸的人', '情感链接');
INSERT INTO `talk_art` VALUES (10739, '为什么你这几天都不联系', '1医生叫我最近要戒荤腥\n2想试试小别胜新婚到底是什么感觉\n3你不联系我还好意思恶人先告状？？\n其他答案：这几天缠着我的小 妹子 实在太多了，没办法呀 ( 一直很想爱你)', '情感链接');
INSERT INTO `talk_art` VALUES (10740, '“你一定经常这样哄/骗小', '1我经常这样哄我兄弟\n2大姐，请不要装嫩\n3不一定，偶尔也哄哄你这样的上年纪的女人', '情感链接');
INSERT INTO `talk_art` VALUES (10741, '我对一个 妹子 说：你不了', '1你听过狐狸吃葡萄的故事吗（暗示她吃不到葡萄就说酸）\n2这样婚后性生活会不协调的\n3是没必要，以你的智商就算来了解也是白搭', '情感链接');
INSERT INTO `talk_art` VALUES (10742, '你说的都是爱你的人会做', '1暗恋我也是一样的\n2谢天谢地\n3我也不爱上班，但还是照样要为它去做那些事呢', '情感链接');
INSERT INTO `talk_art` VALUES (10743, '昨天我对 QQ 上一个美女进', '1不准吗？看来路边摊买的宠物心理指南还是不行啊，下次去新华书店买\n2我刚才的表述其实是针对你的冷笑话，你觉得好笑就算成功了哦\n3你以为你完全了解自己吗？你能了解到自己在某种特定情况下会做出怎样的行为吗？人其实有四个人格，你只了解在人前表现出来的自己和在人后才表现出来的自己，别人了解的是大家印象中的你，而我可以看到每个人内心深处那个连自己都不了解的自我，现在只是把我的感觉说出来，你不相信可以一笑而过，换个话题吧', '情感链接');
INSERT INTO `talk_art` VALUES (10744, '怎么样~小样', '1小样叫谁？（非原创）\n2你被做得不合格，通知爸妈，重做小样\n3你这小样真不怎么样', '情感链接');
INSERT INTO `talk_art` VALUES (10745, '她用开玩笑的口气说：我', '1早就知道你自恋了，不用说出来\n2是哪个女人，你告诉我\n3黄瓜？香蕉？还是火腿肠？不过我猜不是萝卜，太干了', '情感链接');
INSERT INTO `talk_art` VALUES (10746, '还轮不到你', '1其实我没有要参与排队的意思\n2轮暴要被枪毙的，我把风就行了\n3你这个说法让我想起了在洗手间外等包厢拉肚子的场景\n4我给你 10 块，让我插个队吧\n5你喜欢被轮的这种感觉吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10747, '你这几天晚上和我聊通宵', '1你都变熊猫了，就是结了婚我也得负责啊，不然珍稀动物保护协会找过来我可担不起\n2我想她不会跟国宝争风吃醋吧\n3如果你真变成熊猫了，全世界都会抢着要的', '情感链接');
INSERT INTO `talk_art` VALUES (10748, '她叫你兄弟', '1你有胸，我有弟，什么时候让它们团聚下呢\n2女孩子家矜持点，你该叫我亲爱的\n3古惑仔里十三妹也这么叫韩宾的，最后两个人恋爱了哦，你这算在暗示我吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10749, '还有一个月你就滚蛋（离', '1想要我想着你就直说，别这么拐弯抹角的\n2走之前你请我吃顿好的，我回去保证会一直想着你的\n3我从现在就开始想你，你满意不？', '情感链接');
INSERT INTO `talk_art` VALUES (10750, '你打击不了我的', '1我打击你干嘛？你觉得自己长的像沙包吗？\n2我的级别还不足以攻破你这么厚的脸皮\n3我不想打击你，踹你两脚就行了', '情感链接');
INSERT INTO `talk_art` VALUES (10751, '脑残。。。。智残。。。', '1你不用说，我也看得出来，我不会歧视你这缺点的\n2你在夸我长得年轻（90 后）吗？\n3我会尽量放慢语速，这样你理解起来会容易些吗？', '情感链接');
INSERT INTO `talk_art` VALUES (10752, '对一个 妹子 使用了冷读', '妹子：哪有那么复杂？偶像剧看多了吧\n1你看过哪部偶像剧女主角长成你这样的\n2抱歉，我没想到这番话对你的大脑来说会很复杂\n3对你应该来个恐怖片的才行', '情感链接');
INSERT INTO `talk_art` VALUES (10753, '\\', '1没关系，我对小动物都很好，多给几块肉骨头它们就能适应；你想静的话，到庵堂剃度会六根都清净的。你也不可能理解我的角度，因为我们身体构造是很不一样的\n2我要放弃的话，你就得准备请个好律师了，我会通过法律找你拿回精神，青春损失费的\n3我不会放弃的，我要一直等你，等到哪天你能给我答案了我再带着老婆孩子跟你一起吃饭庆祝\n其他答案：没关系，我做你粉丝算了。你爆出艳照门我也不嫌弃你', '情感链接');
INSERT INTO `talk_art` VALUES (10754, '\\', '1那你想怎样，太过分的要求我不会答应的，但如果只是亲一下倒可以考虑一下\n2我知道你是怕我骚扰你，尽管放心，我一天最多也就给你打 20 来个电话（非原创）\n3那我就不勉强你了，现在我想认识下你的 QQ 号码，你给我介绍一下总可以吧', '情感链接');
INSERT INTO `talk_art` VALUES (10755, '滚！', '1你觉得我长得像穿山甲吗？\n2我又不是屎壳郎，怎么滚你？\n3这边地势崎岖，你确定要用滚的吗？如果下定决心要这样我倒可以友情助你一脚', '情感链接');
INSERT INTO `talk_art` VALUES (10756, '\\', '1你有鸟吗？掏出来我看看\n2你从泰国来的吗？你尿尿是站着还是蹲着？\n3我认为你就算每天这样把鸟挂在嘴边也不能改善口活技巧', '情感链接');
INSERT INTO `talk_art` VALUES (10757, '\\', '1宝贝，我已经几天没刷牙了，我怕对你不好哦。。。\n2最近口腔溃疡了，医生不让我吃海鲜和刺激性食物呢，估计舔鲍鱼也不行\n3（先把头接近 妹子 要你舔的那里，逗留一下下后说）：恩，恩，好的妹子：?\n你下面的嘴吧刚跟我说话了\n妹子：少胡扯\n真的！它说不要我的舌头，她想要个麦克风，她要唱《水姑娘》', '情感链接');
INSERT INTO `talk_art` VALUES (10758, '女：你真是油嘴滑舌', '   男：情话连篇，不如你在身边（真有才）', '情感链接');
INSERT INTO `talk_art` VALUES (10759, '你怎么这么紧张', '1我哪里紧张了，我只是出汗，腿软，心跳加快，呼吸急促，这叫。。。肾虚\n2因为第一次这么近距离接近肉食类猛兽\n3我必须得小心谨慎，不然狗仔队拍到我们，明天见报就麻烦了\n其他答案：被女孩子用崇拜的眼光看着，当然会紧张了（一直很想爱你 ）', '情感链接');
INSERT INTO `talk_art` VALUES (10760, '你的 QQ 多少？', '1冒昧问句-----你不是盗号的吧？\n2目前还在骑电动车，交通未改善前就算便宜如 QQ 也不会考虑购买的\n3想要帅哥 QQ 得先请吃饭，咱们可以边吃边聊', '情感链接');
INSERT INTO `talk_art` VALUES (10761, '妹子 说：怎么你说话我这', '1不爱听可以戴个隔音的耳机，当然，买棉花来塞耳朵可以更省钱\n2我的语言艺术犹如浩瀚汪洋，肯定是无边无际的，你再钻研几年文学兴许能听懂点\n3不爱听可以用你的嘴巴封住我说话，但我要先问问我的嘴唇愿不愿意', '情感链接');
INSERT INTO `talk_art` VALUES (10762, '你怎么这么傻。。。', '1还不是被你传染的\n2我在配合你，你竟然傻得看不出来，还是我配合傻的程度不够到位呢\n3唉。。。都说傻人有傻福，但为什么不是一个美女问我这个问题呢', '情感链接');
INSERT INTO `talk_art` VALUES (10763, '女：你怎么不找个女朋友', '  男：外面人潮涌动，只有你入我眼', '情感链接');
INSERT INTO `talk_art` VALUES (10764, '我好累', '1正好那边有家酒店，我陪你上去休息休息吧\n2其实我的吻有恢复疲劳，提神醒脑的作用，给你试试，不灵不要钱\n3出门小卖部就有红牛卖，帮我也带一瓶', '情感链接');
INSERT INTO `talk_art` VALUES (10765, '你忘了我吧！', '1对不起，请问你是？\n2忘了你倒是小事，但你欠我的青春损失费可不能忘\n3说得轻松。。要忘掉你这张诡异的脸谈何容易', '情感链接');
INSERT INTO `talk_art` VALUES (10766, '你喜欢哪个类型的女孩子', '1就喜欢你这种类型的，大方可爱美丽善良，你请我吃个感恩饭吧\n2只要不是你这型的其他都可以考虑考虑\n3喜欢在乎我感受的，比如那种问我喜欢什么类型女孩子并打算拿答案来当标准改善自己的', '情感链接');
INSERT INTO `talk_art` VALUES (10767, '她的空间是开放的', '里面有她本人极其朋友靓照 N 多啊 可以随便进\n只是不准加 QQ\n加她 QQ 就 提示：空间问我答案\n1在空间留言：我跟朋友赌了 100 块，他说你不会把答案告诉我，我赢了分你一半怎么样\n2在空间留言：有一个沙龙很适合你.。。。。（感谢一滴香）\n3在空间留言：恭喜你幸运得到腾讯公司特等奖大帅哥一个，不用任何手续费，只需把答案发过来， 奖品就会送到', '情感链接');
INSERT INTO `talk_art` VALUES (10768, '女：你的丁丁有多长', ' 男：沉睡千年的定海神针，一柱擎天的金箍棒你说它该有多长（骚聊）', '情感链接');
INSERT INTO `talk_art` VALUES (10769, '你真懒，你是懒羊羊吧', '1你真色，你是灰太狼吧？打算唱狼爱上羊吗？\n2你真勤快，你是来做钟点工的保姆吧？\n3乱说，我。。。算了，懒得否认了。。。\n其他答案：你真笨，你是笨羊羊吧？（一直很想爱你 ）', '情感链接');
INSERT INTO `talk_art` VALUES (10770, '\\', '1提鞋这么高端的事我肯定做不来，但要提起你扔到垃圾站我还是绰绰有余\n2对不起，刚没看出来你是残疾人，真可怜这么小连自己的鞋也提不了，你可以去红十字会寻求帮助哦，但就是不清楚它们援不援助脑残\n3我从不会歧视眼睛有问题的 妹子', '情感链接');
INSERT INTO `talk_art` VALUES (10771, '没见过你这么傻的', '1你平时都不照镜子的吗？\n2我也没见过你这种傻到极点还硬说别人傻的厚脸皮\n3（白她一眼，转身背对）：我从来不接傻子的话茬', '情感链接');
INSERT INTO `talk_art` VALUES (10772, '你怎么老上晚班', '1因为最近迷上了蝙蝠侠的电影，我要学习他的精神\n2白天紫外线太强，我要为你保护好自己的皮肤啊，省得人家说你未来老公是非洲来的\n3我也想就这个问题让我们领导发表下看法', '情感链接');
INSERT INTO `talk_art` VALUES (10773, '女：你要听我的知道吗', '男：明明是一个邻家大姐姐（小妹妹）温柔可爱的形象，却偏偏活得那般蛮横', '情感链接');
INSERT INTO `talk_art` VALUES (10774, '你很天真', '1不把自己弄得天真点跟你哪来的共同语言\n2答对一半，我其实是很傻很天真，你要给我拍照吗？\n3没错，我一直天真地认为你长得不难看', '情感链接');
INSERT INTO `talk_art` VALUES (10775, '我想骂人啊。。。', '1我想打人，你给我打，我帮你骂，怎么样\n2谁踩你尾巴了？你想不想咬人呢？\n3你直接几天不刷牙，不用骂，一张嘴就够对方受的了', '情感链接');
INSERT INTO `talk_art` VALUES (10776, '哎呀，你不知道就别说啦', '1恩，我要对你不耻下问\n2我江湖人称“地上全知道，天上知道一半”，看来这问题属于天上那另一半的\n3我怎么会不知道“哎呀”，跌倒时我都会叫哎呀的', '情感链接');
INSERT INTO `talk_art` VALUES (10777, '你很忙吗', '1恩，在忙着回答你这个明知故问的问题\n2不怎么忙，也就一天工作 16 个小时而已\n3确实很忙，但如果是你要找我约会的话，再忙我也会考虑下的', '情感链接');
INSERT INTO `talk_art` VALUES (10778, '女：你和前女友咋个分的', '     男：以前的异地恋女朋友装作男的给我发信息，说我女朋友和他在一起了，然后我急了，发信息直接开骂，给女朋友打好多电话，她接了过后 哈哈哈的笑，说是在考验我，老子当时就挂了电话，直接分手 你的考验我过了，我的考验没有你', '情感链接');
INSERT INTO `talk_art` VALUES (10779, '\\', '1如果你稍微懂点科学，我就继续跟你聊\n2我可以帮你打下来，但要你自己去捡，下回有流星落下我会通知你\n3天上的星星有什么好的，明天带你去看动物园的猩猩，你会有找到亲人般的感觉', '情感链接');
INSERT INTO `talk_art` VALUES (10780, '你很幽默！', '1彼此彼此，你长得也挺搞笑的\n2这就觉得幽默了？那要是以后做了我女朋友你还不笑死掉\n3不得不说。。。。宝贝，你笑点太低了', '情感链接');
INSERT INTO `talk_art` VALUES (10781, '你脑袋被撞了？', '1没看到你无形的傻瓜气场，不小心就撞到了，赔我钱\n2是啊，好晕，呼吸好困难，快给我做人工呼吸\n3我的脑袋不管撞成什么样也比你那颗好用', '情感链接');
INSERT INTO `talk_art` VALUES (10782, '你怎么不下班', '1我要努力奋斗到我也可以让别人下不了班的那天\n2你这话的另一层意思是打算请我共进晚餐吗\n3门外一堆 FANS 在等我下班，可烦人了，等她们散了我再下班', '情感链接');
INSERT INTO `talk_art` VALUES (10783, '女：你第一眼看到我是什', '   男：你让我眼前一亮，我让你兴致盎然，这才是完美邂逅', '情感链接');
INSERT INTO `talk_art` VALUES (10784, '有天喝点酒了，借酒疯给', '我；发现我有点喜欢你呢\n她：我有什么好，你喜欢我什么了？\n1喜欢你的电话，通话质量挺清晰的\n2喜欢你的脸。。。。长得很有安全感，这样不容易招蜂引蝶\n3喜欢听你说话，那个声音和语调，有种听郭德纲相声般的感觉', '情感链接');
INSERT INTO `talk_art` VALUES (10785, '你做我男朋友吧！', '1你说做就做吗？起码得买个戒指什么的表示诚意啊\n2你的意思是想包养我吗？我很贵的哦\n3我现在对你还不够了解，不知道合不合适，等你连续请我吃一个月饭应该就了解得差不多了', '情感链接');
INSERT INTO `talk_art` VALUES (10786, '你正常点行不行', '1精神病人都觉得别人不正常，想不到你也不能免俗\n2正常人是无法与你正常交流的\n3我一直挺长的，正不正就要看你那边长得歪不歪了', '情感链接');
INSERT INTO `talk_art` VALUES (10787, '我问：你想我了没。 妹子', '妹子 说：没有。。\n我：（模仿赵本山语气）这个可以有\n妹子 回答：“这个真没有。。。”怎么应对呢？\n1 咱不差钱~~~小妹儿，你开个价吧\n2（模仿小沈阳语气）：那是为什么呢~~~~\n3 人这辈子最痛苦的事情是什么，你知道不？就是我想你，你却说没想我人这辈子还有更痛苦的事情，是什么你知道不？就是你想了我，还死不承认！”', '情感链接');
INSERT INTO `talk_art` VALUES (10788, '女：你第一眼看到我是什', '  男：我记得初次见你，你像一束古老温暖的阳光，脸白，手软，腿长 ，感觉普天下所有的 水，都在你眼波中荡漾（第一次见我印象怎么样）', '情感链接');
INSERT INTO `talk_art` VALUES (10789, '让她做什么，整天说：不', '1你以后不要再学我说话\n妹子：我哪有学你说话\n（然后再让她做什么时抢在她前面学她语气说）：不要~~\n2你以前有被强暴过的经历吗，心理阴影很严重呢\n3书上说女孩子说不要的潜台词就是很想要的意思。。。你果然饥渴得很\n其他答案：以后不要说”不要“，要说的话就说“雅灭碟”（一直很想爱你）', '情感链接');
INSERT INTO `talk_art` VALUES (10790, '你没女朋友？我不信', '1何止，我还是处男呢，而且可以给你验证哦\n2确实没有，自从跟老婆离婚后，我就只剩一儿一女了\n3你不用相信，因为就算我没女朋友你的机会也不是很大', '情感链接');
INSERT INTO `talk_art` VALUES (10791, '你的情商（EＰ）不高', '1我的智商高你太多，所以你看不出我的情商很正常\n2但我的情人比你高哦\n3跟你交流，完全不需要动用情绪智商\n其他答案：恩，我知道你的情商高达 250.。。 （一直很想爱你 ）', '情感链接');
INSERT INTO `talk_art` VALUES (10792, '什么，你一直忽略我？', '1（走神状沉默半天。。。）：咦，你刚说什么来着？\n2那我现在给你拍张照，弄个镜框挂床头，每日睡觉前后凝视半小时，在不忽略你的前提下还能镇宅辟邪呢\n3我认为你多虑了，你长得这么奇特，一般人是很难忽略掉的', '情感链接');
INSERT INTO `talk_art` VALUES (10793, '女：我们不熟', '男：所以我现在做的事，不就是为了，让熟悉的小船，扬帆起航嘛', '情感链接');
INSERT INTO `talk_art` VALUES (10794, '她说：整天想一些有的没', '1你是有的没的吗？我都在想你诶\n2不然你让我想你吗?还不如有的没的呢\n3那你整天都在想些什么有意义的事呢', '情感链接');
INSERT INTO `talk_art` VALUES (10795, '你人很好！', '1谢谢，但必须说明的是，你再怎么夸我，酬劳也不能少一毛\n2恩，我这个人生来就是喜欢爱护小动物\n3天哪！明目张胆拿到了传说中的好人卡', '情感链接');
INSERT INTO `talk_art` VALUES (10796, '吃饭、逛街时总是说 随便', '1女孩子怎么可以总是这么随便呢？\n2如果真的随我便的话，我打算带你去开个房间休息下\n3（问她意见时，在她开口说随便以前学她语气声音说）：随便~~（非原创）', '情感链接');
INSERT INTO `talk_art` VALUES (10797, '女：你个骗子', '男：骗你干嘛?万一骗回家了，你吃的比我多，我可不划算', '情感链接');
INSERT INTO `talk_art` VALUES (10798, '女：你为什么喜欢我', '   男：因为我想与你一起走过风风雨雨，看过日出日落，一起走过名山大川，一起惬意地煮茶聊天落，日衍山，暮色苍茫，鸡栖敛翅，牛羊归栏', '情感链接');
INSERT INTO `talk_art` VALUES (10799, '\\', '1还可以救的，只要你给我人工呼吸\n2不懂得救可以去看看海岩那本书-----《拿什么拯救你我的爱人》\n3那你打算殉情吗\n其他答案我觉得还可以再抢救一下 （从不善解人衣 ）\n你怎么知道我舅舅没了？ （从不善解人衣 ）\n（说自己不会系鞋带有点 DLV，尽量不要提这样的事。不过要是我估计会掰扯出一个情感故事出来， 说只要自己一系鞋带就想起***。女人喜欢有故事的男人（从不善解人衣 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10800, '妹子 说 UFO 这个怎么回答', '1看到你就能想到你说的交通工具\n2你想骗我回头去看，然后转头过来时偷吻我吗？\n3嘘，小声点，我们可以拍照卖到报社去赚稿费呢', '废物测试');
INSERT INTO `talk_art` VALUES (10801, '妹子：是哦是哦。嫌问题', '1不要盲从，我喜欢有主见的女孩子\n2其实我不会嫌问题多啦，要是林志玲提问我巴不得她问十万个为什么呢\n3看我在忙，你打算自己游戏自己么？', '废物测试');
INSERT INTO `talk_art` VALUES (10802, '妹子：你真是越来越幽默', '1你是不是想找我借钱？\n2你也是越长越幽默了\n3你也可以的，去看舞台男的话术世界吧（广告）', '废物测试');
INSERT INTO `talk_art` VALUES (10803, '妹子：今天你不忙了？', '1我从来不盲，视力好得很\n2怎么，你见不得我清闲是吧\n3这不是忙着回答你问题么', '废物测试');
INSERT INTO `talk_art` VALUES (10804, '你现在一定很开心吧。。', '（包袱是她姐姐。。。说这句话的人是她妹妹。。。。我跟她姐姐分了）\n1你错了，这个包袱会永远压在我心里\n2你是来道贺的？红包带来了吗？\n3不要这样说你姐姐，她是个好女孩，我没这个福气，希望她能找到自己的幸福\n其他答案：你是不是也挺开心的呢,因为我现在终于单身了.(慎用) （一直很想爱你 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10805, '妹子：我约你你会赏面吗', '1那要看你的约会经费有多少了\n2吃面容易胖，约会的晚餐还是赏你一碗饭吧\n3互相欣赏对方的脸没意思，我觉得一起赏月会浪漫得多\n其他答案：请将你的面伸过来,我赏你一下（一直很想爱你）', '废物测试');
INSERT INTO `talk_art` VALUES (10806, '妹子：如果我约你呢？ 后', '1肯定不一样，你的后果要惨很多\n2首先前提是要我愿意接你电话\n3不知道呢，要不我们赌一顿饭，改天约下试试，看看后果会不会一样', '废物测试');
INSERT INTO `talk_art` VALUES (10807, '没人要了，怎么办', '1一般商场里对没人要的东西都是打折或半卖半送的，你可以参考下\n2你先告诉我赔多少嫁妆，我再回答你这个问题\n3亲爱的放心啦，不会没人要的，你见过菜市场猪肉卖不出去么？', '废物测试');
INSERT INTO `talk_art` VALUES (10808, '妹子：有女孩子约你出去', '1我妈小时候经常约我去动物园的\n2一般女孩子都觉得我家里的床比较舒服，没必要出去\n3有，但是不多，平均每天两个', '废物测试');
INSERT INTO `talk_art` VALUES (10809, '女：好啊，你的脸经抽吗', '1不知道，有个参照我心里才会比较有数，我先试试你的经不经抽吧\n2在你的钢铁脸皮面前我哪敢自夸\n3没抽过不清楚，你经常抽自己吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10810, '妹子：你晚上一般有活动', '1不对，我是白天晚上都有活动\n2要约我就赶快，等下行程就排满了\n3你指的活动是活塞运动的简称吗？如果是，那我一般是晚上活动', '废物测试');
INSERT INTO `talk_art` VALUES (10811, '女：好了，你可以走了', '1请问我刚是被你非法拘禁了吗？\n2走？服务费还没给我呢\n3你还没说我们到底去哪家酒店，我怎么走？', '废物测试');
INSERT INTO `talk_art` VALUES (10812, '妹子：你对我有想法吗？', '1想你请我吃饭\n2是啊，最近特别想吃猪肉\n3这你都知道，我对你的想法是要能长得好看些就好了', '废物测试');
INSERT INTO `talk_art` VALUES (10813, '妹子：我要冷落你', '1你是想玩冰火五重天吗\n2像我平时对你那样？\n3太好了，正好最近有点上火', '废物测试');
INSERT INTO `talk_art` VALUES (10814, '妹子 说没意思怎么回答', '1你有读心术？\n妹子：没有啊，怎么了\n不可能，不然怎么知道我对你没意思？\n2去照下镜子你就会发现原来自己长得这么有意思\n3你去街上裸奔大家会觉得很有意思的', '废物测试');
INSERT INTO `talk_art` VALUES (10815, ' 3 个可以接吻的进挪', '1“不要像那样看着我， 否则我就要亲你了。”（妹子 通常回答， “哪样”）\n（然后你把头扭开， 装作发现什么别的东西， 把视线转移到那个东西上， 看一会， 再看回来， 对这 妹子）\n（妹子 也会和你做相同的事情。）\n然后你说 “我警告过你的” 就 kiss 了 （非原创）2这几天做梦总是梦到你。妹子：真的 ？\n可惜，都是噩梦。\n妹子：我有那么可怕吗。想知道都什么内容吗？\n（不用得到 妹子 的同意，直接说）\n比如前天，梦到趁你不注意，吻了你一下，结果你还一个耳光，当时就醒了。还有昨天，梦见想牵你手，你突然把我推开，差点摔在马路上。\n妹子：谁让你犯坏呢 （看 妹子 表现，当时氛围，决定要不要继续）那你也不能这么恶毒啊。\n妹子：做梦你还当真，又不是真的。\n那我试一下（kiss） （非原创）\n3跟她聊天时突然用手一指她背后表情做惊讶状，她回头去看时你把嘴贴上去，转过来就能亲到（可参考周星驰《功夫》里包租公对龅牙珍的做法） （非原创）', '废物测试');
INSERT INTO `talk_art` VALUES (10816, '\\', '妹子：今天你心情好啊1那是在看到你之前的事了\n2恩，大姨妈准时来了，证明我没怀孕，之前吓坏我了\n3要不然你以为我为什么跟你聊天', '废物测试');
INSERT INTO `talk_art` VALUES (10817, '妹子 说 先拿 500 万过来', '1冥钞可以吗\n2你可能不太了解地球上的行情价，500 万可以嫖多少次\n3你脱光了我看看，很好奇究竟哪部分可以值 500 万', '废物测试');
INSERT INTO `talk_art` VALUES (10818, '我觉得你很没气质，我不', '1就是因为考虑到你没气质我才装成这样过来的，不料倒被你给嫌了，失算啊\n2我和你对气质的理解有不同意见，留个电话给我没事可以探讨一下\n3气质是可以通过后天形成和提高的，而你的长相除了整容就没办法改变了，我是 XX 整形医院的， 留个号码呗', '废物测试');
INSERT INTO `talk_art` VALUES (10819, '妹子：我的缺点就是看见', '1你适合去做专业陪聊呢\n2优点是不单聊还激情视频是吗？\n3请你搞清楚自己的角色！我们到底谁才是 PUA?你这个缺点在 PUA 里叫 QQ 流好吧！（慎用）', '废物测试');
INSERT INTO `talk_art` VALUES (10820, '每次你都没有空？', '1干嘛每次你都偏偏在我没空的时候出现呢？我不相信能巧到这个程度\n2亲爱的，是我不对，我想为咱们以后打好一个坚实的基础，所以有时不得不忍痛放弃一些和你相处的快乐时光，你把每次都记下来，先存着，我保证以后都会还给你，还附带利息哦\n3我又不是填空题，怎么会有空', '废物测试');
INSERT INTO `talk_art` VALUES (10821, '你永远也学不会', '1那你学一个我看看\n2送你一句中国移动的口号----我能\n3从措辞可以看出你是典型的悲观主义者，其实女孩子乐观点会老得慢些哦\n其他答案：是的，生孩子我怎么也学不会(猪哥亮)', '废物测试');
INSERT INTO `talk_art` VALUES (10822, '晚上我怕你牵手然后逃回', '1有那么可怕吗？我上完厕所都有洗手的好吧。。。\n2还好你回去了，我真怕牵了你的手以后，你赶也赶不走就麻烦了\n3你多虑了，不管我怎样牵你也绝不会把你卖到菜市场去的，再说，就算我要卖，人家猪肉贩子也不收劣质肉啊', '废物测试');
INSERT INTO `talk_art` VALUES (10823, '你觉得我需要暗恋谁吗？', '1不需要，因为怎么恋都没戏\n2看得出来你会比较直接的表白，如无意外对方应该也会直接的拒绝\n3需要，因为我喜欢腼腆的女孩子', '废物测试');
INSERT INTO `talk_art` VALUES (10824, '我伤不起', '1其实处女膜破掉不算受伤的 \n2每个月流那么多血，确实不能再受伤了\n3我虽然不能支付医药费，但帮你买个创可贴还是可以的', '废物测试');
INSERT INTO `talk_art` VALUES (10825, '你肯定和 N 个人聊天', '1你以为我有 X-\nAN 里的光头博士那么牛 B 吗\n2不跟人聊天难道跟狗聊\n3你猜对了，但其实我最想的还跟 N 个人激情视频', '废物测试');
INSERT INTO `talk_art` VALUES (10826, '爱我的人和我爱的人肿么', '1 你妈和你老公能是同一个吗\n2TA 们也是这么想的\n3 这就是 AFC 和 PUA 的区别（慎用） \n其他答案：这说明你的品位和你吸引力不在同一水平线上(一直很想爱你 )', '废物测试');
INSERT INTO `talk_art` VALUES (10827, '我觉得我还是当你妹妹吧', '1难怪最近老是被人说-------你妹，原来他们说的是你呀\n2亲爱的你真体贴，我懂你的意思，有这个幌子其他 妹子 就不会乱吃醋了，我也听过张震岳的《干妹妹》哦\n3我已经有弟弟了，不想要两个生殖器官呢', '废物测试');
INSERT INTO `talk_art` VALUES (10828, '你别老牵我手，你又不是', '1你认为一定要跟狗处对象才可以遛狗吗\n2我怕不牵着你万一你走散了正好让想找对象的人遇上，会给别人心理带来阴影的\n3我只是手冷握着你取取暖，你怎么就联想到对象了', '废物测试');
INSERT INTO `talk_art` VALUES (10829, '我把你删除(QQ)', '1不用你麻烦了， 我直接把你格式化就行\n2谢谢你放过我，此大恩大德没齿难忘\n3你 QQ 是不是中毒了，最近很流行会自动删帅哥号的病毒呢', '废物测试');
INSERT INTO `talk_art` VALUES (10830, '你现在想找女友还是玩伴', '1如果只是想找玩伴，我推荐你一款充气娃娃配合振动棒效果不错\n2我也想找玩伴，我们一起再去找个玩伴然后玩双飞怎么样\n3你玩我还是我玩你啊，我玩你你要给我钱，你玩我也要给我钱，但太贵你给不起这个钱，所以还是我玩你吧', '废物测试');
INSERT INTO `talk_art` VALUES (10831, '女：想搭讪的话，建议你', '男：我对你的建议很感兴趣，讲来听听，讲得好可以考虑留电话给你哦女：我建议你，离我远一点。1你怕自己忍不住来抱我吗？\n2离远点干嘛？你又不咬人\n3虽然距离产生美，但我还是认为离远点看你也不会变得更好看呢\n其他答案：这个建议挺好，为了不影响我的品味，确实应该这样做。(一直很想爱你 )看来你还是有点自知自明的哟，知道自己会拖累我的形象(一直很想爱你 )', '废物测试');
INSERT INTO `talk_art` VALUES (10832, '我不需要你喜欢啊', '1你很有潜质，我的粉丝团就是不需要我喜欢她们，只需要享受往死了追我的过程，可以考虑让你入团哦2我做不到！那跟嫖娼有什么两样？\n3那你需要我什么？肉体吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10833, '\\', '他妈的居然给我表白：“我喜欢你”1\n是喜欢吗？不是爱吗？那也可以请我吃哈根达斯的吧\n2不管你这个人有多少不足，但就你的品味水准这一点来说，确实可以盖过所有的缺点\n3然后呢,打算要包养我么?', '废物测试');
INSERT INTO `talk_art` VALUES (10834, '想象不出来 你也会弹吉他', '1我还会吹口琴呢，给你试试？\n2我最喜欢的不是吉他，是箫，尤其是喜欢你给我吹奏\n3依你的想象力想象不出来这很正常', '废物测试');
INSERT INTO `talk_art` VALUES (10835, '我不干涉你和 N 人聊天但', '1亲爱的，你这样虽然减轻了我的手指负担，但增加了我的心理负担\n2宝贝，我愿意你增加我的负担，特别是喜欢你整个人压在我身上的那种。。\n3回复慢是因为我在乎你，所以会思考得久些，对别人我一般很快地回复一个“哦”，对你就不一样， 我会回复“哦，原来是这样”', '废物测试');
INSERT INTO `talk_art` VALUES (10836, '女：想搭讪的话，建议你', '男：我对你的建议很感兴趣，讲来听听，讲得好可以考虑留电话给你哦女：那得看你值不值得我建议。\n1那得看你的价值观及不及格\n2我给你 5 块钱，怎么样,还不用找钱哦\n3看你年纪不大，怎么记性这么不好，如果不值得你建议那你刚才怎么会建议我换一个方法呢', '废物测试');
INSERT INTO `talk_art` VALUES (10837, '你喜欢不喜欢关我屁事', '1看着她的屁股说：关你屁股什么事呢？\n2对着她的屁股说：她说关你的事，那就请你发表一下意见吧，只许出声，不许呼气哦\n3一直听人家说长得丑的女孩子说话都很粗鲁，但你非得亲自验证这点吗', '废物测试');
INSERT INTO `talk_art` VALUES (10838, '“晕”', '                        1 是贫血吗？多吃些红枣\n2白痴，你觉得这样我就会给你人工呼吸吗？\n3生理学上说看到极度爱慕的异性在面前会有眩晕感是很正常的', '废物测试');
INSERT INTO `talk_art` VALUES (10839, '你滚。。。。 太丑陋了', '1我不嫌你丑\n2丑媳妇总得见公婆的，不用滚着去，走就行了\n3你跟柏杨大师一个理念呢，丑陋的中国人哦', '废物测试');
INSERT INTO `talk_art` VALUES (10840, '我叫我那妞洗衣服她说：', '1哪里像了，保姆很贵的好不好\n2哪家保姆要是像你一样还负责陪 SC，那你们保姆公司肯定会被顾客挤到爆\n3不会啊，保姆身材比你稍好一点点', '废物测试');
INSERT INTO `talk_art` VALUES (10841, '我从一开始对你有感觉 慢', '1哪里都没感觉吗？会不会是中风了？\n2你这个人最大的毛病就是虎头蛇尾\n3我踹你一脚不信你没感觉\n其他答案：据说婚姻是爱情的坟墓，难道说你已经开始幻想和我的婚姻了?(一直很想爱你)', '废物测试');
INSERT INTO `talk_art` VALUES (10842, '为什么要把电话号码告诉', '1因为你长得不像坏人哦（非原创）\n妹子：我就是坏人\n那太好了，我也是坏人，把号码告诉我下次约你一起去干坏事吧\n2因为。。因为什么来着。。糟了。。我一紧张把本来想好的理由给忘了。。（非原创）你看你害得我多尴尬，留个号码表示歉意吧\n3我是重案组的便衣警察，你的手机号码可能涉嫌一个感情诈骗案，请你配合我们的调查，不然就把你抓到麦当劳去请你喝咖啡哦', '废物测试');
INSERT INTO `talk_art` VALUES (10843, '我：“我对你妈妈没兴趣', '她：“好啦，没人要你有兴趣。”\n1你经常对我眉来眼去的，现在的意思是你不承认自己是人咯？\n2但我对你爸爸比较感兴趣，特别是他的银行存款数额\n3能把你教成这样足可见慈母多败儿，我可没兴趣知道她是怎么宠坏你的', '废物测试');
INSERT INTO `talk_art` VALUES (10844, '你省省吧..', '1你真好，就会替我着想，那我就听你的省了\n2省下来以后留给咱孩子吗\n3恩，省下来等跟你结了婚都给你是吧，想得美哦', '废物测试');
INSERT INTO `talk_art` VALUES (10845, '多给我带点巧克力回来，', '1我会跟巧克力说让它把我带回来的，你不用流口水了\n2今天想玩巧克力吗，那你洗干净脱光了好好在家等我哦\n3我更愿意相信流口水只是幌子，你是想和我过情人节的瘾呢', '废物测试');
INSERT INTO `talk_art` VALUES (10846, '公交车站用什么样的开场', '1（自掏一块钱假装从地上捡起过去搭讪）：你好，你的钱掉了\n妹子：谢谢你\n怎么谢，请我吃饭就不必了，我现在比较忙，如果你确实想报恩就留号码给我，下次有空再一起吃吧\n2（自掏一块钱假装从地上捡起过去搭讪）：你好，你的钱掉了\n妹子：不是我掉的\n天哪，想不到在这个道德沦丧的社会竟然还有你这样既不贪财又长得好看的女孩子，我喜欢跟内在美丽的人交朋友，请你无论如何要留电话给我哦\n3 靠近目标：你好，请问 XX 路是不是在这坐\n妹子：是的\n哇，一出门就碰上好人了，上次问路一个丑丑的女人都不理我，看来外表和内在果然是成正比的\n妹子：BLLBLBLBLBLBL\n我对这边不怎么熟，等下万一迷路遇不到你这样的好人就麻烦了，善良 妹子,要不留个电话给我就可以随时问你路了，你号码多少呢？ 1453 要什么钱，饭店服务员一看你的样子都吓坏了，哪个还敢过来收钱2行，你给我捏个脚我就考虑给你买单\n3我倒是想买单，就是没带钱，要不咱们看看老板肯不肯收下你打工抵债', '废物测试');
INSERT INTO `talk_art` VALUES (10847, '可是我不习惯把电话号码', '1所以我才冒着被拒绝的危险来帮你养成一个能多交朋友的好习惯哦，感动吧？\n2那告诉我 QQ 号也行啊\n3不用告诉我，直接拿我电话拨过去就行\n其他答案：你把号码告诉我，我们就不是陌生人了（一直很想爱你）', '废物测试');
INSERT INTO `talk_art` VALUES (10848, '我：“你妈妈是哪个单位', '她：“你管户口啊，问这么多。”\n1不是，我是对哪个单位的员工会比较容易生怪胎感兴趣\n2公务员可是铁饭碗呢，我决定从一名优秀的户籍调查员先做起，先从你练练手\n3实际上，我是想先了解你是否富二代，才好决定我们的进一步发展', '废物测试');
INSERT INTO `talk_art` VALUES (10849, '把你当狗养（开玩笑要她', '1你平时都喜欢关起门来 TD 狗吗？\n2你打算每天帮我洗澡吗？你好色哦~~\n3这就是你找的抱着我睡觉的理由吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10850, 'DOTA2 的几个英雄，我怎么', '1没关系亲爱的，我想它们也不认识你\n2因为暴雪深知人性的喜新厌旧，所以给你点全新的感觉\n3你不知道真正的英雄都是做好事不留名字的吗', '废物测试');
INSERT INTO `talk_art` VALUES (10851, '知道我中午吃的什么吗？', '1你说的这几个东西（居然还有网）要想集合在一起吃的话，只有一个地点比较会出现------垃圾桶\n2那是，网先被你咬散，然后缠着青菜和菠萝再被番茄的汁盖上一层迷彩，吃起来不纠结就是奇迹\n3最近不吃鸡翅膀了，一吃就老忍不住想起你的胳膊，搞得我欲罢不能的。。。', '废物测试');
INSERT INTO `talk_art` VALUES (10852, '你还是做我哥哥吧', '1你是不是韩剧看太多了\n2好呀，妹子先给哥哥抱抱\n3看过射雕英雄传的都知道你在拿郭靖黄蓉暗示我\n其他答案：情哥哥吗？吓，竟然试图勾引我！（一直很想爱你 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10853, '\\', '她：“是的，这是我的强项。”\n1我不这么认为，我觉得你的强项是吃饭\n2我推荐你常住富士山下，这样小日本的风景都会被你煞完的\n3下次我要挖谁墙角，在 TA 们约会的时候把你带去搞破坏肯定好使', '废物测试');
INSERT INTO `talk_art` VALUES (10854, '我的幸福跟你没关系', '1我知道你为我好。。。你不想拖累我是吗？\n2那跟谁有关系？毛主席？\n3恩，你的幸福跟中国残疾人救助协会有关系，是脑残部的哦\n其他答案：你的性福掌握在自己的指间？(   ', '废物测试');
INSERT INTO `talk_art` VALUES (10855, '我只想认认真真的谈', '1你说的是谈生意还是谈恋爱？\n2那我们现在把影响我们的身外之物都抛却，手机都关掉，衣服都脱掉，心无旁骛全神贯注地谈吧\n3我只想认认真真的做', '废物测试');
INSERT INTO `talk_art` VALUES (10856, '想搭讪的话，建议你换一', '1你可能误会了，我就是想问个路而已。。。\n2我对你的建议很感兴趣，讲来听听，讲得好可以考虑留电话给你哦\n3就算要打我枪也别跟我提关于搭讪的建议，我有 20 万个军师呢（慎用） \n其他答案：喔，你想认识我也不用这么含蓄的嘛\n想让我搭讪你，你再温柔点，我倒考虑一下\n我正努力找个搭讪你的理由，你努力表现下我\n第一次耶，你教教我啊\n明明是你先用眼睛搭讪我的，我只不过给你一个追我的机会而已\n哇，你好漂亮，呵呵，这样的方法行不？\n靠，不是吧，这都被你发现了，你真是我的肚子里的蛔虫啊\n喔，你等一下然后再用同样的方法搭讪一次，保证目标笑一个怎么够呢，像你这么聪明的，我得换几个啊\n好啊，你给我你号码吧，下次你在哪，给个电话我，我好去搭讪你', '废物测试');
INSERT INTO `talk_art` VALUES (10857, '我觉得我还是做你姐姐好', '1你喜欢玩角色扮演吗?去情趣店买点道具会更刺激呢\n2你怎么知道我喜欢姐姐型的女孩子，很用心哦\n3你虽然看上去老点，但请相信我绝不会嫌弃你的\n其他答案：\n现在正流行姐弟恋。没想到你好这个(从不善解人衣 )\n我姐姐最喜欢亲我脸了~ 来，亲个先~ 好的，姐姐，我要抱抱(一直很想爱你)', '废物测试');
INSERT INTO `talk_art` VALUES (10858, '我怀孕了，不是你的', '1亲爱的，你太棒了，把它生下来卖给人贩子我们能狠赚一笔哦\n2看你最近老是往外跑，我就知道不对，这下果然出事了，是隔壁家养的那条大狼狗的吗？\n3好的，先商量一下，咱们讹孩子他爹多少钱合适呢？\n其他答案：那你知道是谁的吗？（一直很想爱你 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10859, '你把我和你家小猫做比较', '1那你想我把你跟隔壁胡同的那只整天翻垃圾吃的野狗比较吗？\n2确实可恶，我这是在侮辱我家小猫\n3没有可比性，我又不会 TD 我家小猫', '废物测试');
INSERT INTO `talk_art` VALUES (10860, 'E 博士的废物测试来了。', '博士：你会向人家献菊花咩1菊花貌似是扫墓时用的。。。\n2可以呀，但我有洁癖的。。。先用硫酸给你消下毒才行\n3白天是教授，晚上是禽兽，称号是博士，爱好是玩屎(慎用)', '废物测试');
INSERT INTO `talk_art` VALUES (10861, '你怎么天天出去鬼混啊？', '1我知错了，以后每隔一天去才去鬼混好吧\n2有你在我怎么出去鬼混，鬼都被你吓跑了\n3不出去，难道你让我在家鬼混吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10862, '妹子 说：想跟我约会呀？', '1是呀，最近准备写一个分析剩女心理的论文，想跟你取取经\n2你说对了一半，我的最终想法是 TD\n3不是，我一个人上街怕被人打劫，有你陪着，歹徒就不敢靠近了', '废物测试');
INSERT INTO `talk_art` VALUES (10863, '向 妹子 要号码,结果 妹子', '1如果你不打算换 QQ 号的话，那把 QQ 留给我也行\n妹子Q 也要换了\n这么彻底的换法。。。难道你是。。。办假证的吗（非原创），我正好要办个帅哥证你给我留个联系方式呗\n2我们认识的时候你是在用这个号码，所以上面寄宿着我们的缘分啦，无论如何请把这串幸运数字报给我，我照着去买彩票中了分你一半怎么样！\n3没关系的，你把这个号给我至少在你换号前我们还能聊天哦，聊得开心了你再把新号码告诉我就是了', '废物测试');
INSERT INTO `talk_art` VALUES (10864, '\\', '呵呵，你好，我不想认识你！1你知道吗，世界上没有不认识的人，只有还没认识的朋友，让我们彼此多一个朋友好吗（非原创）\n2我知道自己就这么错过肯定会留下遗憾，所以才鼓起好大勇气过来的，让我认识你一下好吗（非原创）\n3那你想干嘛，让我请你请饭吗？给我电话号码才能约你哦，你怕我狂打你电话吗，放心好啦，我一天最多也就只打 20 来个（非原创）', '废物测试');
INSERT INTO `talk_art` VALUES (10865, '我只想找一个真心对我的', '1要把心掏出来对着你？那太血腥了，不然我去菜市场买个猪心来对着你行吗\n2为了证明我是真心，我不介意去照个心电图给你看，绝不是人工心脏\n3我也想找个能真心接受我爱的人，你是吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10866, '你怎么这么笨，我们相处', '1你没听过笨鸟先飞吗？不笨怎能让我的鸟先飞到你的窝里去\n2为了怕你理解不了我的话，我一直在辛苦地配合着你的智商，你却完全感觉不到，竟然比我预想的还笨啊。。。。\n3你想太多了，其实我没有打算跟傻瓜相处的意思', '废物测试');
INSERT INTO `talk_art` VALUES (10867, '我看你是闲的没事情做', '1哪里会没事情做，我不正在忙着逗你玩吗\n2对我调查得很仔细哦，不过你是怎么知道我爱吃咸的呢？\n3你想为我们找点什么事情做呢，我不是那么随便的人哦', '废物测试');
INSERT INTO `talk_art` VALUES (10868, '女的对男的说：你很单纯', '1是呀，上次有个纯净水公司还来找我谈代言的事呢\n2任何复杂的人跟你一比，都会单纯的\n3哪里是单唇，我有两片嘴唇好不好\n其他答案：呵呵，这么夸奖我，我怎嚒好意思呢？（一直很想爱你）', '废物测试');
INSERT INTO `talk_art` VALUES (10869, '女同学 总叫我给她买酸奶', '1你自己就有两个酸奶啊，还要我买？还嫌不够酸就切片柠檬贴上去嘛\n2给我捏个一年脚再考虑考虑\n3拿钱来啊，这里到小卖部这么远的距离，快递费很贵的哦\n其他答案：你想吃什么补什么吗？ (一直很想爱你)', '废物测试');
INSERT INTO `talk_art` VALUES (10870, '不愿意说算了，不稀罕', '1你的态度让你失去了做一个狗仔队明日之星的机会\n2物以稀为贵，你给点贵重的东西我才好说啊\n3别急嘛，我不愿意说，但愿意为你唱一段：苏三。。离了洪洞县。。', '废物测试');
INSERT INTO `talk_art` VALUES (10871, '你没有尽到做弟弟的责任', '1那你脱裤子吧，我让弟弟尽尽责任 \n2这位阿婆，请你不要装年轻好吗\n3对不起，我没尽到责任，请问。。。弟弟的责任是保护姐姐的处女膜吗？\n其他答案：\n谢谢提醒，教教我吧，我免费让你教我。。。（游戏人生）\n我没姐姐，我只尽到儿子的责任。你要不跟我爸妈说一下？不过现在也晚了。有也只能有妹妹了（游戏人生）', '废物测试');
INSERT INTO `talk_art` VALUES (10872, '不知道你是不是对每个人', '1那是肯定的，但对每个人收取的服务费是不一样的，像你我就会打九折\n2我对其他残障人士也是一样好的\n3你觉得我长得像雷锋吗', '废物测试');
INSERT INTO `talk_art` VALUES (10873, '（她调侃我）今天吹的哪', '1东南西北四个风，你要胡哪个我就给你哪个\n2你感觉到的不是风，而是帅哥强大的气场\n3那得看谁吹，如果是你吹的话，老人家肯定要中风的\n其他答案：梅超风。。。（游戏人生）\n我不喜欢吹风，我喜欢吹箫。。。（游戏人生）\n凉快不？涂点口水吹一吹更凉快了。。。来，你先吹。。。（游戏人生）', '废物测试');
INSERT INTO `talk_art` VALUES (10874, '你很奇怪呀', '1帅哥的心理世界你是永远无法理解的\n2我去猪圈喂猪的时候，它们看我应该也很奇怪\n3那你帮我申报个吉尼斯世界奇人纪录吧，得了奖请你吃饭', '废物测试');
INSERT INTO `talk_art` VALUES (10875, 'QQ 上主动找女孩聊天的时', 'q 好像中毒了，神奇的是它竟然会自动联系好友里那些不好看的女孩子！\n3 最近我接了一个私人沙龙，要找个主持，觉得你不错，想测试下你的沟通能力（BY 小狗）', '废物测试');
INSERT INTO `talk_art` VALUES (10876, '\\', '你为什么这样做,是什么意思\n1我拼命想用理智控制住自己的嘴唇，但一股莫名的力量让我无法自拔，因为。。我爱你\n2我只能吻你的嘴，因为你全身上下就头部没衣服挡着\n3那你想我哪样做？进挪是要一步步来的，TD 是最后才做的事\n哦\n其他答案：没什么意思。不就是情不自禁，一失足成千古恨嘛。（游戏人生）没事，不会怀孕的。我妈说的。（游戏人生 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10877, '你是 AM，我是 FM，我们根', '1但是我们的终点在同一个地方，你不觉得这是一种缘分吗\n2还好不同路，不然被我的粉丝们看到你和我一起，你还不被打死掉\n3太相似的频道是无法并存的，你不知道性格能互补才是长相厮守的门票吗', '废物测试');
INSERT INTO `talk_art` VALUES (10878, '我鄙视你', '1笔试是我的强项，在学校每次都考很高分的哦\n2有一句谚语很适合你------狗眼看人低\n3你得先戴副墨镜，免得被我的光芒灼伤眼睛', '废物测试');
INSERT INTO `talk_art` VALUES (10879, '妹子： 老男人 （用老女人', '1你的意思是想夸我成熟有味道吗？但我不得不对你的表达能力表示遗憾\n2我是 OLD 男N（奥德曼），你就是怪兽，为了不吓到其他地球人我要消灭你\n3老处女/老剩女', '废物测试');
INSERT INTO `talk_art` VALUES (10880, '女：“你這個人有一點主', '1这。。你的话让我很纠结，我得考虑一下\n2好吧，马上过来给我舔脚\n3你跟我在一起本来就会自卑，我要是再强势点你怎么活哦？', '废物测试');
INSERT INTO `talk_art` VALUES (10881, '\\', '1什么关系？我们想我们还没发生关系\n2最近迷上了动物世界，刚给你模拟的是老鹰给小鹰喂食的场面，有意思吧？下次咱们模拟猴子交配怎么样？\n3亲爱的，你希望我们是什么关系呢\n其他答案：当然是纯洁的男女关系了。（游戏人生）', '废物测试');
INSERT INTO `talk_art` VALUES (10882, '妹子 说：没有房子，没有', '2012 就要来了，到时那些现在大家看重的房和车之类也许会在顷刻间灰飞烟灭，但即使这样，我还是坚信可以看到我们的感情经过世界末日之后依然飘荡在天堂上空的画面\n2感谢裸婚时代的全体剧组工作人员\n3亲爱的，还没那么糟，除了感情，我们还有生殖器官呢（慎用）', '废物测试');
INSERT INTO `talk_art` VALUES (10883, '女孩一直说：我很笨的', '1对于这种公认的事实，就不需要一再重复了\n2你何止笨，你简直就是笨得可爱\n3不用在意，亲爱的这很正常，在我面前任何人都会显得笨点的', '废物测试');
INSERT INTO `talk_art` VALUES (10884, '干嘛？想约我吃饭？', '1你觉得自己长得像巴菲特吗\n2不是，想约你开房\n3对呀，你都好久没请我吃饭了', '废物测试');
INSERT INTO `talk_art` VALUES (10885, '今天有个傻 B 男，说我除', '1你除了嘴贱一点，没啥优点了\n2你除了审美观有问题一点，没啥缺点了\n3还是你比较帅，一到宠物市场，那些狗都往死了蹭你的腿', '废物测试');
INSERT INTO `talk_art` VALUES (10886, '\\', '1我这么说可能直接了一点，但。。。你搭讪的这招连我都已经很久不用了。。。\n2真奇怪，我也有这种感觉，莫非你就是我的百分百女孩（接村上春树百分百女孩惯例）\n3是不是梦里常出现，很多 妹子 都爱梦到我这个类型的。。。', '废物测试');
INSERT INTO `talk_art` VALUES (10887, '你不要对我笑（我微笑看', '1不用不好意思，其他 妹子 也跟你一样无法抵御我的微笑\n2对你微笑，纯属礼貌，你比较喜欢无礼的人吗\n3但。。。。我现在哭不出来呀', '废物测试');
INSERT INTO `talk_art` VALUES (10888, '女生经常在我面前抱怨：', '1你听过一个成语叫庸人自扰吗\n2我或许能帮你出出主意，你的烦恼是到底怎么才能泡到我是吗？\n3不知道为什么，听你这么说我心底油然升起一阵快感', '废物测试');
INSERT INTO `talk_art` VALUES (10889, '你是坏人，不理你', '1你这识别坏人的能力很了不起，但要小心 FBI 把你抓去帮他们破案哦\n2明明知道我是坏人，竟然还敢不理我，你活腻了吗？\n3男不坏，女不爱，你对帅哥表白的方式很特别哦', '废物测试');
INSERT INTO `talk_art` VALUES (10890, '看你不爽', '1拿个振动棒一边用一边看我肯定会爽\n2很正常，人们面对能看到但得不到的都会不爽的\n3不爽的话就要考虑换个牌子的了，那种带护翼超薄透气型的比较爽', '废物测试');
INSERT INTO `talk_art` VALUES (10891, '你说话真有意思', '1带你去客房见识下会更有意思的\n2你的脸长得也很有意思\n3觉得有意思吧。。。那就把演讲费给结了吧\n其他答案：慢慢你回发现有人会更加有意思(菜虫 pua )\n难道你开始爱上我了？(一直很想爱你 )', '废物测试');
INSERT INTO `talk_art` VALUES (10892, '你以前是个坏蛋，为什么', '1你以前来大姨妈都是 XX 号，为什么这个月来得这么快啦？\n2任何坏蛋跟你比较起来都会显得变好很多\n3这几天蛋疼，坏不起来了', '废物测试');
INSERT INTO `talk_art` VALUES (10893, '你无耻', '1让我咬一口，你就知道有没有齿了\n2一个导演说过“人不能无耻到这个地步”，而我又偏偏喜欢挑战权威，所以拿你来实践一下\n3你说的很对，而你又这么卑鄙，我们正好配一对呢', '废物测试');
INSERT INTO `talk_art` VALUES (10894, '能不能不谈工作上的事情', '1我非常明白你面对我这样一个帅哥时迫不及待的心情，但我认为男人还是应该以事业为重，你认\n为呢2那谈什么？谈恋爱？\n3可以，那我们谈谈生理卫生吧', '废物测试');
INSERT INTO `talk_art` VALUES (10895, '就你那鸟样', '1如果你试过我的鸟就不会这么说了\n2恩，我承认比不上你的样子，你才是大陆上的王者------霸王龙\n3说别人前先自省一下，鸟至少还能飞，鲍鱼除了湿漉漉还能干嘛？', '废物测试');
INSERT INTO `talk_art` VALUES (10896, '我的床上有三个公仔，我', '1你家没洗手间吗？\n2你的意思是想过来祸害我吗\n3想不到你喜欢玩 4P......', '废物测试');
INSERT INTO `talk_art` VALUES (10897, '你太老实了', '1有眼光！我那些粉丝们都说我长得很老实哦，其实我有一颗不羁的心啦\n2不要以为夸我一句就会请你吃饭！至少。。。你得再夸一遍\n3诚恳邀请你跟我到开个房去聊聊，我要给你展示更老实的一面', '废物测试');
INSERT INTO `talk_art` VALUES (10898, '你真的很不要脸也，恶心', '1没错。。。我就是千寻的朋友-----无脸男\n2你这脸要得太多了，搞得脸皮厚得跟千层糕似的\n3你错了，我不但要你的脸，还要你的身体', '废物测试');
INSERT INTO `talk_art` VALUES (10899, '这顿饭你准备欠多久', '1准备欠到你良心发现要主动买单的时候\n2山无棱，天地合，才敢请吃饭\n3这次 SC 你准备欠多久', '废物测试');
INSERT INTO `talk_art` VALUES (10900, '一边去', '1如果要爱爱的话，在哪边都一样呢\n2我做不到，我要是去那边了，万一等下哪辆汽车撞到你，谁把你送医院去呢\n3请不要用对宠物的口吻跟你的主人说话\n其他答案：哪边？你的左边还是你的右边？（从不善解人衣 ）', '废物测试');
INSERT INTO `talk_art` VALUES (10901, '那你又不找我玩', '1玩什么呢？床上摔跤吗？\n2想跟帅哥玩必须要学会主动点呢\n3我陪玩费用很贵的哦', '废物测试');
INSERT INTO `talk_art` VALUES (10902, '你不要把我想的太好', '1你多虑了，我的思想从来没有把你跟好字联系起来过\n2你大可不必这么谦虚了，相信我，我完全没有要向你借钱的意思\n3看来你也怕被我发好人卡呀（慎用）', '废物测试');
INSERT INTO `talk_art` VALUES (10903, '男的说 :你是一只鸡', '1如果你长了两只鸡，那推荐你去马戏团表演，我可以做你经纪人\n2那也比你做鸭的好，还是不要钱的鸭。。。\n3你的意思是想表达我是一个像祖国的地图那么雄伟的人吧', '废物测试');
INSERT INTO `talk_art` VALUES (10904, '我不想和你走在一起', '1可我不想跟你抱在一起，睡在一起更不行\n2不用担心，亲爱的，以我的内涵，就算是你和我走在一起，别人也不会因此而看轻我的\n3你的意思是想和我一起打个的吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10905, '我们是两条平行线，永远', '1可以相交的，但我要戴套套\n2我不觉得这样保持平行的距离就能产生你的美\n3亲爱的，如果你那条线前面忽然出现一根电线杆呢', '废物测试');
INSERT INTO `talk_art` VALUES (10906, '我说你不好你就是不好', '1君要臣死臣不得不死\n2还好你没说我阳痿\n3不好后面加个泡字就比较贴切了\n4你说得对，那恳请你说我变长\n5男不坏女不爱，这是你表白的一种方式吗？我很赞同', '废物测试');
INSERT INTO `talk_art` VALUES (10907, '你眼睛多少度（本人是近', '1四十五度仰望天空，泪流满面\n2四十五度俯视胸部，鼻血长流\n3我视力即使再好，也不可能将你看成一个美女', '废物测试');
INSERT INTO `talk_art` VALUES (10908, '你小气！抠门~！', '1我开门都用钥匙，从来不用抠的\n2你怎么不明白呢？我这么节省还不都是为了我们能有个美好的将来\n3那是肯定的，论大方我哪能跟你比，借点钱来花花呗', '废物测试');
INSERT INTO `talk_art` VALUES (10909, '我有什么值得你投入的', '1你身上有一个缺口值得我长线投入\n2虽然红十字会变了，但我对残障人士的爱心会一直投入的\n3你的自知之明，值得我投入', '废物测试');
INSERT INTO `talk_art` VALUES (10910, '你太以自我为中心了！', '1你玩过旋转木马吗，只要能看到你开心地坐在马背上，我愿意做中心的那根默默无闻的柱子\n2你希望我以你为中心吗？免费给我捏一年的脚就考虑一下\n3你说得对，我得改改，明天起以盐浴中心为目标吧', '废物测试');
INSERT INTO `talk_art` VALUES (10911, '睡觉好冷', '1想要我抱就直说，别拐弯抹角的\n2你肾虚吗？\n3我小时候尿床也是像你这样半夜冷醒的', '废物测试');
INSERT INTO `talk_art` VALUES (10912, '没有情人的情人节，多少', '1没有猪骨的猪骨汤，多少会有欺诈的意思\n2没有动车的动车轨，多少会有相信的奇迹\n3没有妞泡的泡学网，多少会有 PUA 的捧场', '废物测试');
INSERT INTO `talk_art` VALUES (10913, '你臭美！', '1不可能呀，我出门刚喷了香水，怎么会臭\n2你给我来个香的看看\n3臭美也是需要资本的，像你就没办法了', '废物测试');
INSERT INTO `talk_art` VALUES (10914, '你找我干什么啊', '1找你非得要干什么吗？你能不能别一天到晚这么色色的\n2干你啊\n3你期望我跟你干点什么呢？你带够钱了吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10915, '你的 qq 不是用来聊天吗', '1不是，是专门拿来试病毒的\n2一般不聊天，都是用来激情视频\n3你平时都喜欢对着一辆国产小汽车自言自语吗？', '废物测试');
INSERT INTO `talk_art` VALUES (10916, '你为什么总是爱拿我跟你', '1我想给朋友们树立一个完美的榜样\n2因为你跟我比不了，所以只能跟 TA 们比咯\n3亲爱的，有压力才有动力，我在帮你提高自己呢', '废物测试');
INSERT INTO `talk_art` VALUES (10917, '女生：你怎么总这么贱，', '1你觉得那些被歹徒强暴的女孩子有罪吗？\n2主要是你太优秀，很多女孩子不服气想挑战你而已，结果我成了无辜的牺牲品\n3亲爱的，你骂的不是我一个人，全世界的 PUA 都被你骂了。。。。（慎用）', '废物测试');
INSERT INTO `talk_art` VALUES (10918, '女：你套路深', '   男：全身瘙痒骨头迷，色字头上一把刀，你这种女生，我不喜欢套路', '废物测试');
INSERT INTO `talk_art` VALUES (10919, '妹子：每次和你讲话都牛', '1你的话我只同意一半，你长得确实像牛没错，但我可不是马脸哦\n2你可以尝试去牲口圈里跟它们沟通，或许感觉会对路点\n3要是对上就变成 KISS 了,那我还不被你把便宜占光了', '废物测试');
INSERT INTO `talk_art` VALUES (10920, '女：你套路深', '    男： 没有套路的人生就像没有高跟鞋的小短裙，少了点湿润和趣味', '废物测试');
INSERT INTO `talk_art` VALUES (10921, '女：我们还不熟', '    男：五秒钟过去了，相信我们已经越过了陌生', '废物测试');
INSERT INTO `talk_art` VALUES (10922, '女：你真美得像花儿一样', '男：哈哈我就是花    女：把你摘回家，浇水灌溉，让你含苞待放', '废物测试');
INSERT INTO `talk_art` VALUES (10923, '女：我靠你老司机啊', '   男：带你飞上秋名山， 七近七出，九浅一深，到达欲望的巅峰', '废物测试');
INSERT INTO `talk_art` VALUES (10924, '女：为什么约我呀', '男：毛主席说：实践是检验真理的唯一标准，所以，让我们遵从组织指示，升级下革命的友谊', '废物测试');
INSERT INTO `talk_art` VALUES (10925, '女：你为什么老是给我夹', '    男：我听说带女朋友参加不熟悉的饭局，不能只顾自己埋头大吃，要给她夹菜，因为她很可能因为害羞而只能看看远处自己喜欢的菜，谁叫我体贴呢', '废物测试');
INSERT INTO `talk_art` VALUES (10926, '女：你好幼稚（小）', ' 男：成熟与不成熟，只和男人的性格经历与心理年龄相关，和身体年龄无关，最好的体现就是能控制自己的情绪，顾及女性的感受。就像我呵护你一样，现在感觉我是不是熟透了', '废物测试');
INSERT INTO `talk_art` VALUES (10927, '女：挺有才啊帅哥', '  男：恩，文能提笔控萝莉，武能床上定人妻', '废物测试');
INSERT INTO `talk_art` VALUES (10928, '女：我们为什么要在一起', '   男：反正不是为了分开', '废物测试');
INSERT INTO `talk_art` VALUES (10929, '很好，真乖啊，好好保持', '1你说的没错，我比你乖多了，你是坏小孩\n2你觉得自己是在马戏团驯兽吗\n3海豚表演得好还有鱼吃呢，我呢？别说给我吃你哦，我是回民\n4你想保持我们的关系就得持续请我吃饭\n5我看日本 AV 里面女优达到高潮时就会跟男优讲你说的这句台词呢（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10930, '女：傻哥哥', '1是啊，就是因为傻才会跟你扯上关系\n2我被你给传染了傻瓜的病毒\n3傻妹妹，你已经那么傻了，哥哥不能再傻了，不然以后我们生小孩肯定傻得不行', '勾起好奇');
INSERT INTO `talk_art` VALUES (10931, '最近刚网搭的 妹子。', '因为第一次开场时，讲了个故事，现在每天网上一见面就让我讲故事。 我说给你讲故事，有什么好处吗。我送你一本故事大王，自己看吧。 但是 妹子，根本不理，还是喊着讲故事。\n1你先告诉我，你是真想听故事呢，还是喜欢我的声音呢？\n2现在我可以讲给你听，但你要答应我以后由你负责将我的这些故事讲给我们的孩子听\n3天桥下听相声还给钱呢，何况听大帅哥讲故事，你先把前面讲故事的费用给我再给你接着讲', '勾起好奇');
INSERT INTO `talk_art` VALUES (10932, '我从不喜欢自傲的人 我说', '1你也挺自傲，难道拿大嘴巴子抽自己？\n2确实不重要，现在都很流行在网上抄那些无病呻吟的说说呢\n3你好像分不清自傲和自信的区别', '勾起好奇');
INSERT INTO `talk_art` VALUES (10933, '女：坏哥哥', '1哪里坏了？我还没对你动手呢\n2我这么坏都是为了衬托你的好。。。好白痴\n3男不坏，女不爱。你可别爱上我，我会让你欲哭无泪的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10934, '妹子：你连这个都不知道', '1下次给你买一本百科全书做男朋友\n2我又不是百度知道，干嘛要知道\n3我不知道的可多了，比如你今天穿什么颜色的内裤我就不知道\n其他答案：故意给你一个表现的机会而已（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10935, '你养得起我么', '1这个我还真不知道。。。现在狗粮很贵的\n2如果你有天不幸失去了行动能力成为植物人，我将很乐意跟你谈这个问题\n3痒得起，你把胳肢窝亮给我吧，保证痒得你欲仙欲死', '勾起好奇');
INSERT INTO `talk_art` VALUES (10936, '你怎么这么喜欢指使别人', '1因为我不喜欢被人指使啊\n2你这人做点事怎么斤斤计较\n3看来我有当官的潜质呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (10937, '女：不理你了，哼', '1哼是谁？你干嘛不理他了？\n2终于能清静一会了\n3那从今天起请叫我狗不理', '勾起好奇');
INSERT INTO `talk_art` VALUES (10938, '你吻过别的女孩没？', '1有啊，小时候经常吻我妈\n2你被别的男孩吻过没\n3有次吻了一个大美女，后来才知道他是人妖，这算不算', '勾起好奇');
INSERT INTO `talk_art` VALUES (10939, '我有男朋友了，即使我们', '1原来你是练杂技的\n妹子：?\n只有练杂技的才能一脚踩两条船呢\n2没关系的，我会去泡其他女孩子，一直等到你跟他分手的那天，再与她结婚\n3我不会破坏你和你男友的感情，也不会强迫你跟他分手，即使只跟你做泡友我也心甘情愿（慎用）\n其他答案：我们？当然可以了，那我们说好了，你白天陪他，晚上陪我(一直很想爱你 )\n当然可以啦，可是我怎么才能相信你是真心想做我的好朋友呢，这样吧，先请我如此如此这般这般', '勾起好奇');
INSERT INTO `talk_art` VALUES (10940, '过节没点气氛', '1你在街上裸奔肯定会给节日增添不少气氛的\n2那要看什么节了，要是过清明节搞得很有气氛会很诡异啦\n3我放个屁给你制造点气氛吧', '勾起好奇');
INSERT INTO `talk_art` VALUES (10941, '女：去去去', '1我郑重地告诉你，请不要用赶鸡的语气跟我说话\n2唱：欧蕾欧蕾欧蕾！（瑞奇马丁-The Cup of Life）\n3去哪？开房也不用这么着急啊', '勾起好奇');
INSERT INTO `talk_art` VALUES (10942, '你是不是经常这样吻女孩', '1今天是第一次主动尝试，感觉太好了，难怪平时那些女孩子都这么吻我\n2吻你一次舌头都要被融化了，哪还能经常吻别人啊\n3你猜疑我的同时也在贬低自己呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (10943, '她说现在愿意做我妹妹，', '不过现在还没有成为女朋友就要我买这买那的，把我当银行了。像这种情况要怎么处理？\n这几天又说：买月饼寄给我，前几天说：买部相机当作礼物送我。晕死了，求解，这是何意？\n1买什么月饼，我觉得你应该直接住到月亮上去，天蓬元帅那么有钱最适合你了\n2买什么相机，你已经很像鸡了好吧\n3妹妹，你叫我买的这些东西我觉得都没有你贵重，我最想买的那个还是你，简称-------买你妹！ \n其他答案：把钱寄过来我马上帮你买（brethart ）\n买个套套送你要不要？(一直很想爱你)', '勾起好奇');
INSERT INTO `talk_art` VALUES (10944, '你真讨厌', '1你知道中国女人跟外国女人最大的不同在哪吗？\n妹子：有什么不同\n外国女人表达爱意是说我爱你，中国女人是说你真讨厌（非原创）\n2真有那么讨厌吗？这是商机哦，哪个男人要去相亲，雇我坐隔壁桌衬托他，女方马上就能感觉男方很讨喜呢\n3背转身对她：你还能面对我说讨厌，而你讨厌的气场使我只能转过来跟你说话呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (10945, '干嘛说给你听啊！', '1以你的八卦个性，不说出来会憋得很难受，我心地善良所以打算免费帮助你\n2你不怕麻烦的话写出来也可以的\n3你面对我会害羞吗？那我把电话的录音功能打开你对着它说也行的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10946, '妹子：看小说有什么用啊', '1可以提高自己的技巧啊，比如金瓶梅\n2书中自有颜如玉，书中自有黄金屋\n3因为小说不会一天到晚问我这个有什么用那个有什么用', '勾起好奇');
INSERT INTO `talk_art` VALUES (10947, '我：＂你已经被这个时代', '她：＂是我抛弃了这个时代＂\n1我替这个时代感谢你的高抬贵手\n2你这个狠心的坏女人，我隐约听到时代被你甩了后低低的抽泣声呢\n3抛弃它是因为我的出现吗？我其实不喜欢喜新厌旧的女人', '勾起好奇');
INSERT INTO `talk_art` VALUES (10948, '你真讨嫌', '1是啊，你一直站我身边，大家都会嫌我太没品味了\n2你又不是卖盐的，我怎么会跟你讨咸？\n3讨嫌总比你讨饭的好', '勾起好奇');
INSERT INTO `talk_art` VALUES (10949, '你真是个用下半身思考的', '1我长得像虾子吗？\n2那也比你用胸部思考好呢，因为我下半身够大，而你的胸部思考能力肯定有限。。。\n3不管哪里能思考就行，但我很感兴趣你知道什么叫思考吗？\n其他答案：那也要下半身感兴趣才会去思考，就你这样我想思考都难（黑魔导）\n你太高估自己了，像你这样的笨 Y 头，我用脚趾头思考就够了', '勾起好奇');
INSERT INTO `talk_art` VALUES (10950, '妹子：我最讨厌这样的书', '1我最讨厌没文化的人\n2那你喜欢哪样的书呆子？\n3那为什么你不看书也那么呆呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (10951, '自己 QQ 好友印象被人评论', '1你这个评价很中肯，我确实一眼就能看出别人假装好人，内藏祸心\n2你真了解我，我就是这么坏，最好离我远一点，小心自己伤不起\n3喂，你这自我简介填错地方了', '勾起好奇');
INSERT INTO `talk_art` VALUES (10952, '我说看着他说你挺善良的', '1我只是礼貌寒暄一下，你不用在意的\n2不善良才好，人善被人欺嘛，那么你肯定不会被人骑了\n3还好你诚实，作为回报，我答应你以后一定不会借给你钱的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10953, '我：所以说我们也算一种', '女：认识是一种缘分\n女：但可能不适合\n1我会给你时间适应的\n2也可能你爱我爱得要死我却不鸟你\n3实在不合适我会考虑适当降低自己的标准', '勾起好奇');
INSERT INTO `talk_art` VALUES (10954, '你知道吗,你真的让我很失', '1不要灰心，不要气馁，失败乃成功之母嘛\n2失望还好啦，没让你怀孕已经很不错了\n3我这招叫反撇，只用了前半段，效果看来很明显哦（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10955, '我：有什么好处', '女：你要什么好处\n1要什么都行吗？那我要你的银行卡和密码\n2让我想想还缺点什么，还缺个。。。女朋友，但可惜你不是我的菜，还是请我吃饭吧（非原创）\n3好像电影里肉体交易都是这个台词呢\n其他答案：1.（上下打量她）貌似我在你身上也捞不到没什么好处\n2.\n> 请我吃饭，得三包。哪三包？\n包吃包玩包什么什么。\n什么什么？\nbalabala（包暖床之类，或者扯其他的。天马行空的瞎扯能力很重要。）（如果她拒绝三包）三包都不行啊，那就三陪（从不善解人衣 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10956, 'XX,我没来参加你的聚会,你', '(我已经冷冻她 3 天了)\n1这种事生气有什么用，扁你一顿比较直接呢\n2什么聚会？哦~~~~你说的是上次吧，没关系啦，后来有别的美女参与弥补了你不在的空位，我们可以下次再聚嘛\n3我最近在卧床养病\n妹子：你病了？\n聚会被放鸽子气病的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10957, '你的感情经历太丰富了', '1正因为有这些经验我才能更好的呵护和关心你\n2没有你参与的感情经历再丰富也是苍白的\n3你怎么这么肤浅，只看那些外在的事情，你不知道我的 TD 姿势更丰富吗？（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10958, '不过我就是那么个女孩 不', '1你不去混帮派可惜寥寥\n2你是拆迁办的？\n3你是城管吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10959, '我最讨厌欺骗我的人了!', '1我最讨厌容易上当的人了\n2知道这个特点以后你男朋友想甩掉你可就好办了\n3以你的智商，岂不是要讨厌很多人？忙得过来吗？\n其他答案；1 谁把你的人给欺骗了？ \n2 一般我只欺骗别人的心（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10960, '见面后那个女孩确实很像', '但是我估计人家都会夸她 我想对她进行打压 偏偏说她不像 应该怎么说好呢\n1我觉得你长得很像一个明星，是。。。。还珠格格里的。。。。皇后！好有气质啊\n2平时走到街上会有人要你帮他签名吗？\n妹子：有/没有\n他眼睛有问题吗？/我可以满足你这个愿望，待会吃完饭买单时你帮我签个名吧\n3你外表果然像赵薇，但不知道你内涵怎么样，我得观察观察\n其他答案：有没有人说过你很像赵薇？\n妹子：balabala\n其实我只是想夸夸你，让你自信一点，因为我怕你在我这么完美的男人面前感到自卑。没想到你居然当真了。（从不善解人衣 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10961, '我：问多了 哈哈~（<b', '她：恩，不聊了，，~！88应该如何回答啊？\n1别急，我可以付你咨询费的\n2下次你也可以问回来哦，这养我们就可以扯平了\n3其实我在研究心理学啦，看不同的人对频繁被提问时的反应呢，下面我们可以正常聊了哦', '勾起好奇');
INSERT INTO `talk_art` VALUES (10962, '你再这么做，我不喜欢你', '1升级为爱我是吗\n2你再不让我这么做，我也不喜欢你了\n3心理学上讲女人说不要，往往就是还要的意思呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (10963, '我干什么也没人在意 你管', '1我会筹钱为你开个孤儿院的\n2管饭\n3你的意思是想表达自己很低调吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10964, '妹子：看你这么文绉绉的', '1摸自己的脸：皱吗？不会啊，我皮肤还是蛮光滑的\n2不，其实不用看就能感受到我极高的文化内涵\n3敢问小生何处文绉绉乎？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10965, '给我介绍的女孩子长的像', '1我从小就爱看还珠格格呢，所以你对我来讲比其他女孩子更有优势呢\n2赵薇都结婚生子了，你也要加把油了，我是来解救你的哦\n3我觉得你去参加超级明星脸很合适，我给你做经纪人怎么样，经纪费就免了，请我吃饭就行', '勾起好奇');
INSERT INTO `talk_art` VALUES (10966, '打车十公里过去找 妹子，', '给他惊喜，到楼下打电话，她说我睡了，你走吧，该怎么回复？ 先为你默哀，然后告诉你她的回应证明你单方面在两人感情里投资十公里路程的行为很挫1我没有回去的路费了。。。\n2你觉得我长得像召唤兽吗\n3睡觉没关系，走也没关系，但我没你家门钥匙，怎么走进去？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10967, '只是那么一说 没人会在意', '1我是人类，我很在意\n2你说猪的语言，人们听不懂当然不在意\n3照你这么说谁在意你谁就不是人？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10968, '妹子： 你好斯文喔', '1其实我也有粗犷的一面，你想见识吗\n妹子：想/不想\n亲她/你错过了一个占我便宜的好机会哦\n2谁跟你站一起都会显得斯文很多的\n3太神奇了，你怎么知道的？小时候叛逆的我最喜欢撕的就是作文书', '勾起好奇');
INSERT INTO `talk_art` VALUES (10969, '在和以前女同桌聊天时，', '她问我有没有女友，我兴致勃勃描述了一堆 \nPS：我对这个同桌有意思！~她来了一句:“她又喜欢你什么呢？”\n2我怎么知道，你得问自己啊\n3喜欢的优点太多，说不完，不过我可以告诉你她不喜欢我跟其他女孩子聊天，但我喜欢跟你聊天， 哈哈', '勾起好奇');
INSERT INTO `talk_art` VALUES (10970, '打压 妹子 后，对面说：不', '1你这语气就像是理发师在挑顾客呢\n2我可以去应聘狗不理的形象代言人了\n3谢谢你让我清静', '勾起好奇');
INSERT INTO `talk_art` VALUES (10971, '可你幽默过火了', '1你快去拿灭火器啊\n2这让你感觉压力很大是吗？我尽量试着降低幽默尺度吧\n3我听过张信哲的过火，但第一次听幽默过火', '勾起好奇');
INSERT INTO `talk_art` VALUES (10972, '她老是喊我：小子！', '1你是有多老？\n2叫老子干嘛\n3我不小哦，我的有这么大（双手比一个夸张的长度）\n其他答案：我记得有一个电影叫“那小子真帅！”。。。。。。 （一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10973, '我喜欢成熟稳重略带点幽', '1我喜欢活泼可爱略带点性感\n2喜欢我就直说，兜这圈子干嘛？\n3那你赚到了，我是成熟稳重狠幽默', '勾起好奇');
INSERT INTO `talk_art` VALUES (10974, '我的女友发信息给我说“', '她在测试我，看我会不会关心她，如果我回“怎么了”是不是进圈套了？应该怎么回？\n问怎么了属于正常的关心，不会掉进圈套，，你敏感过头了如果对方眼睛真的受伤，你的幽默回答会适得其反，但我针对你的问题还是给予回答\n1所以我才不推荐一直长时间盯着我的照片看，至少你得眨眼吧。。事已至此，赶紧趁着还没瞎再多看我几眼吧\n2你的眼睛要是真瞎了，就可以对我唱萧煌奇那首歌了\n3没关系的，我只要你的身体能用就行', '勾起好奇');
INSERT INTO `talk_art` VALUES (10975, '我快要离开了，你舍得我', '1舍不得，至少得请我吃顿饭再走吧\n2狮子舍得把幼崽推到山脚下让它们自己爬上去，鸟儿也舍得把幼鸟推出窝外让它们自己飞翔，我当然也舍得看宠物离开呀\n3你轻轻的走，正如你轻轻的来，不带走一片云彩，请还我那笔欠债（感情债）\n其他答案：开学时我从家里出来，我妈也说过类似这样的话，不过她是这样说的（模仿语气，不用我说怎样的语气了吧？）：哎，又要好久才能再看到你了。(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (10976, '我认为你根本就不了解我', '1我也这么认为，你脱掉衣服让我了解下吧\n2你了解我吗，不然怎么知道我不了解你呢\n3不会啊，我至少了解你的想法是认为我不了解你啊', '勾起好奇');
INSERT INTO `talk_art` VALUES (10977, '\\', '1我们现在就要孩子会不会太早了?\n2你看上去比我小这么多，我像孩子，你岂不是要像精子\n3你是不是有恋童癖啊', '勾起好奇');
INSERT INTO `talk_art` VALUES (10978, '\\', '1你现在才突然发现我其实挺好的吗\n2新买了婷美内衣?（做女人，挺好）\n3那我就放心来跟你拿分手费了', '勾起好奇');
INSERT INTO `talk_art` VALUES (10979, '你这么经不起吓啊', '1你妈妈没告诉你，不能随便受精吗\n2再经得起吓的人对吓人者的长相承受力也是有极限的\n3你吓吓我也就算了，可千万别上街啊，咱们城市会戒严的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10980, '你以为我会再听你的吗', '1你以为我还会再讲给你听吗\n2一般人肯定是不会了，但以你的智商还真难讲\n3你是要把耳朵堵起来吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (10981, '是吗？你还不了解我呢，', '1不用把我的夸奖放在心上，其实我只是礼貌而已\n2那就慢慢了解吧，先从三围开始\n3所以我正打算“深入”了解你，请先脱掉裤子（慎用）\n其他答案：哦？那你知道我印象中的你是怎样的吗？(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (10982, '老爱你了', '1我不要等老了才爱，我要你现在就爱\n2我姥姥肯定爱我啦，不过她看到你肯定也会爱你的\n2 爱我，就请我吃哈根达斯，不过看你这么有诚意，请我吃碗面我也满足了', '勾起好奇');
INSERT INTO `talk_art` VALUES (10983, '你会娶我吗·········', '1娶了你会帮我买房子和车吗\n2那要看你的表现啊\n3我不是唐僧，你也不是经书，我怎么取你\n其他答案：1 你是 A\nT 机里的钱？ 2 我会娶我老婆 3 我不会娶你妈 (从不善解人衣 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (10984, '你不信算了~~~', '1不然要怎样，要把我吊起来打到信为止吗\n2我信，我相信你对我的爱是真诚的，所以你不会骗我的\n3不能算，以我较真的性格，要带你去公安局拿测谎仪测测才死心', '勾起好奇');
INSERT INTO `talk_art` VALUES (10985, '再笑我打你', '1还是换你笑我打你吧（非原创）\n2俗话说伸手不打笑脸人啊\n3打我算是一种表白吗？打是情骂是爱哦', '勾起好奇');
INSERT INTO `talk_art` VALUES (10986, '美眉说我 色咪咪的看着他', '1请搞清楚到底谁色咪咪看谁，你不看我怎么知道我在看你（非原创）\n2这都被你发现了，真不好意思，因为我今天没吃饭，所以饿起来才多看你两眼压制食欲的\n3我在想以后我女朋友要是也弄个你这样的发型（衣服或跟她有关的一切都行）会被我嫌死掉的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10987, '妹子:我已经更你说的很清', 'w:别急，没关系的，慢慢来，很多人表达能力都有问题，这不是你的错\n要是女方回一句：是你理解能力太差了才对怎么接？\n1每次有人听不懂你说话你都是这么把责任赖到人家头上的吗？2如果你把推诿的这股劲用到提高自己的表达上，我们早就沟通得很有默契了3恩，我承认对于非人类的语言还是理解的不够透彻', '勾起好奇');
INSERT INTO `talk_art` VALUES (10988, '男哥 把女孩子带回家 应该', '1（坐在一起看照片，视频等一切可以一起看的东西）：你身上好香。。好像是。。是炒猪头肉的香味妹子：BLBLBLBL\n不对不对，好熟悉的味道，让我再闻闻就想起来了（逐步闻到脖子，吻下去）\n反应不错就继续吧，反应不好就说这个味道让你很有食欲所以才情不自禁咬了下去\n2趴到床上：好累，帮我按摩吧（不肯就玩论坛里介绍的那些捉弄她的必胜游戏叫她输了帮你按）然后嫌她按不好坚持教她按，让她躺好后进挪\n3霸王硬上弓（除非你是很厉害的 PUA 或者你爸比李刚牛 B）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10989, '别讲话，我在发短信（打', '1你按键盘声轻点，我在打电话\n2非得逼着我唱歌？\n3我磁性声音的诱惑使你无法集中精力给别人发信息吗？\n其他答案：1：靠，一边给我打电话，还要一边给我发信息？有什么话不好意思直接跟我讲，非的发信息才行，难道是今晚我们。。？\n2：好呀，那你发完了再给我打过来。\n3：你在变相玩 3P 呀？一边电话一边短信，同时对付 2 个，你够强哟。（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10990, '女生说要去冲凉了，怎么', '1要跟我一起冲吗？\n妹子：你坏死了，才不要呢\n你想到哪去了，我说你在你家冲，我在我家，时间上一起而已，你真色，老想歪（非原创）\n2先告诉我从你家哪里爬上去看洗手间能清楚点呢（非原创）\n3想不到你竟然也会洗澡啊。。。。', '勾起好奇');
INSERT INTO `talk_art` VALUES (10991, '晚上我不出去的', '1晚上出去才合适啊，白天人那么多你出去还不吓死一片\n2这么有原则？无论如何也不出去吗？\n妹子：是的\n那要是晚上家里着火你怎么办\n3这样好，家里不用再养狗来防贼了\n其他答案：你肯定不出去吗，太好了，那我可以跟朋友出去了，就怕跟朋友逛街的时候遇到你跟我打招呼，让我的朋友知道我认识你这样的会让我自卑的(brethart )\n怕被街道巡逻警察看见就逮起来？大晚上看到你，确实挺渗人的。(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (10992, '妹子：你好胖喔', '1我可以帮你省掉买枕头的钱\n2我不嫌你瘦\n3我这叫胖？下次给你介绍一个叫泳湿小狗的朋友让你开开眼界\n其他答案：心宽才能体胖（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10993, '求关系暧昧到一定程度可', '1别动，你头上有个东西，我帮你拿掉，咦？我喜欢你头发的手感耶。。摸着好舒服的感觉（从头发持续进挪）\n2你有多重啊\n妹子：blalalalalalal\n少来，你肯定少报了 20 斤，我拿手一掂量就知道（不让抱就说她心虚，然后抱起她，再然后假装抱不起两人一起摔到地上，想进挪哪就进挪吧，之后找她要医药费，谁让她谎报体重的）\n3听说每个人都有个独特磁场的，男女不一样，有些人的磁场会有共鸣哦\n妹子：怎么共鸣\n身体的摩擦，像有些人爱爱比较容易高潮就是磁场共鸣呢，但你不要妄想马上跟我试啦，我收费很贵的妹子：BLBLBLBLBLB\n不过可以拿手臂摩擦试试也能看出来（手臂摩擦进挪后）差点忘了，像你这样皮太厚是没办法共鸣的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10994, '你和我不是一个级别的（', '1心里有数就行，不用说出来，其实大家都知道你还差我好几个级别\n2我不嫌弃你级别低\n3给你三种颜色，明天去开个染布坊还我吧，你有这个能力\n其他答案：1：有点常识行不行，你我的级别 都是人类。\n2：嗯，你跟芙蓉姐姐是一个级别的，身材超级棒\n3：想不到我礼貌的夸你几句，你竟然可以高兴成这样，看来从来没人夸过你。（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10995, '我相信你的技术（女对我', '1手指还是舌头还是别的？\n2所以我收费比别人贵是有道理的\n3谢谢你的信任，我一定争取成功一次，然后把这宝贵的第一次送给你', '勾起好奇');
INSERT INTO `talk_art` VALUES (10996, '妹子：对于一切事物惯有', '1不可轻视啊，人家说夫妻性生活冷淡是离婚的主要原因呢\n2冷漠不是你的问题，是社会的问题，而且你知错就改，还是好小孩哦\n3那找你做老婆可省钱了，夏天跟你睡一起都不用开空调', '勾起好奇');
INSERT INTO `talk_art` VALUES (10997, '妹子：你好丑喔', '1这就叫丑了？ 那你照完镜子应该会有惊喜的\n2我很丑，可是我很温柔\n3你眼睛长包皮了吗\n其他答案：我只是美的不明显（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (10998, '女孩老是说：老天！', '1你是窦娥的粉丝吗\n2你真土，现在都流行说 OH \nY LADY GAGA!\n3每天被你这么叫，他肯定烦都烦死了，我要离你远点，不然老天爷一个雷打到你头上会波及到我的', '勾起好奇');
INSERT INTO `talk_art` VALUES (10999, '晚上我才不和你一起，我', '1你这是明目张胆的祸害别人呀\n2其实 3P 我也不介意的，一起玩呗\n3你什么时候这么有原则的，确定一晚只接一个客吗？\n其他答案：1：你有了？谁的？不会是我的吧？好像没这么快吧？\n2：晚上你和我一起？天那，你那色狼般的头脑里想的究竟是什么呀？\n3：晚上你要背着我偷汉子？（一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11000, '最近一直很真诚的关心一', '然后女孩问我：你习惯了关心别人吗？\n1没有这个习惯，如果我习惯了那意味着我身边就有很多人受伤了，所以我不想这种关心成为习惯\n2我平时都在忙，几乎没有时间去关心别人，也没有谁会引起我的注意，但唯独对你不知道为什么有种奇怪的感觉会想来关心\n3我没有关心人的习惯，但你可以有被我关心的习惯\n其他答案：1 我曾今也有过像你这样 所以看到你就像看到了我自己\n2只要我能尽力的 我都会帮助\n3哥是个好心的人 为老奶奶让座这种事情做的多了\n4咋样？ 你想给我奖励？ 千万别以身相许啊，我伤不起啊\n5可爱的女孩子我都会多关照一点', '勾起好奇');
INSERT INTO `talk_art` VALUES (11001, '你社会经验太丰富了，感', '1请不要把老字跟我放在一起，其实我刚满 18，只是长得成熟些而已\n2你太看不起人了，我床上经验更丰富呢\n3SHE 有首歌是这么唱的：如果你没有安全感，把安全套戴上\n其他答案：你在羡慕我的成熟呢，还是在痛恨自己的幼稚？(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (11002, '女生看见我笑，“你笑什', '1唱：我一见你就笑，你那翩翩风采太美妙，跟你在一起，永远没烦恼\n2别人笑我太疯癫，我笑他人看不穿\n3因为你长得很有喜感\n其他答案：你猜我应该是笑贫不笑娼还是笑娼不笑贫呢？ （一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11003, '\\', '1怎么不关心，我相当关心你的三围和存款\n2乱讲，我经常关心你的 G 点好不好（慎用）\n3我关心你啊，你吃饭了没呢\n妹子：吃了/没吃\n我正好没吃，看在我这么关心你的份上，请我吃饭吧', '勾起好奇');
INSERT INTO `talk_art` VALUES (11004, '你以后不要再来打扰我的', '1那要先请你离开我的心才办得到\n2你误会了，我不是要打扰你的生活，我不会强迫你做不愿意做的事，我只希望跟你在一起的时候能开心就满足了（非原创）\n3什么时候打扰你了，我又不是那些上门搞推销的\n其他答案：1 只要你不是整天想着我，我就打扰不到你的生活。\n2 为什么我一出现，就有人开始不能安心生活了呢？（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11005, '女孩老是说：神马东东 怎', '1神马不是叫浮云吗？什么时候改名字叫东东了\n2这是要跟我对联吗？我对----灵犬西西\n3看来你很好学哦，不懂就问这点我要给你加分了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11006, '老公我爱你', '1究竟有多爱呢？\n2老婆我也爱你，我们合起来有两个爱哦，无意间发现了爱爱的由来呢\n3真的吗，那先帮我倒杯水吧，老公渴了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11007, '你不是喊我不要再回来吗', '1我现在是帮你妈妈喊你回家吃饭呢\n2小时候我把我家狗带到老远去丢掉，喊它千万别回来，结果它还是跑回来了\n3这么听话？那我喊你把银行卡和密码给我你听不', '勾起好奇');
INSERT INTO `talk_art` VALUES (11008, '我受伤 你不心疼么？', '1心疼得半死，现在连看个小感冒都那么贵，何况疗伤呀\n2何止心疼，看你受伤，我全身能疼的地方都跟着疼，而且竟然连体毛也会疼哦\n3为了看我为你心疼，你会不会再故意多受点伤啊\n其他答案：伤在你身，痛在我心。不过这话一般都是女孩子对我说的(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (11009, '我不想睡，我就呆网吧', '1你有做网管的潜质哦\n2最近刚发生的深夜网吧轮暴案你没听说吗？\n3谁说要你睡了，我拉你一起去做运动呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (11010, '你脑袋被门挤啦', '1大脑袋没被挤，是小脑袋被门挤了，肿的可大了，我使劲要拉出来结果越拉越长，搞得现在又肿又长，不信可以给你看看，当然，要先收费\n2我的脑袋就算被门挤一万次也还是比你的好使啊\n3门要挤脑袋我肯定会躲开的，难道还像你经常想不开拿脑袋去挤门吗\n其他答案：你胸部被门挤啦？这么平。。。(   \n是的呀。就在昨天，你不记得了？你死命的拉我进浴室跟你洗鸳鸯浴，我拼命关门想逃出来，结果就这样挤了(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (11011, '妹子：哎，结识了你这样', '1绝对是惹祸上身，我的粉丝团会因为嫉妒而不择手段地打击报复你的\n2肯定是福啦，傻人有傻福你没听过？\n3是呀，只有我这样的傻冒才会傻到愿意结识你这样的人', '勾起好奇');
INSERT INTO `talk_art` VALUES (11012, '你不是不想见到我吗？', '1对呀，光是远远见你多没意思，要近距离抱着才过瘾\n2我想通了，跟你站在一起，我才会被衬托得比较优秀\n3你觉得自己不想见到某人，某人就一定不会出现在你面前吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (11013, '前些天在电梯里翻看泡学', '1你说的迟钝是在怪我对你暗送秋波视而不见吗\n2你那么敏捷，干脆走楼梯不是更宽敞\n3书上说如果跟反应慢的人呆在一个狭小的空间里就容易被感染，看来我也不能例外', '勾起好奇');
INSERT INTO `talk_art` VALUES (11014, '夜深了，目标和同伴 妹子', '这种情况已经很麻烦，最好还是让僚机先一步带走障碍\n1你们不会是同性恋吧，我信仰的教会不允许同性恋的，所以我的原则是不能见到两个同性睡在一起，要不你先睡过来，等我睡着了再过去吧\n2据说这酒店以前有个女人跳楼砸到了另一个女人身上，她们死在了一起，至此以后两个厉鬼就阴魂不散了，这酒店只要两个女的睡一起，当晚百分百都会出意外的，你们小心点（再加点鬼故事渲染下，女孩子最怕这个了）\n3叫朋友带走目标同伴一段时间，理由可以是（一起帮忙去打包宵夜）（单独带她去看一个有意思的东西）（一起玩真心话大冒险后提要求出去等等）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11015, '冷读的时候 妹子 说 你怎', '1其实，我们是同一类人（非原创）\n2结合看易经的时间和读圣经的时间再配合你来月经的时间一推算就知道了\n3你相信世界上有所谓读心术的超能力吗？他就在你面前（接心灵感应惯例)\n其他答案：我平时没事的时候，去步行街兼职摆摊算卦(    你的表情已经出卖你的内心了(一直很想爱你 )', '勾起好奇');
INSERT INTO `talk_art` VALUES (11016, '我说：你这样夸奖我，我', '结果我的哥们，他直接回了我一句：你蜡笔小新看多了哟。\n1其实我比较喜欢看樱桃小丸子\n2是啊，我一直在好奇他那个大象是怎么画出来的，不然把你的借给我试着画一画呗（对 妹子 的版本：你也喜欢关注他的大象吗）\n3我只是在缅怀臼井仪人（话题可以转到蜡笔小新之父的意外身亡和全部媒体错登照片的笑话）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11017, '不好意思，XX 同学，周六', '1不用不好意思的，下次也让我放你一次鸽子就行\n2看来你很热门呢。。下次有空约会的话我得随身带点冰块给你降降温\n3好吧，这周我就只好勉为其难跟别的女孩子一起过了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11018, 'KTV 包厢里目标叫过来的朋', '1你朋友是在这里兼职做营销的吧\n2对她朋友：您点的这些东西需要给您打包一起带走吗\n3你去赶场就好了，可别偷偷去买单哦，买了我要跟你急哦', '勾起好奇');
INSERT INTO `talk_art` VALUES (11019, '你够猥琐（女孩对我说）', '1还不够，我还有更猥琐的要不要看\n2哪里哪里，这是你言传身教的功劳\n3对你这种厚度的脸皮，我猥琐的程度还远远不够', '勾起好奇');
INSERT INTO `talk_art` VALUES (11020, '目标带出来的 妹子 说喝不', '1喝不了酒就没关系，我叫服务员给你倒一壶开水全喝掉也凑合的\n2人家说该喝却不喝属于酒品不好，而酒品不好就是人品不好，再者物以类聚，所以你一人不喝会连累我们全部人品不好的\n3怕醉就嚼同等数量的冰块吧，还能给我省钱呢', '勾起好奇');
INSERT INTO `talk_art` VALUES (11021, 'KTV 包厢里目标带出来的同', '1对同伴：要不你们去跟经理沟通下看能不能退货吧，没道理给这里老板占便宜啊\n2早就听说有专门帮 HIGH 吧出来拉客的小蜜蜂，想不到今天被我遇见了\n3做悲伤状：突然有种很伤感的感觉\n妹子：为什么呢？\n想起了我的童年往事，现在看到桌上这堆可怜的东西就要无辜地被人给抛弃，顿时勾起了同病相怜的惆怅，我们能不换地方吗。。。', '勾起好奇');
INSERT INTO `talk_art` VALUES (11022, '妹子：天!我一直以为我很', '1不是弱小，是弱智\n2请不要卖萌\n3你的 NLP 学得相当不错\n4你只是腰部以上颈部以下的局部弱小而已\n5像霸王龙那么弱小吗\n其他答案：我最喜欢欺负弱小了         你确实有点小，和小笼包一样小        不要妄图装柔弱，激发我对你的怜爱        ', '勾起好奇');
INSERT INTO `talk_art` VALUES (11023, '\\', '1（买完后）小朋友，把你爸爸的电话号码告诉哥哥，稍后好联系他报账呢\n2这个是你的私生子么。。。我会视如己出的，不过请你先给我赡养费吧\n3不能不劳而获哦，你让姐姐亲哥哥一下，哥哥就给你买', '勾起好奇');
INSERT INTO `talk_art` VALUES (11024, '妹子：搬到我这来住怎么', '1你打算给我多少搬迁费？\n2好啊，你那边隔音好不好，我怕我们影响你的室友呢\n3你在暗示要包养我吗\n其他答案：合租人是女的么？正好一起来个 3P，别误会，我说的是三口之家过日子（第四生存者）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11025, '我说 XX 又有一个补英语的', '1你看见狗吃屎？难道你也去吃？\n2我要是先实施了，你哪里还有机会见识别的男人都是怎么挫的\n3你想看吗？去给我买电影票来呀', '勾起好奇');
INSERT INTO `talk_art` VALUES (11026, '女：我失业了', '男： 来我的部落吧，最近在举办活动，你领个号，就能竞选压寨夫人，不过看我跟你关系好，偷偷塞个号给你，你直接来我帐篷好了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11027, '妹子 故意不接触我- -。知', '1我知道两个人如果彼此有感觉而肢体接触，就会有过电的感觉，你一定是不想电死我才不接触我的\n2你最近得了皮肤病吗？\n妹子：没有啊\n那干嘛不敢让我碰你呢\n3哇！你后背有只好大的蟑螂！别动，我来帮你干掉它', '勾起好奇');
INSERT INTO `talk_art` VALUES (11028, '你喝酒是喝不我赢的', '1那我们可以比谁先呕吐\n2恩，看身材就看得出来\n3我哪能跟你比，你是专业冲酒水的小蜜蜂啊\n其他答案：你打飞机是打不赢我的（第四生存者 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11029, '搭讪 妹子 她说：你这搭讪', '1我原以为对老套的人用这个搭讪方式挺合适呢\n2那请教你一般都用什么新招呢\n3因为我是个怀旧的人\n其他答案：墨菲斯的一个帖子里说过,答案是:但是管用!（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11030, '女：你才性冷淡', '男： 那就挺好，反正我有一个治愈性冷淡的身躯，特别是治愈我喜欢的女人', '勾起好奇');
INSERT INTO `talk_art` VALUES (11031, '你这么花会不会背着女朋', '1我很在乎我女朋友的感受，要是当着她的面她会伤心的\n2不，我一般都是和别的男人发生关系的\n3弱弱地问：什么是发生关系啊。。。人家年幼不懂啦\n其他答案：我很想我背着女朋友来跟你发生点关系嘛？（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11032, '你说话怎么那么难听', '1你的耳朵怎么那么没有品味\n2这我没法解释，就像你长得怎么那么难看一样\n3忠言逆耳\n其他答案：可是我唱歌好听啊 （第四生存者）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11033, '我妈这几天吃饭老叫我找', '1你家被拆迁办盯上了？\n2你妈喜欢制服诱惑吗？我还能扮国家主席呢\n3好啊，找个空军的吧，打飞机厉害\n其他答案：只有吃饭时才叫你找军官老公吗?平时叫不叫呢? （一直很想爱你）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11034, '女：再这样我删了', ' 男： 没事，多个我再次勾引的流程，不复杂，除非你性冷淡', '勾起好奇');
INSERT INTO `talk_art` VALUES (11035, '下次见到再说啦', '1人生无常，谁能保证我们下次见到不是在六十岁以后呢\n2老师没教过你今日事要今日毕吗\n3你知道有利息这一说吗', '勾起好奇');
INSERT INTO `talk_art` VALUES (11036, '你再那样说话我就不理你', '1哪样？你给我学学\n2非要我用唱的吗\n3太感谢了，如果你不理我，整个世界会清净好多的', '勾起好奇');
INSERT INTO `talk_art` VALUES (11037, '我要是找你 就去跳楼', '1我理解你急切想看到我而迫不及待的心情，但我坚持认为还是走楼梯好点\n2那下次过生日送你个降落伞吧\n3梁祝看多了吧你，我可不会陪你干傻事哦\n其他答案：我只听过约会时去跳迪斯科,没听过约会时去玩跳楼（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11038, '邀约', '   男：报上你的位置，我看看穿过一个街道的距离，能不能听到你的呼吸声', '勾起好奇');
INSERT INTO `talk_art` VALUES (11039, '\\', '1先把付我的陪玩费谈好再说\n2你比较喜欢在房间还是野外？\n3一起去看新版还珠格格怎么样\n其他答案：今晚，我们一起在房间玩好了（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11040, '放心我是不会付钱的', '1打算肉偿？\n2可打工抵债人家不知道收不收呢\n3你要把衣服押在这里吗？', '勾起好奇');
INSERT INTO `talk_art` VALUES (11041, '我说你看起来很面善 她说', '1也看内在，你今天的内衣款式我就很喜欢\n2你教教我认人不看脸看哪？看屁股？\n3如果你愿意的话，我很想看看你外套下的身体', '勾起好奇');
INSERT INTO `talk_art` VALUES (11042, '女：你有什么好的', '  男：器大，活好，钱多，懂得欣赏你的美', '勾起好奇');
INSERT INTO `talk_art` VALUES (11043, '你再给我说一句······', '1只要你喜欢听，别说一句，一百句我也说了\n2好的，但你手机如果有录音功能我建议把这句录下来，这样你可以慢慢听，想听多少遍都行\n3想听先给钱，天桥下说书的也得收钱啊', '勾起好奇');
INSERT INTO `talk_art` VALUES (11044, '我是不会跟你一起吃饭的', '1没关系亲爱的，只有封建社会才是老公吃完才准老婆吃，但社会主义早不兴这套了，我们可以一起吃\n2你的意思是只想跟我一起爱爱吗？\n3傻瓜，我不会被你影响胃口的', '勾起好奇');
INSERT INTO `talk_art` VALUES (11045, '妹子：对不起，迟到了', '1没关系，下次我也迟到下咱们就扯平了（非原创）\n2这已经不是亲我一下就可以解决的了，你得亲我两下\n3不用道歉，最近我在刻意锻练修养，专门跟各种让人火大的人交往，不过可惜的是你只让我等了两个小时', '勾起好奇');
INSERT INTO `talk_art` VALUES (11046, '男：知道为什么让你坐我', '   女：为什么    \n   男：因为左边可以直接接收到你的心跳', '勾起好奇');
INSERT INTO `talk_art` VALUES (11047, '和一个女同学聊天，不管', '1同学，从你回复的字数我想问一下，你家键盘究竟是什么材质的能这么值得你爱惜呢\n2没关系的，鼓起勇气来，打字实在不行就多练，我不会嫌你的\n3我知道作家写字要钱所以轻易不写，但没想到你竟比作家还抠', '勾起好奇');
INSERT INTO `talk_art` VALUES (11048, '土', '1我这衣服刚换的，哪来的土，连灰尘都没有（非原创）\n2本土的就是世界的\n3soil~你这是在考我的英文水平吗', '勾起好奇');
INSERT INTO `talk_art` VALUES (11049, '妹子：最近在干嘛？', '1忙着参加人大选举\n2想办个红十字会，听说很赚钱\n3最近在街搭（慎用）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11050, '女：我辞职了', '男：给你介绍一个新工作，绝对有诗和远方，还有辣条薯片 \n   女：什么工作    ----  男： 来我家当个小丫鬟，暖暖被窝，洗洗衣服努力下可以转正为夫人', '勾起好奇');
INSERT INTO `talk_art` VALUES (11051, '和 妹子 说什么 她都说：', '1很感兴趣到底是什么东西没有了要严重到你每句都提醒我呢\n2接着唱：（没有）我你怎么办？你的泪水谁为你擦干。。然后说：到你接了\n3拿一百块给我，（收好后）现在你给说说我拿你钱没\n其他答案：1、比你笨的人有没有？ 2、比我帅的人有没有？ 3、比你更爱我的人有没有？        ', '勾起好奇');
INSERT INTO `talk_art` VALUES (11052, '我要吃巧克力/糖', '1门口超市就有的卖，顺便帮我带瓶可乐，谢谢\n2如果你想找找情人节的感觉其实还有很多别的方法，我家床很宽的\n3其实你的体型已经逐渐往不适合吃那个东西的方向发展了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11053, '妹子 说：一个大男人逛什', '1遛你啊\n2不然你帮我买内衣裤吗？\n3我帮着把马路压平点，这样老爷爷老太太就不容易摔跤了', '勾起好奇');
INSERT INTO `talk_art` VALUES (11054, '女：我最近都长胖了', '   男：有时间就努力锻炼身体吧，你不可以既单身又胖若两人', '勾起好奇');
INSERT INTO `talk_art` VALUES (11055, '我：你有点坏坏的感觉', '坏 是吗？\n1是啊，这样我们走在一起就没人敢打劫了\n2恩，比坏掉的鸡蛋还坏\n3是因为身边没有帅哥才把你憋坏的吧\n其他答案：是哟,要知道男人喜欢坏坏的女孩,但是不喜欢长坏了的女孩哟.你现在就是长坏了的感觉.（一直很想爱你 ）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11056, '女：女生发生气的表情', '  男： 别激动，我一时半会还不会离开你（发怒）', '勾起好奇');
INSERT INTO `talk_art` VALUES (11057, '为什么你总是这么一意孤', '1可能是变形金刚看多了吧\n2你是在夸奖我不达目的决不放弃和游刃有余随机应变的精神吧\n3因为我是一名 PUA(慎用)', '勾起好奇');
INSERT INTO `talk_art` VALUES (11058, '女：你撩妹这么厉害你家', '  男：我妈知道，但是她告诉我，遇到眼前一亮的女生，就不用假装木讷了，这 20 几年来，终于邂逅你', '勾起好奇');
INSERT INTO `talk_art` VALUES (11059, '你和他（她男友）是无法', '1没人告诉你尝试将过去时与现在进行时比较是很愚蠢的做法吗\n2比什么呀？性能力么？我们要先 TD 才好比呀\n3亲爱的。。现在不流行比武招亲了', '设立框架');
INSERT INTO `talk_art` VALUES (11060, '你碰我干什么~不要碰我不', '1实际上，刚刚帮你拍掉了一只小虫，但你的反应着实吓到我了，我拍掉的该不会是你养的宠物吧\n2我想测试下不同类型的人对陌生人的接触会有怎样的反应，现在已经有结果了，想知道你是什么类型的人么（接冷读）\n3有一种人叫玻璃人，天生体质就是像玻璃那样脆弱，别跟我说你也是哦', '设立框架');
INSERT INTO `talk_art` VALUES (11061, '你也不看看你自己什么样', '1抱歉，我刚意识到自己这么帅又这么直接会让你更自卑的\n2没听过追贼前还要先照镜子的\n3连你这素质都敢上街了，我还有什么事不敢做的', '设立框架');
INSERT INTO `talk_art` VALUES (11062, '你真的觉得我很好吗？', '1脉搏不会骗人，你摸着我脉搏再问一遍就知道我有没骗你了\n2在没有更好的出现前，你真的很好\n3目前就我了解的部分确实很好，但没有任何人是完美的，我还想更多的了解你不好的方面，爱你， 就要爱你的全部，包括所有的好与不好', '设立框架');
INSERT INTO `talk_art` VALUES (11063, '目标：我对你不感兴趣', '1你对我感不感兴趣与我何干呢？我只是觉得你看上去比较友善所以才过来跟你打个招呼而已，你想太多了\n2你不用对我感兴趣，只想请教你一个问题，像你这样类型的女孩子一般对怎样的男人感兴趣呢\n3你认为每个男人都需要你对他们感兴趣吗', '设立框架');
INSERT INTO `talk_art` VALUES (11064, '你卑鄙，无耻，下流。', '1你羡慕，嫉妒，恨\n2我长得就是像 BABY 那么可爱，而且 BABY 确实也是没长牙齿的，还有难道你尿尿是往上流的么\n3你听过吃不到葡萄的狐狸的故事吗', '设立框架');
INSERT INTO `talk_art` VALUES (11065, '妹子：请问姐姐真的像猪', '1你自信点好不好，就算全世界说你像猪，但自己一定要坚信----我不是猪！\n2像一只可爱的荷兰猪，真想抱在怀里好好疼你\n3你去猪圈里站一会，看种猪们拱不拱你，你就知道答案了', '设立框架');
INSERT INTO `talk_art` VALUES (11066, '你的僚机用过和你一样的', '“为什么你和你的朋友说话都一样？”1神马？他和我说话一样？！靠！这小子偷看了我昨天在地摊买的《与人沟通一百招》，回去我得把他吊起来打！\n2这不算什么，其实，两个人在一起久了，甚至都有心灵感应，你相信吗？（接心灵感应惯例）妹子：这个他刚也表演过了\n对，我就是要再次向你证明，我和他有心灵感应\n3因为我们晚饭吃了相同的东西，这个理由牵强么\n妹子：牵强\n你好聪明，这都忽悠不了你，好吧，其实我们是相声艺术研究会的会员，最近相声听众每况愈下，所以我们出来测试大家到底对重复听到一样的段子和台词会不会反感，那么现在请说出你的想法吧妹子：BLLBLBLBLBLBLBL\n非常感谢你对我们相声艺术事业的支持，请留个联系方式，稍后我们会给你送上礼物的妹子：不用了吧\n就冲你这么热心回答我的问题，你这个朋友我也交定了，电话给我，改天一起去听新段子\n其他答案：因为他是我的朋友。我相信，你和你的朋友有时候也会这样。（ Figure.09）', '设立框架');
INSERT INTO `talk_art` VALUES (11067, '即使你名草有主，我也要', '1你是移花宫来的么？怜星邀月是你什么人？（感谢绝代双骄）\n2移草接木这么破坏大自然平衡的事你也干得出来呀\n3草皮很昂贵的，移的时候请你轻点', '设立框架');
INSERT INTO `talk_art` VALUES (11068, '和你有一腿，你有意见？', '1你识数吗，我们合起来，应该是五条腿好吧\n2我没意见，你得亲自用嘴问下我小弟弟有没意见\n3你存款几位数？我收费可是很贵的哦', '设立框架');
INSERT INTO `talk_art` VALUES (11069, '晚上半夜街搭', '“你和你的朋友为什么大半夜的还在外面游荡？”\n1因为最近这边经常有个色魔出现，我们一个哥们前几天被他爆了菊花，我们想把他找出来报仇\n2工作压力太大，所以出来赏月缓解一下压力\n妹子：那你们继续赏/可是现在没有月亮呀\n不赏了，我们发现在了比月亮好看得多的东西/那是因为你出现了，月亮惭愧地躲起来了，不过我们待会记住不能经过花店，不然花会谢掉的，店老板就要哭了,你知道原因吗（闭月羞花）\n3老板，我们就是传说中的站街男，100 一次，需要不，看你气质不错可以给你打个折，包夜还送抽奖呢\n其他答案：那么你和你的朋友为什么在大半夜遇见我们？', '设立框架');
INSERT INTO `talk_art` VALUES (11070, '知道我为什么不出去玩吗', '因为出去怕吓到路人\n1因为地球上面太危险\n2因为你的大姨妈来访\n3因为要在床上等我来\n4因为自慰棒不便外带（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11071, '我就是诱惑你，怎么样呢', '1其实诱惑也是要讲究资本的。。。。\n2肥肉只对狗有作用，对我没用\n3等你了解我每天上的那个论坛后就不会这么想了（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11072, '“你经常这样子搭讪女孩', '1我经常这样子被女孩子搭讪，所以今天想亲自试试搭讪到底有多爽，不然她们怎么老是乐此不疲呢（非原创）\n2你经常这样被帅哥搭么？\n妹子：是/不是\n那请你比较后给我打给分吧/这么有气质的女孩都不来搭，那些与你相遇的男人都是盲人么\n3我经常这样搭讪男孩子，因为那样可以练习并克服自己的紧张感，但看到你以后，我以后都不想再搭男孩子练习了，跟和你搭讪比较起来，那样简直无趣至极\n其他答案：我只会去认识我想认识符合我标准的女孩子，你是爱冒险的女生吗？ ', '设立框架');
INSERT INTO `talk_art` VALUES (11073, '哎，你怎么能这样想呢？', '1你要明白，人的想象力是无穷的\n2你怎么能让我不这么想呢\n3恩，下次我那样想吧', '设立框架');
INSERT INTO `talk_art` VALUES (11074, '怎么样，我的衣服漂亮吧', '1不能用漂亮来形容，简直是完美啊!!\n妹子：真的吗？\n恩，我的鼻子有什么变化么\n2漂亮！连穿在你身上都能这么漂亮，我无法想象这衣服挂起来得有多漂亮了\n3穿在你身上相当一般。。。。因为跟你的美丽比起来，任何衣服都会失去它的光彩', '设立框架');
INSERT INTO `talk_art` VALUES (11075, '“你为什么要认识我？”', '1因为我最近在学习卡耐基的人生指南，他教我每天要认识一个新朋友，你是今天的那一位哦（非原创）\n2因为你看上去不像坏人（非原创）\n3你每次认识新朋友都需要给自己一个理由吗？\n其他答案：当你的朋友认识你的时候你也这么问的吗？', '设立框架');
INSERT INTO `talk_art` VALUES (11076, '呵呵，我不稀罕。', '1你又不是卫生巾，当然不吸汗\n2你确实不西汉，西汉女人据说都是以身轻如燕，身姿窈窕为美\n3物以稀为贵，你不稀罕也就不金贵了', '设立框架');
INSERT INTO `talk_art` VALUES (11077, '我要是不去，会怎么样呢', '1我会被很多热情的粉丝瓜分掉，我需要你去保护我\n2那就不一定了，要抛纸团决定下一个陪我去的美女是谁\n3小蝌蚪们会失望的（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11078, '“你的位置在哪里？”', '1回答问题前变个魔术给你看吧，把手给我，好的，请起来，慢慢转一圈（坐在她位置上），回答我， 你的位置在哪里\n妹子：被你坐着呢\n这个？不不不，魔术前这位置是你的，现在是我的，这就是见证奇迹的时刻呢（感谢谜男）2其实。我今天失恋了，朋友们要陪我开导我，但我不想让他们看到我现在的状态，反而想一个人来这个地方摧毁那些失落感，你能明白我这种感受吗？也许他们几分钟后就过来找我了，但至少，在这一刻，只有你这个陌生的朋友跟我分享我的失恋时光，我感觉不错\n3刚刚有个位置的，后来跟朋友打赌输了，被罚 10 分钟不能归位，只有请善良的你收留我一会了', '设立框架');
INSERT INTO `talk_art` VALUES (11079, '约会有女孩有鼻炎怎么和', '1跟你在一起就谁也不怕了，呆会看谁不爽我们就把鼻涕擦在他身上\n2鼻炎很好治的，以前人家教我的每天用盐水洗鼻子就会好，可当时我不知道盐的比例，一次就放了半包 BLBLBLBL....\n3小时候，我和表弟把鼻涕与鸡蛋调到一起煎给同学吃过呢', '设立框架');
INSERT INTO `talk_art` VALUES (11080, '那要是我就是拒绝你呢？', '1你会让我多一次被拒绝的经验，累积一定经验后我就无敌了，但无敌还有另一个捷径，那就是---你直接接受我\n2那就是你瞎了眼呗\n3（深情凝视）你忍心拒绝我吗？ 但总有个把贱贱的会说：忍心 这时候就尽情挠她痒吧', '设立框架');
INSERT INTO `talk_art` VALUES (11081, '”你的朋友在哪里？“', '1目前有一个正站在我面前，是吧？（微笑）\n2先告诉我你是不是坏人，我再把跟朋友走散了这件事告诉你，你不会趁机对我下黑手吧妹子：不会/会\n那太令人失望了/恩，那你可以请我喝杯酒，然后偷偷在酒里放万艾可\n3我的朋友在那边，我很想带你去认识他们，但你得先告诉我你叫什么，并且确认你的职业不是传销我才能把你介绍给他们呢（有朋友就可以直接带过去，万一没有就另打开一桌，跟桌上的人说光自己和妹子 两个人玩没意思，想加入他们一起玩，问清楚对方的名字和职业，然后为双方互相介绍，直接加入）\n其他答案：在那里，（如果你一个人，指向能量最高的，社交认证最好的地方。如果你们有座位，并且能量很高，社交认证很好。指向自己的位置。）', '设立框架');
INSERT INTO `talk_art` VALUES (11082, '我怎么又看见你了？', '1虽然这个套近乎的方法有点点过时，但我很喜欢\n2佛说前世的五百次回眸才换来今生的一次相遇，看来我们前世回眸了 1000 次哦\n3这就是传说中的阴魂不散', '设立框架');
INSERT INTO `talk_art` VALUES (11083, '你为什么会取这个名字啊', '1你为什么会长成这样呀\n2因为算命先生跟我爸妈说有朝一日有个女人问我为什么取这个名字的傻 B 问题时，就可以带她见家长了\n3因为当时我不具备发言权', '设立框架');
INSERT INTO `talk_art` VALUES (11084, '“你经常来这里玩吗？”', '1不经常，每天来一次（非原创）2听你的语气，你好像经常来这里玩是吗？\n妹子：不经常/经常\n难怪你不认识我/那你怎么会不认识我（非原创）\n3不管经常来的人还是不经常来的人，能认识你都是一件令人愉快的事情\n其他答案：1 嘿，告诉我你关注我多久了。\n2 这对你来说很重要吗？（ Figure.09）', '设立框架');
INSERT INTO `talk_art` VALUES (11085, '那好，你介绍下你自己？', '1性别--男，爱好--女\n2我姓秦，叫艾德（亲爱的）\n3我这个人从小到大一直对命理学很感兴趣，前几日算命先生说我今天在这能遇到一位（形容她特征）的朋友，果不其然啊', '设立框架');
INSERT INTO `talk_art` VALUES (11086, '和你说话浪费我时间，或', '1说话又不是接吻，浪费什么口水，跟你接吻才叫浪费口水\n2确实浪费时间，我高估你了，原以为你的思维能力能理解我的话\n3如果你能专心对话而不是在对话时老想些时间和口水之类的东西，对话会顺利很多，顺带提一句， 对话时一直想着时间和口水的通常是桑拿妹呢', '设立框架');
INSERT INTO `talk_art` VALUES (11087, '你和我交友的目的是什么', '1你平时做任何事都一定要有目的吗？\n2交朋友开心就好，我觉得你很友善，所以想跟你交朋友\n3不以结婚为目的的 TD 就是耍流氓，不以 TD 为目的的交友就是伪君子（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11088, '你不觉得你很 2（傻）吗？', '1 以前有觉得，自从看到你以后，就完全不觉得了，谢谢你\n2 我 2，你 213（213=2B）\n3 哪个 2？520 的 2？你觉得我 2 吗？请不要这么自恋', '设立框架');
INSERT INTO `talk_art` VALUES (11089, '现在和你有一腿也只会害', '1不用担心，我已经老婆了\n2我会找个愿意跟我们一起玩双飞的老婆\n3有一腿我就会找不到老婆？你有爱滋吗？', '设立框架');
INSERT INTO `talk_art` VALUES (11090, '我跟你有代沟，和你聊天', '1想要有趣其实很简单的，只是我比较怕累而已（将她横腰抱起来转几个圈，等她大叫救命再放下来）\n2请问导致我们出现代沟的您高寿\n3和我聊天无趣没关系，但和我 TD 肯定会非常非常有趣的\n其他答案：1.你有沟？我怎么看不到？\n2.那你想怎样，老太婆？\n3.我是见人说人话，见鬼说鬼话，见美女说漂亮话，见丑女就像现在一样（从不善解人衣 ）', '设立框架');
INSERT INTO `talk_art` VALUES (11091, '你这么看我干什么？', '1我要把你的样子记在心里。。。。。以后要是别人被你骗财骗色就可以画出来给警方提供线索\n2我觉得你跟其他女孩子有些不同，但又说不上来是哪里，我想要仔细看看才能找出来呢\n3因为你很长得很像我一个朋友。。。就是我经常去买 AV 碟片那家音像店的店员', '设立框架');
INSERT INTO `talk_art` VALUES (11092, '有啥好说的！', '1你要这么问的话，那就来段相声吧，相声挺好\n2女孩子活泼点，不消极才会比较可爱呢\n3没啥好说的，唱首歌算了，不许问有啥好唱的，挑拿手的来吧', '设立框架');
INSERT INTO `talk_art` VALUES (11093, '请教舞台男 最近又给介绍', '1要是我小学就认识你的话，上课就不会无聊得睡觉了\n2学生里有没有那种不听话到想扁死他又碍于老师身份不方便下手的，我可以代劳\n3老师，你燃烧了自己，照亮了别人，为了表达我的敬意，请把我像试卷那样带回家批改吧，我无怨无悔', '设立框架');
INSERT INTO `talk_art` VALUES (11094, '\\', '1我理解，生理学书上说过的，女人高潮到底是怎样的感受\n2赶兽要拿鞭子才能做到，但我不忍心对你下鞭子\n3至少我理解你现在的感受，是很不爽的', '设立框架');
INSERT INTO `talk_art` VALUES (11095, '\\', '1一般来说，爸爸姓张，妈妈姓力，生的孩子就很容易复姓张力（就像我一个同学姓刘李）\n2小时候在家看偷偷边看 AV 边打飞机，随时注意门口的动静，担心家长是否回来了，这就是性张力\n3跟目标谈论性话题，但两人并没有直接的性接触，多讲多提高，记住不能表现出自己的明显性需求，不能让她感觉到你的目的性', '设立框架');
INSERT INTO `talk_art` VALUES (11096, '我觉得你一点都不真诚', '1现在不流行橙，现在全世界都在用苹果\n2你的意思是非要我三点全露才真诚吗\n3请你先了解清楚什么是真诚再质疑我吧', '设立框架');
INSERT INTO `talk_art` VALUES (11097, '本人字典里没有这个字啊', '1建议你去买这字典的书店找老板退钱，他不肯就直接打 315 举报他卖假字典\n2字典里肯定是有，它认识你，你不认识它而已，没文化真可怕\n3那你用手机搜搜看吧\n其他答案：\n1.要不要我借你一本？\n2.你根本就没有字典，因为你不识字！！！\n3.我只听说过新华字典、康熙字典，本人字典是什么鸟玩意？        ', '设立框架');
INSERT INTO `talk_art` VALUES (11098, '你不要吃里扒外好不好', '1看过无间道吗，其实。。我是一个卧底\n2谁知盘中餐，粒粒皆辛苦。不吃里扒外怎么对得起农民伯伯！\n3我有接吻的时候才会吃里扒外，你要试试吗', '设立框架');
INSERT INTO `talk_art` VALUES (11099, '妹子：你根本就不在乎我', '1我如果真不在乎你，你没机会让我听完这句话的\n2我也觉得很不可思议，你长得一般，又不是富婆，还老是这么疑神疑鬼，但傻 B 的我偏偏就是这么没有理由地在乎你\n3你根本就不了解我，不然不会说出这句话', '设立框架');
INSERT INTO `talk_art` VALUES (11100, '妹子：你会告诉我吗', '1告诉你妈？现在见家长会不会太快了点。。。。\n2从小大人们就教我不劳而获是可耻的，所以，告诉你可以，先给我亲一下吧\n3你怎么这么八卦。。。我要重新评估你了', '设立框架');
INSERT INTO `talk_art` VALUES (11101, '你不觉得我们是不可能的', '1李宁说：一切皆有可能\n2不管你觉得可不可能，反正我觉得可能，这是个奇迹\n3我这个人，最喜欢挑战的就是不可能完成的任务', '设立框架');
INSERT INTO `talk_art` VALUES (11102, '你烦不烦啊', '1干嘛这么问，你要给我吹个小曲儿解闷吗\n2不凡，卓尔不凡\n3烦得很呢，最近一直不来大姨妈，什么时候我要去医院看看是不是怀孕了', '设立框架');
INSERT INTO `talk_art` VALUES (11103, '不要仿造我的一切。你并', '1我又不是卖盗版的，仿造你什么\n2请先搞清楚谁在仿造谁\n3你知道自己适合怎样吗？', '设立框架');
INSERT INTO `talk_art` VALUES (11104, '妹子：我就要说！或者 我', '1你在搞武则天模仿秀吗\n2唱：巴拉巴拉波~~(王力宏麦当劳主题曲-《我就喜欢》)\n3磁铁的两面是互相吸引的，你故意唱反调，就是想吸引我吗？', '设立框架');
INSERT INTO `talk_art` VALUES (11105, '妹子：你是做什么的？', '1年薪 500 万，配车配房，你猜是做什么的\n妹子：猜不到\n做梦的（非原创）\n2我是生物工程学研究员专门研究世界上还活着的恐龙\n妹子：世界上还有活着的恐龙吗？ 有啊，比如你。。\n3通常情况我都坐椅子，不坐床，更不坐地上', '设立框架');
INSERT INTO `talk_art` VALUES (11106, '妹子：一万句我爱你抵不', '1一万句我娶你抵不上一张新婚姻法\n2你希望我们的爱情这么快就走到坟墓里去吗？请回答 YES OR NO\n3那句话要等把你的嫁妆搞清楚才能说的', '设立框架');
INSERT INTO `talk_art` VALUES (11107, '我就是不信', '1你是光荣继承了马克思怀疑论思想的精神楷模\n2总是逃避现实不是一个好方法\n3你宁愿相信世上有鬼也不愿相信帅哥这张嘴啊\n其他答案：\n1.不管你信不信，反正我信了\n2.不管你信不信，反正我就是要胡说\n3.你要是相信哥的话，哥给你吃糖（或者棒棒糖？）（从不善解人衣 ）', '设立框架');
INSERT INTO `talk_art` VALUES (11108, '说话越来越放肆了你~', '1早说你比较喜欢我行为越来越放肆不就好了\n2我跟那些没种的伪君子不一样的，畅所欲言多好，老是戴着面具我们大家都很累的\n3从眼神中我看到你内心深处喜欢我这么放肆的感觉呢', '设立框架');
INSERT INTO `talk_art` VALUES (11109, '妹子：汗', '1经常出汗，促进新陈代谢，所以你皮肤不错2出汗了？看到帅哥（或与帅哥聊天）你会觉得热吗？\n3介绍你一种香水很不错，我懒得洗澡时就靠那个遮盖汗味', '设立框架');
INSERT INTO `talk_art` VALUES (11110, '妹子：你哪个单位的？', '1回答问题前先告诉我你是哪个派出所的\n2想给我单位写表扬信夸我帅吗？千万别，我们领导已经很嫉妒我了\n3不用担心单位的事情，我们单位福利可好了，会分房子的', '设立框架');
INSERT INTO `talk_art` VALUES (11111, '你说 你是吃硬的还是吃软', '1我从来都是软硬不吃的，除非你用嘴喂我\n2我还有得选，你要吃我的，就没得选了，肯定就是软，硬，软这个过程\n3我要吃你，你觉得自己硬就拿水泡软，觉得自己软就拿火烤硬，然后直接给我来个冰火九重天', '设立框架');
INSERT INTO `talk_art` VALUES (11112, '男:你好 我想认识你。', '妹子： 恩 ,好啊\n1因为完全认识你得要很长的时间，所以为了不耽误你我打算留下你的电话，然后慢慢认识你\n2你是我今年遇到的唯一一个这么爽快的女孩子，为了表达我对你的敬佩，请一定要留下号码给我\n3为了表达对你这么友善回答的诚意，我决定将电话号码告诉你，你电话几号，我打过去你就知道了', '设立框架');
INSERT INTO `talk_art` VALUES (11113, '就我没有人疼！', '1你希望哪里疼，我帮你掐\n2我疼你啊，宝贝，过来给你检查身体哦\n3你撒娇的样子真可爱（接 KINO）\n其他答案：你想要哪种疼？欲仙欲死的那种吗？（从不善解人衣 ）', '设立框架');
INSERT INTO `talk_art` VALUES (11114, '女：悟空，你又顽皮了', '1哪里还有皮玩，早做手术割掉了\n2连师兄也不叫一声，越来越没礼貌了二师弟\n3拿小鸡鸡对着她：妖精，看棒！', '设立框架');
INSERT INTO `talk_art` VALUES (11115, '如果我不能给你满意的答', '1我早有心理准备的，人生不如意之事，十有八九，何况是给傻瓜提问这种不靠谱的事情\n2那就给我满意的 TD 也行\n3百度一下', '设立框架');
INSERT INTO `talk_art` VALUES (11116, '你...', '1在前面加上我爱两个字听着会顺耳很多\n2怎么？看到我就害羞得接不上话来了\n3不要整天把我挂在嘴边，这样别人会误会的，影响我市场了你赔啊？', '设立框架');
INSERT INTO `talk_art` VALUES (11117, '你说她如何如何她回：怎', '1手背擦碘酒消毒，把针插入静脉，贴上胶带固定，将药水速度调到合理状态，就这么滴\n2尿不净，就会滴，但女孩子比男孩子多个拿纸擦的步骤，所以一般不会弄脏内裤\n3等我把你 TD 内射了，你张开腿，就会看到怎么滴的（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11118, '我可是非常非常纯情滴', '1比武藤兰还纯吗\n2纯。。属虚构，乱。。是佳人（非原创）3我上次去洗桑拿，帮我按摩的小妹也有这句台词呢\n其他答案：1.是啊，你既纯情，脸皮又特别厚\n2.你长得这样娇艳欲滴，生机勃勃，又会装纯情，你真是太好了3.你是不是只喝纯净水跟纯牛奶？（从不善解人衣 ）', '设立框架');
INSERT INTO `talk_art` VALUES (11119, '我不是个很随便的女孩 我', '做爱 这种事应该婚后（时间久一些）在做？\n1我实在不想说你 OUT，但你不知道现在流行婚前互相检查性的基本能力的吗？\n2昏后哪里能做，必须清醒的时候做才行啊\n3在我心里，你早就已经是我老婆了，我以为，你跟我的想法是一致的', '设立框架');
INSERT INTO `talk_art` VALUES (11120, '妹子：你去问百度吧？', '1百度上跟你有关的内容很容易被和谐你不知道吗\n2这里连条河都没有，哪去找摆渡的来问\n3问了，百度回答叫我去问谷歌', '设立框架');
INSERT INTO `talk_art` VALUES (11121, '你是和谁说话都这么欺负', '1柿子要捡软的捏，人要挑笨的欺负\n2我在免费锻炼你的抗压能力，以后走到外面别人欺负你就扛得住了\n3你是被谁都这么欺负，还是就被我一个人欺负啊？', '设立框架');
INSERT INTO `talk_art` VALUES (11122, '女：晚上好冷，睡不着', '我：好想抱抱你\n女：想得美\n1当然也就是想想，真抱我可要收费的\n2这就叫想得美了？要是抱着你扒光掉然后 TD 那该用什么形容词？1我认为，\n想你只能叫想得丑，不能叫想得美', '设立框架');
INSERT INTO `talk_art` VALUES (11123, '不要尽损我撒', '1哪有钱尽笋你，菜市场的笋现在卖很贵的\n2谦受益，满招损，从自己身上找原因才是关键\n3以你的脸皮厚度，我想怎么损都没用的\n其他答案：因为你全身上下没有一个地方值得让我夸的        ', '设立框架');
INSERT INTO `talk_art` VALUES (11124, '你是不是觉得我是个很随', '1我觉得你跟我是一个类型的人，你觉得我随便吗？\n2请不要这么说自己，随变，是牛奶味的冰激凌，吃起来很甜很腻的，我认为你比它还甜，并且我觉得一点也不腻\n3亲爱的。。。我不许你这么假设自己（然后嘴巴亲上去封住她的嘴）（非原创）\n其他答案：没错，你非常随和，又处处与人以方便（乾坤小阿姨）', '设立框架');
INSERT INTO `talk_art` VALUES (11125, '妹子：感觉你很小，跟长', '1没亲自用过之前，请不要谈论我的大小，拿出来吓死你\n2哪里会长不大，每次看 AV 的时候它都长得可大了\n3想不到你喜欢重口味的，给你找个怪叔叔怎么样？', '设立框架');
INSERT INTO `talk_art` VALUES (11126, '你老是唯唯诺诺', '1谁让你长了一对维维豆奶\n2我要是赞同，就又唯唯诺诺了，我要不赞同，你就不乐意了，你说怎么办\n3好像以你的素质还理解不了我对你的尊重和谦让呢', '设立框架');
INSERT INTO `talk_art` VALUES (11127, '能告诉几个有趣的生日祝', '1若干年前的今天，你父母伟大地制造了你，在若干年后的今天，我要把无数的小蝌蚪当生日礼物送给你，我们制造出来的伟大，将全面超越你的父母\n2送你一张彩票当生日礼物，请你好好留着，中奖了就去兑奖，没中奖也没关系，等着我的努力奋斗，等我成功的那天，你照样可以拿这张彩票来兑奖\n3在论坛开一个生日祝福帖，将你朋友的生日和名字写在上面，请所有论坛兄弟在那个时间帮你发信息祝福，当然，落款署名得是你', '设立框架');
INSERT INTO `talk_art` VALUES (11128, '尊重长者嘛！也是你给我', '1打电话太没诚意了，尊重长者应该去老人院看你才对\n2请不要倚老卖老\n3老人家，电话对你身体不太有利，我们直接见面才会有益你身心\n其他答案：1.好吧，老太婆，我从了你\n2.喂喂喂，我才是长（chang）者！\n3.我不打你电话，要打就打你的翘屁股（从不善解人衣 ）', '设立框架');
INSERT INTO `talk_art` VALUES (11129, '我为什么要给你电话', '1因为你长得很大方呀\n妹子：你错了，我一点也不大方\n那正好，我的缺点就是太大方，你赶紧给我电话，我要找你请教怎么才能对人小气点呢\n2你每认识一个朋友，都需要理由么？\n3我不要你的电话，我自己有一个，给我你的电话号码就可以了', '设立框架');
INSERT INTO `talk_art` VALUES (11130, '你真是个开心果', '1开心果壳很硬的，我全身上下哪有那么硬的地方，不过你可别乱摸，你一摸可能就有地方硬了\n2感觉开心吗？\n妹子：开心\n那就爽快点给我逗你开心的费用吧\n3我觉得火腿肠会比开心果更让你开心，关键是两样我都有哦', '设立框架');
INSERT INTO `talk_art` VALUES (11131, '太无趣，跟白开水一样', '1（针对白开水）你当着面夸我像白开水那么纯，我会不好意思的\n2（针对无趣）拐角就有个成人用品店，你去采购些情趣用品就不会觉得无趣了\n3（针对白开水和无趣）你把白开水用鼻子喝下去我想肯定会很有趣', '设立框架');
INSERT INTO `talk_art` VALUES (11132, '你是管什么的', '1我是管钱的，你有这个需要我可以帮你管的，利息是请你吃饭，比银行人性化多了\n2管饭，一日三餐，你来不\n3专管那些喜欢问我笨蛋问题的傻女生', '设立框架');
INSERT INTO `talk_art` VALUES (11133, '请教小朋子 朋友给介绍了', '（他？是男护士吗。。。）\n1见到你我感觉很遗憾\n妹子：?\n要是我小时候能认识你，打针时就不会疼得像杀猪那样叫了\n2有个护士朋友好处就是多，以后我在家躺着，可以帮我端茶递水呢\n3难怪现在街上有气质的女孩子越来越少，原来全跑去做护士了', '设立框架');
INSERT INTO `talk_art` VALUES (11134, '\\', '1宝后面还要加个贝才适合你\n2没错，而且还是无价之宝\n3你也宝一样，而且是活生生的宝，简称活宝', '设立框架');
INSERT INTO `talk_art` VALUES (11135, '太丑了', '1我很丑，可是我也不温柔，你再说我丑我就揍得你比我还丑\n2我眼睛里倒映出的是你的尊容呢\n3你有没有常识，男儿无丑相不懂么？', '设立框架');
INSERT INTO `talk_art` VALUES (11136, '你要是在再我好一点就更', '1你要是再知足一点就更更完美了\n2你把双腿分开，我会对你那个点很好的\n3我就是为了怕太完美导致物极必反才硬是咬咬牙保留那么一点的', '设立框架');
INSERT INTO `talk_art` VALUES (11137, '你身上看不到希望', '1你想让我去纹这两个字在身上吗？那会很疼的哦\n2我身上又没长眼睛，怎么看得到东西\n3穿这么厚怎么看得到，不然我们脱了衣服再看一次？', '设立框架');
INSERT INTO `talk_art` VALUES (11138, '不积极不阳光', '1（针对不积极）你把衣服脱光了在床上等我就能看到我积极的一面\n2（针对不阳光）你是植物么？需要我给你光合作用？\n3（针对不积极不阳光）那我就积极地将窗帘拉起来，阳光会很充足的', '设立框架');
INSERT INTO `talk_art` VALUES (11139, '我：没良心的，怎么不联', '女：不是没良心我怕你忙呢\n1我说你没良心，你就咒我眼盲吗？\n2亲爱的你真体贴，下次我们吃完饭买单时希望你将这份体贴发扬下去\n3你联系我，再忙我也会很开心的，看来我得纠正下自己的话，你不是没良心，是没脑子', '设立框架');
INSERT INTO `talk_art` VALUES (11140, '太胖', '1你终于发现自己该减肥了？\n2嫌肥别买，现在猪肉可是卖方市场\n3你是担心在床上被我压骨折吗？提前去买个人身意外险就万无一失了', '设立框架');
INSERT INTO `talk_art` VALUES (11141, '不吃早饭你不听话哦', '1不吃早饭是不动嘴，跟耳朵听不听话没关系好吧\n2乖，还是你比较听话，帮我把那份早饭吃了\n3听你话了就给我 TD 么', '设立框架');
INSERT INTO `talk_art` VALUES (11142, '遇到一个不爱说话的女孩', '1嘴是不是刚动过手术不方便说话呢，我家狗狗以前动完手术也是这样，都不叫唤了，吃点清淡的恢复得快哦\n2我对不爱说话的女孩子其实很感兴趣的，但你又不爱跟我说，这样吧，下次见面我送个笔和笔记本给你把要对我说的话写下来，这样咱们就方便交流了，我很体贴吧？\n3一直觉得自己不爱说话，想不到今天竟然遇到了对手，这让我很不爽，我们打个赌吧，谁先开口说话谁就算输，赌一顿饭，敢不？（然后去抱她或挠她痒）', '设立框架');
INSERT INTO `talk_art` VALUES (11143, '你一无是处', '1不会呀，能坚持住跟刻薄的你聊天我觉得是很大的是处了\n2如果你被我 TD 过就不会这么说了\n3请不要把你的特点随便转移到别人身上，这可不像脱衣服那么简单\n其他答案：不但一无所有，还是处（乾坤小婀姨）', '设立框架');
INSERT INTO `talk_art` VALUES (11144, '妹子：你不想混了是不是', '1我这个人做事从来都一丝不苟，绝不会混的\n2只要你这种人还在，我就一定混得下去\n3怎么，你敢看不起混混吗？', '设立框架');
INSERT INTO `talk_art` VALUES (11145, '你怕不怕(。。。。。）', '1我怕（。。。。。)不够多\n2去百度下郭美美的成名曲就知道了\n3请不要以你的胆量为标准来问我，我会觉得回答这问题都是一种对自己的亵渎', '设立框架');
INSERT INTO `talk_art` VALUES (11146, '还有一个，妹子 说：你眼', '（因为常用相关废物测试的回复= =）\n1钱要是进了眼里会很疼的，还是放口袋里好\n2我眼里现在明明是你，哪来的钱，还是说你觉得自己可以卖很多钱？\n3你眼里没有钱的话，我不介意你把钱都送给我\n其他答案：\n1.谁叫你一点都不值钱\n2.没有钱的话，那你请我吃顿饭都请不起\n3.在我的眼里你最值钱（从不善解人意）', '设立框架');
INSERT INTO `talk_art` VALUES (11147, '你这孩子，怎么这么调皮', '1你的不调皮可以中和我的调皮，以后我们的孩子就乖了\n2你想跟我要个调皮的孩子吗？那得先把嫁妆拿过来\n3看到好玩的玩具任何孩子都会忍不住调皮的（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11148, '一个 妹子 问我：男人，是', '1是的，但是，你肯定不会被搭的\n2女人，是不是都喜欢和帅哥搭讪？\n妹子：不会呀\n手伸出来，（打她一下）这是对你说谎的惩罚！你现在不是正在搭讪我嘛？\n3也不一定，同性恋就不搭女的', '设立框架');
INSERT INTO `talk_art` VALUES (11149, '女：你先回吧，不用等我', '1我正有要走的意思，你这么一说，搞得我倒不好意思了，你害的我很纠结呢\n2那好吧，如果我不在，你遇到危险就打 110\n3那我在家等你，你也快点，不然一会洗澡水该凉了', '设立框架');
INSERT INTO `talk_art` VALUES (11150, '我也没有打算领奖哦', '1你最近进步很多，越来越有自知之明了\n2拜托，那你不会是打算演讲得奖感言吧\n3你真没常识，举办方式不会把奖品快递到你家的', '设立框架');
INSERT INTO `talk_art` VALUES (11151, '扒了你的皮，怎么解。。', '1请问贵姓，姓周吗？\n2扒皮多疼，还是扒了我的裤子吧，那样你才会爽\n3虽然你长得像猩猩，但我长得可不像香蕉\n其他答案：弱弱地问一句，你是想扒了我的包皮？co男，baby！ohyes！        ', '设立框架');
INSERT INTO `talk_art` VALUES (11152, '女：女生讲了比较污的话', '    男：哟，我微信里，终于找到个真正意义上同流合污的人了', '设立框架');
INSERT INTO `talk_art` VALUES (11153, '不要污染我幼小的心灵', '1你幼小。。的心灵？请先不要侮辱我正常的智商\n2心灵幼不幼小看不到，但心灵外面的胸确实挺小\n3卖萌是发不了财的，卖身才可以，要卖身首先得污染你幼小的心灵', '设立框架');
INSERT INTO `talk_art` VALUES (11154, '不敢随便说话啊，不然某', '（ta 很少上线，我说过 ta 神出鬼没）\n1我觉得你做个会说话的鬼还稍好点\n2你已经迈出了成为一名合格黑客的第一步3你潜水这么厉害，以后游泳一定不能叫上你，不然你潜在下面一直偷看我内裤就完蛋了', '设立框架');
INSERT INTO `talk_art` VALUES (11155, '阿姨也饿了哦', '1岔辈分了，哪里能叫阿姨，叫你老太婆才贴切2那阿姨打算请我去哪里吃饭呢\n3我不喜欢人云亦云的女孩子', '设立框架');
INSERT INTO `talk_art` VALUES (11156, '碰见一极品', '妹子 说：你会照顾我一辈子吗，我不会做饭，不会家务，生病的时候会背我去看病吗？做错事情你会包容我，爱护我吗？ 男哥来点浪漫点的回答\n1难怪我一直觉得我们很有缘，今天终于发现原因了，原来你连的择偶要求都跟我一模一样\n2我以前的那个也不会做饭，不会家务，生病我也抱着她去看医生，在家里弄坏东西我也包容爱护她，遗憾的是。。。。她还是离我而去了。。。。从你身边我看到了她的影子。。。她是一只可爱的沙皮\n3我不能照顾你一辈子，因为我要比你离开这个世界，我不忍心看到你先离开我的样子；\n你不会做饭没关系，将来有钱咱们就出去吃馆子，没钱咱们还可以在家吃泡面；\n不会家务没关系，将来有钱可以请保姆，没钱咱们可以不买房子，这样家务都省掉了；你\n生病我会把医生背回来给你在家看病，我知道你不会喜欢医院的味道；\n你做错事情一定是因为我没有好好教你，没有好好帮你，我会尽量教你和帮你把事情做对，又怎么会不包容你爱护你呢', '设立框架');
INSERT INTO `talk_art` VALUES (11157, '女：壁咚', '     男：那我要是霸气的将你双手紧握，依偎在墙角，你会不会含羞欲滴，心跳加速呢', '设立框架');
INSERT INTO `talk_art` VALUES (11158, '如果我老了难看了你还会', '1你老了难看了你觉得我那时候还能年轻好看么，我又不是妖怪\n2你的意思是觉得自己现在还挺年轻挺好看？\n3肯定不会，不管你老成什么样，你妈肯定比你更老，我又怎么会爱她', '设立框架');
INSERT INTO `talk_art` VALUES (11159, '女：你能不能以后不要来', '1亲爱的你真体贴人，舍不得我累是吧，那下次你来找我也行啊\n2你又不是失踪人口，我找你干嘛\n3行啊，那你现在把青春损失费给我', '设立框架');
INSERT INTO `talk_art` VALUES (11160, '请教舞台男，给女孩发短', '1你知道最喜欢哼哼的是哪种家畜吗\n2你再哼我就“哈”，我们可以组成传说中的哼哈二将\n3哼字拆开，是口享，是暗示以后专门跟我玩口爆吗（慎用）', '设立框架');
INSERT INTO `talk_art` VALUES (11161, '每次 妹子 都很客气的说：', '1你打算怎么谢？你可千万别说以身相许，我要求很高的\n2咱们这关系还说谢谢就太见外了，你还是请我吃饭实际点\n3竟然会说谢谢？看来我低估了你的素质', '设立框架');
