INSERT INTO `talk_art` VALUES (11190, '妹子：你技术真好啊！', '1要分对象的哦，因为你让我很有感觉呢\n\r\n2那当然，我每天都买块猪肉来练习的\n\r\n3恩，因为现在富婆的要求都很高，所以不得不把技术练好些\n\r\n\n\r\n其他答案： 1.你想学呀？我免费教你好了。\n\r\n2.都是兰姐教我的。\n\r\n3.看跟谁比了。。 ', '化解IOD');
INSERT INTO `talk_art` VALUES (11191, '妹子：你不知道我们俩的', '2我知道，可是洗衣机不知道\n\r\n3为了做一个合格的居家男人，我想把洗衣粉的钱节省下来', '化解IOD');
INSERT INTO `talk_art` VALUES (11192, '对于那种甜蜜书香型的', '妹子，她们对于一些刺激性质的感官动作和话术，是不是不会拒绝？针对这一类型的 妹子 应该去哪里约会，或者约会时候该用什么方法最恰当！~？\n\r\n1甜蜜书香，是指喜欢往书上喷香水的女孩子么，还是喜欢将蜂蜜抹到书上大口朵颐的另类呢?如果是后者建议带她去医院就诊\n\r\n2任何女人都不会排斥刺激性质的感官动作和话术，关键是使用的时机\n\r\n3所谓甜蜜书香型是什么？书卷气浓厚的矜持 妹子?\n\r\n你应该从目标的生活习性和圈子来判断该带她去哪约会，她整天泡图书馆，你就带她去酒吧，她整天去酒吧，你就带她去户外。记住，给她平时经历不到的', '化解IOD');
INSERT INTO `talk_art` VALUES (11193, '妹子：我想找个能在市区', '如果说你没有这条件，我想我们还是做朋友吧！（小朋子帮我打压她）1市区有个监狱，那里面可以免费常住的，爱住多久都行\n\r\n2我想找个不那么庸俗的女友，看来我们确实只适合做朋友\n\r\n3不一定要做男女朋友，以你要求的条件，很多男人都会愿意的\n\r\n其他答案： 1：我也想找个能在市区买房子的女友，如果你有这个条件，那我们再来谈别的吧。\n\r\n2：原来你的择偶要求这么低呀。\n\r\n3：我想找个长相对得起市区观众的女友，如果你没有这条件，我想我们连朋友都没得做哟(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11194, '我的白马王子，你到底撒', '1等你变成公主以后\n\r\n2骑白马的不一定是王子，也有可能是唐僧；王子也不一定骑白马，很有可能他只是为了来骑你的', '化解IOD');
INSERT INTO `talk_art` VALUES (11195, '你也太色了吧你！', '1恩，从小我就对颜色特别敏感呢，但你是怎么知道我这个体质的？\n\r\n如果我是你，终于被帅哥色了一回，就不会问这个问题，而会马上去烧香还愿呢\n\r\n2女施主，空即是色色即是空，请放下执念吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11196, '呵呵。你泡-妞的手段蛮特', '1我什么时候泡学了，你认为每个跟你聊天的帅哥都是想泡你吗？\n\r\n2亲爱的，我根本还不了解你，并且我从来不会对还不够了解的妞有想泡的兴趣\n\r\n\n\r\n', '化解IOD');
INSERT INTO `talk_art` VALUES (11197, '妹子：可以一起去看落日', '落日。。。。我目前还没有后羿那么能射呢\n\r\n2一起看落日多没意思，干脆简单点\n\r\n3据说日落的时候许个愿就可以成真哦\n\r\n妹子：应该是流星原来你喜欢快的\n\r\n其他答案：小心砸到你的头（一直很想爱你 ）', '化解IOD');
INSERT INTO `talk_art` VALUES (11198, '妹子：我的经验告诉我，', '1看你外表纯纯的，原来对男人这么有经验\n\r\n2经验只能证明过去的事，不能决定未来的事，你其实可以改变下这种保守的思维方式\n\r\n\n\r\n', '化解IOD');
INSERT INTO `talk_art` VALUES (11199, '妹子：我感到悲哀，你的', '2我也感到很悲哀，我的行为只能让你受伤，却不能让你受精\n\r\n3唱：我料你现在很受伤很受伤，很受伤，大不了痛哭一场，日子要过路还长（任贤齐-很受伤）', '化解IOD');
INSERT INTO `talk_art` VALUES (11200, '妹子：我没成仙，道行不', '1但我觉得你很神\n\r\n2没鲜可以加点鸡精', '化解IOD');
INSERT INTO `talk_art` VALUES (11201, '妹子：你不是我想要的那', '1你想要哪种，我可以去买，现在百货很全的\n\r\n2我确实可以找到更好的女孩，但关键谁让我就只喜欢你呢\n\r\n3但算命的师父说你就是我的真命', '化解IOD');
INSERT INTO `talk_art` VALUES (11202, '妹子：你认同我的想法吗', '1我还不够了解你，所以不好评价，你可以先多给我讲讲你自己\n2寻求认同也是缺乏安全感的表现，我想我可以给你缺的东西\n3想法不重要，因为想再多没有行动也是空谈，关键是做法，所以。。要对我表白的话就赶快吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11203, '妹子：你觉得多交朋友的', '1好处就是经常有惊喜，比如像你的出现并让我认识了你\n2你觉得多交朋友的坏处是什么\n妹子：不知道\n没有坏处的事干嘛不多干呢\n3走投无路时借钱的对象会多些呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11204, '妹子：哈哈,难道你暗恋我', '1摸她的脸\n妹子：你干嘛\n看看这么厚的脸皮手感会是怎样\n2请告诉我怎么回答又能说实话又能不伤害你呢\n3现在女孩子都流行用反问句表白么', '化解IOD');
INSERT INTO `talk_art` VALUES (11205, '妹子：你不要太天真了，', '一个稳定的成熟男人，这你都能给我吗？你自己衡量下吧！\n1一份工作，稳定的成熟男人，宋山木无论体重还是事业都完全符合你的条件\n2我知道一家夜总会待遇不错，基本全是成熟稳重的男人去光顾，你可以去应聘的\n3现在这个用工荒的社会找工作会难么？我现在也许还不够成熟稳重，但迟早有一天会变成那样的', '化解IOD');
INSERT INTO `talk_art` VALUES (11206, '妹子：我只是喜欢落日黄', '1我知道有个地方欣赏落日黄昏不错哦，下次我们一起去感受吧\n2恩，一般偶像剧演到这里接下来就是 KISS 了\n3稍等（拿手机出来按几下）我们把手机调振动的才能避免打扰这个安静的氛围', '化解IOD');
INSERT INTO `talk_art` VALUES (11207, '妹子 跟她一刚结婚的姐妹', '1赌气说要找一个有权有势又有钱的男人作老公，就是结了再离都无所谓，只为了比过她的姐妹，我该怎么说？\n2我完全支持你的想法，到时候我们里应外合骗光那个男人的家产\n3有权有势又有钱？你让我太失望了，你漏掉了有才的呀，我一般看不起庸俗的女人呢\n4如果要对我表白也不用这么直接吧？\n妹子：我哪有\n我既有拳又没去势，每月还有几百块工钱，你不是说我是说谁', '化解IOD');
INSERT INTO `talk_art` VALUES (11208, '我说：你喊那么大声干什', '怕别人听不见啊？妹子：恩啊 就是怕别人听不见捏 哈哈哈\n1这样万一被我的粉丝们听到了她们会发疯的\n2但别人会以为我家是私人堕胎的诊所呀\n3你喊得像杀猪一样，明天人家上门来买猪肉怎么办', '化解IOD');
INSERT INTO `talk_art` VALUES (11209, '妹子：让我们做回真实的', '对我都好。，也不用那么累~\n1对你对我都好的方法是，用汇仁肾宝\n2做回真实的自己？外面人这么多，把衣服裤子都脱掉挺难为情的呢，咱们回去再做真实的自己吧\n3不用那么麻烦，困了累了喝红牛', '化解IOD');
INSERT INTO `talk_art` VALUES (11210, '有人说你很花心哦！', '1还有人说你很滥交呢，但关键是我相信你，完全不信别人造的谣\n2别人要说我像刘德华，我难道就会更帅吗？\n3说我花心你就来问，说我有钱又大方你现在是不是就该来借钱了', '化解IOD');
INSERT INTO `talk_art` VALUES (11211, '妹子：你觉得我胸部小还', '1那得看跟肉包子比还是跟小笼包比\n2亲爱的，我喜欢的是你的人，胸部大小真的不重要，鲍鱼不要太大就好了\n3每个人都会有缺陷，真的不用太在意', '化解IOD');
INSERT INTO `talk_art` VALUES (11212, '妹子：我就想找个依靠~可', '1（唱一首任贤齐的依靠送给她）\n2我当然不是你的依靠，我是你的港湾，依靠只是暂时的，港湾才是永久的\n3门外拐弯 50 米就有根电线杆，靠去吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11213, '妹子：你不要转移话题，', '1你先告诉我，为什么要问为什么？\n2喂神马，那是弼马温的工作，不是我的，我没这个义务\n3我没有转移话题，我是在使用话术（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11214, '妹子：我希望我能找到一', '1对我提要求能不能婉转点\n2怎么好，一夜七次？\n3你比紫霞还贪心，人家也就是要个 10000 年，你竟希望永远', '化解IOD');
INSERT INTO `talk_art` VALUES (11215, '妹子：都怪你~要不是你', '1确实怪我，我不该跟你扯上关系的\n2我一直很佩服你这种出了问题能迅速推诿掉的能力，可以教教我么，教会了一定请你吃饭\n3我终于进步了，要是按以前的做事方法，你现在已经不会跟我说话了', '化解IOD');
INSERT INTO `talk_art` VALUES (11216, '妹子：那我就是要骑在你', '1大头不行，小头可以考虑下\n2只有爸爸才让女儿骑头上，你觉得我长得像他吗\n3以我目测你的体重推断，你是想弄折我的脖子么\n其他答案：1：从来只有男骑女，没有女骑男。2：你喜欢玩这种姿势呀？ （一直很想爱你）', '化解IOD');
INSERT INTO `talk_art` VALUES (11217, '你不是说我不是你的菜吗', '1你不是我的菜，却是我的饭（FAN），我是很疼自己 FANS 的\n2正因为你不是我的菜，所以我打算来偷菜，我喜欢这种感觉\n3最近吃惯了大鱼大肉，想换换口味', '化解IOD');
INSERT INTO `talk_art` VALUES (11218, '\\', '2想认识帅哥先做自我介绍吧\n3这个真不重要，等我们多了解对方一点再说也不迟\n其他答案：1 请说中文（一直很想爱你）', '化解IOD');
INSERT INTO `talk_art` VALUES (11219, '你做过最疯狂的事是什么', '1喜欢上你\n2假装正常人\n3地狱 500 搭（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11220, '妹子：你说你不是大男子', '为什么要撒谎，一个人独断独行，不和我商量！\n1不和你商量，是不想让你操心，撒谎，是不忍心看你伤心，现在回答你这些问题，是表达我对你的真心\n2我确实不是大男子主义，不然你说这话早就被一巴掌抡上脸了\n3你喜欢优柔寡断前怕狼后怕虎的男人还是我？', '化解IOD');
INSERT INTO `talk_art` VALUES (11221, '\\', '1恩，早听说过最毒妇人心，我就是想见识下能有多毒，我的目标是超越尝百草的神农氏呢\n2爱的越深会恨得越深，但如果给我重来一次的机会，我不会后悔那深深的爱\n3你说的是谜男方法的事后后悔吗（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11222, '我是个慢热的人', '1我会随身携带打火机给你加热的\n2冬天抱着你睡觉肯定很舒服，暖和的感觉是逐级递增的\n3别人今天给你送花，要等到后年才会爱上他的那种么', '化解IOD');
INSERT INTO `talk_art` VALUES (11223, '妹子：你说我是上天赐给', '派你来保护我的~真的吗？我怎么没感觉到！\n1难道你非得从楼顶上跳下来一次才能感觉到吗\n2脱衣服，亲密，马上让你感觉到\n3可能从天上下来时没着陆好，摔到脑袋，感官神经受了损伤吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11224, '妹子：你没听清楚吗？你', '1小时候，老师经常也给我讲你这句话，后来事实证明他很多时候都是忽悠人的\n2为了保持礼貌，我就当没听清楚吧，同时也希望你不要再重复了\n3凡事都有对错两面，你看问题太单一了', '化解IOD');
INSERT INTO `talk_art` VALUES (11225, '妹子：如果和你分开是痛', '我愿意接受这样的痛苦~\n1如果分开会让你痛苦的话，我是无论如何不会分开的\n2你有自虐的情结呢，建议你用 502 把手粘在头发上，再使劲分开，我想那个痛苦更能满足你\n3分开并不痛苦，痛苦的是失去了一种习惯', '化解IOD');
INSERT INTO `talk_art` VALUES (11226, '妹子：怎么样？我的衣服', '你没有吧？！气死你~呵呵????\n1我嫉妒，我生气，我为什么没有你这样的一身行头，看来我无法跟你争夺丐帮帮主之位了\n2确实气得半死，我恨铁不成钢，我生气你怎么连最起码的穿衣品位都没有\n3衣服确实很好看，你是属于那种穿什么都好看的自然美类型，而且自然美最大的优点就是---如果什么都不穿会更好看', '化解IOD');
INSERT INTO `talk_art` VALUES (11227, '妹子：我想知道你吃相怎', '1我想先欣赏你的睡相呢\n2男人基本都差不多的，你爸爸怎么吃，我就怎么吃\n3吃什么？吃鲍鱼？你好色哦\n其他答案： 1：大象可是不能吃的呀，要杀头的哟。\n2：干嘛，你又不是我妈！\n3：那你想不想知道我的大象是怎么样的呢？（一直很想爱你 ）', '化解IOD');
INSERT INTO `talk_art` VALUES (11228, '妹子：你知道吗~你让你自', '1还好啦，我觉得让自己的女人在别人面前流水才是我的耻辱\n2那我让别人的女人在我面前流泪是不是就应该感到骄傲？你这是鼓励我去挖墙脚么\n3你说的对，下次你打算哭的时候我就把你一个人锁在家里', '化解IOD');
INSERT INTO `talk_art` VALUES (11229, '妹子：我丢不下心里的包', '1女孩子的包袱一般在肚子(子宫)里呢\n2有包袱多好呀，郭德纲就是因为包袱多才红的\n3没关系，亲爱的，有我在，一切包袱我会帮你扛着的', '化解IOD');
INSERT INTO `talk_art` VALUES (11230, '妹子：你说！我流泪的时', '我一个人默默承受生活压力的时候你又在哪里？~\n1你流泪时我的心在滴血，你承受生活压力时我在承受无法帮到你的内心摧残\n2我不忍心亲眼看到你痛苦的样子，于是躲起来了\n3你流泪时我在流精，你默默承受压力时我也正压在别的女人身上呢，不过她很大声就是了（恶搞）', '化解IOD');
INSERT INTO `talk_art` VALUES (11231, '妹子：抱歉我从不吃晚餐', '1你们家是有多穷？\n2不吃晚餐不用跟我抱歉，你该跟辛苦做饭的妈妈抱歉\n3虽然节俭是传统美德，但也不用省成这样吧\n其他答案： 1：反正吃了也是浪费粮食。\n2：你还想吃夜宵呀？\n3：知道你胖，但也不至于为了减肥这样亏待自己呀。（一直很想爱你）', '化解IOD');
INSERT INTO `talk_art` VALUES (11232, '妹子：你知道吗？我从来', '1傻瓜，顺理成章的事情，干嘛要整天去想呢\n2我在你心里难道就是那样的人么？你太不了解我了，这么开心的场面我怎么会错过呢\n3这个要实现其实很简单，下次我在你身边你却不伤心难过的时候，我会想办法把你变成那个状态的', '化解IOD');
INSERT INTO `talk_art` VALUES (11233, '妹子：那当然哦，我是主', '1恩，一看你就是《战国》里景甜那样的主角\n2如果我们抢完银行被抓了希望你还能继续保持口供一致\n3谁主角谁配角真的无所谓，因为日本爱情动作片一般都只有一个主角和一个配角\n其他答案：1：我们是在演春光灿烂猪九妹吗？你是主角猪九妹。\n2：小德子给慈禧太后请安了。\n3：据说女主角要上位，都的跟导演潜规则一番，请问主角，导演是哪位？（一直很想爱你）', '化解IOD');
INSERT INTO `talk_art` VALUES (11234, '妹子 望着我说：谁要是帮', '1建议你去信耶稣\n2咱们的想法总是这么心有灵犀地一致\n3你知道援龘助交际么', '化解IOD');
INSERT INTO `talk_art` VALUES (11235, '妹子：你知道死字怎么写', '1你不知道写吗？小学没学好哦，不过我可以教你\n2你是良品吗？\n3我懂的，小时候一个同学就是掉粪坑里淹死的，从那时开始老师就教我们要远离粪坑\n其他答案：1：横憋横折点。。。2：等等，我去查一下字典。3：多远？ （一直很想爱你）', '化解IOD');
INSERT INTO `talk_art` VALUES (11236, '妹子：我不笑，难道你要', '1你除了笑就是哭吗？这属于是情绪紊乱综合症\n2哭是释放压力很好的方法，你不懂么\n3我其实更喜欢看你哭笑不得的样子\n其他答案：1：我打算把缺德放到手推车上去，这样我可以推缺德。（郭德纲）\n 2：或者你可以啼呀？\n3：你以为你是哭笑佛呀，除了笑就是哭！ (一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11237, '听到你的话，我自己就要', '1我知道一般女孩子听我的话就想和我 TD,但想不到你听到我的话直接就有怀孕的感觉了\n2别担心，有些人在情绪特别感动的时候是有这样的反应的\n3坚持住，不能把营养吐出来，很伤身的，实在忍不住到嘴里了再喝下去就好了', '化解IOD');
INSERT INTO `talk_art` VALUES (11238, '妹子：下次你再穿成这样', '1下次和我逛街，别再鸡鸡歪歪的了\n2如果你还不去整容，也不要和我再逛街了\n3不和你逛街的意思是说我这身行头更适合与你开房对吗\n4怕我抢你的风头？放心，我会适当收敛的\n5你如果看不下去可以逛街给我买新衣服', '化解IOD');
INSERT INTO `talk_art` VALUES (11239, '妹子：以后你要多迁就我', '1你是喜欢那种我躺着，你主动爬上来的感觉吗\n2你主倒，我主上\n3我一般只迁就残障人士，你哪里残？脑袋？\n其他答案：1：明白，以后我会什么事情都先向你申请的。好了，请问我现在可以去上厕所吗？ 2：好的，以后赚龘钱养家的事就由你来主导了。 3：以后家里所有活都由你来主导好了。(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11240, '其实我也没和你吃饭的意', '1大排挡原来是这么理解的\n2你的裆排满了？我不喜欢太滥交的女人呢\n3我没有直接开房的习惯，先吃个饭比较有情调', '化解IOD');
INSERT INTO `talk_art` VALUES (11241, '妹子：我讨厌你跟着我，', '1其实完全不需要我帮忙，你的名声已经够坏了\n2我讨厌你走在我前面，不要让我的粉丝误会，OK?!!\n3那你可以跟着我，你的名声会大幅提高的\n其他答案： 1：盖过你的风采了？2：那好，总算以后不要陪你逛街了。 (一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11242, '我在想如果我不答应你的', '1我会很生气，后果很严重（模仿葛优语气）\n2有想这些的时间，干嘛不直接答应呢\n3当然严重，你后半生也许会生活在悔恨中呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11243, '看了我空间咋的不留言啊', '1因为对你空间很无语\n2盐吃多了不好，所以没留给你\n3看你的空间要留言，那看你的身体要流什么呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11244, '妹子：你这饭做得也太难', '1你吃了？？那份是我专门拿来毒耗子的呀！！\n2想不到我的饭会使人怀孕\n3我也想吐，但不是因为饭，而是因为你\n其他答案：好了，以后都你来做。(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11245, '我不想再见到你对我漠不', '1我的性格如此，不会刻意让你感觉到我的热情，但你在需要我的时候，我一定会出现\n2我不会让你自残双眼的\n3去买个墨镜吧，不够黑就拿墨再涂一遍，一定看不见', '化解IOD');
INSERT INTO `talk_art` VALUES (11246, '妹子：难道你不知道女士', '1我同意你说的，女士优先让开\n2不好意思，还真没分清楚你的性别\n3女士这个称呼跟你的年龄段不适合，你属于（小 P 孩或老太婆）\n其他答案：1：啊，原来你是女士呀，差点没认出来，实在是不好意思。2：女士优先，男士靠边。请问，我该靠那边呢？靠你身上？3：你们不是天天喊男女平等吗？(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11247, ' 我在进挪阶段的时候 &l', '这时候我该来强的~ 还是用其他的方法？还是我的铺垫没做好？\n1我来给你演段戏吧，霸王别姬的外传----霸王硬上弓\n2你听到什么声音没？\n妹子：没有啊\n你抛开的不是我的手，而是我的心，刚刚是它摔碎了的声音，你得赔我\n3得视情况而定，我不知道具体细节，所以只能说个大概：要么是前一步骤没做好，导致她还没解除防护罩，这个情况你可以做个服从测试，如果真是如此就返回上个步骤好好重新开始吧；还有一种情况就是她跟你闹着玩呢，但这个通过表情完全可以判断出来，那就不用顾忌什么，以打闹的形式进入角色，然后再继续肢体进挪就好了', '化解IOD');
INSERT INTO `talk_art` VALUES (11248, '当心我叫我老公揍你！', '1怎么奏？太监给皇帝奏本那么奏么\n2你这恶毒女人，想利用我铲除你老公？\n3我不是乐章，你老公也不是音乐家', '化解IOD');
INSERT INTO `talk_art` VALUES (11249, '妹子：你什么意思哦！&', '1说话怎么带刺，会扎到舌头的好吧\n2你脱掉裤子，我现在就可以给你证明我是不是男人\n3我觉得以你脸皮的厚度，我说什么根本影响不了你\n其他答案：1：谁是桑，谁是槐？2：你可以来检验一下我是不是男人。(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11250, '难道我就是你前女友的影', '1你要是影子，我就是你的太阳，有我在的地方，都有你的存在\n2你是你，她是她，我不喜欢你妄自菲薄\n3你不是别人的影子，是我的，如影随形，我走哪都要你跟着', '化解IOD');
INSERT INTO `talk_art` VALUES (11251, '妹子：你说你要钱没钱，', '想找漂亮的又顾家的，你凭什么啊你~\n1还好你不是我要找的类型，不然爸爸妈妈会很失望的\n2没钱可以赚，赚到可以买车买房，这些比你想变漂亮得往脸上动刀子自残强吧\n3这个答案以你的价值观是理解不了的，我就不浪费口水了\n其他答案：1：这个问题你应该去问我妈。2：你说你要脸蛋没脸蛋，要身材没身材，想找一个帅气的又顾家的，你凭什么啊你？3：谁说我没钱，看，我身上还有正好几毛呢。(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11252, '你又不知道我是个什么样', '1你不就是我面前这个样子吗？难道你整过容？\n2你怎么确定我不知道你是什么样子的人呢\n3大概知道。。。两个眼睛，两个耳朵，一个鼻子，一张嘴。。哦不，是两张嘴（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11253, '我说：反正你肚里的孩子', '妹子：是你的！\n1不会这么巧我上次去捐精的受赠者是你吧\n2那你让他叫声爸爸3从嘴里吞下去也会怀孕？', '化解IOD');
INSERT INTO `talk_art` VALUES (11254, '妹子：你太不会说话，太', '1你没预付我陪龘聊费，当然就是这个结果\n2谢谢你让我清静\n3你觉得自己很会说话和陪龘聊的话可以去声讯台兼职赚外快的', '化解IOD');
INSERT INTO `talk_art` VALUES (11255, '我过两天就要走了，你不', '1你想听我讲什么，我都可以满足你\n2还要两天？不能快点走么？\n3有一句话，但现在不说，等你回来再告诉你', '化解IOD');
INSERT INTO `talk_art` VALUES (11256, '呵呵，我怕被你女朋友知', '有女朋友怎么说，没有呢怎么说）这个问题我自己感觉总是回答不够好。\n1你喜欢自己打自己么？（暗示她就是你女友）\n2你这么壮实，谁打得过你呀\n3我女朋友那么多，不可能都知道的。。。至少你不会被群殴嘛\n其他答案： 1：没事，顶多她逼我收你做小三。 2：不知道了吧，我女朋友出门时给我戴了一个玄铁贞操锁，所以她很放心我的。怎样，要不要我给你看看那把玄铁贞操锁？ (一直很想爱你)', '化解IOD');
INSERT INTO `talk_art` VALUES (11257, '你怎么这么开心', '1跟你在一起不知道为什么就是特别开心，是因为你长得搞笑吗？\n2出门刚吃了一包开心果，我要将它的效果释放出来，不然就白吃了\n3我在幻想跟你开房的情景（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11258, '妹子：不清楚，你想什么', '1因为我对你来说没有可得感吧\n2你曾经被帅哥深深伤害过是么\n3真遗憾，你这些话让我对你也失去兴趣了', '化解IOD');
INSERT INTO `talk_art` VALUES (11259, '为了庆祝我找到男友（老', '1祝你们在天为比翼鸟，在地为连理枝，然后我就是猎鸟的和砍柴的\n2祝你们白头偕老，相拥长眠\n3你得先祝我逃离你的魔爪', '化解IOD');
INSERT INTO `talk_art` VALUES (11260, '妹子：我们一起去吧,我妈', '1你妈真好，我们以后一定要好好孝顺她\n2不会说，她是要选择直接动手吗？\n3关键男人打人比较疼，所以我想先了解下你爸的意见\n其他答案：1：你这么快就问过你妈了？ 2：你爸会不会说呢？ 3：那等我一会，我也去问问我妈。', '化解IOD');
INSERT INTO `talk_art` VALUES (11261, '我：要感谢我，就请我吃', '她：好啊，我请你买单~\n1买单没问题呀，一张纸能有多贵，你把饭菜钱付了就行\n2你这句话太老套了，我在小学就已经没人讲了，换个有创意的行不\n3中国绅士风度，凡事女士优先，买单当然也不例外', '化解IOD');
INSERT INTO `talk_art` VALUES (11262, '见面没多久就问：你结婚', '1（针对结婚）请问我们是来相亲的吗？\n2（针对多大了）你想了解我的生辰八字跟你合不合吗？\n3（针对结婚和多大了）没结婚，我的小孩 6 岁了', '化解IOD');
INSERT INTO `talk_art` VALUES (11263, '你没有女朋友？我不信？', '1你是 FBI 派来的还是居委会计生办派来的？\n2那你有没有过男朋友，还是不是处女？\n3你去派龘出所应聘应该会很顺利的', '化解IOD');
INSERT INTO `talk_art` VALUES (11264, '我想听听你对第三者的看', '1当局者迷，旁观者清，所以第三者看得最客观呢\n2凡事皆有两面，如果第三者真的出现，那也只能证明两人世界本来就有问题存在导致不牢固呢\n3现在很流行小三哦，一切能流行起来的事物我觉得都可称之为时尚', '化解IOD');
INSERT INTO `talk_art` VALUES (11265, '妹子：去！把碗和衣服洗', '1请不要抢我的台词\n2如果你是认真的，我想先把你的脑洗了\n3亲爱的，你可以放心，我绝不会把你惯成一个好吃懒做的女人\n其他答案： 1：好，赶紧把身上的衣服脱下来呀。\n2：拿衣服洗碗吗？3：是男人就不能入厨房！', '化解IOD');
INSERT INTO `talk_art` VALUES (11266, '你经常这样亲别的女人吗', '1你难道希望我经常这样亲别的女人吗？\n2六岁前经常这样亲各种大姐姐，长大后就不行了\n3别的女人经常想这样亲我，不过都被我拒接了', '化解IOD');
INSERT INTO `talk_art` VALUES (11267, '你别癞蛤蟆吃天鹅肉了哦', '1但我真的不会嫌你像癞蛤蟆\n2天鹅在哪没看到，野鸡面前倒是有一只\n3原来你喜欢动物这么重口味的玩法', '化解IOD');
INSERT INTO `talk_art` VALUES (11268, '妹子：如果我家人不同意', '1那就坚持到他们同意为止\n2为什么不同意呢？怕我太优秀么？\n3你居然这么快就想到见家长了呀。。。怕我跑掉么', '化解IOD');
INSERT INTO `talk_art` VALUES (11269, '妹子：反正不管怎样。我', '1怎样是你亲戚？不着得罪了你？\n2你有你的原则，我有我的中指（比中指，慎用）\n3新发现哦，第一次听说你有原则呢\n其他答案：1：你的原则就是固执？2：我又不是你爸，干嘛要管你？(一直很想爱你 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11270, '这种话你是不是和很多人', '1哪有很多人，绝对不超过 500 个\n没有啊，刚从电视里学的，第一次实践呢\n你经常听到别人跟你这么说吗？', '化解IOD');
INSERT INTO `talk_art` VALUES (11271, '我好想好想那个······', '1那快把裤子脱了，不然弄湿了又得拿去洗呢\n2心动不如行动，想到就去做呀\n3哪个？', '化解IOD');
INSERT INTO `talk_art` VALUES (11272, 'TD 的时候 妹子：你捏我咪', '1亲爱的，要控制自己对你的爱真的很困难\n2你吸我 GG 的时候能不能重点\n3只有在前戏的时候将我的力度分化一些出去，真正上马的时候你才能承受得了我的冲击呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11273, '男:我来帮你拍照', '洗照片的费用我 1 你 9！OK？妹子：成交。。。\n1那好，脱衣服吧\n妹子：干嘛脱衣服？ \n拍艳龘照不脱怎么拍2我就喜欢你这么爽快的女孩子，然后我的摄像费你打算怎么给呢\n3洗完照片你请我去洗桑拿吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11274, '老实说你是不是和女人开', '1你该不会只跟处男交往吧\n2老实说你是不是和男人开过房\n3我宝贵的第一次就是这么失去的。。。请不要提我的伤心往事', '化解IOD');
INSERT INTO `talk_art` VALUES (11275, '你以后发达了，会不会包', '1你以后做了二奶，会不会发达啊？\n2别开玩笑了！光你就够我受的了，那还有心思包龘二奶\n3不要二奶，太费钱了，小三会好一点', '化解IOD');
INSERT INTO `talk_art` VALUES (11276, '最后我需要让我感动的表', '1我不善于花言巧语，我喜欢从行动上体现我对你的爱\n2我不觉得你是注重所谓口头表白的肤浅女孩子\n3有的人会因为一朵花而感动，有的人会因为一个钻戒而感动，你先告诉我，你知道感动是什么吗？妹子：BLBLBLBLBL\n我想，目的性很强地刻意去说一句话是很难让你体验感动的', '化解IOD');
INSERT INTO `talk_art` VALUES (11277, '有自信没自信，跟你没关', '1有关系呀，一般跟自信点的女孩子相处起来会令人愉快很多呢\n2我以前的女朋友就是因为对自己不自信，很不放心我，整天疑神疑鬼最后导致我们分手了。。你觉得两个人在一起老是去查对方应该吗？\n3我只说自信不自信，没说要发生关系呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11278, '给我让我和你交往的理由', '1因为找不到不和我交往的理由\n2做任何事都需要理由吗？这样的人生会很累的。。你让我想起了一个朋友(LBLBLBLLB)\n3因为有爱', '化解IOD');
INSERT INTO `talk_art` VALUES (11279, '给我相信你的理由', '1给我个不相信我的理由\n2相信一个人需要理由吗\n3信任是互相的，因为我相信你，所以我觉得你也会相信我', '化解IOD');
INSERT INTO `talk_art` VALUES (11280, '我回家告诉我妈妈你耍流', '1你真没常识，看到耍流氓的应该打 110 好吧\n2这么迫不及待想叫我见家长么，我们是不是发展的太快了\n3如果你妈妈知道终于能有流氓肯光顾你了，她得多么感动呀', '化解IOD');
INSERT INTO `talk_art` VALUES (11281, '我就是那么小气怎么样啊', '1我为你感到遗憾，如果你早生一百多年就出名了，巴尔扎克会把你写进小说里替换掉葛朗台的\n2就是因为气小，所以供氧不足，胸部才会长不大\n3以你这么小气的性格我很难想象每个月都要买卫生巾的这笔巨款你是怎么付得出去的\n其他答案：听过“气鼓鼓”这个词么？怎么你这么小气也不鼓的？不科学啊(从不善解人衣 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11282, '干嘛叫我小妞！', '1难道你喜欢我叫你小龘姐么\n2把妞字拆开你就知道为什么了\n3那你说说你希望我叫你什么？我参考一下', '化解IOD');
INSERT INTO `talk_art` VALUES (11283, '马上我也有房了', '1是那种积木玩具模型吗？\n2要房还不简单，随便找个酒店我和你开间就是\n3我不会因为你有房而爱上你的', '化解IOD');
INSERT INTO `talk_art` VALUES (11284, '女：我没微信（为什么要', ' 男： 我知道你把微信偷偷藏起来待到那个有趣的灵魂出现，你就会打开心门，柔情似水', '化解IOD');
INSERT INTO `talk_art` VALUES (11285, '你们读书人都跟你一样这', '1我这算什么，有些读书人读到极处都是直接用对联交流的，有时候想上大号厕所里却有人占着，急得一下子对不上来，就只能拉在裤子上了\n2想跟上我的思维节奏，你多读几本书自然就跟我有共同语言了\n3不一定，要看读什么书，读了金瓶梅肉龘蒲团什么的就变成我这样了', '化解IOD');
INSERT INTO `talk_art` VALUES (11286, '妹子：以后谁再说我可爱', '吧哥回复：你好可爱！\n妹子：便宜你，去跳粪坑吧！1我夸的是河。。。\n2难怪没人说你可爱，原来大家都在粪坑里呆着呢\n3谢谢你便宜我，但我没有占女孩子便宜的习惯，你经常喜欢让人占便宜么？', '化解IOD');
INSERT INTO `talk_art` VALUES (11287, '我可不是外星人！', '1我也不是黑超特龘警，你不用害怕\n2但你长得很像外星人，可以去参加 ET 模仿秀呢\n3你真让我失望，我一直期待有外星人能来把我带走的', '化解IOD');
INSERT INTO `talk_art` VALUES (11288, '女：谁给你的勇气', ' 男：我是因为看到这张照片，才这么勇敢的，我不想错过我内心的？（发她的照片）', '化解IOD');
INSERT INTO `talk_art` VALUES (11289, '你只是想和我做朋友？', '1我对你还不够了解，我想多了解你一点再说其他的吧\n2听你这问题，你是想跟我做什么？我很感兴趣\n3宝贝，你很失望么？', '化解IOD');
INSERT INTO `talk_art` VALUES (11290, '我可不是小三！', '1恩，轮我后宫排行的话，你得是小六\n2明天给你立个牌坊---《不是小三》，怎么样\n3所有杀人犯也说自己没杀人呢', '化解IOD');
INSERT INTO `talk_art` VALUES (11291, '女：再见的表情', '    女：？？？？      男：再次相见，希望你温柔一点', '化解IOD');
INSERT INTO `talk_art` VALUES (11292, '你不会真的被女人给骗怕', '1其实，以前我是被男人给骗怕了，后来我就喜欢女人了\n2是呀，以前被女人骗光我存了几十年的小蝌蚪\n3那你被男人骗过吗', '化解IOD');
INSERT INTO `talk_art` VALUES (11293, '你在我眼里就是一个废物', '1你在我眼里就是一个废品站，各种废物都去你那卸货哦\n2你喜欢垃圾桶和废物这种奇怪的角色扮演么\n3彼此彼此，但我们还是有区别的，我是可回收的，你是不可回收的', '化解IOD');
INSERT INTO `talk_art` VALUES (11294, '怎么让女人主动发信息的', '1我跟你赌一百块，今天一定是我给你先发信息\n2我手机好像出问题了，一到某个时段就好多信息收不到，你晚点到那个时候帮我个忙发几条信息给我，我想看看到底收不收得到\n3手机闹铃坏了，我 X 点有个会议，所以想麻烦你帮个忙。到那时候短信通知我一下，我记性不太好哈哈哈', '化解IOD');
INSERT INTO `talk_art` VALUES (11295, '女：戴套吗', '  男：你的话我必须戴，你长得这么清纯呆萌，我不让你有后顾之忧', '化解IOD');
INSERT INTO `talk_art` VALUES (11296, 'W:你回的太慢了，在你回复', ':那你就去吃呗\n1不吃了，最近减肥中，不节制饮食的话你就我的榜样\n2边吃夜宵还边等你回复，急都急死了，哪里还吃得下\n3说到吃的，我知道有一家很特色的小吃哦，你饿没，带你一起去吧', '化解IOD');
INSERT INTO `talk_art` VALUES (11297, '不是说了吗 不要再联系我', '1太久不联系，我怕自己会忘了你的\n2不是我要联系你的，是我的电话想你的电话了。。。\n3女人一般都口不对心的，我怕误会你的意思给彼此留下遗憾，所以才厚着脸皮试一试，你可以生气，你可以不屑，但，真的不可以不让我联系你', '化解IOD');
INSERT INTO `talk_art` VALUES (11298, '即使我再认真你也认为我', '1相信我，我真的从没觉得你是在扮演小丑，我觉得，你就是小丑\n2傻瓜，小丑要是扮演好了，你就是幽默大师，关键看自己怎么定位了\n3我没告诉过你，我最喜欢的就是小丑么？（抱住她）\n其他答案：你虽然丑，但是很温柔(从不善解人衣 )', '化解IOD');
INSERT INTO `talk_art` VALUES (11299, '女：与妹子谈旅游', '   男：你去多的地方多吗      女：还没去到你心里，能叫去过很多地方嘛', '化解IOD');
INSERT INTO `talk_art` VALUES (11300, '那你也要穿的帅气点啊，', '1第一次听说你有脸\n2太帅了别的女孩子都会被我吸引过来的，到时候你就傻眼了\n3你直接戴个面具不是更简单么', '化解IOD');
INSERT INTO `talk_art` VALUES (11301, '你只有在无聊的时候才想', '1你怎么这么没自信呢，要坚定自己的信念-------我有聊无聊都不会想你才对\n2因为你是我专属的灵丹妙药，只有你能拯救我的寂寞\n3其实无聊的时候我一般都去嫖娼的', '化解IOD');
INSERT INTO `talk_art` VALUES (11302, '女：喜欢一个人时什么感', '   男：就是一眼触电，一眼就勇敢，一眼想被你榨干，一眼就想和你吃早餐的那种感觉', '化解IOD');
INSERT INTO `talk_art` VALUES (11303, '嘴巴给我放干净点！', '1（摸自己嘴）我嘴巴上有脏东西吗？\n2 你多虑了，其实我有每天刷两次牙的习惯\n3（慢慢逼近她）\n妹子 退开:你干嘛？\n我想看看你干净的嘴巴是什么样子', '化解IOD');
INSERT INTO `talk_art` VALUES (11304, '\\', '1要直接搬来我家住吗\n2那你现在赶紧借我点钱吧\n3想要人家主动联系你就直说，还拐弯抹角的', '化解IOD');
INSERT INTO `talk_art` VALUES (11305, '女：额，我们能不能不聊', ' 男：这个可以拉近我们彼此的距离，达到零点零一公分，我就可以顺理成章的对你，柔情似水', '化解IOD');
INSERT INTO `talk_art` VALUES (11306, '我已经不指望你请我吃饭', '1恭喜你，终于顿悟了\n2那你是改变主意打算请我吃饭了?\n3直接指望我请你开房吗（慎用）', '化解IOD');
INSERT INTO `talk_art` VALUES (11307, '那你就去找一个你喜欢的', '1实际上我正在这么做\n2要真能碰到我喜欢的女孩子，别说女友，让她当女神又何妨\n3粉丝太多，导致我很难取舍呀，我不忍心伤害她们其中任何一个人', '化解IOD');
INSERT INTO `talk_art` VALUES (11308, '女：你是猴子派来的逗逼', '   男：我是月老派来的........男朋友', '化解IOD');
INSERT INTO `talk_art` VALUES (11309, '你牵我手做什么~？！或我', '1怕你摔倒啊，地板砸坏了要赔钱的呢\n2嘘。。。小声点，我发现那边那个女的盯我很久了，被看得浑身不自在，为了绝她的念头，你假装一下我女朋友吧\n3我刚忽然感觉到天旋地转，呼吸不过来，情急下未经同意借你的手扶了一下，对于擅自触摸你私人财物表示歉意，如果你还有不满，随时也可以牵回来的', '化解IOD');
INSERT INTO `talk_art` VALUES (11310, '说实话，我到底在你心里', '1你看过白眉大侠么，刀是什么样的刀，剑是什么样的剑，再接下来后面那句形容词就很适合你\n2亲爱的，我认为，你不会因为我的赞美而抬高价值，也不会因为我的贬低而降低价值，无论别人怎么看，你依然是你，对吗\n3在我心里当然就是放在心里的人咯', '化解IOD');
