INSERT INTO `talk_art` VALUES (11313, '女：你不老实', '（求个好笑点的 ，能曲解的，嘻嘻）\n\r\n12你就希望我对你不老实是吧，想得美\n\r\n13恩，还是你老湿\n\r\n我一没摸你二没亲你还嫌我不老实。', '联系感建立');
