INSERT INTO `talk_art` VALUES (11318, '妹子：你的 星座让我感到', '1难道你要跟我异地恋？\n\r\n2你的星座让我感到你很迷信\n\r\n3早知道有今天我就把星座冷读术好好研究下了（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11319, '每天只准联系一次', '(PS:其实这句话是我说的，掌握主动。假如是正在交往中，女人这么提出来，怎么打发她。)\n\r\n1那一次就用在你打电话叫我起床吧，省得定闹钟了\n\r\n2恩，不错，懂得帮我省话费，以后应该是个贤内助，给你加分\n\r\n3中国移动或者联通都很讨厌的你这种类型的人呢\n\r\n其他答案：一天联系一次，每次 24 小时（一直很想爱你） 一次都不联系行吗？（一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11320, '妹子：哈！看见你这样，', '1你知道吗，从一个人的言语可以看出她的品德和素质\n\r\n2哪还用得着别人帮，你自己就是一位天使了，天上的鸟屎姐姐\n\r\n3还好你出了这口恶气，要是一直憋着出不来，呆会放个屁，我死得更惨', '联系感建立');
INSERT INTO `talk_art` VALUES (11321, '妹子：我们之间有隔阂！', '有隔阂不一定是坏事，距离还能产生美呢', '联系感建立');
