INSERT INTO `talk_art` VALUES (11323, '妹子：呵呵，你到底还是', '1你没听过什么叫欲擒故纵，后发制人么\n\r\n2这个领域你赢一回合而已，大战三百回合你就知道输字怎么写\n\r\n3你不认识书吗？一本本的是实体书，能放电脑和手机里的叫电子书', '联系感建立');
INSERT INTO `talk_art` VALUES (11324, '你这个号码不错哦，让给', '2好啊，你开个价\n\r\n3号码的主人更不错，一起送给你吧。。。想得美哦你', '联系感建立');
