INSERT INTO `talk_art` VALUES (11337, '为什么眼泪是咸的?', '12早叫你吃菜别放那么多盐，你看看，现在好了吧\n13因为咸蛋超人（奥德曼）的眼睛是咸鸭蛋\n14忘了放糖吧', '联系感建立');
INSERT INTO `talk_art` VALUES (11338, '那你不要跟我说话好了', '12不说话那该跟你做什么？你好色呀\n13好啊那给我唱首歌吧\n12但我一看到你这样长得好玩的女孩子就忍不住要跟你讲话呢，怎么办', '联系感建立');
INSERT INTO `talk_art` VALUES (11339, '不要为了结婚而结婚。我', '12你是为了结婚而跟我结婚的吗？\n13你真诚实，又发现你一个优点了，这让我越来越急不可待跟你结婚了\n3 我哪是为了结婚而结婚的，我是为了长期霸占你的身体呀（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11340, '很多时候会这样，一样东', '我为什么要给你或我就是不给你 （与对方已经有很强的熟悉度， 属于一种相互打压的玩笑话\n语）12又不是要你把第一次给我，搞得这么小气干嘛\n13我再给你最后一次机会，到底给不给\n女：就是不给\n直接挠她胳肢（多好的进挪机会）\n3 请不要对我用猫绳理论（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11341, '你的搭讪方式好老套哦', '12我一般是因人而异的，对什么类型的人就用什么类型的方式\n13那你觉得自己这种类型的适合用什么方式搭呢\n14这个东西就跟情歌一样，越老越有共鸣', '联系感建立');
INSERT INTO `talk_art` VALUES (11342, '呸', '11能不能稍微有点素质，你这么随地吐痰我跟你站一起都脸红12你呸错对象了，我不是红十字会的\n根据你这一呸的口水量和口气腥度，我判断你最近有点上火，去买盒牛黄解毒片吃吧', '联系感建立');
INSERT INTO `talk_art` VALUES (11343, '搭讪过程中~~~~babalabnabbl', '男：你电话号码多少呀？\n女：10086\n求楼主还有什么好的回应？？？\n我的答案是：那服务态度是不是很好呀？？\n12原来你是在声讯台坐台的\n13然后查动物园的号吗？\n14你是移动的呀，移动好，动起来才会爽', '联系感建立');
INSERT INTO `talk_art` VALUES (11344, '3 个嫉妒话术~！', '1（大众方法）在 妹子 面前赞美其他女人（特别是要针对 妹子 不具备的优点）\n2（想让她嫉妒某女）晚上 XX 约了我一起吃饭，你觉得我该穿哪件衣服合适点呢\n3（如果自己嫉妒某男）这个男人很不错哦，一看就是个好男人，我看得出来他很需要你，并且很怕失去你，现在这么老实的人不多了，你可要好好把握呀', '联系感建立');
INSERT INTO `talk_art` VALUES (11345, 'ＭＭ：放屁！', '12我一般用屁股放，想不到你用嘴都能放\n13你想用放屁来吸引我的注意吗？这招倒是有点创意\n14好的，亲，请问您需要几个？亲，你那用什么快递能到呢？', '联系感建立');
INSERT INTO `talk_art` VALUES (11346, '你觉得我是怎么样的女人', '1 你是一个执着的女人\n女：为什么\n因为我不告诉你为什么你会一直追着我问（非原创）\n11你是个缺乏安全感的女人，因为你一直需要得到别人的认同12夸我一句，我就告诉你\n女：你长得很帅你是个诚实的女人（非原创）', '联系感建立');
INSERT INTO `talk_art` VALUES (11347, 'ＭＭ：我靠！', '12我比较喜欢能自力更生谁也不靠的女人\n13请别靠过来，男女授受不亲\n14你能不能注意点站姿，别这么懒，站哪都想靠着', '联系感建立');
INSERT INTO `talk_art` VALUES (11348, '不带你这样欺负人的！ ！', '1（针对头疼）虽然你长得有点像孙猴子，可我也不是唐三藏，你头疼关我白龙马事\n2（针对欺负）我从不欺负人，只是偶尔欺负下小猫小狗\n3（针对欺负和头疼）欺负是在磨练你的意志，等你什么时候头不疼了就可以来磨练我的身体了', '联系感建立');
INSERT INTO `talk_art` VALUES (11349, '女：最近比较烦', '男：烦什么？说来听下\n女：没什么了\n（求接下如何回复比较有趣。 。 。 。）\n11你是想听李宗盛那首歌是吧，难怪不能说来听，得用唱的\n12烦，没什么。 。 。联系起来是不是可以理解成大姨妈该来却没来呢\n13讲一讲嘛，我也挺烦的，讲出来给我解解闷呗', '联系感建立');
INSERT INTO `talk_art` VALUES (11350, '我漂亮不', '1 漂亮。 。 。 （一会自捶胸口）\n女：你干嘛\n刚说了违心的话，心脏疼\n11你在我身边让我觉得很有面子（非原创）\n12跟凤姐比还是跟芙蓉姐比？', '联系感建立');
INSERT INTO `talk_art` VALUES (11351, '女：问你个问题哦，你为', '7我觉得你很懂得饮水思源，这点我很欣赏，相比之下，养殖场的猪就从来不会问饲养员这个问题\n8我不知道是什么令你误会的，但我的本意，是想叫你请我吃这顿\n9最近胃口不好，我想吃饭时看到你的吃相我会比较有食欲', '联系感建立');
INSERT INTO `talk_art` VALUES (11352, '我很自恋的', '11那你品味可不咋地呀12我也很自恋的，不如我们躺在一张床上比一比，看谁比较不会移情别恋吧那你习惯用哪只手恋自己', '联系感建立');
INSERT INTO `talk_art` VALUES (11353, '你偏爱幼齿？（这个测试', '5我没有收集牙齿的癖好\n6比起牙齿，我更喜欢女孩子的舌头\n7你要说象牙的话，幼齿可不值钱', '联系感建立');
INSERT INTO `talk_art` VALUES (11354, '女生说： 我只喜欢靓仔', '6没关系，我只喜欢丑女，我觉得你很符合要求\n7你说的喜欢靓仔是指喜欢将最靓的那个宰掉么你的喜好和审美正好成反比', '联系感建立');
INSERT INTO `talk_art` VALUES (11355, '女：和你走在一起，完全', '3这个我明白，所以在我的光环下你要学会提高自己了\n4你从什么时候开始有面子了？\n5如果你有体力的话，我不反对你跑步前进，这样看上去别人就会以为我在追你，只要他们不把你当小偷，你会很有面子的', '联系感建立');
INSERT INTO `talk_art` VALUES (11356, '我很聪明的', '11你在跟我解释自作聪明这句成语的意思吗\n12有多聪明？你会叼报纸回来吗？给你脖子上套个菜篮子你能去菜市场买菜吗？\n13是吗，看来人真是不可貌相啊', '联系感建立');
INSERT INTO `talk_art` VALUES (11357, '女：我无法承受你再让我', '5去买个千斤顶吧，修车的用这个连汽车都能承起，别说是失望了\n6希望越大，失望越大，你如果不抱希望，得到的往往就是惊喜\n7这是个磨练坚强意志的好机会，我在帮你提高自己呢', '联系感建立');
INSERT INTO `talk_art` VALUES (11358, '女生问： 你是做什么的', '5做鸭的，烤鸭，啤酒鸭，盐水鸭都会做呢\n6有凳子的情况我会选择坐凳子，地上我基本是不会坐的\n3 那要看面对什么了，面对你我就适合做男朋友', '联系感建立');
INSERT INTO `talk_art` VALUES (11359, '你不用天天给我短信电话', '6有负担是想表达我在你心目中有很重的份量吗\n7我觉得你还是让我继续短信电话的好，因为我向来对朋友都是直接去串门不带打招呼的\n3 亲爱的，我知道你在心疼我的话费，这让我越来越爱你了', '联系感建立');
INSERT INTO `talk_art` VALUES (11360, '女：你是？', '1 你知道吗，你冷漠的言辞深深伤害了一个帅哥的心。 。 。 。\n11你是不是经常忘了把朋友的号码存起来呢\n12给你三个答案 A 前男友 B 现男友 C 下任男友', '联系感建立');
INSERT INTO `talk_art` VALUES (11361, '女：我不想那么快付出！', '4菜市场里你不给钱人家是不会把猪肉给你的\n5你有被害妄想症吗你是想我一直付出然后伤害起来会比较方便是吧', '联系感建立');
INSERT INTO `talk_art` VALUES (11362, '感觉你挺眼熟的，你之前', '5其实…我喜欢你这个套近乎的方式\n6我觉得你也挺眼熟的，上辈子我们是不是认识\n7有的，之前在这里掉了钱所以回来找找，请问是不是你捡到了', '联系感建立');
INSERT INTO `talk_art` VALUES (11363, '\\', '2那你是想老牛吃嫩草咯？\n3挺识货的嘛\n我身上有个地方是嫩中之嫩，你表现好可以考虑给你尝尝鲜', '联系感建立');
INSERT INTO `talk_art` VALUES (11364, '我说过， 我想谈次恋爱到', '我不可能这么草率的决定， 如果你硬要答案， 我只能说 sorry\n11你是不是想太多了，你跟每个异性朋友都这么说么\n12我们的目标一样，但达到目标的方法有分歧呢，我喜欢听不同的意见，这样很有挑战性哦\n3 我不要答案，我对男 PUA 没兴趣，我只要你（恶搞）', '联系感建立');
INSERT INTO `talk_art` VALUES (11365, '女：这个要看你表现了！', '3关键是我的表现太好了你的情敌会比现在多很多的\n4要普通女人满意很简单，但要你满意我得冒着肾虚的危险呢床上的表现吗？你真色呀', '联系感建立');
INSERT INTO `talk_art` VALUES (11366, '女：我不为你的真诚所动', '5为这个而动吗（挠她痒痒）\n6你是拉拉吗？\n7等我把我的兄弟放进你身体了，你自然就动了（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11367, '我觉得我从来都没有真正', '1 莫非你。 。 。喜欢动物？\n5那你爱谁？如来佛还是玉皇大帝\n6如果是因为怕受伤，那你就错过了爱的幸福；如果是因为没有发现值得你真正去爱的人，那你就错过了我\n其他答案： 那你觉得有没有哪一个人真正爱过你？（一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11368, '我兄弟说他老婆在做展会', '一个老外跑过来搭讪，兄弟老婆说我已经结婚了，结果老外很失望的走了！ 我兄弟的老婆是想单纯的告诉我兄弟这件事情，还是有其他的想法！ 是你的话，你会怎么做！ ？\n1其实你显出咱们中国人落落大方的一面，可以跟老外介绍一下你老公呀，然后他也该显示外国人的大方的一面，让他请咱们吃个饭嘛\n2想不到还有品味跟我一样这么古怪的人，还是跨国的\n3恩，不错，挺爱国的', '联系感建立');
INSERT INTO `talk_art` VALUES (11369, '你好像对很多事情都没多', '10恩，现在习惯每天晚上到梦里与睡神沟通\n11就是以前与人沟通太多了，我逐渐对这种虚伪的生物失去了兴趣\n那我们开个房去试着沟通下？（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11370, '女：你会等我吗？', '3不会，我会陪着你一起前进，而不是先走一步去等你\n4会的，我在床上等你\n3XX 酒店 XX 房，不见不散', '联系感建立');
INSERT INTO `talk_art` VALUES (11371, '一个 妹子 调侃我:HI！ 小', '5你从哪里看我小了，掏出来吓死你哦\n6我发现你一个很大的优点----看人的眼光不错\n7你这搭讪方式可不怎么样，有点老套了', '联系感建立');
INSERT INTO `talk_art` VALUES (11372, '女：不熟悉的人觉得我很', '只有我最好的朋友知道，其实我就是一疯子。我：你就是一疯子\n女：那你还要一疯子，看来你比疯子还疯！哈哈\n5我的志愿就是做一名精神病院的院长\n6我下一步打算去抢银行，需要一个不怕死的帮手\n7我要证明自己是一个超级 PUA，因为据我所知，还没听说哪个PUATD 过疯子呢（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11373, '女：你以为你是我啊！和', '1请不要做这种假设，一想到要变成你那样我就从心底难受\n2恩，要论泚尿我绝对没你泚得远\n3恩我来一次高潮就得歇了，你能一直来呢\n其他答案： 我还以为你想说你中有我，我中有你呢。 ', '联系感建立');
INSERT INTO `talk_art` VALUES (11374, '女：你会回信息给我吗？', '3稍等下， （马上拿手机给她发信息）--会\n4不会，我会直接给你打电话，因为我喜欢听你的声音\n5现在流行微信了，又好玩又省钱，比短信有意思多了，我给你装一个吧\n其他答案： 1：看什么信息了\n2：信息费你得给我报销\n3：有什么好处？ （一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11375, '女：你喜欢，但是我不喜', '5你总是这样故意唱反调来显示独特个性么\n6那就对了，我喜欢的连你也喜欢不是显得我品味差么\n3 你喜欢我就可以了，其他夫唱妇随', '联系感建立');
INSERT INTO `talk_art` VALUES (11376, '借我 1000 元，我急需', '关键是你们关系到哪一步了，以下答案是假定不借情况下的恶搞\n1 我的钱存了 100 年的死期，暂时拿不出来（慎用）\n5打胎要不了那么多钱吧（慎用）\n6我不用 1000，你借我 500 就解了我燃眉之急了（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11377, '我从后面蒙住 妹子 的眼睛', '问她是谁，女：是猪！ 我该怎么回击！\n1我问的是蒙眼睛的那个，不是被蒙的那个\n2你见过猪蹄子长五根手指什么样吗？没见过吧，但我刚见识了\n人长一幅猪脑子什么样， 一点常识都没有3你以前经常跟猪这么调情吗？', '联系感建立');
INSERT INTO `talk_art` VALUES (11378, '当时在酒吧里面做客户经', '沟通的时候，其中难住我的一句话！\n女孩：“你要我电话干嘛？”\n我： “你觉得一个男的问一个女的要电话还能干嘛？” （当时气氛超暧昧的， 女孩在靠近我）\n女孩：“我不知道”\n这句我不知道难到我了，后来我朋友建议我说：“你的想象力可以再丰富一点”\n8我没那么贪心，不需是要你电话，只要你电话号码就行了\n9不知道就去百度知道查一下给我后你不就知道了嘛 ', '联系感建立');
INSERT INTO `talk_art` VALUES (11379, '女：最近受的打击真不少', '3平时有兼职去健身房当沙包吗\n4以你的心理承受能力和脸皮厚度，不敢相信还有什么能打击到你的\n5崩溃前把银行卡和密码给我呗，别浪费呀\n其他答案：1：最近我越来越帅了，别人看到我，都感觉备受打击。\n2：凤姐一出门，随时随刻都会受到打击的。 （一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11380, '女：原来你是这种人！', '5原来你到现在才了解我，我也得重新评估你的智商\n6是不是有惊喜的感觉\n7你有学过女生密码分类法？（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11381, '妹子 动不动就说“好啊”', '5你能不能稍微有点个性和原则\n6今晚我们玩玩 性虐 怎么样\n3 下次带你去看中国队踢球，看你现场还说不说的出来这两个字', '联系感建立');
INSERT INTO `talk_art` VALUES (11382, '女：这件事我做的还算妥', '1何止妥当，简直是天衣无缝，为了表示鼓励，我决定赏你一个吻，把嘴靠过来\n2不错。 。挺妥当的。 。 。不过，以后这种事还是交给我来做吧\n3由你出马，我绝对是放心的，看都不用看，就知道一定搞砸了\n其他答案：4你给我捏捏背，我再来分析是否妥当。\n5妥当不妥当，起决于明天你是否会给我把臭袜子洗掉。 （一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11383, '我:能认识一下?', '女：为什么?\n我:因为你不像坏人\n女：可是我怎么知道你是不是坏人 或 可是你是坏人\n7没办法，男不坏女不爱呀\n8我要是坏人就直接把你打晕拖走了，哪会用认识你这么没效率的方法\n伪装成这样都能被你认出来我是坏人？人民警察跟你比起来都会脸红呢，你教教我呗', '联系感建立');
INSERT INTO `talk_art` VALUES (11384, '女：你好没有良心~把借口', '5这才证明了你在我心目中的重要地位\n6这是检验你以后是否能做个合格老婆的测试\n7我不会推借口，只会 TD 你', '联系感建立');
INSERT INTO `talk_art` VALUES (11385, '女：好！我养你！', '5那先把这个月生活费打人家卡上嘛\n6那你要每天抱着我睡哦\n7我的泡学目标就是 TD 富婆，看来这个目标实现一半了（慎用）8', '联系感建立');
INSERT INTO `talk_art` VALUES (11386, '女：我想知道，我在你心', '1只能回答你一半，因为目中有，但还没进入心中呢\n2你是无语伦比的，而且是非常令人无语的那种\n3在我心目中。 。 。 。让我想想。 。 。我现在脸红没？女：没有啊（窃喜）\n不可能啊，我应该会为你感到脸红啊\n其他答案： 1：老太婆。2：饭不会做，菜难吃，衣服不会洗。\n3：你的品位倒是可以评价一下，你是怎么看中我的呢？ （一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11387, '女：要不就是你怕冷，就', '5怕冷的话抱着你就好了，干嘛要窝在被子里呢\n6小时候尿床就是这麽回事，然后在被子里不出来直到捂干它为止\n7怕冷就窝被子里，那怕热不是要在冰箱里呆着？', '联系感建立');
INSERT INTO `talk_art` VALUES (11388, '男:改天去找你玩啊', '妹子：找我干嘛，你难道就没有其它喜欢的女孩子?\n九百问达成！特别赠送 5 个答案！！下一次，就是令人激动的 1000 问了，大家的掌声，为舞台上的节目准备好了吗\n1我就喜欢跟比较男人味的女孩子玩\n2干嘛？干你呀\n3喜欢的归喜欢的，打炮的归打炮的，这都不懂呀\n4山珍海味吃多了，偶尔也要吃点清淡的\n你的意思是要我再带一个喜欢的女孩子过来一起双飞？看不出来你还蛮重口味的哦', '联系感建立');
INSERT INTO `talk_art` VALUES (11389, '女：你这么玩不开，还是', '早说嘛，我一直在纠结要不要跟你玩 性虐 呢，快趴下去给我舔脚\n3就是因为你比我还像男人，所以总感觉有点怪怪的，总觉得跟\n你玩自己像 GAY 一样，所以有点放不开\n4非逼着我马上把男性器官对你使用才满意吗\n其他答案： 1：玩的开的应该是女人的大腿。\n2：拿手过来，我让你摸摸，看我是不是男人。 （一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11390, 'GG：以后找个有情的女孩和', '你好好努力，还是有机会的妹子：我肯定会要找个有情的人，肯定不是你\n1女孩子总这么说一套做一套么\n2你找的肯定非常有情，情多到滥情，到处留情的那种\n3到时候再哭着对我唱梁静茹的《可惜不是你》吗', '联系感建立');
INSERT INTO `talk_art` VALUES (11391, '妹子：你老家是广东的，', '1都说距离产生美，我过来看看离老家那么远的女孩子是不是真的好看些\n妹子：看了以后什么感觉呢\n2我们广东好吃珍禽异兽，听说这边有种稀有动物，打算过来弄一只回去，你也许还能帮上忙呢妹子：什么动物九头鸟\n3这边 PUA 超多，我专门过来学习的（慎用）\n其他答案：1：支援湖北的发展来了！2：我跟我朋友猜拳猜输了，只好来这里了。3：你老家是哪的？（一直很想爱你 ）', '联系感建立');
INSERT INTO `talk_art` VALUES (11392, '求教小朋子男：给女孩子', '1她每次说完以后总是带着哈哈或者是一个笑脸，怎么和她开玩笑呢？\n2你是娃哈哈公司的形象代言人么？\n34你要小心点，每次发信息都有在笑，这样虽然很开心，但鱼尾纹很快就出来了呀，但比较起来我还是喜欢开心的女孩子，所以我不会嫌弃你啦，敞开来笑吧\n5是不是跟帅哥聊特别开心\n妹子：BLBLBLLBLB\n你每个信息都在笑呢', '联系感建立');
INSERT INTO `talk_art` VALUES (11393, '妹子：为什么你不会讲本', '1火星人生在地球的话也不会讲火星文呢\n2我一看到你就连自己是哪里人都搞不清楚了，你长得这么能迷惑人，下次去抢银行带着你就万无一失了\n3小时候被人贩子拐走了，现在好不容易才跑回来呢\n其他答案：1：我只会讲本地普通话。2：为什么你不会发嗲，你还是女人呢？ 3：你的思维逻辑有待提高（一直很想爱你 ）', '联系感建立');
INSERT INTO `talk_art` VALUES (11394, '妹子：你也有今天啊！嘿', '1每个人都有今天啊，我又不是穿越时空过来的，我是一天一天实打实走过来的\n2我托你的福才有今天，比不了阁下天天有今天这样的情况\n3今天天气晴朗，万里无云，有什么问题么？', '联系感建立');
INSERT INTO `talk_art` VALUES (11395, '妹子：我不喜欢你！ 或者', '1我知道你不喜欢我，喜欢只是前期的表现形式，现在已经过渡到爱的阶段了\n2还好不是我，不然可就头疼了\n3为你喜欢的那个人默哀', '联系感建立');
INSERT INTO `talk_art` VALUES (11396, '有时候我感觉碰见 9 分美', ' 就脸红了 这是为什么 总是情不自禁的 舞台狼这个你觉得正常吗？\n1我觉得不正常，见到 9 分美女要能自然勃起才正常\n2正常呀，你要看见 5 分女脸红就不正常了\n3这时候可以直接走过去：你看我脸红没？\n妹子：没有/有\n那就奇怪了，我一看见你就脸上火辣辣的，很奇怪呀，你长得也不像辣椒呀还是你喷了辣椒香水呢/ 都是因为你啦，一见你就这样了，呆会我要去见客户，是个欧巴桑，呆会这脸红一直保持过去她要是误会你让我怎么办呀', '联系感建立');
INSERT INTO `talk_art` VALUES (11397, '我感觉第一次约很重要?', '要是拒绝了我就压力很大 如果 妹子 拒绝你 ,你怎么办？1以死相逼：不出来我就死给你看，在你家门口割腕，或从你家楼顶跳下来等等\n2努力赚钱，然后拿钱砸到她肯出来为止\n3任何一次约会都没有你想的那么重要，只是跟朋友出来玩而已，轻松是最适当的情绪，越觉得重要就越会紧张，越紧张就越容易搞砸，如果被拒绝，先想想自己邀约方式有没问题，如果没问题就隔几天再约，再拒绝就过几天再约，还拒绝就换下一个，所以备胎一定要多，这次约会就显得不那么重要了，自然也就不那么紧张了，约会过程就能越自然了，当然，对于初学者再自然的约会你也得准备些惯例和技巧备着可以时不时给自己加分呢', '联系感建立');
INSERT INTO `talk_art` VALUES (11398, '妹子：我看你就是披上羊', '1我看你就是脱了衣服，也没法让男人变成狼\n2厉害呀，你就是传说中的欲女猎人吗？别想打我主意，离我远点\n3我的最终目标不是狼，我要成为墨菲斯那样的狮子（慎用）', '联系感建立');
INSERT INTO `talk_art` VALUES (11399, '妹子：我可以理解为，你', '1早知道你不聪明，但没想到你的理解能力能差成这样\n2没错，我求你有多远走多远。。。\n3你觉得自己长得像个球吗？', '联系感建立');
INSERT INTO `talk_art` VALUES (11400, '对方问一定要问年龄，让', '1看你这么执着，该不会是问了年龄去买六合彩吧\n2要想知道帅哥的年龄得先自报家门才算礼貌的\n3美女，年龄有那么重要吗？我们不是在相亲呢', '联系感建立');
INSERT INTO `talk_art` VALUES (11401, '人家不喜欢当妹妹', '1我说的是红楼梦里宝哥哥和林妹妹那样的关系\n2不是叫你当我妹妹，是叫你当我老婆妹妹，一般正室都是叫二奶妹妹的，小的才会受宠哦\n3原来你喜欢爱上弟弟的感觉，看不出来你喜欢老牛吃嫩草哦\n其他答案：好吧！去我家，我介绍个小弟弟给你认识一下！（liushuijin）\n拜托，想当我老婆的女的实在是太多了，怎么的也的先排好队！（一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11402, '如果 妹子 总是撒娇而且很', '你又不好拒绝，又不想做怎么办~ 比如：不要嘛！你去拖地，做家务好吗？ \n这该怎么办\n！1我想，如果你在床上我脱你内裤时这么说，会比较管用2一不做二不休，要不干脆孩子我也帮你生掉得了3亲爱的，你爱我吗？\n妹子：爱/不爱/做完这些卫生就爱\n爱我还叫我做这些，你忍心吗？/不爱我凭什么做这些呀/原来你的爱是要讲条件的，我的心被你伤到了\n妹子：那你怎么就忍心让我做呢\n就是因为爱你才不忍心亲眼看着你变成一个好吃懒做的女人，女孩子要贤惠才会好看才惹人疼爱的嘛', '联系感建立');
INSERT INTO `talk_art` VALUES (11403, '妹子：反向思维你用的太', '1你说的是指你想我而我不想你这种反向思维么，我承认\n2烂点对胃好，容易消化\n3你用得好，给我来一个见识下呗', '联系感建立');
INSERT INTO `talk_art` VALUES (11404, '妹子：你不要那么粗鲁好', '1关于这个粗我还真没办法改变，爹妈生下来就这个尺寸\n2你不要那么矜持好吗~火辣点！CO男 ON BABY！\n3我这还叫粗鲁？明天得带你去猪圈见识下猪是怎么配种的', '联系感建立');
INSERT INTO `talk_art` VALUES (11405, '\\', '打扰别人是有点冒昧~那我该怎么去开场，再给一个理由消化她的不满~做到吸引！\n1你好，刚不小心瞄到你这本书，我有看豆瓣推荐过哦（只要不是教科书。。），一直想看但不知道到底怎么样，给我讲讲阅后感呗，先知（她先看所以叫她先知，此为话题后续的伏笔，她给你讲内容的话就以报恩为理由收号为约会后续）\n2你好，我在那边被你看书时的气质吸引了，就想过来认识你，但觉得打扰你看书又不太好，我就这么一直等着，想等你看完了再过来，结果你完全沉醉于书中的世界，我一看书剩下的页数，结合你的阅读速度粗略一算，觉得自己等不到明天早上，所以就冒昧过来了，呵呵，你看的是什么书呢，给我介绍下呗\n3同学，你好用功哦，在温习功课吗，咦！不是教科书呀？那。。。也挺用功的（表情神态同谜男假指甲惯例），除了教科书还看得这么认真，这书应该还不错，我平时也很爱看书的，各种古典名著，像什么金瓶梅肉蒲团我都看过好几遍的，你这是什么书呢，给我介绍下呗', '联系感建立');
INSERT INTO `talk_art` VALUES (11406, '妹子：哎！那算了，你都', '1你这个人怎么一点爱心都没有，对于健忘的人就是要不停打扰他才能对他加深记忆有帮助呀\n2健忘是假装的啦，主要是筛选掉一批没诚意的，非诚勿扰嘛，结果你马上就原形毕露哦\n3我哪里健忘了，我记性可好了，对了，你是哪位？', '联系感建立');
INSERT INTO `talk_art` VALUES (11407, '妹子：我才不呢！', '1我有点小感动了，你裁布是要给我做衣服吗？\n2只要你答应，我愿意马上为你付出我目前所有的蛋白质\n3我就喜欢这种欲拒还迎的感觉，可刺激了，赶紧再来一个', '联系感建立');
INSERT INTO `talk_art` VALUES (11408, '妹子：说实话，你的思维', '1确实不怎么样，只比你好一截而已，太失败了\n2你可以去角逐金马奖了，你说起假话来真像那么回事呢\n3恩，跟你在一起呆久了，思维都变迟钝了', '联系感建立');
INSERT INTO `talk_art` VALUES (11409, '妹子：就算你不要我，我', '1我在街上看到很多男生追的。。。。一般是小偷\n2我不要你了你就打算去参加马拉松比赛减肥吗？\n3对于这点，我知道是我的错，我诚恳的道歉，是我害了他们', '联系感建立');
INSERT INTO `talk_art` VALUES (11410, '妹子：或许我这样做，你', '1我觉得，你只有把我从楼顶推下去我才会有失落的感觉\n2一直没跟你说，其实从见到你的那一刻起我已经很失落了\n3有玩蹦极那么刺激么?', '联系感建立');
INSERT INTO `talk_art` VALUES (11411, '妹子：你说：“开始我还', '1我以为是苍井空，后来想想她也不会说国语啊，所以就知道不是了\n2以为是我未来老婆呢，没想到还真是\n3以为是我一个哥们，他挺有男人味的', '联系感建立');
INSERT INTO `talk_art` VALUES (11412, '妹子：切，我才不给你按', '1别那么心急好吗？再怎么样也不能跳过前戏嘛\n2那你是要用嘴帮我服务么？\n3如果我给小费呢？妹子：给多少看你表现咯', '联系感建立');
INSERT INTO `talk_art` VALUES (11413, '妹子：你知道吗~我的心被', '1你的心？不是早就被狗吃了吗？\n2去买个速效救心丸吃下\n3莫非我无意间练会了九阴正经里的摧心掌', '联系感建立');
INSERT INTO `talk_art` VALUES (11414, '求教舞台狼：不小心让她', '妹子 总是说我双子座的人花心，不可靠，滥情什么的！\n下次她在这么说，你教教我该怎么反击！又可以把她搞上手！\n1双子之所以给世人这个印象，只是因为没有遇到他的真命天女，你，对自己没自信吗？\n2双子座还不可靠啊？拜托，两个人诶\n3舞台男也是双子的，你看看人家多专一（慎用）\n其他答案：1：想不到你对花心的男人这么敏感，看来你伤的挺重的呀。2：我们的星座般配吗？\n3：双子座的人最可悲，心都被人家给刮花了。4错！男人是没有心的\n妹子：心呢？ 花出去了（一直很想爱你）', '联系感建立');
INSERT INTO `talk_art` VALUES (11415, '妹子：你说实话撒，我又', '1你这句话很耳熟，小时候，我犯错妈妈就跟我说过这句话，结果是---一顿好打，我当时就发誓不再相信女人\n2不是怕你怪我，我怕你接受不了事实昏过去\n3你先说说，我说了实话能给我什么好处吧', '联系感建立');
INSERT INTO `talk_art` VALUES (11416, '妹子：什么时候把你女友', '1你这个说话的语气让我想起小时候要求爸爸带我去动物园看猴子的情景\n2那可是个大工程，每天见一个的话，你得忙到年底才能见完，还不算中途加入的哦\n3买一个戒指盒大小的精致小盒子，跟她说：我无法带她来，但可以给你看看照片，打开盒子就行（然后，盒子打开是一面小镜子展现在她面前）', '联系感建立');
INSERT INTO `talk_art` VALUES (11417, '妹子：你现在都不诚实！', '1如果你还想有以后的话，最好先懂得相信对方\n2我宁愿选择善意的谎言，也不喜欢残酷的诚实\n3你可以不相信我，但你得相信自己的眼光', '联系感建立');
INSERT INTO `talk_art` VALUES (11418, '你觉得什么才是真正的社', '1举例一：路边的乞丐能凭着敏锐的洞察力知道哪个路人比较有可能施舍给自己\n2举例二：夜总会的小姐能够在一瞬间就判断出哪位客人是真正的买单者，给小费会比较大方\n3举例三：能快速打开各种社交场所里的组合，并游刃有余地 GA男 任何目标', '联系感建立');
INSERT INTO `talk_art` VALUES (11419, '妹子：不要用你的那些小', '1在扑克牌中，每一个人都能找到自己的定位，而小丑就是王\n2在欧洲中世纪，小丑是只有宫廷里才有的高贵表演，我也不会怪你的孤陋寡闻\n3恩我错了，对你小把戏是行不通的，我得用我这根大家伙', '联系感建立');
INSERT INTO `talk_art` VALUES (11420, '我没有手机。给一个吧。', '1我靠！遇见富婆了！我只要手机号码，你却送我一辆车哦\n2我喜欢含蓄的妹妹，打算 QQ 里再告诉我电话号码是吧\n3但我不是 QQ 流呀，我是话术流的，必须打电话呢（恶搞）', '联系感建立');
INSERT INTO `talk_art` VALUES (11421, '1妹子：怎么样？我终于报', '1可喜可贺，赶紧到酒店摆两桌庆祝下吧，我肚子都饿了\n2仇恨会改变一个人，你看你现在都扭曲成什么样了\n3冤冤相报何时了，施主，放下屠刀，立地成佛，回头是岸呀', '联系感建立');
INSERT INTO `talk_art` VALUES (11422, '想得美', '1我知道你想自己更美点，但还是要告诉你事实和理想往往有很大差距的\n2没有想得美，你比我想象中要难看呢\n3卡耐基说过，敢想才是成功的第一步呢', '联系感建立');
INSERT INTO `talk_art` VALUES (11423, '求教舞台狼：以你的理解', '1你 KINO 她，她 KINO 你\n2你打她，她踹你\n3你们聊天不是单方面的你讲她听，而是她也参与进来，根据你饿话题分享很多她的故事，这就是互动', '联系感建立');
INSERT INTO `talk_art` VALUES (11424, '妹子：对!没错，你要是有', '1不要说嫁不嫁了，我要是有钱人，你认为我还会在这里跟你讲话么？\n2钱贿赂得到的，不是爱情，是欲望（感谢冷爱）\n3等你嫁给了我，我把你卖窑子里去，我就有钱了', '联系感建立');
INSERT INTO `talk_art` VALUES (11425, '妹子：我们之间的问题已', '1有问题，找百度\n2多重？拿个电子秤能称出来么？称不出来那就是还不够重\n3我是一个后知后觉的人，也很在乎你的感受，你说的这些问题如果让你觉得难受，那我们可以躺（坐）下来好好沟通沟通，我相信我们可以一起解决它的（躺和坐差一个字可以将调侃变成诚恳，依情况选择）', '联系感建立');
INSERT INTO `talk_art` VALUES (11426, '对于那种喜欢安静的女生', '（其实我不认为她们情愿安静）你说什么她们都静静的听着，你邀请她们去玩，开心。她们都会用肢体语言表明玩的开心，但是就是话少，这是为什么呢~？我老是以为我做的不够好，还是有别的方法让她们打开话匣子！\n1找个开锁的师傅\n2带她们去参加相声培训班\n3我觉得，两个人在一起聊天最开心的是能够互动，但你好像不太爱说话，这样我感觉有点怪怪的， 你能告诉我原因么（话题随即转移到她们谈论自己，在引导其参与互动的同时你还能更多地了解她们）', '联系感建立');
INSERT INTO `talk_art` VALUES (11427, '\\', '1我什么时候跟你说过甜言蜜语了，我压根就不是一个会浪费口水的人\n2你这么可爱，我怎么舍得打你呢？再说你又不是傻子，我打你你怎么可能不动来让我打呢，你得跑嘛\n3我能感觉到你是务实的人，甜言蜜语这种东西对你不起作用的，那咱们谈个价吧', '联系感建立');
INSERT INTO `talk_art` VALUES (11428, '女：发张可爱的自拍', ' 男：我是照骗不美     \n  女：那就让美图秀秀，把我这颗心一起骗走好了（要照片）', '联系感建立');
INSERT INTO `talk_art` VALUES (11429, '妹子：我要面包，不要爱', '1这个。。。很多发廊和桑拿的 妹子 都是这么选的\n2刚还在纠结没有爱的 TD 不道德呢，谢谢你解开我心结，我马上去给你买面包，你去开个房等着我\n3来大姨妈了当然不能爱 爱啦，这样对你身体不好嘛', '联系感建立');
INSERT INTO `talk_art` VALUES (11430, '女：女生老是推迟你们的', ' 男：感觉我们两个的唯美邂逅，要变成一场拉锯战了', '联系感建立');
INSERT INTO `talk_art` VALUES (11431, '妹子：我认为，我应该跟', '1牵着手的话，你们会不方便打伞的2面对风风雨雨，我觉得这个男人为你叫辆的士显得更实在\n3你这个想法的最后结果确实是白头到老-------白了少年头，空悲切', '联系感建立');
INSERT INTO `talk_art` VALUES (11432, '女：你太有才了', '  男：你不要套路我，这样我会觉得你只想得到我，而不是欣赏我的才华横溢', '联系感建立');
INSERT INTO `talk_art` VALUES (11433, '妹子：说实话~我是带着诚', '1你这么诚实我也不能隐瞒，我也是带着诚意来寻找性伴侣的\n2你的诚意在哪里呢？给我说说吧，我挺感兴趣的\n3又没跟你玩真心话大冒险，忽然说这个干嘛？', '联系感建立');
INSERT INTO `talk_art` VALUES (11434, '女：你太有才了', '  男：一身的才华横溢，只为和你邂逅在红唇滚滚之中', '联系感建立');
INSERT INTO `talk_art` VALUES (11435, '妹子：我就不相信我找不', '1恩，我也不相信你找得到男朋友\n2最后如果实在找不到，就用百度搜搜吧\n3这句话你跟我说说还行，可别出去说，不然都没男人敢上街了', '联系感建立');
INSERT INTO `talk_art` VALUES (11436, '女：你太有才了', '  男：饱读诗书三百卷，只为遇见你', '联系感建立');
INSERT INTO `talk_art` VALUES (11437, '妹子：哎 为什么你们男人', '1 注意到了呀，但我想你的气质使大家都错把你认成男人了\n2（四下张望一圈）：咦？刚刚谁在跟我说话\n3 那是因为你穿着衣服', '联系感建立');
INSERT INTO `talk_art` VALUES (11438, '女：你骗我', '    男：哪有？你自己过来摸摸我的良心，一片赤诚', '联系感建立');
INSERT INTO `talk_art` VALUES (11439, '女：女生称呼你“亲”', '      男：我没亲你，你别诬陷老子.......我隔壁就住着警察叔叔', '联系感建立');
INSERT INTO `talk_art` VALUES (11440, '女：你敢欺负我就不理你', '男：你要走，我不留，你要敢留，我他妈就惯坏你，把你惯的上天入地无所不能', '联系感建立');
INSERT INTO `talk_art` VALUES (11441, '女：约会干什么', '    男：来邂逅一个不一样的烟火', '联系感建立');
INSERT INTO `talk_art` VALUES (11442, '女：我胸小', '   男：看来我这双治愈系的大手，就是抚慰你小小心灵的最佳尺寸了', '联系感建立');
INSERT INTO `talk_art` VALUES (11443, '女：我今天玩的很开心哦', '24这次只是免费体验版，下次我们再一起玩就是正式付费版了哦\n25穿着衣服跟我玩都这么开心了，不穿衣服跟我玩不是要开心死掉？\n那当然，开心法则我可不是白学的（慎用）', '文字进逻');
INSERT INTO `talk_art` VALUES (11444, '\\', '18我一拳打过去，不信你不动\n19一看你就没经验，打动一个人心的过程，用肉眼是看不到的你又不是沙包，我干嘛要打动你', '文字进逻');
INSERT INTO `talk_art` VALUES (11445, '女：你可以告诉我你到底', '你能告诉我这跟前前个问题除了句型上不同以外还有什么区别吗？10我在想昨天在外面吃饭老板到底有没有少找我一块钱\n11我在想现在买的房子 70 年后到底会被怎么处理我在想炎 黄 子孙到底什么时候才能够真正崛起', '文字进逻');
INSERT INTO `talk_art` VALUES (11446, '女：你恨对你说慌话的人', '23如果你已病入膏肓无可救药，别人骗你说还可以治愈的，你会恨他吗？\n24不会的，小时候我问妈妈我是从哪里来的，我妈说天上掉下来的，我到现在都不恨她\n如果我现在跟你说你长得很漂亮，你会恨我吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (11447, '女：说！你有什么阴谋！', '17阴谋谈不上，我只是想跟你借点钱\n18我最大的阴谋，就是想趁你不注意把你娶回家做老婆我没有阴谋，只有阴茎', '文字进逻');
INSERT INTO `talk_art` VALUES (11448, '女：或许也是我太蠢了吧', '9英雄所见略同\n10不管你蠢成什么样，我都会对你不离不弃的亲爱的，别着急，明天我就给你买点脑白金补补', '文字进逻');
INSERT INTO `talk_art` VALUES (11449, '\\', '今天早上删，晚上后悔了\n1 有什么好的杀毒软件推荐？今天电脑中了巨毒，自动将阿里旺旺和QQ 的联系人删了一大批，我要崩溃了\n2 你知道我现在有多郁闷吗？女：？\n今天照着书上的内容把你的 QQ 删掉做了一个测试，如果我在你的心目中占足够的比重， 你会快速加我回来，结果你没有。 。。\n3 我在腾讯工作的一个哥们告诉我今天他们公司系统被攻击出现了BUG,将同一个 Q 号删 5\n次再加 5 次系统就会自动增加 5 个 Q 币，据说腾讯正在抢修补救，随时会被修好，我们的抓紧刷点 Q 币呀', '文字进逻');
INSERT INTO `talk_art` VALUES (11450, '女：黄鼠狼给鸡拜年！', '16你一定要把自己比喻成鸡吗？\n17黄鼠狼也是生物，它也需要嫖娼的嘛\n这个比喻很不恰当，黄鼠狼给鸡拜年是想吃鸡，我又不想吃你。。。我只想上你（慎用）', '文字进逻');
INSERT INTO `talk_art` VALUES (11451, '女：真的不知道你心里到', '1 我在想明天带你去哪吃好吃的\n8在想苍井老师和松岛老师哪个更受大家爱戴我在想你每天到底在想些什么？', '文字进逻');
INSERT INTO `talk_art` VALUES (11452, '碰到一个小妹妹', '我：放心，我不会在乎你年纪太小的，别自卑。\n女：我还在乎你年纪太大呢！\n22没错，我虽然年纪大，但下半身却很年轻\n23那你还不叫叔，脱裤子，叔叔给你体检何止年纪，我还有一个地方更大呢', '文字进逻');
INSERT INTO `talk_art` VALUES (11453, '女：你有手淫过吗？', '15从小我就立志一定要把第一次留给我未来的新娘，包括手淫\n16你是想叫我帮你手淫吗？\n从没手淫过，我买了充气娃娃的', '文字进逻');
INSERT INTO `talk_art` VALUES (11454, '女：那你又有陪过我吗，', ' 看来你是真不知道，最近严打 三 陪呢\n2 你又不给小费。 。\n3 亲爱的，是我错了，为了咱们的将来所以我把时间都花到工作上去了，我以为你会明白所以也没特别跟你讲过，这是我的疏忽，我会改善的，好吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (11455, '请说一些加 妹子 QQ 的幽默', '21你吃过河豚吗？我刚自己煮了一条吃了，不知道会不会有事\n22本人非办证工作者也不是推销人员，请验证\n看你的样子很面熟，我们是在哪里见过吗。 。 。 。 。 。 。好吧，我承认我很老套。 。', '文字进逻');
INSERT INTO `talk_art` VALUES (11456, '女：你看过 A 片吗？什么', '1 看之前像刘翔打了兴奋剂一样极度亢奋，看完之后像刘翔刚跑完 5万米以后浑身乏力\n15没看过诶，要不我们一起看看到底什么感觉\n16看过，眼睛会很疼\n女：眼睛怎么会疼呢你试试半小时不眨眼看眼睛会不会疼', '文字进逻');
INSERT INTO `talk_art` VALUES (11457, '女：我会很乖不会让你生', '7如果你每天都像现在这个样子，估计我可以活到一百岁呢\n8我就受不了你这么乖，赶紧说，想吃点什么好吃的，穿点什么好看的？我恨不得马上就全部买给你\n真的？那赶紧口爆一个（慎用）', '文字进逻');
INSERT INTO `talk_art` VALUES (11458, '我-听你这么说真想亲你一', '妹子-啊...嫌弃小痘痘~\n我-考！我亲你，你嫌弃什么~\n妹子-嫌弃你脸上的环形山和小痘痘~嘿嘿~\n21这证明我是一个站在山顶上满面青春的男人\n22就是因为想你睡不着觉才害我内分泌失调变成这样的，你更加应该补偿我了\n23满面的环形山，山上种着很多豆豆，要给它们浇水才能茁壮成长，但水从哪来，从你身上来，所以我是来取水的（慎用）', '文字进逻');
INSERT INTO `talk_art` VALUES (11459, '女孩说,矛盾,怎麽回', '你赚到了， 一般还在矛盾中的女孩子只要看到我都会毫不犹豫地选择我而放弃其他男人的\n14我很喜欢他写的《林家铺子》\n15我的毛又长又硬，扎在肉上都会痛，一点也不钝的', '文字进逻');
INSERT INTO `talk_art` VALUES (11460, '你会一直在我身边吗', '6我又不是藤原佐为，你也不是近藤光\n7当然不会在你身边，那多没意思，我要进入你身体里会的，TD 前会一直在的', '文字进逻');
INSERT INTO `talk_art` VALUES (11461, '用故事引诱 妹子，然后', '我：真的想知道？\n女：不说拉倒\n1 从来都是我 TD 别人，想不到今天终于遇到一个主动拉倒我的\n26单纯拉倒没关系，你不会拉倒我后还要在地板上拖吧那说了是不是就可以 TD 你呢', '文字进逻');
INSERT INTO `talk_art` VALUES (11462, '\\', '女：可以啊！\n21你是我遇见的最友善的女孩子，我决定请你喝点东西，想喝点什么？\n22你答应得这么爽快，我接下来的台词都白准备了呢。 。 。\n23在冒昧问一下，你是只跟我交朋友才这么豪爽，还是跟所有陌生人交朋友都这么豪爽呢\n女：只跟你/都一样\n谢谢，你令我心情一下子灰常好，我要请你吃饭/你说谎哦，要是那边那个大叔（指一个附近最猥琐的人）你也毫不犹豫跟他做忘年交吗？', '文字进逻');
INSERT INTO `talk_art` VALUES (11463, '一说到 妹子 的相貌时候', '女：我都不觉得自己有气质，我觉得我好丑。 。\n遇到这些话真不知道怎样答，安慰她，我怕像其他男人一样讨好她，这该怎么回\n首先要看对方的样子是不是真不怎么样，如果确实低分，那千万不要再调侃她，如果本身长得高分还要故意这么装逼，那就毫不犹豫地调侃吧\n1（针对低分）你本身长得就属于可爱类型的嘛，而且再好看的女人，到了某个年龄段都是一样，不过这也不重要，因为我注重的女人的，从来都只有内涵\n2（针对高分）其实丑点真没关系，关键是要有内涵，你觉得自己有内涵吗？\n3（针对装逼的高分）长相是父母注定的，丑并不是你的错，但你确实不应该出门，更不应该让我看到，我睡觉要是不幸做噩梦你得全额赔偿精神损失费', '文字进逻');
INSERT INTO `talk_art` VALUES (11464, '那你保护我啊', '6恩，保护女人是男人的天职，我这就去买套套\n7那你站着别动，我在你周围画一个圈，千万别出来哦，不然被妖怪抓走了我不负责的\n8我倒是很愿意保护你，不过我觉得没有那个坏蛋会对你有兴趣呢', '文字进逻');
INSERT INTO `talk_art` VALUES (11465, '三个字，八个字母，说出', '1I LOVE YOU ....你想对我说就直说，干嘛非得要我帮你说出来\n2QIN YI XIA---亲一下\n3 我最讨厌中国人放着好好的国语不说，非对着同胞放洋屁', '文字进逻');
INSERT INTO `talk_art` VALUES (11466, '女：希望我的选择是对的', '21没问题的，这个概率比选择体育彩票头奖大多了\n22是呀。 。 。你从小到大经历了这么多次选择，总也得对一次吧\n23肯定对不了，因为我还没给你幸运之吻呢', '文字进逻');
INSERT INTO `talk_art` VALUES (11467, '我旅游时候给她发信息，', '你就天天气我吧！欺负我哪也去不了。你怎么不说带我一起去\n13天天气你的不是我，是天气预报\n14你怎么会哪也去不了，你跟我隔这么远，还能一直来到我心里呢\n真把你带来了，这边女粉丝们就不敢近我的身了', '文字进逻');
INSERT INTO `talk_art` VALUES (11468, '我又胖了（其实还好，不', '5你希望我说还好，不怎么胖是吧？做梦！\n6再加把劲长肉吧，我一直想亲眼看看人变猪经历的过程感动呀，你一直孜孜不倦地在为我抱起你的困难程度添砖加瓦呢', '文字进逻');
INSERT INTO `talk_art` VALUES (11469, '妹子 的一闺蜜看见我和别', '跑过来告诉我：你如此的不堪和肮脏！求解！\n25你知道有个游戏叫国王游戏吗，输的人要听赢的人命令去做任何一件事，我只想说我现在正好是那个游戏的输家\n26请不要血口喷人，我哪里脏了，刚洗的澡诶那是我干妹妹，她有哮喘，刚发病了我帮她做个人工呼吸而已', '文字进逻');
INSERT INTO `talk_art` VALUES (11470, '等我想学打球的时候再找', '20好的，记得带够学费\n21想约我吃饭的时候更欢迎你来找我\n你这个人能不能不这么现实，非得有求于人才联系呀', '文字进逻');
INSERT INTO `talk_art` VALUES (11471, '牵了妹妹手', '妹妹把正牵着的手抬起来 用另外一只手 指着 正牵着的手 笑着用质问的眼神看着我~\n潜台词：你不规矩噢~还不放开（我是这么理解的）\n12你在实际解释示范贼喊捉贼这句成语吗\n13你知道吗，据说两样东西到了一定的吸引程度，就会不自觉合到一起呢，还好刚不是在跟你面对面说话。 。 。\n我手冷(我手热)借你的给我暖和（冷却）下，不要钱吧', '文字进逻');
INSERT INTO `talk_art` VALUES (11472, '我很胖的', '4肚子胖还是胸胖\n5这才好，咱们买一个枕头给你用就行了\n正好，这样我就省的买杠铃了', '文字进逻');
